-- ----------------------------
-- Table structure for account
-- ----------------------------
DROP TABLE IF EXISTS `account`;
CREATE TABLE `account` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `user_name` varchar(80) NOT NULL,
  `full_name` varchar(255) DEFAULT NULL,
  `shop_name` varchar(255) DEFAULT NULL,
  `password` varchar(255) DEFAULT NULL,
  `address` varchar(255) DEFAULT NULL,
  `avatar` varchar(255) DEFAULT NULL,
  `mobile_phone` varchar(50) DEFAULT NULL COMMENT 'so dien thoai',
  `create_time` int(11) DEFAULT '0',
  `modify_time` int(11) DEFAULT NULL,
  `status` tinyint(4) NOT NULL DEFAULT '0',
  `limit_shop_product` tinyint(5) DEFAULT '0' COMMENT '0: không giới hạn SL đăng sản phẩm',
  `block_time` int(11) DEFAULT '0',
  `last_ip` varchar(20) DEFAULT NULL,
  `last_login` int(11) DEFAULT '0' COMMENT 'thời gian truy cập cuối cùng',
  `province_id` tinyint(3) DEFAULT '0',
  `email` varchar(255) DEFAULT NULL,
  `website` varchar(255) DEFAULT NULL,
  `contact` varchar(255) DEFAULT NULL COMMENT 'lan thao tac cuoi cung cua user nay',
  `create_user_name` varchar(255) DEFAULT NULL,
  `create_user_id` int(11) DEFAULT '1' COMMENT 'user tao account',
  `district_id` tinyint(3) DEFAULT '0',
  `id_card` varchar(50) DEFAULT NULL,
  `is_shop` tinyint(2) DEFAULT '0' COMMENT '1: là shop, 0: account thường',
  `id_shop_primary` int(11) DEFAULT '0' COMMENT 'Id shop chính',
  `fax` varchar(50) DEFAULT NULL,
  `birth_day` date DEFAULT NULL,
  `gender` tinyint(1) DEFAULT NULL,
  `permit_action` varchar(255) DEFAULT NULL COMMENT 'list danh sách quyền: 1,2,3',
  `gids` varchar(10) NOT NULL DEFAULT '0' COMMENT 'Nhom Account (VD: 1,2 - thuoc nhom 1 va 2)',
  `page_view` int(11) DEFAULT '0' COMMENT 'số lượt visit',
  `is_active` tinyint(4) DEFAULT '0',
  `clock_user` tinyint(2) DEFAULT NULL COMMENT '0:khóa user, 1: đang hoạt động',
  PRIMARY KEY (`id`),
  UNIQUE KEY `user_name_unique` (`user_name`),
  KEY `user_name` (`user_name`),
  KEY `home_phone` (`is_shop`),
  KEY `mobile_phone` (`mobile_phone`)
) ENGINE=MyISAM AUTO_INCREMENT=6 DEFAULT CHARSET=utf8;

