-- ----------------------------
-- Table structure for account_permit
-- ----------------------------
DROP TABLE IF EXISTS `account_permit`;
CREATE TABLE `account_permit` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT 'O: group, 1: user',
  `ref_id` int(11) NOT NULL DEFAULT '0' COMMENT 'id of group or user',
  `pids` text NOT NULL COMMENT 'list of permission',
  `cids` text NOT NULL COMMENT 'list of category can actions',
  `cat_id` text COMMENT 'category có quyền thao tác',
  `depart_id` text COMMENT 'depart có quyền thao tác',
  `cat_product_id` text COMMENT 'category của sản phẩm có quyền thao tác',
  PRIMARY KEY (`id`),
  KEY `ref_idx` (`ref_id`,`type`) USING BTREE
) ENGINE=MyISAM AUTO_INCREMENT=86 DEFAULT CHARSET=utf8;

