DROP TABLE IF EXISTS `acc_lock`;
CREATE TABLE `acc_lock` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `time` int(11) NOT NULL DEFAULT '0' COMMENT 'thời gian bắt đầu',
  `time_expire` int(11) NOT NULL DEFAULT '0' COMMENT 'Thời gian có hiệu lực',
  `user_id` int(11) NOT NULL DEFAULT '0',
  `user_name` varchar(255) DEFAULT NULL,
  `type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '0:theo ngày; 1: vĩnh viễn; 2: kiểm duyệt;3:vĩnh viễn + cookies',
  `note` varchar(255) DEFAULT NULL,
  `admin_id` int(11) DEFAULT NULL COMMENT 'id cua admin khoa thanh vien',
  `admin_name` varchar(50) DEFAULT NULL COMMENT 'ten cua admin khoa thanh vien',
  `unlock_time` int(11) NOT NULL DEFAULT '0' COMMENT 'Thời gian mở khoá (nếu có)',
  `unlock_user` varchar(50) DEFAULT NULL COMMENT 'Người mở khoá',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM DEFAULT CHARSET=utf8;

