-- ----------------------------
-- Table structure for slm_category
-- ----------------------------
DROP TABLE IF EXISTS `slm_category`;
CREATE TABLE `slm_category` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `parent_id` int(11) DEFAULT '0',
  `name` varchar(255) NOT NULL DEFAULT '',
  `linkview` varchar(255) DEFAULT NULL,
  `images` varchar(255) DEFAULT NULL,
  `level` tinyint(10) DEFAULT '0',
  `description` text,
  `type` tinyint(2) DEFAULT '0' COMMENT '0: danh mục tin tức, 1: danh mục sản phẩm',
  `status` tinyint(1) DEFAULT '0',
  `order` smallint(4) DEFAULT '0',
  `date` int(11) DEFAULT '0',
  `update_date` int(11) DEFAULT NULL,
  `create_user_id` int(11) DEFAULT NULL,
  `update_user_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `cat_idx` (`status`),
  KEY `parent_idx` (`parent_id`)
) ENGINE=MyISAM AUTO_INCREMENT=29 DEFAULT CHARSET=utf8 CHECKSUM=1 DELAY_KEY_WRITE=1 ROW_FORMAT=DYNAMIC;

