DROP TABLE IF EXISTS `slm_category_news`;
CREATE TABLE `slm_category_news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) NOT NULL,
  `parent_id` int(11) DEFAULT '0',
  `description` text NOT NULL,
  `images` varchar(255) DEFAULT NULL,
  `level` int(4) NOT NULL COMMENT 'level của category',
  `order` smallint(4) DEFAULT '0' COMMENT 'Vị trí hiển thị của category',
  `status` tinyint(1) DEFAULT '1' COMMENT '0: Ẩn ,1 : Hiện',
  `date` int(11) DEFAULT NULL,
  `update_date` int(11) DEFAULT NULL,
  `user_id` int(11) DEFAULT NULL,
  `update_user_id` int(11) DEFAULT NULL,
  PRIMARY KEY (`id`),
  KEY `position` (`order`),
  KEY `status` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=5 DEFAULT CHARSET=utf8;

