-- ----------------------------
-- Table structure for slm_news
-- ----------------------------
DROP TABLE IF EXISTS `slm_news`;
CREATE TABLE `slm_news` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `images` varchar(255) DEFAULT NULL,
  `images_other` text COMMENT 'lưu trữ ảnh trong nội dung bài viết',
  `images_other_temp` text COMMENT 'lưu file anh tam thoi',
  `name` varchar(255) DEFAULT NULL,
  `cat_product_id` int(11) DEFAULT NULL,
  `cat_product_name` varchar(255) DEFAULT NULL,
  `cat_new_id` int(11) DEFAULT NULL,
  `cat_news_name` varchar(255) DEFAULT NULL,
  `description` varchar(500) DEFAULT NULL,
  `content` text CHARACTER SET utf8 COLLATE utf8_unicode_ci COMMENT 'Nội dung tin tức',
  `order` tinyint(4) DEFAULT NULL,
  `hot_news` tinyint(1) DEFAULT '0' COMMENT '1: tin nỏi bật; 0: tin bình thường',
  `comment_count` int(4) NOT NULL DEFAULT '0',
  `review_count` int(11) NOT NULL DEFAULT '0',
  `status` tinyint(1) NOT NULL DEFAULT '0' COMMENT '1: hien, 2:noi bat, 0: an',
  `is_read` int(1) NOT NULL DEFAULT '0' COMMENT 'moi lan co comment moi thi =1=> thong bao cho biet co comment moi',
  `create_user_id` int(11) DEFAULT NULL,
  `create_user_name` varchar(255) DEFAULT NULL,
  `modify_user_id` int(11) DEFAULT NULL,
  `modify_user_name` varchar(255) DEFAULT NULL,
  `date` int(11) DEFAULT NULL,
  `modify_time` int(11) DEFAULT NULL,
  `del_flag` tinyint(1) DEFAULT '0' COMMENT '0: Chưa xóa. 1: Xóa',
  `create_time` int(11) DEFAULT '0' COMMENT 'thời gian tạo tin',
  `start_time` int(11) DEFAULT '0' COMMENT 'thời gian bắt đầu mở tin',
  `end_time` int(11) DEFAULT '0',
  PRIMARY KEY (`id`),
  KEY `idx_user_id` (`status`)
) ENGINE=MyISAM AUTO_INCREMENT=120 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of slm_news
-- ----------------------------
INSERT INTO `slm_news` VALUES ('59', '0.941056001397106293.JPG', 'a:2:{i:0;s:24:\"0.941056001397106293.JPG\";i:1;s:24:\"0.919429001397106325.JPG\";}', null, 'Chuyện thánh vật ở đình làng Lệ Mật', null, null, '25', '', '\r\n	Theo sử s&aacute;ch ghi lại, đ&igrave;nh Lệ Mật được x&acirc;y dựng từ thế kỷ 12, đời vua L&yacute; Nh&acirc;n T&ocirc;ng (1072 - 1127, cũng c&oacute; nơi ghi 1066 - 1127) tại x&atilde; Việt Hưng thuộc tổng Gia Thụy, huyện Gia L&acirc;m, phủ Thuận An, trấn Kinh Bắc (nay l&agrave; phường Việt Hưng, quận Long Bi&ecirc;n, H&agrave; Nội). L&agrave; di t&iacute;ch lịch sử h&agrave;ng ngh&igrave;n năm tuổi, đ&igrave;nh Lệ Mật c&oacute; rất nhiều huyền t&iacute;ch b&iacute; ẩn v&agrave; l&agrave;', '<p>\r\n	<strong>Truyền thuyết về đức th&agrave;nh ho&agrave;ng văn v&otilde; song to&agrave;n</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Theo &ocirc;ng &ocirc;ng Trương Văn Hoạt (SN 1948), ph&oacute; Ban quản l&yacute; di t&iacute;ch đ&igrave;nh l&agrave;ng Lệ Mật, đến nay, c&oacute; rất nhiều truyền thuyết b&iacute; ẩn về vị th&agrave;nh ho&agrave;ng được thờ trong đ&igrave;nh. Tương truyền rằng, v&agrave;o đời vua L&yacute; Nh&acirc;n T&ocirc;ng, tại l&agrave;ng Lệ Mật c&oacute; hai vợ chồng nh&agrave; nọ hiếm muộn về đường con c&aacute;i khi ra ch&ugrave;a th&igrave; thấy một bức tượng đ&aacute;. Hai vợ chồng n&agrave;y liền sờ v&agrave;o đầu bức tượng v&agrave; cầu khấn xin một đứa con. Sau khi trở về, người vợ bỗng nhi&ecirc;n mang thai v&agrave; hạ sinh một đứa con trai. Đứa b&eacute; lớn l&ecirc;n rất kh&ocirc;i ng&ocirc; tuấn t&uacute;, văn v&otilde; song to&agrave;n. Đến năm 16 tuổi, v&igrave; chăm chỉ học h&agrave;nh n&ecirc;n ch&agrave;ng rất tinh th&ocirc;ng v&otilde; nghệ, am hiểu văn ph&aacute;p.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/59/700_0.941056001397106293.JPG\" /></p>\r\n<p>\r\n	<em><strong>H&igrave;nh phạt cho những kẻ bất k&iacute;nh?</strong></em></p>\r\n<p style=\"text-align: justify;\">\r\n	<em>Theo d&acirc;n l&agrave;ng Lệ Mật, đ&igrave;nh l&agrave;ng nơi đ&acirc;y rất linh thi&ecirc;ng. Trong những năm th&aacute;ng chiến tranh, đ&igrave;nh l&agrave; nơi che chở v&agrave; cầu an cho những người con qu&ecirc; hương trước khi l&ecirc;n đường nhập ngũ. Do vậy, sau chiến tranh, hầu hết họ đều trở về khỏe mạnh v&agrave; dần dần c&oacute; cuộc sống ấm no, hạnh ph&uacute;c. Cũng nhờ ph&uacute;c đức v&agrave; sự độ tr&igrave; của th&agrave;nh ho&agrave;ng m&agrave; người d&acirc;n l&agrave;ng Lệ Mật bao đời nay đ&atilde; ăn n&ecirc;n l&agrave;m ra với nghề nu&ocirc;i rắn truyền thống. Ch&iacute;nh v&igrave; sự linh thi&ecirc;ng ấy m&agrave; người l&agrave;ng Lệ Mật cho rằng, bất cứ ai bất k&iacute;nh v&agrave; x&acirc;m phạm đến c&aacute;c vị th&aacute;nh trong l&agrave;ng sẽ bị trừng trị v&agrave; bị th&aacute;nh phạt rất nặng.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	<em>Xưa nay, d&acirc;n l&agrave;ng Lệ Mật truyền tai nhau kh&ocirc;ng &iacute;t c&acirc;u chuyện về những người bị th&aacute;nh &quot;vật&quot;. Những người sinh sống l&acirc;u năm ở l&agrave;ng Lệ Mật kể lại rằng, trước đ&acirc;y, đ&aacute;m trẻ trong l&agrave;ng thường rủ nhau ra đ&igrave;nh l&agrave;ng chơi bắn s&uacute;ng phốc (s&uacute;ng tre hạt xoan). Trong số đ&oacute;, c&oacute; một cậu con trai nghịch ngợm đ&atilde; v&eacute;n bức m&agrave;nh trước mặt thần ho&agrave;ng l&ecirc;n cho người bạn của m&igrave;nh bắn s&uacute;ng phốc v&agrave;o tượng(?). Sau đ&oacute;, khi đứa trẻ về nh&agrave; ăn cơm th&igrave; cứ bị hắt b&aacute;t cơm ra ph&iacute;a sau như c&oacute; ai giật tay, c&ograve;n người v&eacute;n m&agrave;n th&igrave; bị hộc cả m&aacute;u mồm (?). Gia đ&igrave;nh biết chuyện con nghịch dại, phạm thượng đến thần th&aacute;nh trong đ&igrave;nh l&agrave;ng n&ecirc;n đ&atilde; mua lễ ra cầu khấn. C&acirc;u chuyện đ&atilde; xảy ra mấy chục năm, cậu b&eacute; bắn s&uacute;ng phốc v&agrave;o tượng thờ giờ đ&atilde; l&agrave; cha của ch&agrave;ng trai cắt t&oacute;c gần đ&igrave;nh. Tuy vậy, d&acirc;n l&agrave;ng Lệ Mật thi thoảng vẫn kể lại cho nhau nghe để nhắc nhở mọi người kh&ocirc;ng được l&agrave;m điều g&igrave; bất k&iacute;nh tại đ&igrave;nh l&agrave;ng.</em></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/59/700_0.919429001397106325.JPG\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<em>&Ocirc;ng Trương Văn Hoạt.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	<em>Một dịp kh&aacute;c, c&oacute; một người trong l&agrave;ng v&ocirc; t&igrave;nh đ&atilde; lấy đ&ocirc;i h&agrave;i của c&ocirc;ng ch&uacute;a đeo v&agrave;o ch&acirc;n. Khi về nh&agrave;, kh&ocirc;ng hiểu v&igrave; sao đ&ocirc;i ch&acirc;n của người đ&oacute; cứ li&ecirc;n tục bị giật v&agrave; giơ l&ecirc;n. Người trong nh&agrave; đ&agrave;nh phải sang chợ Đồng Xu&acirc;n mua một đ&ocirc;i gi&agrave;y mới mang ra đ&igrave;nh lễ tạ th&igrave; người th&acirc;n của m&igrave;nh mới th&ocirc;i kh&ocirc;ng bị th&aacute;nh phạt nữa.</em></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969140', '0', '1397106293', '0', '0');
INSERT INTO `slm_news` VALUES ('91', '0.577682001399263229.jpg', 'a:6:{i:0;s:24:\"0.577682001399263229.jpg\";i:1;s:24:\"0.966296001399263229.jpg\";i:2;s:24:\"0.137245001399263230.jpg\";i:3;s:24:\"0.409322001399263230.jpg\";i:4;s:24:\"0.645298001399263230.jpg\";i:5;s:24:\"0.930310001399263230.jpg\";}', null, '6 cánh đồng đẹp mê hồn xứ Bắc', null, null, '12', '', '\r\n	Bạn c&oacute; thể đi thuyền giữa những c&aacute;nh đồng l&uacute;a xanh m&aacute;t của Ninh B&igrave;nh hay chạy xe ngang qua sắc hồng l&atilde;ng mạn của tam gi&aacute;c mạch H&agrave; Giang.', '<p class=\"Normal\">\r\n	Dưới đ&acirc;y l&agrave; 6 c&aacute;nh đồng thu h&uacute;t kh&aacute;ch du lịch ở miền Bắc:</p>\r\n<p class=\"subtitle\">\r\n	<strong>1. C&aacute;nh đồng l&uacute;a M&ugrave; Cang Chải</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"526\" src=\"http://langquetoi.com/images/news/91/700_0.966296001399263229.jpg\" width=\"687\" /></strong></p>\r\n<p class=\"Normal\" style=\"text-align: center;\">\r\n	Những bậc thang l&uacute;a hệt như một bức tranh sơn dầu.<em> Ảnh: phuonglinh94</em></p>\r\n<p class=\"Normal\">\r\n	M&ugrave; Cang Chải từ l&acirc;u đ&atilde; trở th&agrave;nh điểm đến quen thuộc của d&acirc;n x&ecirc; dịch v&agrave; y&ecirc;u th&iacute;ch bộ m&ocirc;n nhiếp ảnh mỗi m&ugrave;a l&uacute;a về. Nằm tại tỉnh Y&ecirc;n B&aacute;i, những ruộng l&uacute;a ở huyện M&ugrave; Cang Chải tập trung chủ yếu tại ba x&atilde;: La P&aacute;n Tẩn, Chế Cu Nha v&agrave; Dế Xu Ph&igrave;nh.</p>\r\n<p class=\"Normal\">\r\n	Ruộng bậc thang M&ugrave;a Cang Chải hấp dẫn từ khi l&uacute;a mới chỉ l&agrave; những mạ xanh mới nh&uacute; cho tới th&aacute;ng 9,10 khi những ruộng l&uacute;a chuyển sang m&agrave;u v&agrave;ng ươm. Đứng từ đ&egrave;o Khau Phạ, du kh&aacute;ch c&oacute; thể chi&ecirc;m ngưỡng khung cảnh h&ugrave;ng vĩ của v&ugrave;ng n&uacute;i T&acirc;y Bắc với bạt ngạt l&uacute;a. Những nếp nh&agrave; s&agrave;n ẩn m&igrave;nh trong đường cong mềm mại của l&uacute;a ng&agrave;y m&ugrave;a khiến M&ugrave; Cang Chải đẹp tựa bức tranh vẽ quyến rũ l&ograve;ng người.</p>\r\n<p class=\"subtitle\">\r\n	<strong>2. Hoa cải Mộc Ch&acirc;u</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"416\" src=\"http://langquetoi.com/images/news/91/700_0.137245001399263230.jpg\" width=\"706\" /></strong></p>\r\n<p class=\"Normal\" style=\"text-align: center;\">\r\n	Hoa cải trắng l&agrave;m xao xuyến l&ograve;ng người.<em> Ảnh:</em> markettinghp</p>\r\n<p class=\"Normal\">\r\n	Nằm c&aacute;ch H&agrave; Nội khoảng 200 km, Mộc Ch&acirc;u l&agrave; điểm hẹn y&ecirc;u th&iacute;ch của rất nhiều d&acirc;n phượt mỗi dịp đ&ocirc;ng về. V&agrave;o thời điểm đ&oacute;, tr&ecirc;n khắp cao nguy&ecirc;n, m&agrave;u trắng tinh khiết của hoa cải lại phủ đầy, n&iacute;u giữ bước ch&acirc;n người lữ h&agrave;nh. C&aacute;c khu vực như bản Pa Ph&aacute;ch hay ph&iacute;a sau rừng th&ocirc;ng bản &Aacute;ng l&uacute;c n&agrave;o cũng tấp nập những đo&agrave;n xe nối đuối nhau săn t&igrave;m khoảnh khắc đẹp.</p>\r\n<p class=\"Normal\">\r\n	Thung lung Mộc Ch&acirc;u đẹp nhất v&agrave;o s&aacute;ng sớm, khi lớp sương đ&ecirc;m c&ograve;n vương lại tr&ecirc;n những c&agrave;nh c&acirc;y, th&acirc;n l&aacute; l&agrave; l&uacute;c những vạt hoa cải trở n&ecirc;n quyến rũ nhất. Trong kh&ocirc;ng gian bao la c&ugrave;ng m&ugrave;i hương thơm nh&egrave; nhẹ, sắc đỏ của trạng nguy&ecirc;n hay m&agrave;u n&acirc;u trầm của đất điểm xuyến lại c&agrave;ng l&agrave;m cho kh&ocirc;ng gian trở n&ecirc;n thơ mộng v&agrave; huyền ảo.</p>\r\n<p class=\"subtitle\">\r\n	<strong>3. Đồi ch&egrave; Mộc Ch&acirc;u</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"403\" src=\"http://langquetoi.com/images/news/91/700_0.409322001399263230.jpg\" width=\"678\" /></strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	Những đồi ch&egrave; m&aacute;t mắt ở Mộc Ch&acirc;u. Ảnh: <em>Cảnh Nguyễn Đức</em></p>\r\n<p>\r\n	Kh&aacute;c với c&aacute;nh đồng hoa cải, đồi ch&egrave; xanh ng&aacute;t tạo n&ecirc;n sức hấp dẫn của Mộc Ch&acirc;u bất cứ thời gian n&agrave;o trong năm. V&igrave; l&agrave; loại c&acirc;y kinh tế ch&iacute;nh n&ecirc;n khắp thị trấn, du kh&aacute;ch đều c&oacute; thể bắt gặp những đồi ch&egrave; cao thấp với cảnh đẹp kh&aacute;c nhau.</p>\r\n<p class=\"Normal\">\r\n	Đặc biệt v&agrave;o th&aacute;ng 12, khi đặt ch&acirc;n tới Mộc Ch&acirc;u, du kh&aacute;ch c&ograve;n c&oacute; thể tận mắt chi&ecirc;m ngưỡng những b&ocirc;ng hoa ch&egrave; trắng muốt, nằm lẫn trong vạt l&aacute; xanh, thu h&uacute;t biết bao lo&agrave;i ong bướm bay lượn, v&ocirc; t&igrave;nh tạo th&agrave;nh bức tranh rất đẹp.</p>\r\n<p class=\"subtitle\">\r\n	<strong>4. Tam gi&aacute;c mạch H&agrave; Giang</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"398\" src=\"http://langquetoi.com/images/news/91/700_0.645298001399263230.jpg\" width=\"688\" /></strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	Mi&ecirc;n man c&aacute;nh đồng tam gi&aacute;c mạch. Ảnh:<em> Cảnh Nguyễn Đức</em></p>\r\n<p>\r\n	Được trồng nhiều ở H&agrave; Giang, đặc biệt tr&ecirc;n con đường từ X&iacute; Mần đi Ho&agrave;ng Su Ph&igrave;, những c&aacute;nh đồng tam gi&aacute;c mạch l&agrave; điểm đến kh&ocirc;ng thể bỏ lỡ của d&acirc;n phượt mỗi dịp cuối thu, đầu đ&ocirc;ng. V&agrave;o thời gian n&agrave;y, tam gi&aacute;c mạch v&agrave; H&agrave; Giang lại trở th&agrave;nh chủ đề n&oacute;ng hổi tr&ecirc;n khắp c&aacute;c diễn đ&agrave;n du lịch.</p>\r\n<p class=\"Normal\">\r\n	Hoa tam gi&aacute;c mạch c&oacute; v&ograve;ng đời khoảng một th&aacute;ng, khi mới nở c&oacute; m&agrave;u trắng sau chuyển sang phớt hồng, &aacute;nh t&iacute;m v&agrave; đỏ sậm. C&oacute; lẽ v&igrave; thế m&agrave; v&agrave;o ng&agrave;y m&ugrave;a, sắc trắng lẫn hồng phớt trải d&agrave;i khiến cao nguy&ecirc;n H&agrave; Giang trở n&ecirc;n tươi mới.</p>\r\n<p class=\"subtitle\">\r\n	<strong>5. Thung lũng Bắc Sơn, Lạng Sơn</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"379\" src=\"http://langquetoi.com/images/news/91/700_0.930310001399263230.jpg\" width=\"689\" /></strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	Thung lũng Bắc Sơn nh&igrave;n từ tr&ecirc;n cao. Ảnh: <em>yeunhiepanh</em></p>\r\n<p>\r\n	Bắc Sơn l&agrave; một huyện thuộc tỉnh Lạng Sơn, c&aacute;ch H&agrave; Nội khoảng 160 km. Nơi đ&acirc;y kh&ocirc;ng chỉ được biết đến như một di t&iacute;ch lịch sử l&acirc;u đời m&agrave; c&ograve;n nổi tiếng bởi một thung lũng với những c&aacute;nh đồng l&uacute;a đẹp.</p>\r\n<p class=\"Normal\">\r\n	Để ngắm được to&agrave;n cảnh thung lũng tr&ugrave; ph&uacute;, bạn phải mất c&ocirc;ng leo l&ecirc;n đỉnh Bắc Sơn. B&ecirc;n con s&ocirc;ng uốn kh&uacute;c l&agrave; những mảng m&agrave;u rực rỡ của l&uacute;a, xen lẫn l&agrave; những nếp nh&agrave; s&agrave;n. Tất cả kết hợp với nhau như tăng th&ecirc;m vẻ hữu t&igrave;nh cho một miền thung lũng.</p>\r\n<p class=\"subtitle\">\r\n	<strong>6. C&aacute;nh đồng l&uacute;a Ninh B&igrave;nh</strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	<strong><img height=\"453\" src=\"http://langquetoi.com/images/news/91/700_0.577682001399263229.jpg\" width=\"712\" /></strong></p>\r\n<p class=\"subtitle\" style=\"text-align: center;\">\r\n	C&aacute;nh đồng l&uacute;a đẹp mắt b&ecirc;n con s&ocirc;ng Ng&ocirc; Đồng<em>. Ảnh: Khapnamchau</em></p>\r\n<p>\r\n	Nằm c&aacute;ch H&agrave; Nội khoảng 110 km dọc theo quốc lộ 1A, khu du lịch Tam Cốc - B&iacute;ch Động l&agrave; một địa danh du lịch nổi tiếng ở Ninh B&igrave;nh. Du kh&aacute;ch muốn tham quan ph&iacute;a b&ecirc;n trong hang động đều phải đi thuyền xu&ocirc;i theo con s&ocirc;ng Ng&ocirc; Đồng v&agrave; nh&igrave;n ngắm bức tranh đồng qu&ecirc; được tạo th&agrave;nh từ những thửa ruộng l&uacute;a v&agrave; con s&ocirc;ng mềm mại.</p>\r\n<p class=\"Normal\">\r\n	Khung cảnh đẹp nhất v&agrave;o khoảng cuối th&aacute;ng 5 đến đầu th&aacute;ng 6, khi cả một v&ugrave;ng rộng lớn được bao phủ bởi những ruộng l&uacute;a xanh xen lẫn v&agrave;ng, tạo th&agrave;nh những mảng m&agrave;u đẹp mắt.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399366453', '0', '1399263229', '0', '0');
INSERT INTO `slm_news` VALUES ('56', '0.776336001396964511.jpg', 'a:3:{i:0;s:24:\"0.776336001396964511.jpg\";i:1;s:24:\"0.699430001396965595.jpg\";i:2;s:24:\"0.950966001396965595.jpg\";}', null, 'Muốn ăn rắn ngon thì về Lệ Mật', null, null, '25', '', '\r\n	Một con rắn hổ mang b&agrave;nh hơn một c&acirc;n sau nửa tiếng đ&atilde; th&agrave;nh 7 đến 10 m&oacute;n ngon kho&aacute;i khẩu cho kh&aacute;ch thưởng thức', '<p class=\"content_font_class\">\r\n	&nbsp; L&agrave;ng Lệ Mật (Việt Hưng, Long Bi&ecirc;n) c&aacute;ch trung t&acirc;m H&agrave; Nội khoảng 7 km kh&ocirc;ng r&otilde; từ bao giờ đ&atilde; nổi tiếng về ẩm thực m&oacute;n rắn. Truyện xưa kể rằng, năm 1072, c&ocirc;ng ch&uacute;a L&yacute; Ngọc Hoa, con g&aacute;i vua L&yacute; C&ocirc;ng Uẩn dạo chơi tr&ecirc;n s&ocirc;ng Thi&ecirc;n Đức (nay l&agrave; s&ocirc;ng Đuống) bị con Giảo Long cuốn tr&ocirc;i. V&agrave;o l&uacute;c đ&oacute; ch&agrave;ng trai ngh&egrave;o l&agrave;ng Lệ Mật sống ở ven s&ocirc;ng bằng nghề bắt rắn đ&atilde; giết con Giảo Long cứu c&ocirc;ng Ch&uacute;a.</p>\r\n<p class=\"content_font_class\">\r\n	Đươc vua ban thưởng v&agrave;ng bạc ch&acirc;u b&aacute;u nhưng ch&agrave;ng trai ngh&egrave;o Lệ Mật khước từ chỉ xin vua mảnh đất ở ph&iacute;a bắc kinh th&agrave;nh Thăng Long để khai hoang rồi lập l&ecirc;n 13 l&agrave;ng trại gọi l&agrave; &quot; Tam Thập Trại&quot;. Từ đ&oacute; c&oacute; l&agrave;ng Lệ Mật chuy&ecirc;n nghề bắt rắn v&agrave; nu&ocirc;i rắn, lấy ng&agrave;y 23 th&aacute;ng 3 &acirc;m lịch h&agrave;ng năm l&agrave; ng&agrave;y hội l&agrave;ng.</p>\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" style=\"width: 490px;\" width=\"1\">\r\n	<tbody style=\"width: 490px;\">\r\n		<tr style=\"width: 490px;\">\r\n			<td class=\"content_font_class\" style=\"width: 490px;\">\r\n				<table class=\"content_font_class\" style=\"margin:auto;\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<img alt=\"634561246705327628-4584-1394685393.jpg\" data-natural-width=\"500\" height=\"0\" src=\"http://farm10.gox.vn/tinmoi/store/images/thumb/23032014/2/1706726/muon_an_ran_ngon_thi_ve_le_mat_0.jpg\" style=\"height: auto; width: 490px;\" title=\"Muốn ăn rắn ngon thì về Lệ Mật\" /></td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr style=\"width: 490px; text-align: center; font-style: italic;\">\r\n			<td class=\"content_font_class\">\r\n				<p class=\"content_font_class\">\r\n					Rắn được nu&ocirc;i trong khoảng một năm l&agrave; c&oacute; thể d&ugrave;ng l&agrave;m m&oacute;n, ai th&iacute;ch con n&agrave;o chọn con đấy. Ảnh: <em class=\"content_font_class\">Lemat.</em></p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class=\"content_font_class\">\r\n	C&oacute; thời nghề nu&ocirc;i rắn trở th&agrave;nh nghề ch&iacute;nh của l&agrave;ng, cả l&agrave;ng lớn nhỏ ai cũng biết bắt rắn v&agrave; rắn trở th&agrave;nh m&oacute;n h&agrave;ng kinh tế l&agrave;m gi&agrave;u cho Lệ Mật. Trải qua nhiều biến cố thăng trầm, đất n&ocirc;ng nghiệp mất dần, con rắn của l&agrave;ng từ những ng&agrave;y xuất khẩu chuyển th&agrave;nh những m&oacute;n ăn tr&ecirc;n b&agrave;n nhậu v&agrave; giờ l&agrave;ng rắn hưng thịnh một thời nổi danh với m&oacute;n ngon thịt rắn.</p>\r\n<p class=\"content_font_class\">\r\n	Ai nghĩ đến ăn rắn cũng nghĩ ngay đến Lệ Mật. Qua cầu Long Bi&ecirc;n đến đầu Việt Hưng l&agrave; đến l&agrave;ng. L&agrave;ng qu&ecirc; giờ kh&ocirc;ng c&ograve;n c&acirc;y tre, bến nước, thay v&agrave;o đ&oacute; l&agrave; con đường nhựa chạy xuy&ecirc;n suốt l&agrave;ng với hai b&ecirc;n nh&agrave; n&agrave;o cũng cao tầng.</p>\r\n<p class=\"content_font_class\">\r\n	Rắn để l&agrave;m m&oacute;n chủ yếu l&agrave; rắn r&aacute;o hay rắn hổ mang b&agrave;nh, tầm hơn một c&acirc;n. Kh&aacute;ch đến ăn rắn được ra chọn rắn trong lồng, th&iacute;ch ăn con n&agrave;o th&igrave; bắt con đấy rồi c&acirc;n l&ecirc;n v&agrave; chờ m&oacute;n được dọn l&ecirc;n</p>\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" style=\"width: 490px;\" width=\"1\">\r\n	<tbody style=\"width: 490px;\">\r\n		<tr style=\"width: 490px;\">\r\n			<td class=\"content_font_class\" style=\"width: 490px;\">\r\n				<table class=\"content_font_class\" style=\"margin:auto;\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<img alt=\"1897735-10202599097514161-1959-1143-5403\" data-natural-width=\"500\" height=\"0\" src=\"http://farm10.gox.vn/tinmoi/store/images/thumb/23032014/2/1706726/muon_an_ran_ngon_thi_ve_le_mat_1.jpg\" style=\"height: auto; width: 490px;\" title=\"Muốn ăn rắn ngon thì về Lệ Mật\" /></td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr style=\"width: 490px; text-align: center; font-style: italic;\">\r\n			<td class=\"content_font_class\">\r\n				<p class=\"content_font_class\">\r\n					Người ta c&oacute; thể chế biến rắn th&agrave;nh rất nhiều m&oacute;n ngon, bổ dưỡng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p class=\"content_font_class\">\r\n	Con rắn nhỏ nhưng cũng đủ 10 m&oacute;n bưng l&ecirc;n cho kh&aacute;ch. Đầu ti&ecirc;n l&agrave; s&uacute;p rắn rồi đến chả rắn l&aacute; lốt, l&ograve;ng rắn x&agrave;o, rắn nộm hoa chuối, x&ocirc;i rắn, nem rắn, da rắn chi&ecirc;n gi&ograve;n, rắn tẩm bột, rắn nhồi thịt... Thịt rắn ăn c&oacute; vị ngọt v&agrave; được chế biến dễ ăn c&ugrave;ng với c&aacute;c loại gia vị.</p>\r\n<p class=\"content_font_class\">\r\n	Từ l&acirc;u, thịt rắn đ&atilde; được c&ocirc;ng nhận l&agrave; một vị thuốc qu&yacute; với t&ecirc;n l&agrave; x&agrave; nhục. Từ thịt rắn c&oacute; thể chế biến th&agrave;nh nhiều m&oacute;n ăn ngon, bổ dưỡng, v&agrave; c&aacute;c t&aacute;c dụng chữa bệnh. Thịt rắn được xếp v&agrave;o h&agrave;ng những m&oacute;n ăn cao cấp v&agrave; bổ dưỡng. M&oacute;n rắn kh&ocirc;ng phải ai cũng ăn được, cẩn thận với trẻ nhỏ v&agrave; phụ nữ v&igrave; c&oacute; thể bị dị dứng.</p>\r\n<table align=\"center\" border=\"0\" cellpadding=\"3\" cellspacing=\"0\" style=\"width: 490px;\" width=\"1\">\r\n	<tbody style=\"width: 490px;\">\r\n		<tr style=\"width: 490px;\">\r\n			<td class=\"content_font_class\" style=\"width: 490px;\">\r\n				<table class=\"content_font_class\" style=\"margin:auto;\">\r\n					<tbody>\r\n						<tr>\r\n							<td>\r\n								<img alt=\"10003453-10202599099594213-904-9053-7412\" data-natural-width=\"500\" height=\"0\" src=\"http://farm10.gox.vn/tinmoi/store/images/thumb/23032014/2/1706726/muon_an_ran_ngon_thi_ve_le_mat_2.jpg\" style=\"height: auto; width: 490px;\" title=\"Muốn ăn rắn ngon thì về Lệ Mật\" /></td>\r\n						</tr>\r\n					</tbody>\r\n				</table>\r\n			</td>\r\n		</tr>\r\n		<tr style=\"width: 490px; text-align: center; font-style: italic;\">\r\n			<td class=\"content_font_class\">\r\n				<p class=\"content_font_class\">\r\n					Rắn được chế biến lần lượt th&agrave;nh 7 đến 10 m&oacute;n v&agrave; bưng dần l&ecirc;n, ăn n&oacute;ng.</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1428054503', '0', '1396964511', '0', '0');
INSERT INTO `slm_news` VALUES ('71', '0.297468001397279607.jpg', 'a:10:{i:0;s:24:\"0.297468001397279607.jpg\";i:1;s:24:\"0.751083001397279607.jpg\";i:2;s:24:\"0.939923001397279607.jpg\";i:3;s:24:\"0.092252001397279608.jpg\";i:4;s:24:\"0.592724001397279608.jpg\";i:5;s:24:\"0.848507001397279608.jpg\";i:6;s:24:\"0.979794001397279608.jpg\";i:7;s:24:\"0.322286001397279609.jpg\";i:8;s:24:\"0.595540001397279609.jpg\";i:9;s:24:\"0.047352001397279610.jpg\";}', null, 'Ngôi làng Bắc bộ với những giếng nước hình bàn chân', null, null, '12', '', '\r\n	L&agrave;ng Y&ecirc;n Trường (Chương Mỹ, H&agrave; Nội) mang nhiều n&eacute;t kiến tr&uacute;c truyền thống miền qu&ecirc; Bắc Bộ với c&aacute;c ng&ocirc;i nh&agrave; cổ, đặc biệt l&agrave; những giếng nước h&igrave;nh b&agrave;n ch&acirc;n.', '<p>\r\n	L&agrave;ng Y&ecirc;n Trường (Chương Mỹ, H&agrave; Nội) mang nhiều n&eacute;t kiến tr&uacute;c truyền thống miền qu&ecirc; Bắc Bộ với c&aacute;c ng&ocirc;i nh&agrave; cổ, đặc biệt l&agrave; những giếng nước h&igrave;nh b&agrave;n ch&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.047352001397279610.jpg\" /></p>\r\n<p>\r\n	L&agrave;ng Y&ecirc;n Trường mang đậm n&eacute;t kiến tr&uacute;c của l&agrave;ng qu&ecirc; Bắc Bộ với c&acirc;y đa, bến nước, s&acirc;n đ&igrave;nh. L&agrave;ng c&ograve;n giữ được nhiều giếng cổ bằng đ&aacute; ong c&oacute; h&igrave;nh th&ugrave; kỳ dị như b&agrave;n ch&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.595540001397279609.jpg\" /></p>\r\n<p>\r\n	C&aacute;c bậc cao ni&ecirc;n cũng kh&ocirc;ng biết giếng c&oacute; từ bao giờ. Trước đ&acirc;y, ng&ocirc;i l&agrave;ng c&oacute; hơn 20 giếng nước như thế n&agrave;y. Qua thời gian, nhiều giếng bị v&ugrave;i lấp, giờ c&ograve;n 7 chiếc, nằm rải r&aacute;c ở c&aacute;c x&oacute;m An Ninh, Trung Tiến, Nhật Tiến, Quyết Tiến, Trung T&acirc;m.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.297468001397279607.jpg\" /></p>\r\n<p>\r\n	B&ecirc;n mỗi giếng nước đều c&oacute; c&acirc;y cổ thụ v&agrave; am nhỏ để thờ. Cạnh giếng x&oacute;m An Ninh c&oacute; c&acirc;y si gi&agrave; gần trăm tuổi soi b&oacute;ng m&aacute;t. Để tr&aacute;nh l&aacute; c&acirc;y rụng xuống l&agrave;m &ocirc; nhiễm nguồn nước, người d&acirc;n x&acirc;y tường bao quanh miệng giếng v&agrave; căng bạt để l&agrave;m m&aacute;i che. Nước giếng được lọc qua đ&aacute; ong trong vắt, kh&ocirc;ng bao giờ cạn nước, nhiều gia đ&igrave;nh dẫn nước từ đ&acirc;y về nh&agrave; d&ugrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.322286001397279609.jpg\" /></p>\r\n<p>\r\n	Chuyện x&oacute;m l&agrave;ng, nh&agrave; cửa, chuyện ri&ecirc;ng tư được người d&acirc;n t&acirc;m sự b&ecirc;n giếng l&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.979794001397279608.jpg\" /></p>\r\n<p>\r\n	Một giếng nước ở x&oacute;m Trung Tiến. Nhiều người l&yacute; giải, giếng c&oacute; h&igrave;nh lạ như vậy v&igrave; được h&igrave;nh th&agrave;nh tr&ecirc;n lớp đất đ&aacute; ong. Người xưa kh&ocirc;ng thể đ&agrave;o giếng h&igrave;nh tr&ograve;n như ở nơi kh&aacute;c m&agrave; chỉ đ&agrave;o quanh lớp đất mềm, đ&aacute; cứng để lại tạo n&ecirc;n những giếng nước c&oacute; h&igrave;nh b&agrave;n ch&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.848507001397279608.jpg\" /></p>\r\n<p>\r\n	Ngo&agrave;i giếng nước kỳ lạ, Trường Y&ecirc;n c&ograve;n tr&ecirc;n 30 ng&ocirc;i nh&agrave; cổ, nhiều nhất l&agrave; của d&ograve;ng họ Trịnh v&agrave; B&ugrave;i. Ng&ocirc;i nh&agrave; cổ tr&ecirc;n trăm tuổi của gia đ&igrave;nh &ocirc;ng Trịnh Văn H&ugrave;ng c&oacute; m&aacute;i uốn cong rất đẹp mắt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.592724001397279608.jpg\" /></p>\r\n<p>\r\n	Nhiều ng&ocirc;i nh&agrave; c&oacute; tường bao quanh bằng đ&aacute; ong cổ k&iacute;nh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.092252001397279608.jpg\" /></p>\r\n<p>\r\n	Cổng nh&agrave; n&agrave;y x&acirc;y dựng từ năm 1925. Chủ nh&agrave; chỉ thay cửa tre bằng cửa sắt, c&ograve;n lại c&aacute;c cột cổng th&igrave; giữ nguy&ecirc;n. Hầu hết ng&ocirc;i nh&agrave; ở đ&acirc;y đều c&oacute; bậc cổng l&aacute;t đ&aacute; xanh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.939923001397279607.jpg\" /></p>\r\n<p>\r\n	H&agrave;ng r&agrave;o 22 năm tuổi được tỉa t&oacute;t kh&eacute;o l&eacute;o của cụ &ocirc;ng Trịnh Nh&acirc;n Kỳ. &Ocirc;ng học tập kiến tr&uacute;c đ&igrave;nh ch&ugrave;a, dựng khung th&eacute;p, trồng c&acirc;y &ocirc;-r&ocirc; từ năm 1992 với mong muốn tạo n&ecirc;n một h&agrave;ng r&agrave;o kh&aacute;c lạ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/71/700_0.751083001397279607.jpg\" /></p>\r\n<p>\r\n	Tr&ecirc;n cổng c&oacute; đề d&ograve;ng chữ &#39;Ngũ ph&uacute;c l&acirc;m m&ocirc;n&#39; với &yacute; nguyện cầu mong những điều tốt đẹp cho gia đ&igrave;nh.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1397280097', '0', '1397279607', '0', '0');
INSERT INTO `slm_news` VALUES ('72', '0.467767001397280773.jpg', 'a:12:{i:0;s:24:\"0.467767001397280773.jpg\";i:1;s:24:\"0.661157001397280773.jpg\";i:2;s:24:\"0.769454001397280773.jpg\";i:3;s:24:\"0.980981001397280773.jpg\";i:4;s:24:\"0.208498001397280774.jpg\";i:5;s:24:\"0.370190001397280774.jpg\";i:6;s:24:\"0.663717001397280774.jpg\";i:7;s:24:\"0.990261001397280774.jpg\";i:8;s:24:\"0.404213001397280775.jpg\";i:9;s:24:\"0.698377001397280775.jpg\";i:10;s:24:\"0.144172001397280776.jpg\";i:11;s:24:\"0.424028001397280776.jpg\";}', null, 'Hoa gạo đỏ rực làng quê Bắc Bộ', null, null, '12', '', '\r\n	Dịp cuối m&ugrave;a xu&acirc;n, hoa gạo nở rộ khắp miền Bắc, đặc biệt l&agrave; v&ugrave;ng n&ocirc;ng th&ocirc;n.', '<p style=\"text-align: center;\">\r\n	Dịp cuối m&ugrave;a xu&acirc;n, hoa gạo nở rộ khắp miền Bắc, đặc biệt l&agrave; v&ugrave;ng n&ocirc;ng th&ocirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.661157001397280773.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trước đ&acirc;y ở miền Bắc, ngay cả c&aacute;c khu phố H&agrave; Nội, đều trồng c&acirc;y gạo. Cứ tới th&aacute;ng 3-4, hoa gạo lại nở đỏ cả c&acirc;y. Lễ hội ch&ugrave;a Thầy (H&agrave; Nội) năm nay được tổ chức đ&uacute;ng v&agrave;o dịp c&acirc;y hoa gạo tr&ecirc;n bờ nở rộ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.769454001397280773.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ng&agrave;y nay, chỉ c&ograve;n s&oacute;t lại rất &iacute;t c&acirc;y hoa gạo được trồng tr&ecirc;n phố như ở Nam Định.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.980981001397280773.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&acirc;y hoa gạo ở th&agrave;nh phố bị chặt nhiều bởi đ&ocirc; thị đất chật v&agrave; hoa rụng nhiều c&ugrave;ng l&uacute;c, kh&ocirc;ng th&iacute;ch hợp khi đường đ&ocirc;ng người qua lại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.208498001397280774.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	D&acirc;n gian thường c&oacute; c&acirc;u &quot;Ma c&acirc;y gạo, c&uacute; c&aacute;o c&acirc;y đề&quot;, nhưng vẻ đẹp của hoa gạo khiến người ta qu&ecirc;n hết nỗi sợ đ&oacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.370190001397280774.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&acirc;y gạo đỏ rực b&ecirc;n s&ocirc;ng ở x&atilde; Thuần Mỹ (Ba V&igrave;, H&agrave; Nội).</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.663717001397280774.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Với lũ trẻ thời n&agrave;o cũng vậy, m&ugrave;a hoa gạo lu&ocirc;n để lại nhiều kỷ niệm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.990261001397280774.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Con trai nghịch ngợm, nhặt hoa để n&eacute;m nhau. C&ograve;n c&aacute;c c&ocirc; b&eacute; mới bước v&agrave;o tuổi thiếu nữ bắt đầu mơ mộng mỗi m&ugrave;a hoa nở, hoa t&agrave;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.467767001397280773.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hoa nở đỏ rực như xua tan đi sự ảm đạm của những ng&agrave;y cuối m&ugrave;a xu&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.404213001397280775.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nhiều đ&ocirc;i trai g&aacute;i hẹn h&ograve; nhau dưới gốc c&acirc;y hoa gạo đầu l&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.698377001397280775.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trước cổng nhiều trường học cũng trồng một c&acirc;y hoa gạo. Thế n&ecirc;n, trong k&yacute; ức nhiều người xa qu&ecirc;, ngo&agrave;i h&igrave;nh ảnh th&acirc;n quen về ng&ocirc;i nh&agrave; lợp ng&oacute;i, giếng nước c&ograve;n c&oacute; th&ecirc;m những b&ocirc;ng hoa đỏ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.144172001397280776.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nhiều nh&agrave; thơ đ&atilde; v&iacute; hoa gạo như h&ograve;n than đỏ rực, như ngọn lửa tr&ecirc;n trời...</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/72/700_0.424028001397280776.jpg\" /></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1397281068', '0', '1397280773', '0', '0');
INSERT INTO `slm_news` VALUES ('60', '0.408682001397111046.jpg', 'a:8:{i:0;s:24:\"0.408682001397111046.jpg\";i:1;s:24:\"0.168563001397109377.jpg\";i:2;s:24:\"0.595179001397111046.jpg\";i:3;s:24:\"0.794346001397111046.jpg\";i:4;s:24:\"0.051794001397111047.jpg\";i:5;s:24:\"0.367280001397111047.jpg\";i:6;s:24:\"0.559340001397111047.jpg\";i:7;s:24:\"0.749920001397111047.jpg\";}', null, 'Đến Lệ Mật xem múa Giảo Long', null, null, '25', '', '\r\n	Nổi tiếng xa gần với nghề&nbsp;nu&ocirc;i rắn, n&ecirc;n khi nhắc đến l&agrave;ng Lệ Mật (Long Bi&ecirc;n &ndash; H&agrave; Nội) người ta thường gọi bằng một c&aacute;i t&ecirc;n th&acirc;n thuộc l&agrave;&nbsp;&ldquo;l&agrave;ng rắn&rdquo;. Đặc sản l&agrave; rắn thế nhưng Lệ Mật c&ograve;n nức tiếng với một thứ đặc sản tinh thần đ&atilde; được liệt v&agrave;o h&agrave;ng 1 trong 10 điệu m&uacute;a cổ đất Thăng Long,&nbsp;đ&oacute; l&agrave;&nbsp;m&uacute;a Giảo Long.', '<p style=\"text-align: center;\">\r\n	H&agrave;ng ng&agrave;y, Lệ Mật tấp nập với những d&ograve;ng người, d&ograve;ng xe v&agrave;o l&agrave;ng để thưởng thức m&oacute;n đặc sản rắn Lệ Mật tại c&aacute;c nh&agrave; h&agrave;ng của ch&iacute;nh người d&acirc;n l&agrave;ng Lệ Mật mở ra. Với người H&agrave; Nội cũng như du kh&aacute;ch thập phương đến mảnh đất kinh kỳ n&agrave;y, nếu đ&atilde; l&agrave; người chuộng thịt rắn th&igrave; &iacute;t nhất cũng đ&atilde;&nbsp;một lần đặt ch&acirc;n đến Lệ Mật.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.595179001397111046.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	<br />\r\n	Thế nhưng, v&agrave;o khoảng cuối th&aacute;ng 3&nbsp;&Acirc;m lịch,&nbsp;những d&ograve;ng người đ&ocirc;ng đ&uacute;c đổ về Lệ Mật kh&ocirc;ng chỉ đến để thưởng thức m&oacute;n đặc sản thịt rắn, m&agrave; h&agrave;o hứng hơn, họ đến &ldquo;l&agrave;ng rắn&rdquo; để được trực tiếp xem người Lệ Mật biểu diễn tr&ograve;&nbsp;&ldquo;ch&eacute;m rắn&rdquo; qua&nbsp;điệu m&uacute;a cổ Giảo Long (lo&agrave;i thủy qu&aacute;i m&igrave;nh&nbsp;rồng - PV)&nbsp;. Hội l&agrave;ng Lệ Mật được tổ chức h&agrave;ng năm từ ng&agrave;y 20 đến 24 th&aacute;ng 3 &Acirc;m lịch, hội ch&iacute;nh diễn ra v&agrave;o ng&agrave;y 23 th&aacute;ng 3 &Acirc;m lịch.&nbsp;Lễ hội&nbsp;l&agrave; dịp&nbsp;để d&acirc;n l&agrave;ng Lệ Mật&nbsp;tưởng nhớ v&agrave; t&ocirc;n&nbsp;vinh c&ocirc;ng đức Th&agrave;nh ho&agrave;ng l&agrave;ng. &Ocirc;ng l&agrave; người đ&atilde; c&oacute; c&ocirc;ng lớn&nbsp;trong&nbsp;việc&nbsp;ch&eacute;m Giảo Long cứu c&ocirc;ng ch&uacute;a nh&agrave; L&yacute; v&agrave; sau&nbsp;đ&oacute;&nbsp;đứng ra&nbsp;xin đất cho d&acirc;n l&agrave;ng lập ấp khai hoang, an cư lạc nghiệp, lập n&ecirc;n l&agrave;ng Lệ Mật ng&agrave;y nay. Bởi vậy, điểm nhấn v&agrave; thu h&uacute;t nhất trong hội l&agrave;ng Lệ Mật ch&iacute;nh l&agrave; m&agrave;n m&uacute;a Giảo Long. Đ&acirc;y cũng ch&iacute;nh l&agrave; 1 trong 10 điệu m&uacute;a cổ đất Thăng Long v&agrave; đ&atilde; từng được chọn biểu diễn trong dịp Đại lễ kỷ niệm 1000 năm Thăng Long &ndash; H&agrave; Nội hồi năm 2010 vừa qua.</p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.408682001397111046.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	Điệu m&uacute;a Giảo Long c&oacute; 4 trường đoạn. Trường đoạn 1 mi&ecirc;u tả&nbsp;chuyện c&ocirc;ng ch&uacute;a nh&agrave; L&yacute; đi du ngoạn tr&ecirc;n s&ocirc;ng Đuống rồi kh&ocirc;ng may bị Giảo Long bắt. Trường đoạn 2&nbsp;m&ocirc; tả cả&nbsp;ch&agrave;ng trai họ Ho&agrave;ng l&agrave;ng Lệ Mật xin ph&eacute;p vua L&yacute;&nbsp;đi đ&aacute;nh Giảo Long cứu c&ocirc;ng ch&uacute;a nhưng chưa th&agrave;nh c&ocirc;ng. Trường đoạn 3 l&agrave; cảnh ch&agrave;ng trai quyết đấu trận&nbsp;cuối với Giảo Long v&agrave; cuối c&ugrave;ng đ&atilde; chiến thắng, cứu được c&ocirc;ng ch&uacute;a,&nbsp;ch&eacute;m được Giảo Long. Trường đoạn cuối l&agrave; cảnh nh&agrave; vua thưởng cho ch&agrave;ng trai họ Ho&agrave;ng v&agrave;ng bạc ch&acirc;u b&aacute;u nhưng ch&agrave;ng trai kh&ocirc;ng nhận m&agrave; xin vua cấp cho v&ugrave;ng đất ở ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long, lập n&ecirc;n 13 l&agrave;ng, tục gọi l&agrave; thập tam trại, trong đ&oacute; c&oacute; l&agrave;ng Lệ Mật. Trong c&aacute;c trường đoạn đ&oacute; cao tr&agrave;o v&agrave; hấp dẫn nhất l&agrave; trường đoạn ch&agrave;ng trai họ Ho&agrave;ng l&agrave;ng Lệ Mật đấu tr&iacute;, đấu sức với Giảo Long. Đ&acirc;y l&agrave; trường đoạn thể hiện cho sức mạnh v&agrave; &yacute; ch&iacute; muốn chinh phục thi&ecirc;n nhi&ecirc;n, b&agrave;i trừ c&aacute;i xấu, l&agrave; cuộc chiến giữa c&aacute;i thiện v&agrave; c&aacute;i &aacute;c&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.794346001397111046.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<br />\r\n	Qua bao đời, điệu m&uacute;a Giảo Long vẫn lu&ocirc;n thu h&uacute;t v&agrave; được người d&acirc;n l&agrave;ng Lệ Mật cũng như du kh&aacute;ch thập phương xa gần mong đợi v&agrave;o mỗi dịp hội l&agrave;ng, bởi n&oacute; vẫn lu&ocirc;n giữ được những&nbsp;n&eacute;t tinh hoa v&agrave; đẹp đẽ nhất của văn h&oacute;a l&agrave;ng x&atilde;, văn h&oacute;a truyền thống d&acirc;n gian của l&agrave;ng qu&ecirc; Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.051794001397111047.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nh&igrave;n cảnh người d&acirc;n h&ograve; reo, v&ograve;ng trong v&ograve;ng ngo&agrave;i , thậm ch&iacute; l&agrave; tr&egrave;o l&ecirc;n cả những c&acirc;y cối xung quanh s&acirc;n đ&igrave;nh để xem m&agrave;n đấu tr&iacute;, đấu sức của ch&agrave;ng trai họ Ho&agrave;ng l&agrave;ng Lệ Mật với con thủy qu&aacute;i Giảo Long khiến ch&uacute;ng t&ocirc;i c&agrave;ng t&ograve; m&ograve; hơn về một điệu m&uacute;a cổ d&acirc;n gian độc đ&aacute;o n&agrave;y. T&ograve; m&ograve; v&igrave; tại sao những người d&acirc;n thường m&agrave; lại c&oacute; thể x&acirc;y dựng v&agrave; diễn xuất những m&agrave;n m&uacute;a đặc sắc, hấp dẫn đến như vậy. Bởi vậy, mặc d&ugrave; đ&atilde; được &ldquo;m&atilde;n nh&atilde;n&rdquo; với m&agrave;n m&uacute;a nhưng ch&uacute;ng t&ocirc;i vẫn t&igrave;m c&aacute;ch gặp cho bằng được những đạo diễn, diễn vi&ecirc;n&nbsp;n&ocirc;ng d&acirc;n ch&iacute;nh hiệu&nbsp;n&agrave;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.367280001397111047.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<br />\r\n	B&aacute;c Ho&agrave;ng Ngọc Kỹ người&nbsp;l&agrave;ng Lệ Mật,&nbsp;năm nay đ&atilde; gần 70 tuổi, l&agrave; người đ&oacute;ng vai ch&agrave;ng trai họ Ho&agrave;ng ch&eacute;m rắn trong điệu m&uacute;a Giảo Long suốt hơn 30 năm qua. Cho đến b&acirc;y giờ, tuổi gi&agrave; sức yếu kh&ocirc;ng thể đủ sức khỏe để thể hiện được những động t&aacute;c cần cơ bắp của sức trai trẻ, b&aacute;c mới chịu lui về l&agrave;m hậu kỳ, nhường lại vai diễn cho lớp hậu bối. Nhiệt huyết của &ldquo;ch&agrave;ng trai ch&eacute;m rắn l&agrave;ng Lệ Mật&rdquo; n&agrave;y vẫn cứ hừng hực ngay cả khi kh&ocirc;ng c&ograve;n diễn xuất nữa. Sự nhiệt huyết n&agrave;y thể hiện rất r&otilde; trong việc &ocirc;ng lu&ocirc;n đau đ&aacute;u t&igrave;m c&aacute;ch l&agrave;m thế n&agrave;o để c&oacute; thể ng&agrave;y c&agrave;ng ho&agrave;n thiện hơn kịch bản&nbsp;gồm 4&nbsp;phần của điệu m&uacute;a Giảo Long. Nhất l&agrave; việc chọn cho được người thay &ocirc;ng v&agrave;o vai diễn ch&agrave;ng trai l&agrave;ng Lệ Mật, bởi đ&acirc;y l&agrave; vai diễn quan trọng nhất trong điệu m&uacute;a Giảo Long.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.559340001397111047.jpg\" /><br />\r\n	<br />\r\n	Người được chọn v&agrave;o vai diễn phải hội đủ những yếu tố t&agrave;i đức song to&agrave;n v&agrave; phải c&oacute; một sức khỏe, một th&acirc;n h&igrave;nh đẹp, vạm vỡ bởi đ&oacute; l&agrave; h&igrave;nh ảnh l&agrave; bộ mặt của cả d&acirc;n l&agrave;ng Lệ Mật. M&uacute;a Giảo long được v&iacute; như một m&agrave;n kịch. Bởi vậy, từng động t&aacute;c diễn xuất đều phải v&ocirc; c&ugrave;ng kh&eacute;o l&eacute;o, uyển chuyển v&agrave; biểu cảm,&nbsp;chỗ n&agrave;o cần cứng, chỗ n&agrave;o cần dẻo, chỗ n&agrave;o cần thể hiện c&aacute;i thần uy... người diễn phải kỳ c&ocirc;ng thể hiện cho bằng&nbsp;được, c&oacute; như thế mới chuyển tải&nbsp;được hết&nbsp;&yacute; nghĩa nh&acirc;n văn của&nbsp;điệu m&uacute;a. N&oacute;i đến&nbsp;điệu m&uacute;a cổ của l&agrave;ng, b&aacute;c Ho&agrave;ng Ngọc Kỹ t&acirc;m sự: &ldquo;Hồi những năm 1981, 1982&hellip; ai cũng tưởng hội l&agrave;ng phải bỏ v&igrave; kh&ocirc;ng kiếm đ&acirc;u ra người đ&oacute;ng vai ch&agrave;ng trai họ Ho&agrave;ng trong điệu m&uacute;a Giảo Long, v&igrave;&nbsp;v&agrave;o vai n&agrave;y phải người c&oacute; d&aacute;ng đẹp, m&uacute;a giỏi v&agrave;&nbsp;đặc biệt l&agrave;&nbsp;phải c&oacute; cơ duy&ecirc;n&nbsp;th&igrave;&nbsp;mới c&oacute; thể nhập vai được&rdquo;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/60/700_0.749920001397111047.jpg\" /><br />\r\n	<br />\r\n	Lời t&acirc;m sự của b&aacute;c Ho&agrave;ng Ngọc Kỹ&nbsp;cho thấy điệu m&uacute;a Giảo Long c&oacute; một vai tr&ograve; đặc biệt&nbsp;trong đời sống văn h&oacute;a truyền thống của l&agrave;ng Lệ&nbsp;Mật. V&igrave; vậy, n&oacute; lu&ocirc;n tồn tại v&agrave;&nbsp;c&oacute; dấu ấn đặc biệt trong t&acirc;m thức của d&acirc;n l&agrave;ng v&agrave;&nbsp;lu&ocirc;n&nbsp;được chờ&nbsp;đ&oacute;n mỗi khi hội l&agrave;ng&nbsp;v&agrave;o đ&aacute;m.</p>\r\n<p style=\"text-align: justify;\">\r\n	Năm nay, người kế nghiệp b&aacute;c Ho&agrave;ng Ngọc Kỹ l&agrave; anh Trần Minh Tr&iacute;, người&nbsp;được d&acirc;n l&agrave;ng &ldquo;chọn mặt gửi v&agrave;ng&rdquo;&nbsp;để thủ vai ch&agrave;ng trai họ Ho&agrave;ng ch&eacute;m Giảo Long. T&acirc;m sự với&nbsp;ch&uacute;ng t&ocirc;i, anh cho biết, lần&nbsp;đầu ti&ecirc;n&nbsp;được d&acirc;n l&agrave;ng giao cho trọng tr&aacute;ch lớn n&ecirc;n anh&nbsp;vừa bỡ ngỡ vừa&nbsp;lo &acirc;u. Bởi theo quan niệm&nbsp;của người l&agrave;ng Lệ Mật,&nbsp;năm n&agrave;o hội l&agrave;ng diễn ra tưng bừng, điệu m&uacute;a Giảo Long được d&acirc;n ch&uacute;ng tung h&ocirc; th&igrave; năm đ&oacute; d&acirc;n l&agrave;ng Lệ Mật sẽ c&oacute; một cuộc sống b&igrave;nh y&ecirc;n, may mắn v&agrave; su&ocirc;n sẻ</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969116', '0', '1397109377', '0', '0');
INSERT INTO `slm_news` VALUES ('58', '0.557980001396966642.jpg', 'a:1:{i:0;s:24:\"0.557980001396966642.jpg\";}', null, 'Đến Lệ Mật xem múa rắn', null, null, '25', '', '\r\n	L&agrave;ng Lệ Mật giờ thuộc phường Việt Hưng, Long Bi&ecirc;n, H&agrave; Nội. Hội đ&igrave;nh của l&agrave;ng Lệ Mật diễn ra ng&agrave;y 23-3 &acirc;m lịch hằng năm, l&agrave; ng&agrave;y vui kh&ocirc;ng chỉ của ri&ecirc;ng l&agrave;ng Lệ Mật m&agrave; với cả thập tam trại. Trong ng&agrave;y hội đ&igrave;nh Lệ Mật, người l&agrave;ng sẽ biểu diễn điệu vũ Rảo Long đặc sắc v&agrave; &ocirc;n lại t&iacute;ch truyện h&igrave;nh th&agrave;nh 13 l&agrave;ng trại của &ocirc;ng tổ nghề', '<p class=\"pBody\">\r\n	Từ nhiều thế hệ, người d&acirc;n l&agrave;ng Lệ Mật tự dạy bảo c&aacute;ch săn bắt rắn, đ&uacute;c kết th&agrave;nh những kinh nghiệm độc đ&aacute;o. Vị thủ đền của đ&igrave;nh l&agrave;ng Lệ Mật, cụ &ocirc;ng Ho&agrave;ng Ngọc Dậu, n&oacute;i: &ldquo;Cha &ocirc;ng t&ocirc;i ng&agrave;y xưa chuy&ecirc;n đi bắt rắn rồi truyền nghề lại cho con ch&aacute;u, con ch&aacute;u truyền cho nhau. Khi bắt rắn phải biết c&aacute;ch ph&acirc;n biệt đ&oacute; l&agrave; lo&agrave;i rắn g&igrave;, c&oacute; tập t&iacute;nh g&igrave; quen thuộc&rdquo;.</p>\r\n<p class=\"pBody\">\r\n	Trong tất cả l&agrave;ng trại thuộc kinh th&agrave;nh Thăng Long xưa, duy chỉ c&oacute; người l&agrave;ng Lệ Mật theo nghề bắt rắn. Điều n&agrave;y được cụ &ocirc;ng Nguyễn Đắc Li&ecirc;n - người l&agrave;ng Lệ Mật, hiện l&agrave; trưởng ban li&ecirc;n lạc của 13 l&agrave;ng trại ph&iacute;a t&acirc;y kinh th&agrave;nh Thăng Long xưa - l&yacute; giải: &ldquo;Lệ Mật c&oacute; nghề bắt rắn v&igrave; xưa l&agrave;ng nằm tr&ecirc;n c&aacute;c doi s&ocirc;ng Hồng bồi đắp, nước v&agrave; cỏ nhiều n&ecirc;n rắn rết cũng nhiều. B&agrave; con giữa x&oacute;m n&agrave;y sang x&oacute;m kia, nh&agrave; n&agrave;y sang nh&agrave; kh&aacute;c qua lạch phải d&ugrave;ng thuyền độc mộc. Để tự bảo tồn, c&aacute;c cụ nghĩ c&aacute;ch bắt rắn để đỡ tổn thương cho con ch&aacute;u, xuất ph&aacute;t từ đ&oacute; c&aacute;c cụ c&oacute; nghề bắt rắn n&ecirc;n nghề n&agrave;y tồn tại đến giờ&rdquo;.</p>\r\n<p class=\"pBody\">\r\n	N&oacute;i về gốc t&iacute;ch của v&ugrave;ng đất Lệ Mật với mối li&ecirc;n quan đến rắn, nh&agrave; sử học L&ecirc; Văn Lan cho biết: &ldquo;Thời L&yacute;, khi xuất hiện những v&ugrave;ng x&acirc;y dựng b&ecirc;n ph&iacute;a t&acirc;y của kinh th&agrave;nh Thăng Long, miền đất gốc ch&iacute;nh l&agrave; l&agrave;ng Lệ Mật, nảy sinh truyền thuyết &ocirc;ng ho&agrave;ng Lệ Mật... Từ đ&acirc;y c&oacute; sự hỗn dung của rất nhiều quan niệm phong tục với nhiều dụng &yacute; kh&aacute;c nhau th&agrave;nh ra sự t&iacute;ch &ocirc;ng ho&agrave;ng Lệ Mật v&agrave; chuyện đ&aacute;nh rắn&rdquo;.</p>\r\n<p class=\"pBody\">\r\n	B&ecirc;n cạnh ng&ocirc;i đ&igrave;nh l&agrave;ng Lệ Mật bề thế l&agrave; chiếc giếng ngọc, tr&ograve;n vạnh như chiếc gương soi khổng lồ, hắt b&oacute;ng c&acirc;y đa nơi c&oacute; ng&ocirc;i miếu nhỏ, cụ &ocirc;ng Ho&agrave;ng Ngọc Dậu, 85 tuổi, giới thiệu: &ldquo;Miếu n&agrave;y thờ c&ocirc; c&ocirc;ng ch&uacute;a con vua L&yacute; Th&aacute;i T&ocirc;ng, người gặp nạn khi du thuyền rong chơi tr&ecirc;n d&ograve;ng Thi&ecirc;n Đức khi xưa v&agrave; được một trai l&agrave;ng Lệ Mật xung phong, xin vua đi t&igrave;m thi h&agrave;i c&ocirc;ng ch&uacute;a. &Ocirc;ng ch&iacute;nh l&agrave; Nguyễn Qu&yacute; C&ocirc;ng, đức th&aacute;nh của l&agrave;ng Lệ Mật, tương truyền đ&atilde; lặn xuống d&ograve;ng s&ocirc;ng nơi c&ocirc;ng ch&uacute;a gặp nạn, giao đấu với rắn khổng lồ v&agrave; mang được thi h&agrave;i c&ocirc;ng ch&uacute;a về&rdquo;.</p>\r\n<p class=\"pBody\">\r\n	Sự t&iacute;ch giao đấu với rắn t&igrave;m thi h&agrave;i c&ocirc;ng ch&uacute;a được người l&agrave;ng Lệ Mật x&acirc;y dựng th&agrave;nh một diễn xướng m&ocirc; tả lại lịch sử của đức th&aacute;nh l&agrave;ng Lệ Mật, gọi l&agrave; điệu vũ Rảo Long. Cụ &ocirc;ng Ho&agrave;ng Ngọc Dậu cũng ch&iacute;nh l&agrave; người phụ tr&aacute;ch đội d&acirc;n vũ ấy. Ng&agrave;y ng&agrave;y &ocirc;ng tập luyện những miếng v&otilde; tự s&aacute;ng t&aacute;c để hằng năm v&agrave;o đ&uacute;ng ng&agrave;y hội đ&igrave;nh, &ocirc;ng m&uacute;a v&otilde; diễn lại t&iacute;ch xưa cho người l&agrave;ng v&agrave; c&aacute;c thế hệ con ch&aacute;u hiểu th&ecirc;m về c&acirc;u chuyện h&agrave;o h&ugrave;ng của vị th&agrave;nh ho&agrave;ng l&agrave;ng cũng l&agrave; &ocirc;ng tổ nghề rắn, người đ&atilde; c&oacute; c&ocirc;ng khai sinh 13 l&agrave;ng trại (thập tam trại) ph&iacute;a t&acirc;y th&agrave;nh Thăng Long khi xưa.</p>\r\n<p class=\"pBody\">\r\n	Nguy&ecirc;n do sự t&iacute;ch 13 l&agrave;ng trại ấy được cụ Nguyễn Đắc Li&ecirc;n kể th&ecirc;m: &ldquo;Năm 1043, c&ocirc;ng ch&uacute;a cả con vua L&yacute; Th&aacute;i T&ocirc;ng xin ph&eacute;p vua cha về thăm qu&ecirc;, dong thuyền đi từ s&ocirc;ng Hồng sang Đuống, về Thi&ecirc;n Đức gặp s&oacute;ng to gi&oacute; lớn, c&ocirc;ng ch&uacute;a đắm thuyền chết đuối, c&aacute;c quan đi hộ vệ c&ocirc;ng ch&uacute;a kh&ocirc;ng t&igrave;m thấy thi h&agrave;i.</p>\r\n<p class=\"pBody\">\r\n	Ng&agrave;y xưa việc chết mất x&aacute;c l&agrave; tối kỵ, vua phải thu&ecirc; người thưởng cho ai t&igrave;m được thi h&agrave;i c&ocirc;ng ch&uacute;a. Trong cung c&oacute; th&aacute;i gi&aacute;m Nguyễn Qu&yacute; C&ocirc;ng l&agrave; người l&agrave;ng Lệ Mật xin ph&eacute;p vua đi t&igrave;m thi h&agrave;i c&ocirc;ng ch&uacute;a, vua cho thuyền chở th&aacute;i gi&aacute;m ra chỗ hợp nguồn, nước xo&aacute;y tr&ograve;n như phễu khổng lồ. Người nhảy xuống t&igrave;m v&agrave; giao đấu với con giải hay c&ograve;n gọi l&agrave; rắn khổng lồ, thuồng luồng, cuối c&ugrave;ng t&igrave;m được thi h&agrave;i c&ocirc;ng ch&uacute;a đưa l&ecirc;n bờ.</p>\r\n<p class=\"pBody\">\r\n	Nh&agrave; vua hay tin triệu vời Nguyễn Qu&yacute; C&ocirc;ng thưởng 100 c&acirc;n v&agrave;ng, 100 cuốn lụa, phong cho th&aacute;i gi&aacute;m nội thị thượng khanh, tức chức th&aacute;i tể đứng đầu h&agrave;ng th&aacute;i gi&aacute;m. Người kh&ocirc;ng nhận v&igrave; c&ograve;n &iacute;t tuổi, nghĩ đến qu&ecirc; cha đất tổ ngh&egrave;o, xin vua vườn t&acirc;y cấm ph&iacute;a t&acirc;y Thăng Long để khai hoang.</p>\r\n<p class=\"pBody\">\r\n	Nh&agrave; vua đồng &yacute; cho vườn t&acirc;y cấm, người về l&agrave;ng gọi 13 người con ch&aacute;u bản trang tập hợp một số gia đ&igrave;nh hằng ng&agrave;y đi vỡ đất khai hoang, x&acirc;y dựng nh&agrave; cửa đ&agrave;ng ho&agrave;ng dọn v&agrave;o ở, lập n&ecirc;n 13 l&agrave;ng trại đặt t&ecirc;n l&agrave; Kh&aacute;n Xu&acirc;n, Ngọc H&agrave;, Hữu Tiệp, tiếp đến c&oacute; Vĩnh Ph&uacute;c, Đại Y&ecirc;n, Liễu Giai, Cống Y&ecirc;n, về b&ecirc;n t&acirc;y nam c&oacute; Giảng V&otilde;, H&agrave;o Nam, Kim M&atilde;, ở giữa c&oacute; Thủ Lệ, Cống Vị, Vạn Ph&uacute;c&rdquo;.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969128', '0', '1396966642', '0', '0');
INSERT INTO `slm_news` VALUES ('61', '0.389676001397111896.jpg', 'a:7:{i:0;s:24:\"0.389676001397111896.jpg\";i:1;s:24:\"0.494181001397111896.jpg\";i:2;s:24:\"0.615654001397111896.jpg\";i:3;s:24:\"0.697598001397111896.jpg\";i:4;s:24:\"0.733689001397111896.jpg\";i:5;s:24:\"0.816300001397111896.jpg\";i:6;s:24:\"0.903457001397111896.jpg\";}', null, 'Chùm ảnh: Rắn thần ở làng Lệ Mật', null, null, '25', '', '\r\n	Lễ hội Rắn, một trong những lễ hội đặc biệt kỳ lạ với nhiều &quot;tr&ograve;&quot; diễn xướng c&ograve;n lưu vết t&iacute;n ngưỡng d&acirc;n gian cổ đại, vừa được d&acirc;n ch&uacute;ng v&ugrave;ng Thập tam trại (khu vực quận Ba Đ&igrave;nh - H&agrave; Nội ng&agrave;y nay) tổ chức quy m&ocirc; ho&agrave;nh tr&aacute;ng nhất từ trước tới nay...', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/61/700_0.389676001397111896.jpg\" /></p>\r\n<p>\r\n	<font face=\"arial, helvetica, sans-serif\">Trong k&yacute; ức của d&acirc;n l&agrave;ng Lệ Mật v&agrave; những g&igrave; được ghi ch&eacute;p lại trong 13 đạo sắc phong tại đ&igrave;nh l&agrave;ng th&igrave; lễ hội rắn nhằm ng&agrave;y 23/3 &acirc;m lịch h&agrave;ng năm ghi nhớ c&ocirc;ng ơn th&agrave;nh ho&agrave;ng l&agrave;ng l&agrave; Ho&agrave;ng Đức Trung. Vị th&agrave;nh ho&agrave;ng l&agrave;ng n&agrave;y c&oacute; c&ocirc;ng cứu sống c&ocirc;ng ch&uacute;a thời L&yacute; Th&aacute;nh T&ocirc;ng, quy tụ d&acirc;n cư lập th&agrave;nh 13 trại (thập tam trại). &Ocirc;ng được triều L&yacute; sắc phong l&agrave;: Th&agrave;nh ho&agrave;ng uy linh Hộ quốc khang d&acirc;n.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/61/700_0.494181001397111896.jpg\" /></p>\r\n<p>\r\n	<font face=\"Arial\">Trưởng ban di t&iacute;ch đ&igrave;nh Lệ Mật, &ocirc;ng Trương Khắc Tiệp cho biết: Năm n&agrave;o d&acirc;n l&agrave;ng Lệ Mật cũng mở lễ hội m&uacute;a rắn, 5 năm c&oacute; một đại lễ quy tụ d&acirc;n ch&uacute;ng khắp 13 trại ( b&acirc;y giờ l&agrave; c&aacute;c phường: Vĩnh Ph&uacute;c, Ngọc Kh&aacute;nh, Cống Y&ecirc;n, Ngọc H&agrave;, Đại Y&ecirc;n, Liễu Giai, Gi&atilde;ng V&otilde;, Cống Vị, Vạn Ph&uacute;c, Thủ Lệ, Xu&acirc;n Biểu, Hữu Tiệp v&agrave; Kim M&atilde;). Ch&uacute;ng t&ocirc;i kh&ocirc;ng hiểu r&otilde; tr&ograve; m&uacute;a rắn nhưng d&acirc;n l&agrave;ng t&ocirc;i c&oacute; nghề bắt rắn v&agrave; nu&ocirc;i rắn từ h&agrave;ng ng&agrave;n năm nay...&quot;.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/61/700_0.615654001397111896.jpg\" /></p>\r\n<p align=\"justify\">\r\n	<font face=\"Arial\">C&aacute;c nh&agrave; nghi&ecirc;n cứu folklor vẫn chưa c&oacute; kết luận thoả đ&aacute;ng về t&iacute;ch tr&ograve; m&uacute;a rắn kỳ lạ n&agrave;y nhưng người d&acirc;n v&ugrave;ng thập tam trại, bằng k&yacute; ức của m&igrave;nh, vẫn tiếp tục t&aacute;i hiện tr&ograve; m&uacute;a rắn với những nghi lễ c&oacute; một kh&ocirc;ng hai. Thần rắn được trang tr&iacute; v&acirc;n m&acirc;y tr&ecirc;n nền xanh da trời, đầu c&oacute; m&agrave;o v&agrave; chiếc lưỡi đỏ khổng lồ. Thần rắn được c&aacute;c tr&aacute;ng đinh m&uacute;a như m&uacute;a rồng nhưng tư thế trầm ổn v&agrave; thần b&iacute; hơn nhiều chứ kh&ocirc;ng vui nhộn như m&uacute;a rồng.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/61/700_0.697598001397111896.jpg\" /></p>\r\n<p align=\"justify\">\r\n	<font face=\"Arial\">Sắc th&aacute;i của lễ hội m&uacute;a rắn l&agrave;ng Lệ Mật mang t&iacute;nh nghi lễ thần b&iacute; n&agrave;y c&oacute; thể l&agrave; dấu vết t&iacute;n ngưỡng cổ đại kh&ocirc;ng chỉ cầu m&ugrave;a ( Rắn l&agrave; biểu tượng &acirc;m t&iacute;nh, hội tụ m&acirc;y mưa n&ecirc;n n&oacute; thể hiện kh&aacute;t vọng mưa thuận gi&oacute; ho&agrave; của cư d&acirc;n n&ocirc;ng nghiệp) m&agrave; c&ograve;n l&agrave; nỗi sợ h&atilde;i, bất lực của con người trước sự sống v&agrave; c&aacute;i chết: Nọc rắn vừa l&agrave;m chết người vừa c&oacute; t&aacute;c dụng cứu sống người (biểu hiệu của ng&agrave;nh y), rắn lột x&aacute;c n&ecirc;n người ta coi n&oacute; l&agrave; bất tử (người gi&agrave; người chết/ rắn gi&agrave; rắn lột x&aacute;c t&aacute;i sinh).</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/61/700_0.733689001397111896.jpg\" /></p>\r\n<p align=\"justify\">\r\n	<font face=\"Arial\">Một điểm kỳ lạ nữa trong lễ hội rắn l&agrave;ng Lệ Mật l&agrave; c&oacute; sự xuất hiện của b&agrave; ch&uacute;a Thượng ng&agrave;n. Liệu c&oacute; phải đ&acirc;y l&agrave; dấu vết cổ xưa thể hiện quyền năng ph&aacute;p thuật chế ngự thần rắn hoặc l&agrave; vị &quot;nữ tư tế&quot; của thần rắn vĩ đại ban ph&uacute;c cho d&acirc;n l&agrave;ng? C&acirc;u hỏi n&agrave;y hiện vẫn chưa c&oacute; lời giải đ&aacute;p thoả đ&aacute;ng nhưng ch&uacute;ng ta n&ecirc;n biết rằng d&acirc;n l&agrave;ng Lệ Mật c&oacute; truyền thống bắt rắn (trừ hại cho d&acirc;n n&ecirc;n mới được sắc phong l&agrave; Uy linh hộ quốc khang d&acirc;n) v&agrave; nu&ocirc;i rắn ( lấy nọc l&agrave;m thuốc cứu người bị rắn cắn) &iacute;t nhất từ h&agrave;ng ng&agrave;n năm nay c&ograve;n danh tiếng đặc sản thịt rắn Lệ Mật chỉ mới xuất hiện từ cuối thế kỷ trước m&agrave; th&ocirc;i.</font></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969101', '0', '1397111896', '0', '0');
INSERT INTO `slm_news` VALUES ('63', '0.850420001397117402.jpg', 'a:7:{i:0;s:24:\"0.850420001397117402.jpg\";i:1;s:24:\"0.167875001397117403.jpg\";i:2;s:24:\"0.399670001397117403.jpg\";i:3;s:24:\"0.553988001397117403.jpg\";i:4;s:24:\"0.684787001397117403.jpg\";i:5;s:24:\"0.907525001397117403.jpg\";i:6;s:24:\"0.019060001397117404.jpg\";}', null, 'Tại sao loài rắn lại là sát thủ độc?', null, null, '12', '', '\r\n	Tại sao qu&aacute; tr&igrave;nh tiến h&oacute;a lại để cho&nbsp;lo&agrave;i rắn&nbsp;sở hữu nhiều độc tố đến mức trở th&agrave;nh &ldquo;thương hiệu&rdquo; như vậy?', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.850420001397117402.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Australia l&agrave; quốc gia nổi tiếng với c&aacute;c sinh vật c&oacute; nọc độc, điển h&igrave;nh l&agrave; nhiều&nbsp;lo&agrave;i rắn c&oacute; nọc độc&nbsp;cao nhất thế giới. Lo&agrave;i rắn nắm giữ &ldquo;vương vị&rdquo; độc nhất thế giới l&agrave; Oxyuranus microlepidotus, lo&agrave;i rắn bản địa của Australia, nọc độc từ một vết cắn của n&oacute; c&oacute; khả năng giết 250.000 con chuột trong ph&ograve;ng th&iacute; nghiệm.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.167875001397117403.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Nọc độc của Oxyuranus microlepidotus đ&atilde; thu h&uacute;t nhiều nghi&ecirc;n cứu khoa học về độc tố. Tại sao nọc độc của lo&agrave;i n&agrave;y lại cao đến vậy? V&agrave; tại sao qu&aacute; tr&igrave;nh tiến h&oacute;a lại để cho&nbsp;lo&agrave;i rắn&nbsp;sở hữu nhiều độc tố đến mức trở th&agrave;nh &ldquo;thương hiệu&rdquo; như vậy?</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.399670001397117403.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Thực tế, con người ch&uacute;ng ta vẫn c&ograve;n hiểu biết rất &iacute;t về c&aacute;ch thức&nbsp;lo&agrave;i rắn&nbsp;sử dụng nọc độc của n&oacute; trong tự nhi&ecirc;n. Tuy nhi&ecirc;n, chế độ ăn uống c&oacute; ảnh hưởng kh&aacute; lớn đến th&agrave;nh phần trong nọc độc của lo&agrave;i n&agrave;y.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.553988001397117403.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Ngo&agrave;i ra, tổ ti&ecirc;n chung của tất cả c&aacute;c lo&agrave;i rắn sở hữu c&ugrave;ng hệ thống nọc độc th&ocirc; sơ. Điều đ&oacute; c&oacute; nghĩa tất cả c&aacute;c lo&agrave;i rắn c&oacute; cơ hội tiến h&oacute;a trở n&ecirc;n độc hơn.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.684787001397117403.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">C&aacute;c lo&agrave;i rắn nhanh ch&oacute;ng đa dạng h&oacute;a trong m&ocirc;i trường mới. Một l&yacute; thuyết từng tồn tại phổ biến l&agrave; lo&agrave;i rắn chỉ đơn giản ph&aacute;t triển nọc độc để c&oacute; thể giết nhanh ch&oacute;ng một con mồi tiềm năng.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.907525001397117403.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Nhưng nghi&ecirc;n cứu mới đ&acirc;y tiết lộ sự tương quan mạnh mẽ giữa sở th&iacute;ch con mồi v&agrave; th&agrave;nh phần nọc độc. Mỗi lo&agrave;i rắn chuy&ecirc;n về một loại mồi kh&aacute;c nhau, ch&uacute;ng c&oacute; thể c&oacute; nọc độc kh&aacute;c nhau.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/63/700_0.019060001397117404.jpg\" /></p>\r\n<p>\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Sống trong một khắc nghiệt, m&ocirc;i trường kh&ocirc; hạn cũng l&agrave; nguy&ecirc;n nh&acirc;n lo&agrave;i rắn ph&aacute;t triển nọc độc, n&oacute; phải bảo tồn t&agrave;i nguy&ecirc;n bằng c&aacute;ch chỉ d&ugrave;ng một phần rất nhỏ nọc độc mỗi khi cắn một con mồi.</span></p>\r\n<p>\r\n	<span style=\"color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; font-size: 16px; line-height: 26px; text-align: justify; background-color: rgb(244, 245, 246);\">Lo&agrave;i rắn cũng phải đồng tiến h&oacute;a với con mồi tự nhi&ecirc;n của ch&uacute;ng, một số con mồi theo thời gian đ&atilde; ph&aacute;t triển đề kh&aacute;ng với nọc độc của rắn. Đ&oacute; l&agrave; một trong những nguy&ecirc;n nh&acirc;n m&agrave; ch&uacute;ng cũng phải &ldquo;n&acirc;ng cấp&rdquo; nọc độc của m&igrave;nh.</span></p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399972125', '0', '1397117402', '0', '0');
INSERT INTO `slm_news` VALUES ('62', '0.588475001397112513.jpg', 'a:4:{i:0;s:24:\"0.588475001397112513.jpg\";i:1;s:24:\"0.856011001397112513.jpg\";i:2;s:24:\"0.053358001397112514.jpg\";i:3;s:24:\"0.298797001397112514.jpg\";}', null, 'Rợn người chuyện mãng xà quẫy đuôi, cả làng mang lời nguyền “chết trẻ”', null, null, '12', '', '\r\n	Rắn l&agrave; một lo&agrave;i vật linh thi&ecirc;ng, năm 2013 dư luận đ&atilde; kh&ocirc;ng &iacute;t lần được chứng kiến nhiều c&acirc;u chuyện ly kỳ về lo&agrave;i rắn.', '<p style=\"text-align: justify;\">\r\n	<strong>Huyền thoại thần rắn hộ người tr&ecirc;n đất Mỏ C&agrave;y</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	C&acirc;u chuyện gắn liền với di t&iacute;ch Đ&igrave;nh Rắn tọa lạc ở x&atilde; Định Thủy, huyện Mỏ C&agrave;y, nay l&agrave; Mỏ C&agrave;y Nam, tỉnh Bến Tre. &Ocirc;ng Trịnh Văn Cước, Ph&oacute; ban Kh&aacute;nh tiết của đ&igrave;nh Rắn cho biết, gọi l&agrave; đ&igrave;nh Rắn bởi theo &ocirc;ng b&agrave; kể lại, từ rất xưa tại gốc me cổ thụ trước đ&igrave;nh c&oacute; đ&ocirc;i rắn rất to. Mỗi khi &ldquo;&ocirc;ng b&agrave; rắn&rdquo; cuộn m&igrave;nh c&ograve;n lớn hơn c&aacute;i lu chứa nước. D&acirc;n trong v&ugrave;ng sợ h&atilde;i, kh&ocirc;ng ai d&aacute;m lại gần nơi &ldquo;&ocirc;ng b&agrave; rắn&rdquo; đang tr&uacute; ngụ. Ng&agrave;y nọ, c&oacute; anh n&ocirc;ng d&acirc;n đi về khuya gh&eacute; ngang gốc c&acirc;y nghỉ m&aacute;t. &ldquo;Mắt nhắm mắt mở&rdquo; thế n&agrave;o lại chọn ngay gốc c&acirc;y me l&agrave; nh&agrave; của &ldquo;&ocirc;ng b&agrave; rắn&rdquo;. Thất kinh, anh n&ocirc;ng d&acirc;n toan chạy, được dăm ba bước đ&atilde; thấy c&oacute; con beo đốm nhe nanh chắn ngang đường. Anh n&ocirc;ng d&acirc;n chắc mẩm phen n&agrave;y tan x&aacute;c, liền quỳ sụp xuống khấn trời đất độ tr&igrave;.</p>\r\n<p style=\"text-align: center;\">\r\n	B&ecirc;n tai anh nghe tiếng x&eacute; gi&oacute; vun v&uacute;t, rồi tiếng beo đốm gầm l&ecirc;n kinh h&atilde;i, mở mắt ra đ&atilde; thấy beo đốm đ&atilde; nằm trong bụng &ldquo;&ocirc;ng rắn&rdquo;, th&acirc;n &ldquo;&ocirc;ng rắn&rdquo; ph&igrave;nh to ra như gốc c&acirc;y cổ thụ. &ldquo;&Ocirc;ng&rdquo; nh&igrave;n con người nhỏ b&eacute; đang &ldquo;x&aacute;m hồn&rdquo;, lết cũng kh&ocirc;ng nổi, b&ograve; c&agrave;ng kh&ocirc;ng xong v&igrave; sợ h&atilde;i, rồi đủng đỉnh quay đầu trườn đi. Anh n&ocirc;ng d&acirc;n về nh&agrave; bệnh 3 ng&agrave;y mới định thần trở lại. Sau khi tỉnh t&aacute;o, liền tụ tập b&agrave; con, d&acirc;n l&agrave;ng lập đ&igrave;nh tr&ecirc;n m&ocirc; đất c&oacute; c&acirc;y me nơi &ldquo;&ocirc;ng b&agrave; rắn&rdquo; ở để thờ c&uacute;ng v&igrave; c&aacute;i ơn cứu mạng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/62/700_0.588475001397112513.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Người d&acirc;n Mỏ C&agrave;y c&ograve;n kể rằng, đ&igrave;nh Rắn vốn dĩ rất nhiều rắn. Mỗi khi c&uacute;ng kiếng, rắn tr&egrave;o l&ecirc;n cả m&aacute;i đ&igrave;nh, nhiều đến nỗi cuộn v&agrave;o nhau, &ldquo;rối n&ugrave;i&rdquo;. C&aacute;c vị l&atilde;o l&agrave;ng trong ban Kh&aacute;nh tiết phải van v&aacute;i, mong c&aacute;c &ldquo;&ocirc;ng b&agrave;&rdquo; đừng cắn ai, rồi d&ugrave;ng cả v&aacute;n để l&agrave;m r&agrave;o chắn xung quanh đ&igrave;nh, bởi sợ rắn nhiều qu&aacute;, người đi sẽ giẫm phải mang tội với thần rắn linh thi&ecirc;ng.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>B&iacute; ẩn rắn hổ m&acirc;y hiểu tiếng người v&agrave; th&acirc;n thiết với gia chủ</strong></p>\r\n<p style=\"text-align: center;\">\r\n	C&acirc;u chuyện kỳ b&iacute; diễn ra ở ấp Vồ Đầu, x&atilde; An Hảo, huyện Tịnh Bi&ecirc;n, tỉnh An Giang. C&aacute;ch đ&acirc;y hơn 10 năm, &ocirc;ng Đinh Phi V&acirc;n (40 tuổi) lập gia đ&igrave;nh với b&agrave; Nguyễn Thị Ngọc Sơn &ndash; người sinh ra v&agrave; lớn l&ecirc;n ở n&uacute;i Cấm, rồi chọn vồ Mồ C&ocirc;i tr&ecirc;n lưng chừng n&uacute;i để mưu sinh.<br />\r\n	Từng đối mặt với rất nhiều th&uacute; dữ, nhưng với gia đ&igrave;nh &ocirc;ng V&acirc;n, chuyện gặp lo&agrave;i rắn độc giữa n&uacute;i rừng l&agrave; điều kỳ lạ nhất. Một lần, &ocirc;ng V&acirc;n ra ph&iacute;a sau nh&agrave; để l&agrave;m c&aacute;, chuẩn bị nấu cơm th&igrave; bắt gặp một con rắn hổ m&acirc;y d&agrave;i hơn 4m, nặng khoảng 10kg đang ng&oacute;c đầu như chuẩn bị bắt mồi. Khi đ&oacute; vợ &ocirc;ng V&acirc;n ở trong nh&agrave; nh&igrave;n ra thấy vậy nhưng kh&ocirc;ng n&oacute;i th&agrave;nh tiếng v&igrave; sợ. &Ocirc;ng V&acirc;n thấy con rắn như muốn tấn c&ocirc;ng m&igrave;nh. Kinh nghiệm sống ở rừng l&acirc;u năm, n&ecirc;n &ocirc;ng biết lo&agrave;i rắn hổ m&acirc;y n&agrave;y ban đầu chỉ l&agrave;m động t&aacute;c dọa đối thủ. Nếu đe dọa tới t&iacute;nh mạng ch&uacute;ng mới tấn c&ocirc;ng lại. Biết vậy n&ecirc;n &ocirc;ng V&acirc;n l&ugrave;i v&agrave;o nh&agrave; để con rắn bỏ đi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/62/700_0.856011001397112513.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Nửa th&aacute;ng sau, vợ chồng &ocirc;ng V&acirc;n mắc v&otilde;ng ở gốc Xo&agrave;i ph&iacute;a sau nh&agrave; ngủ trưa lại gặp con rắn n&agrave;y lần nữa. Con rắn b&ograve; ngang dưới v&otilde;ng, ph&aacute;t hiện c&oacute; người đang nằm đu đưa, n&oacute; chỉ khựng lại rồi b&ograve; đi tiếp. Từ đ&oacute; trở đi, cứ v&agrave;i ng&agrave;y, khi đ&ecirc;m về, n&oacute; lại b&ograve; gần về nh&agrave; &ocirc;ng V&acirc;n để bắt chuột. Căn nh&agrave; &ocirc;ng V&acirc;n được thiết kế đặc biệt: Lưng dựa v&agrave;o n&uacute;i, m&aacute;i t&ocirc;n nằm s&aacute;t v&aacute;ch đ&aacute;, n&ecirc;n chuột, rắn đều c&oacute; thể b&ograve; l&ecirc;n m&aacute;i nh&agrave; dễ d&agrave;ng. V&igrave; vậy con rắn hổ m&acirc;y n&agrave;y chọn m&aacute;i nh&agrave; &ocirc;ng l&agrave;m &ldquo;địa b&agrave;n&rdquo; săn bắt chuột. Mỗi khi tới gần nh&agrave;, con rắn đều ph&aacute;t ra &acirc;m thanh giống tiếng hu&yacute;t s&aacute;o như n&ecirc;n b&aacute;o hiệu cho gia đ&igrave;nh biết c&oacute; sự xuất hiện của n&oacute;. Biết vậy, n&ecirc;n gia đ&igrave;nh &ocirc;ng V&acirc;n kh&ocirc;ng t&igrave;m c&aacute;ch hại con rắn n&agrave;y m&agrave; sống h&ograve;a b&igrave;nh v&igrave; n&oacute; cũng bắt chuột gi&uacute;p gia đ&igrave;nh.</p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>M&atilde;ng x&agrave; quẫy đu&ocirc;i, cả l&agrave;ng mang lời nguyền &ldquo;chết trẻ&rdquo;</strong></p>\r\n<p style=\"text-align: center;\">\r\n	Chuyện xảy ra ở th&ocirc;n Vũ Dương, x&atilde; Hồng Quang, huyện &Acirc;n Thi, tỉnh Hưng Y&ecirc;n. Nơi đ&acirc;y được mệnh danh l&agrave; &lsquo;v&ugrave;ng đất ma&rsquo; bởi những c&acirc;u chuyện b&iacute; ẩn kh&ocirc;ng lời giải đ&aacute;p. Hơn chục năm trở lại đ&acirc;y, những hộ sinh sống tr&ecirc;n khu đất n&agrave;y đều gặp chuyện kh&ocirc;ng may. Theo thống k&ecirc; của &ocirc;ng trưởng th&ocirc;n Nguyễn Văn Mỹ, số người chết ở v&ugrave;ng n&agrave;y phải l&ecirc;n tới hơn 30 người, nhất l&agrave; đều v&agrave;o độ tuổi dưới 40. Nhiều người chết đột tử, chết đuối, chết tai nạn&hellip; khi mới đang ở độ tuổi thanh xu&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/62/700_0.053358001397112514.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i ra, những hộ sinh sống ở đ&acirc;y đều kh&ocirc;ng được y&ecirc;n, kh&ocirc;ng người c&oacute; vấn đề thần kinh th&igrave; cũng c&oacute; người bị ma l&agrave;m. L&yacute; giải cho một loạt hiện tượng lạ xảy ra trong khoảng hơn chục năm trở lại đ&acirc;y, &ocirc;ng Nguyễn Văn Mỹ &ndash; trưởng th&ocirc;n cho biết: &ldquo;T&ocirc;i cũng chỉ được nghe c&aacute;c cụ truyền lại c&acirc;u chuyện rằng khu đất đ&oacute; vồn nằm tr&ecirc;n địa thế đu&ocirc;i của một con m&atilde;ng x&agrave; lớn. Mỗi khi n&oacute; chuyển động th&igrave; những người sinh sống tr&ecirc;n đ&oacute; đều gặp chuyện kh&ocirc;ng hay. C&oacute; lẽ những chuyện n&agrave;y li&ecirc;n quan tới truyền thuyết đ&oacute; chăng. Thật hư thế n&agrave;o th&igrave; ch&uacute;ng t&ocirc;i c&ograve;n phải chờ c&aacute;c nh&agrave; khoa học chuy&ecirc;n ng&agrave;nh xem x&eacute;t nhưng quả l&agrave; những người sống ở khu đ&oacute; kh&ocirc;ng nhiều th&igrave; &iacute;t đều gặp chuyện kh&ocirc;ng hay lắm&rdquo;.</p>\r\n<p style=\"text-align: justify;\">\r\n	&Ocirc;ng Mỹ cho&nbsp;hay, th&ocirc;n Vũ Dương trước kia c&oacute; hai ng&ocirc;i đ&igrave;nh rất lớn m&agrave; d&acirc;n l&agrave;ng vẫn gọi l&agrave; đ&igrave;nh tr&ecirc;n, đ&igrave;nh dưới. Theo nhiều vụ b&ocirc; l&atilde;o trong l&agrave;ng hai ng&ocirc;i đ&igrave;nh như hai địa điểm trấn yểm linh kh&iacute; của v&ugrave;ng đất n&agrave;y. Một ng&ocirc;i trấn ở ph&iacute;a tr&ecirc;n, một ng&ocirc;i trấn ở ph&iacute;a dưới.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tuy nhi&ecirc;n, sau năm 1945, do hai ng&ocirc;i đ&igrave;nh c&oacute; diện t&iacute;ch qu&aacute; lớn n&ecirc;n thực d&acirc;n Ph&aacute;p đ&atilde; cho đốt hết. Hai ng&ocirc;i đ&igrave;nh bị ch&aacute;y hầu như kh&ocirc;ng c&ograve;n g&igrave;. Thế rồi sau ng&agrave;y h&ograve;a b&igrave;nh, thay v&igrave; tu bổ th&igrave; d&acirc;n lại đua nhau ph&aacute; đ&igrave;nh, ch&ugrave;a, lấy gạch x&acirc;y dựng c&aacute;c c&ocirc;ng tr&igrave;nh kh&aacute;c. Thế l&agrave; hai ng&ocirc;i đ&igrave;nh cổ đ&atilde; bị x&oacute;a sổ từ đấy. C&oacute; lẽ v&igrave; chuyện n&agrave;y m&agrave; c&agrave;ng về sau, c&agrave;ng c&oacute; nhiều chuyện kh&ocirc;ng hay xảy ra ở v&ugrave;ng đất đ&oacute; cũng v&igrave; hai ng&ocirc;i đ&igrave;nh đ&atilde; bị ph&aacute; bỏ.</p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969353', '0', '1397112513', '0', '0');
INSERT INTO `slm_news` VALUES ('64', '0.420815001397118170.jpg', 'a:5:{i:0;s:24:\"0.420815001397118170.jpg\";i:1;s:24:\"0.932746001397118268.jpg\";i:2;s:24:\"0.068956001397118269.jpg\";i:3;s:24:\"0.180970001397118269.jpg\";i:4;s:24:\"0.283728001397118269.jpg\";}', null, 'Người khai phá Thập tam trại', null, null, '25', '', '\r\n	Thập tam trại l&agrave; quần thể 13 l&agrave;ng nghề ở ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long xưa. Đ&acirc;y l&agrave; khu vực được nhiều nh&agrave; sử học, khảo cổ học quan t&acirc;m khảo cứu v&igrave; c&oacute; quan hệ mật thiết với khu Ho&agrave;ng th&agrave;nh Thăng Long. Ai l&agrave; người c&oacute; c&ocirc;ng khai ph&aacute; v&agrave; th&agrave;nh lập khu Thập tam trại hiện vẫn c&ograve;n l&agrave; vấn đề g&acirc;y tranh c&atilde;i.&nbsp;', '<p>\r\n	Thập tam trại l&agrave; quần thể 13 l&agrave;ng nghề ở ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long xưa. Đ&acirc;y l&agrave; khu vực được nhiều nh&agrave; sử học, khảo cổ học quan t&acirc;m khảo cứu v&igrave; c&oacute; quan hệ mật thiết với khu Ho&agrave;ng th&agrave;nh Thăng Long. Ai l&agrave; người c&oacute; c&ocirc;ng khai ph&aacute; v&agrave; th&agrave;nh lập khu Thập tam trại hiện vẫn c&ograve;n l&agrave; vấn đề g&acirc;y tranh c&atilde;i. Tuy nhi&ecirc;n, được nhiều người đồng t&igrave;nh nhất vẫn l&agrave; ch&agrave;ng trai họ Ho&agrave;ng, c&oacute; tư liệu c&ograve;n ghi r&otilde; l&agrave; Ho&agrave;ng Ph&uacute;c Trung, vị Th&agrave;nh ho&agrave;ng của l&agrave;ng cổ Lệ Mật v&agrave; cả khu Thập tam trại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/64/700_0.932746001397118268.jpg\" /></p>\r\n<p>\r\n	Sự t&iacute;ch về ch&agrave;ng trai họ Ho&agrave;ng được viết trong cuốn Lược sử H&agrave; Nội của t&aacute;c giả Nguyễn Văn T&acirc;n. Cuối thế kỷ XI, dưới thời L&yacute;, c&oacute;&nbsp;<span class=\"ry8ca2786\" id=\"ry8ca2786_1\" style=\"box-sizing: border-box; list-style: none; float: none; padding: 0px; margin: 0px; border-width: 1px; border-style: solid; border-top-color: transparent; border-right-color: transparent; border-left-color: transparent; text-decoration: underline; color: rgb(0, 153, 0); cursor: pointer; display: inline !important;\">m</span>ột n&agrave;ng c&ocirc;ng ch&uacute;a dong thuyền chơi tr&ecirc;n s&ocirc;ng Đuống. Gặp cơn gi&ocirc;ng tố bất ngờ, thuyền lật v&agrave; c&ocirc;ng ch&uacute;a bị chết đuối. Tin về kinh, quan qu&acirc;n hoảng sợ, thay nhau m&ograve; lặn khắp cả kh&uacute;c s&ocirc;ng m&agrave; chẳng t&igrave;m thấy x&aacute;c. Nh&agrave; vua ra lệnh thưởng tước, cấp ruộng cho ai t&igrave;m được x&aacute;c c&ocirc;ng ch&uacute;a.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/64/700_0.420815001397118170.jpg\" /></p>\r\n<p>\r\n	C&oacute; một ch&agrave;ng trai ngh&egrave;o họ Ho&agrave;ng, qu&ecirc; ở Lệ Mật, chuy&ecirc;n nghề ch&agrave;i lưới, xin được lặn t&igrave;m. Thuyền đưa ch&agrave;ng trai ra ng&atilde; ba s&ocirc;ng, chỗ nước xo&aacute;y, ch&agrave;ng nhảy xuống lặn m&ograve;. Sau v&agrave;i lượt, quả nhi&ecirc;n vớt được x&aacute;c c&ocirc;ng ch&uacute;a. Nh&agrave; vua định trọng thưởng cho ch&agrave;ng trai, nhưng ch&agrave;ng trai đ&atilde; từ chối mọi tước lộc, chỉ xin vua được chi&ecirc;u tập d&acirc;n ngh&egrave;o đến khai hoang ở v&ugrave;ng đất hoang vu ph&iacute;a T&acirc;y kinh th&agrave;nh. Được vua ưng thuận, ch&agrave;ng về qu&ecirc; t&igrave;m rủ d&acirc;n ngh&egrave;o sang khai hoang. Họ c&ugrave;ng nhau khai ph&aacute; rừng rậm, đồng lầy dọc bờ s&ocirc;ng T&ocirc; Lịch, lập được 13 trại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/64/700_0.068956001397118269.jpg\" /></p>\r\n<p>\r\n	Ch&agrave;ng trai dũng cảm v&agrave; chỉ nghĩ tới hạnh ph&uacute;c của d&acirc;n ngh&egrave;o được d&acirc;n Lệ Mật v&agrave; 13 trại ở Thăng Long v&ocirc; c&ugrave;ng qu&yacute; mến. Khi mất, ch&agrave;ng được t&ocirc;n l&agrave;m Th&agrave;nh ho&agrave;ng ở 13 trại v&agrave; ở qu&ecirc; nh&agrave;. Hiện nay, ở khu Vĩnh Ph&uacute;c (quận Ba Đ&igrave;nh, H&agrave; Nội) c&ograve;n ng&ocirc;i mộ v&agrave; đền thờ ch&agrave;ng trai ở đ&oacute;. Tuy nhi&ecirc;n, năm 2010, lần đầu ti&ecirc;n, H&agrave; Nội tổ chức Lễ hội Thập tam trại với quy m&ocirc; lớn hướng tới 1000 năm Thăng Long. Nhiều cơ quan b&aacute;o ch&iacute; đồng loạt đưa&nbsp;<span class=\"ry8ca2786\" id=\"ry8ca2786_2\" style=\"box-sizing: border-box; list-style: none; float: none; padding: 0px; margin: 0px; border-width: 1px; border-style: solid; border-top-color: transparent; border-right-color: transparent; border-left-color: transparent; text-decoration: underline; color: rgb(0, 153, 0); cursor: pointer; display: inline !important;\">tin</span>&nbsp;về Lễ hội n&agrave;y với th&ocirc;ng tin:</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/64/700_0.180970001397118269.jpg\" /></p>\r\n<p>\r\n	Đ&acirc;y l&agrave; lễ hội tưởng nhớ Nguyễn Qu&yacute; C&ocirc;ng, người l&agrave;ng Lệ Mật (nay thuộc phường Việt Hưng, quận Long Bi&ecirc;n, H&agrave; Nội) c&oacute; c&ocirc;ng khai khẩn v&ugrave;ng đất hoang ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long, tạo n&ecirc;n v&ugrave;ng n&ocirc;ng nghiệp tr&ugrave; ph&uacute; ven kinh đ&ocirc; với địa danh Thập tam trại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/64/700_0.283728001397118269.jpg\" /></p>\r\n<p>\r\n	Th&ocirc;ng tin n&agrave;y được đăng tải tr&ecirc;n b&aacute;o điện tử Đảng Cộng sản v&agrave; website th&ocirc;ng tin về lễ hội của Trung t&acirc;m C&ocirc;ng nghệ th&ocirc;ng tin thuộc Bộ Văn h&oacute;a, Thể thao v&agrave; Du lịch. Cũng về Lễ hội n&agrave;y, b&aacute;o H&agrave; Nội mới viết: Lễ hội tại đ&igrave;nh Liễu Giai (Ba Đ&igrave;nh, H&agrave; Nội) năm 2010 để kỷ niệm 984 năm ng&agrave;y sinh Đức Th&agrave;nh ho&agrave;ng l&agrave;ng Lệ Mật Ho&agrave;ng Ph&uacute;c Trung, người c&oacute; c&ocirc;ng khai ph&aacute; miền đất ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long, s&aacute;ng lập Thập tam trại, được c&aacute;c triều đại phong kiến nước ta sắc phong l&agrave; &ldquo;Thượng đẳng ph&uacute;c thần đại vương&rdquo;.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969087', '0', '1397118170', '0', '0');
INSERT INTO `slm_news` VALUES ('65', '0.359053001397118795.JPG', 'a:2:{i:0;s:24:\"0.359053001397118795.JPG\";i:1;s:24:\"0.617508001399367698.jpg\";}', null, 'Lệ Mật: Làng nghề độc nhất Việt Nam', null, null, '25', '', '\r\n	Trong kho t&agrave;ng ấy kh&ocirc;ng thể kh&ocirc;ng nhắc đến l&agrave;ng nu&ocirc;i rắn Lệ Mật, một trong những ng&ocirc;i l&agrave;ng c&oacute; lịch sử l&acirc;u đời của đất Kinh kỳ từ thế kỷ XI (nay thuộc phường Việt Hưng, quận Long Bi&ecirc;n). Nghề săn bắt, chăn nu&ocirc;i v&agrave; chế biến rắn l&agrave; một nghề độc đ&aacute;o của Lệ Mật m&agrave; &iacute;t nơi c&oacute; được', '<p>\r\n	Tiết mục m&uacute;a rắn độc đ&aacute;o trong lễ hội l&agrave;ng Lệ Mật.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/65/700_0.847153001397118879.jpg\" /></p>\r\n<p>\r\n	C&aacute;c cụ cao ni&ecirc;n l&agrave;ng Lệ Mật n&oacute;i rằng, nghề nu&ocirc;i rắn ở đ&acirc;y c&oacute; từ bao giờ chẳng ai nhớ r&otilde;. Tương truyền v&agrave;o thời nh&agrave; L&yacute;, c&ocirc;ng ch&uacute;a con vua L&yacute; Nh&acirc;n T&ocirc;ng đang dạo chơi tr&ecirc;n s&ocirc;ng Thi&ecirc;n Đức chợt s&oacute;ng gi&oacute; nổi l&ecirc;n, thủy qu&aacute;i xuất hiện, bắt c&ocirc;ng ch&uacute;a. Nh&agrave; vua thương con, cử nhiều tướng t&agrave;i đi diệt thủy qu&aacute;i nhưng kh&ocirc;ng ai l&agrave;m được.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/65/700_0.359053001397118795.JPG\" /></p>\r\n<p>\r\n	C&oacute; ch&agrave;ng trai họ Ho&agrave;ng sống ở l&agrave;ng Tr&ugrave; Mật v&otilde; nghệ cao cường lại c&oacute; t&agrave;i bơi lội xin vua cho đi. Cuộc chiến đấu s&ocirc;i sục cả l&ograve;ng s&ocirc;ng, với sự mưu tr&iacute;, dũng cảm, ch&agrave;ng trai đ&atilde; diệt được thủy qu&aacute;i. Nh&agrave; vua ban thưởng cho ch&agrave;ng trai họ Ho&agrave;ng quan tước, v&agrave;ng bạc nhưng ch&agrave;ng kh&ocirc;ng nhận chỉ xin về lập ấp ở ph&iacute;a T&acirc;y th&agrave;nh Thăng Long, biến nơi hoang vu th&agrave;nh 13 l&agrave;ng trại tr&ugrave; ph&uacute;. Sau khi &quot;khai l&agrave;ng lập ấp&quot;, ch&agrave;ng quay về củng cố l&agrave;ng cũ. Sau n&agrave;y, ch&agrave;ng mất, d&acirc;n l&agrave;ng lập đ&igrave;nh thờ ch&agrave;ng, suy t&ocirc;n l&agrave; Th&agrave;nh ho&agrave;ng Theo gương ch&agrave;ng, d&acirc;n ch&uacute;ng l&agrave;ng Lệ Mật ngo&agrave;i việc nh&agrave; n&ocirc;ng c&ograve;n ph&aacute;t triển th&ecirc;m nghề bắt rắn, nu&ocirc;i rắn v&agrave; nghề rắn cũng bắt nguồn từ đ&oacute;. Thời gian đầu d&acirc;n l&agrave;ng Lệ Mật chỉ nu&ocirc;i rắn để ng&acirc;m rượu l&agrave;m thuốc chữa bệnh, sau n&agrave;y da rắn được l&agrave;m đồ mỹ nghệ như d&acirc;y lưng, v&iacute; da, gi&agrave;y da&hellip;</p>\r\n<p>\r\n	Do c&oacute; sự giao lưu kinh tế n&ecirc;n rắn Lệ Mật c&ograve;n trở th&agrave;nh đặc sản của người d&acirc;n kinh kỳ v&agrave; thực kh&aacute;ch gần xa. Ban đầu chỉ c&oacute; v&agrave;i m&oacute;n rất đơn giản như rắn ướp muối ti&ecirc;u nướng, rắn x&agrave;o h&agrave;nh tỏi, rắn băm nhỏ x&agrave;o gi&ograve;n&hellip; ng&agrave;y nay, c&aacute;c nh&agrave; h&agrave;ng đ&atilde; phục vụ nhiều m&oacute;n ăn mới hơn, thỏa m&atilde;n khẩu vị của thực kh&aacute;ch. Hiện ở l&agrave;ng đ&atilde; c&oacute; tr&ecirc;n 100 hộ với 370 nh&agrave; h&agrave;ng với c&aacute;c m&oacute;n ăn chuy&ecirc;n rắn, mỗi ng&agrave;y đ&oacute;n tr&ecirc;n 500 lượt kh&aacute;ch trong v&agrave; ngo&agrave;i nước. Lệ Mật được đ&aacute;nh gi&aacute; l&agrave; l&agrave;ng rắn nổi tiếng, độc nhất ở Việt Nam v&agrave; tr&ecirc;n thế giới. Mặc d&ugrave; quận Long Bi&ecirc;n c&oacute; tốc độ đ&ocirc; thị h&oacute;a nhanh song Lệ Mật vẫn giữ được d&aacute;ng dấp của l&agrave;ng Việt cổ, c&oacute; m&aacute;i đ&igrave;nh, giếng nước, c&acirc;y đa, những ng&ocirc;i nh&agrave; kiến tr&uacute;c cổ, thu h&uacute;t kh&aacute; đ&ocirc;ng du kh&aacute;ch trong v&agrave; ngo&agrave;i nước tới tham quan. Đ&oacute;n ch&uacute;ng t&ocirc;i v&agrave;o đ&uacute;ng ng&agrave;y l&agrave;ng nghề đ&oacute;n nhận quyết định của UBND TP H&agrave; Nội c&ocirc;ng nhận danh hiệu &quot;L&agrave;ng nghề truyền thống nu&ocirc;i rắn Lệ Mật&quot;, &ocirc;ng Nguyễn Danh Vĩnh, Chủ tịch UBND phường Việt Hưng kh&ocirc;ng giấu nổi niềm vui: Với người d&acirc;n Lệ Mật, h&igrave;nh tượng con rắn đ&atilde; trở th&agrave;nh biểu tượng của l&agrave;ng, nghề bắt v&agrave; nu&ocirc;i rắn l&agrave; nghề ch&iacute;nh hoặc duy nhất của nhiều gia đ&igrave;nh ở đ&acirc;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"484\" src=\"http://langquetoi.com/images/news/65/700_0.617508001399367698.jpg\" width=\"621\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Rắn được chế biến lần lượt th&agrave;nh 7 đến 10 m&oacute;n v&agrave; bưng dần l&ecirc;n, ăn n&oacute;ng.</p>\r\n<p>\r\n	Ngọn lửa truyền thống của l&agrave;ng nghề từ thời &ocirc;ng cha xưa đ&atilde; được g&igrave;n giữ cho đến tận h&ocirc;m nay. Anh Trương Xu&acirc;n Chu, một trong những người nu&ocirc;i rắn giỏi ở l&agrave;ng cho biết, cuộc sống đ&ocirc; thị đang len lỏi v&agrave;o từng con ng&otilde; nhỏ trong l&agrave;ng, nhưng kh&ocirc;ng v&igrave; thế m&agrave; d&acirc;n bỏ nghề. Ai cũng mong c&oacute; th&ecirc;m tiền để mở rộng quy m&ocirc; sản xuất, nhưng đ&acirc;y l&agrave; vấn đề kh&oacute; khăn. Ngo&agrave;i nu&ocirc;i rắn, người d&acirc;n ở đ&acirc;y c&ograve;n l&agrave;m du lịch sinh th&aacute;i kết hợp ẩm thực, vừa tạo việc l&agrave;m cho lao động địa phương vừa để du kh&aacute;ch trong v&agrave; ngo&agrave;i nước biết đến l&agrave;ng nghề - Chủ nhiệm HTX Kinh doanh dịch vụ, Trưởng ban Quản l&yacute; l&agrave;ng nghề Lệ Mật Trương B&aacute; Hu&acirc;n cho biết, để bảo tồn v&agrave; ph&aacute;t huy gi&aacute; trị của l&agrave;ng nghề truyền thống, trong những năm tới, Việt Hưng sẽ trở th&agrave;nh phường đ&ocirc; thị đặc biệt với m&ocirc; h&igrave;nh &quot;l&agrave;ng cổ trong phố&quot;.</p>\r\n<p>\r\n	Phường sẽ tổ chức khảo s&aacute;t, nghi&ecirc;n cứu m&ocirc; h&igrave;nh chăn nu&ocirc;i rắn tập trung tại một số điểm xa khu d&acirc;n cư; tiếp tục mở rộng m&ocirc; h&igrave;nh chăn nu&ocirc;i rắn sinh trưởng kết hợp với du lịch sinh th&aacute;i ẩm thực, ho&agrave;n thiện đề &aacute;n x&acirc;y dựng tuyến phố ẩm thực l&agrave;ng nghề Lệ Mật với nhiều nh&agrave; h&agrave;ng đặc sản mang bản sắc truyền thống; x&acirc;y dựng trung t&acirc;m giới thiệu l&agrave;ng nghề&hellip; để Lệ Mật th&ecirc;m gi&agrave;u c&oacute;.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969074', '0', '1397118795', '0', '0');
INSERT INTO `slm_news` VALUES ('83', '0.175098001398767430.jpg', 'a:14:{i:0;s:24:\"0.175098001398767430.jpg\";i:1;s:24:\"0.618763001398767430.jpg\";i:2;s:24:\"0.132756001398767431.jpg\";i:3;s:24:\"0.516817001398767431.jpg\";i:4;s:24:\"0.937265001398767431.jpg\";i:5;s:24:\"0.168841001398767432.jpg\";i:6;s:24:\"0.595726001398767432.jpg\";i:7;s:24:\"0.067418001398767433.jpg\";i:8;s:24:\"0.760104001398767433.jpg\";i:9;s:24:\"0.145724001398767434.jpg\";i:10;s:24:\"0.695278001398767434.jpg\";i:11;s:24:\"0.185362001398767435.jpg\";i:12;s:24:\"0.665134001398767435.jpg\";i:13;s:24:\"0.023546001398767436.jpg\";}', null, 'Hà Nội ngày cuối xuân', null, null, '12', '', '\r\n	Đi qua những con phố như Phan Đ&igrave;nh Ph&ugrave;ng, Trần Ph&uacute;... v&agrave;o những ng&agrave;y cuối th&aacute;ng tư, người ta c&oacute; cảm gi&aacute;c như H&agrave; Nội đang giữa m&ugrave;a thu.', '<p>\r\n	Đi qua những con phố như Phan Đ&igrave;nh Ph&ugrave;ng, Trần Ph&uacute;... v&agrave;o những ng&agrave;y cuối th&aacute;ng tư, người ta c&oacute; cảm gi&aacute;c như H&agrave; Nội đang giữa m&ugrave;a thu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.618763001398767430.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Kh&aacute;c với c&aacute;c nước xứ lạnh, l&aacute; v&agrave;ng ở H&agrave; Nội kh&ocirc;ng rụng v&agrave;o m&ugrave;a thu. Phải qua xu&acirc;n, những c&acirc;y sấu tr&ecirc;n phố mới tr&uacute;t l&aacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.132756001398767431.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Phố Phan Đ&igrave;nh Ph&ugrave;ng với hai h&agrave;ng c&acirc;y cổ thụ trồng tr&ecirc;n một vỉa h&egrave; l&agrave; nơi đẹp nhất v&agrave;o dịp l&aacute; sấu rụng</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.516817001398767431.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Kh&ocirc;ng kh&iacute; m&aacute;t mẻ của trận mưa r&agrave;o cuối xu&acirc;n v&agrave; những thảm l&aacute; v&agrave;ng rụng đầy ắp khiến cho bất kỳ ai cũng c&oacute; cảm gi&aacute;c man m&aacute;c, nhẹ nh&agrave;ng như những ng&agrave;y thu vừa mới bắt đầu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.937265001398767431.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Sau cơn mưa, những h&agrave;ng sấu tr&ecirc;n đường Phan Đ&igrave;nh Ph&ugrave;ng c&agrave;ng tr&uacute;t nhiều l&aacute; hơn. Những v&ograve;m l&aacute; c&ograve;n tr&ecirc;n c&acirc;y xanh mướt tạo n&ecirc;n sự tương phản th&uacute; vị với m&agrave;u v&agrave;ng của l&aacute; dưới vỉa h&egrave;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.168841001398767432.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Khi l&aacute; v&agrave;ng tr&uacute;t xuống cũng l&agrave; l&uacute;c hoa loa k&egrave;n v&agrave;o cuối vụ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.595726001398767432.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Dọc con phố Trần Ph&uacute;, người đi đường cũng cảm nhận được m&ugrave;a thu trong l&ograve;ng phố.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"525\" src=\"http://langquetoi.com/images/news/83/700_0.067418001398767433.jpg\" width=\"492\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Điều th&uacute; vị của m&ugrave;a l&aacute; rụng ở H&agrave; Nội l&agrave; c&acirc;y kh&ocirc;ng trơ trụi m&agrave; vẫn rợp l&aacute; xanh, che nắng cho người đi đường.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.760104001398767433.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	D&ugrave; ng&agrave;y n&agrave;o những người lao c&ocirc;ng cũng qu&eacute;t dọn nhưng l&aacute; tr&uacute;t nhiều n&ecirc;n nhanh ch&oacute;ng phủ k&iacute;n vỉa h&egrave;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"518\" src=\"http://langquetoi.com/images/news/83/700_0.145724001398767434.jpg\" width=\"452\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đường Đinh Ti&ecirc;n Ho&agrave;ng (ven hồ Ho&agrave;n Kiếm) l&agrave; nơi tập trung nhiều kh&aacute;ch du lịch tham quan.</p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"538\" src=\"http://langquetoi.com/images/news/83/700_0.695278001398767434.jpg\" width=\"448\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Vỉa h&egrave; dọc hồ Ho&agrave;n Kiếm cũng phủ đầy l&aacute; v&agrave;ng. Đ&acirc;y l&agrave; nơi đi bộ, r&egrave;n luyện thể thao y&ecirc;u th&iacute;ch của người d&acirc;n thủ đ&ocirc;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"558\" src=\"http://langquetoi.com/images/news/83/700_0.185362001398767435.jpg\" width=\"451\" /></p>\r\n<p style=\"text-align: center;\">\r\n	L&aacute; rụng trong nắng sớm ở đường H&agrave;ng Khay.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.665134001398767435.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&acirc;y sấu gắn b&oacute; với tuổi thơ của nhiều người, m&ugrave;a l&aacute; sấu rụng cũng đem lại nhiều k&yacute; ức đẹp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.175098001398767430.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c c&ocirc; g&aacute;i H&agrave; th&agrave;nh tranh thủ lưu giữ tuổi trẻ với t&agrave; &aacute;o d&agrave;i trắng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/83/700_0.023546001398767436.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	G&aacute;nh hoa quả b&aacute;o hiệu m&ugrave;a h&egrave; sắp tới với nhiều loại tr&aacute;i ngon.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1398767884', '0', '1398767430', '0', '0');
INSERT INTO `slm_news` VALUES ('67', '0.426647001397190957.jpg', 'a:3:{i:0;s:24:\"0.426647001397190957.jpg\";i:1;s:24:\"0.622663001397190957.jpg\";i:2;s:24:\"0.835049001397190957.jpg\";}', null, 'Huyền thoại làng rắn Lệ Mật', null, null, '25', '', '\r\n	Rắn từ l&acirc;u trong d&acirc;n gian đ&atilde; l&agrave; một biểu tượng văn h&oacute;a của c&aacute;c nước phương Đ&ocirc;ng, v&agrave; rắn c&ugrave;ng với sự ph&aacute;t t&iacute;ch của lịch sử cũng đ&atilde; trở th&agrave;nh biểu tượng của một l&agrave;ng ẩm thực truyền thống - l&agrave;ng rắn Lệ Mật.&nbsp;', '<p>\r\n	Lệ Mật l&agrave; một l&agrave;ng qu&ecirc; thuộc x&atilde; Việt Hưng, huyện Gia L&acirc;m, c&aacute;ch trung t&acirc;m th&agrave;nh phố H&agrave; Nội khoảng 7 km về ph&iacute;a Đ&ocirc;ng Bắc. Lệ Mật l&agrave; một l&agrave;ng cổ, xưa c&oacute; t&ecirc;n l&agrave; &ldquo;Tr&ugrave; Mật&rdquo; c&oacute; lẽ v&igrave; kỵ h&uacute;y ch&uacute;a Trịnh Ch&ugrave; n&ecirc;n đổi t&ecirc;n như hiện nay. C&aacute;ch đ&acirc;y hơn 900 năm, Lệ Mật đ&atilde; nổi tiếng trong cả nước với nghề truyền thống nu&ocirc;i rắn, bắt rắn. L&agrave;ng rắn gắn liền với truyền thuyết chinh phục lo&agrave;i thủy qu&aacute;i mang h&igrave;nh tượng rắn khổng lồ của ch&agrave;ng trai họ Ho&agrave;ng ở Lệ Mật v&agrave;o đời vua L&yacute; Th&aacute;i T&ocirc;ng năm 1028- 1054.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/67/700_0.622663001397190957.jpg\" /></p>\r\n<p>\r\n	Tương truyền, v&agrave;o đời L&yacute; Th&aacute;i T&ocirc;ng, c&oacute; một c&ocirc;ng ch&uacute;a cưng của vua thường bơi thuyền du ngoạn tr&ecirc;n s&ocirc;ng Thi&ecirc;n Đức v&agrave; v&agrave;o một h&ocirc;m, c&ocirc;ng ch&uacute;a bị đắm thuyền chết đuối kh&ocirc;ng thấy x&aacute;c. Vua trao giải cho ai t&igrave;m thấy nhưng kh&ocirc;ng người n&agrave;o t&igrave;m được. C&oacute; một ch&agrave;ng th&agrave;nh ni&ecirc;n họ Ho&agrave;ng ở Lệ Mật đ&atilde; chiến đấu dũng cảm với thủy qu&aacute;i v&agrave; cuối c&ugrave;ng đưa được ngọc thể c&ocirc;ng ch&uacute;a l&ecirc;n bờ. Để cảm ơn ch&agrave;ng trai dũng cảm, nh&agrave; vua đ&atilde; đồng &yacute; cho d&acirc;n ch&uacute;ng Lệ Mật c&ugrave;ng ch&agrave;ng vượt d&ograve;ng s&ocirc;ng Nhị H&agrave; sang khai khẩn đất ph&iacute;a T&acirc;y th&agrave;nh Thăng Long. V&ugrave;ng đất ấy trở n&ecirc;n tr&ugrave; ph&uacute;, mở rộng th&agrave;nh 13 trại ấp m&agrave; s&aacute;ch sử gọi l&agrave; khu &ldquo;Thập tam trại&rdquo;. Sau khi khai lập được 13 trại, ch&agrave;ng trai họ Ho&agrave;ng quay về củng cố l&agrave;ng cũ rất tr&ugrave; ph&uacute; n&ecirc;n gọi l&agrave;ng l&agrave; &ldquo;Tr&ugrave; Mật&rdquo;.<br />\r\n	<br />\r\n	Để tưởng nhớ đến ch&agrave;ng trai họ Ho&agrave;ng - người đ&atilde; khai đất lập l&agrave;ng, khi ch&agrave;ng mất, người d&acirc;n đ&atilde; lập đ&igrave;nh thờ ch&agrave;ng ở r&igrave;a ph&iacute;a nam l&agrave;ng Lệ Mật, b&ecirc;n bờ nam s&ocirc;ng Đuống, suy t&ocirc;n ch&agrave;ng l&agrave; Đức Th&aacute;nh Ho&agrave;ng. H&agrave;ng năm cứ đến ng&agrave;y 23 th&aacute;ng 3 &acirc;m lịch, người d&acirc;n Lệ Mật lại tổ chức hội l&agrave;ng nhằm b&agrave;y tỏ l&ograve;ng biết ơn đối với tổ ti&ecirc;n v&agrave; suy t&ocirc;n Th&agrave;nh Ho&agrave;ng l&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/67/700_0.426647001397190957.jpg\" /><br />\r\n	<br />\r\n	Hội l&agrave;ng thường được tổ chức từ ng&agrave;y 20 đến 24 th&aacute;ng 3 &acirc;m lịch v&agrave; hội ch&iacute;nh v&agrave;o ng&agrave;y 23 th&aacute;ng 3. Khắp trong đ&igrave;nh ngo&agrave;i l&agrave;ng đều được trang ho&agrave;ng lộng lẫy, cờ quạt d&agrave;n b&agrave;y đủ loại, đ&egrave;n nến s&aacute;ng rực, kh&oacute;i hương nghi ng&uacute;t. V&agrave;o ng&agrave;y hội ch&iacute;nh, đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ kinh đ&ocirc; về đ&igrave;nh l&agrave;ng Lệ Mật để dự hội. Phần hội diễn ra s&ocirc;i nổi với c&aacute;c tr&ograve; chơi như m&uacute;a rắn hết sức độc đ&aacute;o. Con rắn được l&agrave;m bằng nan tre lợp vải nhiều m&agrave;u sắc sặc sỡ tượng trưng cho lo&agrave;i thủy qu&aacute;i xưa kia đ&atilde; từng bị ch&agrave;ng trai họ Ho&agrave;ng hạ gục.<br />\r\n	<br />\r\n	Lễ hội c&ograve;n n&aacute;o nhiệt hơn nhờ sự g&oacute;p phần của cuộc thi rắn. H&agrave;ng ng&agrave;n con rắn từ khắp nơi tr&ecirc;n cả nước qua sự tuyển chọn kỹ lưỡng, &ldquo;ch&agrave;ng - n&agrave;ng&rdquo; rắn n&agrave;o cũng hi vọng m&igrave;nh sẽ đoạt vương miện của c&aacute;c ti&ecirc;u ch&iacute; rắn to, rắn đẹp hay rắn lạ...</p>\r\n<p>\r\n	Điểm hấp dẫn thu h&uacute;t kh&aacute;ch tham dự lễ hội nhất vẫn l&agrave; được thưởng thức c&aacute;c m&oacute;n đặc sản chế biến từ thịt rắn. C&ograve;n g&igrave; th&uacute; vị hơn khi du kh&aacute;ch tận mắt chứng kiến những b&agrave;n tay đi&ecirc;u luyện cắt tiết, l&agrave;m thịt rắn, chế biến v&agrave; được thưởng thức ngay tại lễ hội. Chỉ ri&ecirc;ng một con rắn, du kh&aacute;ch c&oacute; thể được thưởng thức tới hơn chục m&oacute;n ăn như: ch&aacute;o thịt rắn, x&ocirc;i mỡ rắn, miến thịt rắn, thịt rắn x&agrave;o mềm, x&agrave;o lăn, x&agrave;o b&aacute;nh đa, da rắn x&agrave;o mềm. C&aacute;c m&oacute;n r&aacute;n c&oacute; gan rắn bao trứng, thịt rắn bao l&aacute; lốt, rắn tẩm bột, rắn nhồi thịt, chả rắn, rắn r&aacute;n kh&uacute;c, sườn rắn rang gi&ograve;n x&uacute;c b&aacute;nh đa. C&aacute;c m&oacute;n nộm cũng kh&ocirc;ng k&eacute;m phần hấp dẫn với rắn nộm hoa chuối, nộm rau ng&oacute;t. B&ecirc;n cạnh đ&oacute; c&ograve;n c&aacute;c m&oacute;n nướng như thịt rắn nướng ngũ vị hương, gan rắn nướng ngũ vị, nướng kh&uacute;c l&aacute; chanh, nước bao d&acirc;n d&atilde;. Một số m&oacute;n hầm bổ dưỡng như rắn hầm sả gừng, rắn hầm m&uacute;a sả, rắn om. Hay rắn tẩm thuốc bắc, đu&ocirc;i rắn luộc, lẩu rắn, thịt rắn hấp l&aacute; bưởi...</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/67/700_0.835049001397190957.jpg\" /></p>\r\n<div style=\"text-align: justify;\">\r\n	Chỉ đứng v&agrave; nh&igrave;n th&ocirc;i, con mắt hiếu kỳ của du kh&aacute;ch sẽ phải đảo li&ecirc;n tục theo từng cử động tay của người chế biến. Chỉ trong chớp mắt, h&agrave;ng chục m&oacute;n ăn về rắn được ho&agrave;n tất. Du kh&aacute;ch kh&ocirc;ng cầm l&ograve;ng nổi cơn th&egrave;m thuồng m&agrave; kh&ocirc;ng ngại nh&oacute;n tay ăn thử. Chả l&agrave; chiếc mũi &ldquo;tham lam&rdquo; của họ đ&atilde; h&iacute;t h&agrave; đến đầy khoang bụng m&ugrave;i thơm thịt rắn phảng phất trong gi&oacute;, để l&agrave;m cồn c&agrave;o, nghe s&ocirc;i r&eacute;o c&aacute;i &ldquo;th&egrave;m&rdquo; đang giục gi&atilde; chủ nh&acirc;n của n&oacute; mau mau tận hưởng đặc sản H&agrave; th&agrave;nh. Trong l&uacute;c n&agrave;y đ&acirc;y, lai rai một miếng thịt rắn, thật chậm, thật kỹ để thấy vị ngọt, thơm kh&ocirc;ng qu&ecirc;n nhấp theo một ch&uacute;t rượu rắn ng&acirc;m thuốc bắc, rượu rắn x&agrave; cừ hay rượu nếp bao tử rắn... cay cay t&ecirc; t&ecirc; nơi đầu lưỡi, bạn sẽ hiểu th&uacute; chơi ẩm thực của người d&acirc;n đất Kinh Kỳ tinh tế biết bao.<br />\r\n	<br />\r\n	Ai đ&atilde; một lần đến với l&agrave;ng Lệ Mật v&agrave; đặc biệt được tham dự hội l&agrave;ng, thưởng thức đặc sản thịt rắn hẳn sẽ lu&ocirc;n nao l&ograve;ng m&agrave; t&igrave;m về th&ecirc;m nhiều lần nữa. Để t&igrave;m kiếm cho m&igrave;nh một kh&ocirc;ng kh&iacute; trong l&agrave;nh thư th&aacute;i, nh&acirc;m nhi mấy c&uacute;t rượu b&ecirc;n đĩa thịt rắn m&agrave; qu&ecirc;n kh&ocirc;ng gian, qu&ecirc;n thời gian đang lấp đầy bởi những bộn bề lo toan đời thường...</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969059', '0', '1397190957', '0', '0');
INSERT INTO `slm_news` VALUES ('68', '0.469039001397191368.jpg', 'a:6:{i:0;s:24:\"0.469039001397191368.jpg\";i:1;s:24:\"0.661199001397191368.jpg\";i:2;s:24:\"0.842227001397191368.jpg\";i:3;s:24:\"0.030001001397191369.jpg\";i:4;s:24:\"0.209385001397191369.jpg\";i:5;s:24:\"0.295421001397191369.jpg\";}', null, 'Lễ hội làng Lệ Mật (23-3 Âm lịch)', null, null, '25', '', '\r\n	Lệ Mật l&agrave; một l&agrave;ng qu&ecirc; thuộc x&atilde; Việt Hưng, huyện Gia L&acirc;m, c&aacute;ch trung t&acirc;m th&agrave;nh phố H&agrave; Nội khoảng 7 km về ph&iacute;a Đ&ocirc;ng Bắc (nay thuộc phường Việt Hưng, quận Long Bi&ecirc;n, th&agrave;nh phố H&agrave; Nội). L&agrave;ng nổi tiếng về nghề bắt rắn v&agrave; chế biến đặc sản thịt rắn.', '<p style=\"text-align: center;\">\r\n	Nhớ ng&agrave;y 23 th&aacute;ng 3</p>\r\n<p style=\"text-align: center;\">\r\n	D&acirc;n trại ta vượt Nhị H&agrave; thăm qu&ecirc;</p>\r\n<p style=\"text-align: center;\">\r\n	Kinh qu&aacute;n, cựu qu&aacute;n đề huề</p>\r\n<p style=\"text-align: center;\">\r\n	Hồ T&acirc;y c&aacute; nhảy đi về trong m&acirc;y</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.469039001397191368.jpg\" /></p>\r\n<p>\r\n	Hội l&agrave;ng Lệ Mật l&agrave; cơ hội để h&agrave;ng năm con ch&aacute;u trong l&agrave;ng (d&acirc;n cựu qu&aacute;n) v&agrave; con ch&aacute;u đi xa khai hoang b&ecirc;n kinh th&agrave;nh Thăng Long (d&acirc;n kinh qu&aacute;n) gặp gỡ nhau để t&acirc;m sự v&agrave; tỏ l&ograve;ng biết ơn đối với tổ ti&ecirc;n, những người khai hoang. Hội l&agrave;ng được tổ chức h&agrave;ng năm từ ng&agrave;y 20 đến 24 th&aacute;ng 3 &Acirc;m lịch, hội ch&iacute;nh v&agrave;o ng&agrave;y 23 th&aacute;ng 3 &Acirc;m lịch, suy t&ocirc;n Th&agrave;nh Ho&agrave;ng l&agrave;ng Lệ Mật.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.661199001397191368.jpg\" /></p>\r\n<p>\r\n	V&agrave;o ng&agrave;y hội ch&iacute;nh, đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ kinh đ&ocirc; về đ&igrave;nh l&agrave;ng Lệ Mật để dự hội. Trong ng&agrave;y hội l&agrave;ng c&oacute; tr&ograve; m&uacute;a rắn độc đ&aacute;o, con rắn được l&agrave;m bằng nan tre lợp vải tượng trưng cho lo&agrave;i thuỷ qu&aacute;i đ&atilde; bị ch&agrave;ng trai họ Ho&agrave;ng d&ugrave;ng sức mạnh v&agrave; &yacute; ch&iacute; của m&igrave;nh hạ gục. Người ta c&ograve;n tổ chức thi rắn to, rắn đẹp, rắn lạ,..phổ biến c&aacute;c b&iacute; quyết bắt rắn, nu&ocirc;i rắn, khai th&aacute;c nọc, chữa rắn độc cắn...</p>\r\n<p>\r\n	Du kh&aacute;ch tham dự lễ hội c&oacute; thể được thưởng thức c&aacute;c m&oacute;n đặc sản chế biến từ thịt rắn. Tục truyền, đời vua L&yacute; Th&aacute;i T&ocirc;ng (1028 - 54), c&ocirc;ng ch&uacute;a đang đi thuyền rồng c&ugrave;ng với cung nữ tr&ecirc;n s&ocirc;ng Thi&ecirc;n Đức (s&ocirc;ng Đuống ng&agrave;y nay) th&igrave; bất ngờ một con thuỷ qu&aacute;i (rắn lớn) hung dữ khiến gi&oacute; to s&oacute;ng cả nổi l&ecirc;n lật thuyền c&ocirc;ng ch&uacute;a. C&oacute; một ch&agrave;ng trai họ Ho&agrave;ng đang thả thuyền ven s&ocirc;ng k&eacute;o lưới đ&aacute;nh c&aacute;, thấy vậy đ&atilde; dũng cảm lao xuống s&ocirc;ng giết thuỷ qu&aacute;i cứu được c&ocirc;ng ch&uacute;a.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.209385001397191369.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.842227001397191368.jpg\" /></p>\r\n<p>\r\n	Nh&agrave; vua v&ocirc; c&ugrave;ng cảm k&iacute;ch, ban thưởng cho nhiều v&agrave;ng bạc, ch&acirc;u b&aacute;u v&agrave; quyền cao chức trọng, nhưng ch&agrave;ng từ chối, chỉ xin vua cho ch&agrave;ng đưa d&acirc;n ngh&egrave;o khai hoang lập ấp mở trại quanh th&agrave;nh Thăng Long để l&agrave;m ăn sinh sống. Từ đ&oacute; d&acirc;n l&agrave;ng Lệ Mật, một phần ở lại l&agrave;ng gọi l&agrave; Cựu qu&aacute;n, c&ograve;n phần lớn v&agrave;o th&agrave;nh khai khẩn đất hoang gọi l&agrave; Kinh qu&aacute;n, tương truyền đ&oacute; l&agrave; c&aacute;c l&agrave;ng Cống Vị, Giảng V&otilde;, Liễu Giai, Thủ Lệ, Vạn Ph&uacute;c, Vĩnh Ph&uacute;c, Ngọc H&agrave;, Hữu Tiệp, Ngọc Kh&aacute;nh, Kim M&atilde;, Phủ Đề, Thuỵ Kh&ecirc; ng&agrave;y nay. Để ghi nhớ c&ocirc;ng ơn, d&acirc;n l&agrave;ng t&ocirc;n ch&agrave;ng l&agrave;m Th&agrave;nh ho&agrave;ng thờ ở đ&igrave;nh l&agrave;ng. Hằng năm đến 23.3 &acirc;m lịch, d&acirc;n của 13 l&agrave;ng tập trung tại Lệ Mật để mở hội tưởng nhớ c&ocirc;ng đức của người xưa.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.030001001397191369.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/68/700_0.295421001397191369.jpg\" /></p>\r\n<p>\r\n	Mở đầu lễ hội l&agrave; nghi thức rước nước v&agrave; đ&aacute;nh c&aacute; thờ, tế lễ rất trang nghi&ecirc;m, long trọng tại đ&igrave;nh l&agrave;ng. Sau đ&oacute; l&agrave; tiết mục m&uacute;a rắn, do đội m&uacute;a của l&agrave;ng tr&igrave;nh diễn nhắc nhở lại sự t&iacute;ch Th&agrave;nh ho&agrave;ng đ&aacute;nh thuỷ qu&aacute;i cứu c&ocirc;ng ch&uacute;a, đồng thời diễn tr&ograve; bắt rắn độc. Ngo&agrave;i ra, c&ograve;n nhiều t&iacute;ch vui với sự tham gia của nhiều tầng lớp v&agrave; lứa tuổi kh&aacute;c nhau. Hội l&agrave;ng c&ograve;n l&agrave; dịp những người anh em họ h&agrave;ng th&acirc;n th&iacute;ch ở &quot;Kinh qu&aacute;n&quot; v&agrave; &quot;Cựu qu&aacute;n&quot; gặp gỡ, trao đổi thăm hỏi th&acirc;n t&igrave;nh. Ng&agrave;y nay, Lệ Mật l&agrave; một v&ugrave;ng sản xuất n&ocirc;ng nghiệp của ngoại th&agrave;nh H&agrave; Nội v&agrave; vẫn duy tr&igrave; nghề nu&ocirc;i, bắt rắn độc truyền thống.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969043', '0', '1397191368', '0', '0');
INSERT INTO `slm_news` VALUES ('73', '0.938895001397464240.jpg', 'a:16:{i:0;s:24:\"0.938895001397464240.jpg\";i:1;s:24:\"0.196253001397464241.jpg\";i:2;s:24:\"0.307290001397464241.jpg\";i:3;s:24:\"0.497108001397464241.jpg\";i:4;s:24:\"0.711096001397464241.jpg\";i:5;s:24:\"0.789930001397464241.jpg\";i:6;s:24:\"0.860593001397464241.jpg\";i:7;s:24:\"0.889668001397464241.jpg\";i:8;s:24:\"0.063699001397464242.jpg\";i:9;s:24:\"0.246024001397464242.jpg\";i:10;s:24:\"0.319545001397464242.jpg\";i:11;s:24:\"0.599494001397464242.jpg\";i:12;s:24:\"0.727508001397464242.jpg\";i:13;s:24:\"0.698586001397464244.jpg\";i:14;s:24:\"0.979384001397464244.jpg\";i:15;s:24:\"0.121760001397464245.jpg\";}', null, 'Nét ẩm thực đặc trưng của 3 miền Bắc Trung Nam', null, null, '12', '', '\r\n	Ở mỗi miền đất nước, người d&acirc;n lại c&oacute; khẩu vị ăn uống kh&aacute;c nhau, g&oacute;p phần mang đến sự phong ph&uacute;, đa dạng cho văn h&oacute;a ẩm thực Việt Nam.', '<p>\r\n	<span style=\"font-weight: bold;\">Sự tinh tế trong ẩm thực miền Bắc</span></p>\r\n<p>\r\n	Bắc bộ l&agrave; nơi tổ ti&ecirc;n định cư l&acirc;u đời n&ecirc;n từ m&oacute;n ăn đến c&aacute;i mặc đều được s&agrave;ng lọc v&agrave; trở th&agrave;nh chuẩn mực, kh&ocirc;ng dễ g&igrave; thay đổi. Người miền Bắc chọn m&oacute;n ăn thanh đạm, nhẹ nh&agrave;ng, c&oacute; vị chua nhẹ. C&aacute;c m&oacute;n đặc trưng của người miền Bắc c&oacute; thể kể đến phở H&agrave; Nội, b&uacute;n thang H&agrave; Nội, b&uacute;n chả H&agrave; Nội, b&aacute;nh t&ocirc;m Hồ T&acirc;y, b&uacute;n ốc, thịt đ&ocirc;ng, x&ocirc;i cốm v&ograve;ng, chả gi&ograve;, miến x&agrave;o cua bể...</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.196253001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Phở b&ograve; H&agrave; Nội</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.307290001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&uacute;n thang H&agrave; Nội</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.497108001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&uacute;n chả H&agrave; Nội</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.938895001397464240.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&uacute;n đậu mắm t&ocirc;m H&agrave; Nội</p>\r\n<p>\r\n	Một đặc trưng nữa rất Bắc bộ ch&iacute;nh l&agrave; những m&oacute;n qu&agrave; b&aacute;nh. Đ&acirc;y kh&ocirc;ng phải l&agrave; những m&oacute;n ăn để no nhưng n&oacute; lại đem lại cho người ta nhiều h&aacute;o hức, đặc biệt, n&oacute; lưu giữ nhiều kỷ niệm đẹp trong tuổi thơ của mỗi người d&acirc;n xứ Bắc. C&aacute;c m&oacute;n đặc trưng như c&aacute;c loại mứt l&agrave;m từ sấu, b&aacute;nh cốm&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.711096001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Cốm V&ograve;ng</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.789930001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mứt Sấu H&agrave; Nội</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">Đậm đ&agrave; m&oacute;n ăn miền Trung</span></p>\r\n<p>\r\n	Người miền Trung sử dụng cay nhiều nhưng độ ngọt lại &iacute;t hơn miền Nam. Ẩm thực miền Trung rất đa dạng về m&oacute;n, nhiều gia vị; vị chua v&agrave; cay được ưa chuộng như mắm c&agrave;, mắm t&ocirc;m&hellip; C&aacute;c m&oacute;n đặc trưng của người miền Trung: b&uacute;n b&ograve; Huế, b&aacute;nh b&egrave;o, b&aacute;nh x&egrave;o, b&aacute;nh đập, chả ram, b&uacute;n c&aacute;, b&aacute;nh tr&aacute;ng thịt luộc&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.860593001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mắm c&agrave;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.889668001397464241.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mắm t&ocirc;m chua</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.063699001397464242.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&uacute;n b&ograve;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.246024001397464242.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&aacute;nh b&egrave;o</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.319545001397464242.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&aacute;nh x&egrave;o</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.599494001397464242.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ch&egrave; Chuối</p>\r\n<p>\r\n	<span style=\"font-weight: bold;\">N&eacute;t ẩm thực đa dạng của miền Nam</span></p>\r\n<p>\r\n	M&oacute;n ăn của người miền Nam đơn giản, kh&ocirc;ng cầu kỳ như ch&iacute;nh con người nơi đ&acirc;y. Miền Nam m&oacute;n ăn đa dạng, biến h&oacute;a kh&ocirc;n lường với vị ngọt, cay, b&eacute;o do sử dụng nước dừa. C&aacute;c m&oacute;n ăn đặc trưng sử dụng ngọt nhiều: b&aacute;nh (b&aacute;nh in, b&aacute;nh men, b&aacute;nh &iacute;t, b&aacute;nh b&ograve;&hellip;), ch&egrave; (ch&egrave; kiếm, ch&egrave; chuối), x&ocirc;i, nem nướng, ch&aacute;o g&agrave;, g&agrave; r&ocirc; ti&hellip; đều sử dụng nước dừa hay cốm dừa để tăng vị b&eacute;o, vị ngọt. C&aacute;c m&oacute;n đặc trưng c&oacute; thể kể đến như c&aacute; l&oacute;c nướng trui, gỏi cuốn, b&uacute;n mắm, hủ tiếu Nam Vang&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.727508001397464242.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hủ tiếu Nam Vang</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.698586001397464244.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute; L&oacute;c nướng chui</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.979384001397464244.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Lẩu mắm miền t&acirc;y</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/73/700_0.121760001397464245.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&aacute;nh b&ograve;</p>\r\n<p>\r\n	Đặc điểm m&oacute;n ăn từng miền tuy kh&aacute;c nhau, nhưng vẫn c&oacute; những điểm tương đồng, thể hiện qua cơ cấu bữa ăn, nguy&ecirc;n tắc chế biến như nước d&ugrave;ng, nước mắm, gia vị hỗn hợp, rau phong ph&uacute;, c&aacute;c loại nước chấm chế biến đa dạng ph&ugrave; hợp với m&oacute;n ăn. V&igrave; vậy, kh&ocirc;ng chỉ người Việt m&agrave; nhiều người nước ngo&agrave;i đều rất y&ecirc;u th&iacute;ch văn h&oacute;a ẩm thực của đất nước h&igrave;nh chữ S.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1397464674', '0', '1397464240', '0', '0');
INSERT INTO `slm_news` VALUES ('69', '0.865602001397192137.jpg', 'a:1:{i:0;s:24:\"0.865602001397192137.jpg\";}', null, 'Làng quê Việt Nam', null, null, '0', '', '\r\n	Vẻ đẹp đơn sơ, mộc mạc của dải đất h&igrave;nh chữ S gần như được th&acirc;u t&oacute;m to&agrave;n bộ trong ch&ugrave;m ảnh của một nhiếp ảnh gia chuy&ecirc;n nghiệp.Nằm trong loạt b&agrave;i mang chủ đề &quot;Những khoảnh khắc đẹp từ khắp mọi nơi tr&ecirc;n thế giới&quot;, trang mạng Livejournal đ&atilde; giới thiệu với độc giả một ch&ugrave;m ảnh phong cảnh đặc sắc của Việt Nam được thực hiện bởi nhiếp ảnh gia Ho&agrave;ng Nam.&nbsp;', '<p>\r\n	Vẻ đẹp đơn sơ, mộc mạc của dải đất h&igrave;nh chữ S gần như được th&acirc;u t&oacute;m to&agrave;n bộ trong ch&ugrave;m ảnh của một nhiếp ảnh gia chuy&ecirc;n nghiệp.Nằm trong loạt b&agrave;i mang chủ đề &quot;Những khoảnh khắc đẹp từ khắp mọi nơi tr&ecirc;n thế giới&quot;, trang mạng Livejournal đ&atilde; giới thiệu với độc giả một ch&ugrave;m ảnh phong cảnh đặc sắc của Việt Nam được thực hiện bởi nhiếp ảnh gia Ho&agrave;ng Nam. Bộ ảnh n&agrave;y được chụp ở rất nhiều nơi kh&aacute;c nhau tr&ecirc;n khắp đất nước Việt Nam như Lăng C&ocirc;, An Giang, L&acirc;m Đồng, Nha Trang, Bạc Li&ecirc;u, Đ&agrave; Lạt... Bộ ảnh của Ho&agrave;ng Nam đ&atilde; chiếm được cảm t&igrave;nh của đ&ocirc;ng đảo độc giả Nga, khiến họ kh&ocirc;ng thể kh&ocirc;ng xu&yacute;t xoa, thậm ch&iacute; nhiều người c&ograve;n b&agrave;y tỏ mong muốn được đến thăm Việt Nam một lần.</p>\r\n<p style=\"text-align: center;\">\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b30.jpg\" style=\"font-family: Arial;\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<i style=\"font-family: Arial;\">S&oacute;ng nước Miền T&acirc;y</i></p>\r\n<div style=\"text-align: center;\">\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b234.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b20.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b29.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b19.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b2-2.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Thu hoạch vụ m&ugrave;a tr&ecirc;n c&aacute;nh đồng</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b3-1.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b22.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>K&eacute;o lưới l&uacute;c ho&agrave;ng h&ocirc;n</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b4-1.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Nụ cười tươi tr&ecirc;n m&ocirc;i c&ocirc; g&aacute;i đẹp ng&agrave;y m&ugrave;a</i><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b5-1.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b6-1.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b7.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Sếu đầu đỏ tr&ecirc;n c&aacute;nh đồng</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b8.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b9.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Phan Thiết</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b10.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b11.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Nha Trang</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b12.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Lăng C&ocirc;, Huế</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b13.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b14.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b15.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>L&acirc;m Đồng</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b16.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Hậu Giang</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b17.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Đ&agrave; Lạt</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b18.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>Bạc Li&ecirc;u</i></span></font><br />\r\n	<br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"> <img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b21.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b24.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b25.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b26.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b27.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b28.jpg\" /><br />\r\n	<br />\r\n	<img alt=\"\" border=\"0\" src=\"http://img2.news.zing.vn/2012/05/04/b31.jpg\" /></span></font><br />\r\n	<font color=\"#000000\"><span style=\"font-family: Arial\"><i>C&ocirc; g&aacute;i ch&egrave;o thuyền ở rừng tr&agrave;m Tr&agrave; Sư</i></span></font></div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1398665399', '0', '1397192137', '0', '0');
INSERT INTO `slm_news` VALUES ('70', '0.891525001397193038.jpg', 'a:7:{i:0;s:24:\"0.891525001397193038.jpg\";i:1;s:24:\"0.193374001397193039.jpg\";i:2;s:24:\"0.235007001397193039.jpg\";i:3;s:24:\"0.300199001397193039.jpg\";i:4;s:24:\"0.479132001397193039.jpg\";i:5;s:24:\"0.635884001397193039.jpg\";i:6;s:24:\"0.699777001397193039.jpg\";}', null, 'Cổng làng – Biểu tượng văn hoá của làng quê Bắc bộ !', null, null, '12', '', '\r\n	Lũy tre thấp tho&aacute;ng đ&agrave;ng xa Mấy mươi bước nữa về qua cổng l&agrave;ng Trong l&ograve;ng bỗng thấy xốn xang Qu&ecirc; hương hai tiếng nặng mang suốt đời... Xưa, ở những l&agrave;ng qu&ecirc; ch&acirc;u thổ Bắc bộ, L&agrave;ng khi ấy tương đối kh&eacute;p k&iacute;n, chỉ để một hoặc hai lối ra v&agrave;o v&agrave; cổng l&agrave;ng được quy định l&agrave; giới hạn giao lưu li&ecirc;n l&agrave;ng v&agrave; mở ra khu thổ canh của l&agrave;ng đ&oacute;.&nbsp;', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.891525001397193038.jpg\" /></p>\r\n<p>\r\n	<span class=\"fbPhotoCaptionText\">Lũy tre thấp tho&aacute;ng đ&agrave;ng xa Mấy mươi bước nữa về qua cổng l&agrave;ng Trong l&ograve;ng bỗng thấy xốn xang Qu&ecirc; hương hai tiếng nặng mang suốt đời... Xưa, ở những l&agrave;ng qu&ecirc; ch&acirc;u thổ Bắc bộ, L&agrave;ng khi ấy tương đối kh&eacute;p k&iacute;n, chỉ để một hoặc hai lối ra v&agrave;o v&agrave; cổng l&agrave;ng được quy định l&agrave; giới hạn giao lưu li&ecirc;n l&agrave;ng v&agrave; mở ra khu thổ canh của l&agrave;ng đ&oacute;. V&agrave;o buổi s&aacute;ng, cổng l&agrave;ng mở, d&acirc;n l&agrave;ng đi l&agrave;m, đến tối lặn mặt trời, sau khi d&acirc;n l&agrave;ng về th&ocirc;n th&igrave; cổng l&agrave;ng được đ&oacute;ng lại, &quot;nội bất xuất, ngoại bất nhập&quot;. L&acirc;u dần, cổng l&agrave;ng kh&ocirc;ng chỉ l&agrave; nơi ph&acirc;n địa giới giữa c&aacute;c l&agrave;ng với nhau, m&agrave; trở th&agrave;nh biểu tượng văn ho&aacute; của l&agrave;ng qu&ecirc; ch&acirc;u thổ Bắc bộ, c&ugrave;ng với c&acirc;y đa, m&aacute;i đ&igrave;nh. V&agrave; cổng l&agrave;ng lu&ocirc;n c&oacute; vị tr&iacute; quan trọng trong đời sống thực v&agrave; tinh thần người Việt. Theo quan niệm truyền thống, b&ecirc;n cạnh c&acirc;y đa, giếng nước, đ&igrave;nh ch&ugrave;a, cổng l&agrave;ng vốn c&oacute; &yacute; nghĩa như một sự ch&agrave;o đ&oacute;n ch&acirc;n th&agrave;nh, hồ hởi của người địa phương d&agrave;nh cho kh&aacute;ch. Với những người con xa qu&ecirc; l&acirc;u, sau một h&agrave;nh tr&igrave;nh d&agrave;i trở về, chiếc cổng l&agrave;ng lu&ocirc;n mang đến cảm gi&aacute;c y&ecirc;n b&igrave;nh, th&acirc;n thương, tạo những gi&aacute; trị hữu h&igrave;nh v&ocirc; gi&aacute;. </span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.193374001397193039.jpg\" /></p>\r\n<p>\r\n	<span class=\"fbPhotoCaptionText\">Cổng l&agrave;ng xưa &ndash; Biểu tượng văn ho&aacute; của l&agrave;ng qu&ecirc; Bắc bộ Cổng l&agrave;ng l&agrave; một trong những bộ phận cấu th&agrave;nh kh&ocirc;ng thể thiếu của l&agrave;ng qu&ecirc; cổ truyền v&ugrave;ng Bắc Bộ. Cổng l&agrave;ng thường tập trung nhiều ở v&ugrave;ng đồng bằng Bắc bộ như ở tỉnh H&agrave; Bắc, H&agrave; T&acirc;y, Nam Định, Hưng Y&ecirc;n, Th&aacute;i B&igrave;nh, chủ yếu l&agrave; những v&ugrave;ng trồng l&uacute;a v&agrave; c&oacute; văn ho&aacute; l&agrave;ng x&atilde;. C&oacute; l&agrave;ng gi&agrave;u, c&oacute; l&agrave;ng khoa bảng, c&oacute; l&agrave;ng nghề... tất cả những c&aacute;i hay c&aacute;i đẹp đều được c&aacute;c vị t&uacute;c nho viết th&agrave;nh c&acirc;u đối khắc trước cổng. V&agrave; đ&oacute; l&agrave; một phần của văn h&oacute;a l&agrave;ng. Cổng l&agrave;ng hai b&ecirc;n c&oacute; lũy tre, lại mở ra c&aacute;nh đồng nhiều gi&oacute; m&aacute;t, d&acirc;n l&agrave;ng thường đến ngồi chơi v&agrave; nhiều khi cổng l&agrave;ng l&agrave; nơi giao lưu t&igrave;nh cảm, trao đổi th&ocirc;ng tin. Một chiếc cổng bằng gỗ cũ kỹ đ&atilde; tr&oacute;c sơn, một c&aacute;nh hay một chiếc cổng x&acirc;y bằng gạch r&ecirc;u phong đ&atilde; mọc đầy l&agrave; h&igrave;nh ảnh quen thuộc của l&agrave;ng qu&ecirc; Bắc bộ Việt Nam - một biểu tượng th&acirc;n thương v&agrave; đặc trưng của mỗi l&agrave;ng qu&ecirc; truyền thống. Cổng l&agrave;ng ra đời từ rất sớm, gắn liền với sự h&igrave;nh th&agrave;nh v&agrave; ph&aacute;t triển của l&agrave;ng. &ETH;&oacute; l&agrave; nơi đất lề, qu&ecirc; th&oacute;i, l&agrave;ng đ&atilde; đủ tuổi để cất dựng n&ecirc;n một chiếc cổng. </span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.235007001397193039.jpg\" /></p>\r\n<p>\r\n	<span class=\"fbPhotoCaptionText\">D&ugrave; to d&ugrave; nhỏ, d&ugrave; x&acirc;y bằng gạch hay gh&eacute;p đ&aacute;, chiếc cổng l&agrave;ng ch&iacute;nh l&agrave; dấu ấn minh chứng cho một nếp l&agrave;ng bề thế, chỉn chu. Cửa nh&agrave; trong l&agrave;ng c&oacute; thể xộc xệch, sơ s&agrave;i; con người c&oacute; thể lam lũ, nhếch nh&aacute;c nhưng cổng l&agrave;ng th&igrave; phải đ&agrave;ng ho&agrave;ng, chững chạc. &ETH;ơn giản chỉ bởi cổng l&agrave;ng l&agrave; bộ mặt, l&agrave; biểu tượng của l&agrave;ng qu&ecirc;, phần n&agrave;o thể hiện được cốt c&aacute;ch của l&agrave;ng, tư chất của mỗi người d&acirc;n trong l&agrave;ng. Cổng l&agrave;ng được d&ugrave;ng như một qui ước kh&ocirc;ng gian hơn l&agrave; một giới hạn địa l&yacute; của l&agrave;ng. Tam quan cổng l&agrave;ng được d&ugrave;ng như một qui ước kh&ocirc;ng gian hơn l&agrave; một giới hạn địa l&yacute; của l&agrave;ng. N&oacute; chẳng ngǎn che được g&igrave; về vật l&yacute; lẫn thị gi&aacute;c. Vậy m&agrave; l&agrave;ng kh&ocirc;ng cổng chẳng kh&aacute;c g&igrave; nh&agrave; kh&ocirc;ng cửa. N&oacute; như một dấu hiệu đ&aacute;nh mốc trong v&agrave; ngo&agrave;i của kh&ocirc;ng gian l&agrave;ng - đ&oacute; l&agrave; một phần của văn h&oacute;a l&agrave;ng. C&aacute;i kiểu đ&aacute;nh dấu n&agrave;y lu&ocirc;n tồn tại trong t&acirc;m thức người Việt. Một buổi giao lưu trong thi&ecirc;n nhi&ecirc;n, một đ&aacute;m hội h&egrave;, hễ c&oacute; nhiều quần thể kh&aacute;c nhau th&igrave; thế n&agrave;o cũng dựng cổng, định vị &quot;x&aacute;c định chủ quyền&quot; dẫu thực tế chẳng c&oacute; g&igrave; ngǎn c&aacute;ch. Cổng l&agrave;ng như một nghi thức trong cấu tr&uacute;c m&ocirc;i trường l&agrave;ng. C&oacute; cổng th&igrave; ở s&aacute;t r&igrave;a l&agrave;ng, c&oacute; cổng th&igrave; ở t&iacute;t đầu đường, chỗ giao với c&aacute;i quan (đường c&aacute;i) v&agrave; l&agrave;ng. </span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.300199001397193039.jpg\" /></p>\r\n<p>\r\n	<span class=\"fbPhotoCaptionText\">Vẻ đẹp của cổng l&agrave;ng gắn với nền văn minh l&uacute;a nước, mang t&iacute;nh ph&aacute;c họa, gợi n&ecirc;n những ước vọng của cộng đồng từ đời n&agrave;y qua đời kh&aacute;c, nơi như c&oacute; d&aacute;ng đứng của cha, c&oacute; v&ograve;ng tay của mẹ, nơi t&acirc;m điểm để cho ta lượng đo sức m&igrave;nh khi đi xa v&agrave; ngược lại n&oacute; cũng l&agrave; nơi h&uacute;t về những nỗi nhớ, những ho&agrave;i niệm về qu&ecirc; hương. Cố gi&aacute;o sư Từ Chi cho rằng, cổng l&agrave;ng c&oacute; vị tr&iacute; quan trọng trong đời sống thực v&agrave; đời sống tinh thần người d&acirc;n Việt Nam. Bởi lẽ, d&ugrave; đ&ocirc; thị h&oacute;a đến đ&acirc;u, nhiều nh&agrave; cao tầng hay biệt thự, l&agrave;ng vẫn kh&ocirc;ng thể l&agrave; phố. Chưa cần bước s&acirc;u v&agrave;o l&agrave;ng, chưa đặt ch&acirc;n tới s&acirc;n đ&igrave;nh, đứng trước cổng l&agrave;ng, người xa lạ cũng c&oacute; thể cảm được phần n&agrave;o cốt c&aacute;ch của l&agrave;ng, tư chất của mỗi người d&acirc;n. Bởi vậy, cổng l&agrave;ng phải được đặt ở vị tr&iacute; trang trọng nhất v&agrave; dễ nh&igrave;n nhất, để người của l&agrave;ng d&ugrave; c&oacute; tha phương cầu thực nơi đất kh&aacute;ch qu&ecirc; người, trở về trong một đ&ecirc;m kh&ocirc;ng trăng, kh&ocirc;ng sao chỉ khẽ chạm tay cũng biết m&igrave;nh đ&atilde; về tới m&aacute;i nh&agrave; th&acirc;n y&ecirc;u. Ph&iacute;a sau c&aacute;nh cổng l&agrave;ng ch&iacute;nh l&agrave; sự kết nối cộng đồng gia tộc l&agrave;ng x&atilde;, l&agrave; những n&eacute;t chung về phong tục, tập qu&aacute;n, văn ho&aacute; ri&ecirc;ng biệt kh&ocirc;ng l&agrave;ng n&agrave;o giống với l&agrave;ng n&agrave;o.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.479132001397193039.jpg\" /></p>\r\n<p>\r\n	<span class=\"fbPhotoCaptionText\">Con đường đi qua cổng l&agrave;ng, để lại theo nǎm th&aacute;ng những lớp bụi qu&ecirc; v&ocirc; thường, v&ocirc; thức, chứng kiến kh&ocirc;ng biết bao nhi&ecirc;u sự kiện lớn của l&agrave;ng. Những đ&aacute;m rước, đ&aacute;m hội, thương nhau cũng h&ograve; hẹn ở chốn cổng l&agrave;ng, rồi khi về l&agrave;m d&acirc;u, bước qua cổng l&agrave;ng về nh&agrave; chồng, trở th&agrave;nh một th&agrave;nh vi&ecirc;n trong cộng đồng d&acirc;n cư... như đều được bắt đầu hay kết th&uacute;c ở đ&acirc;y. C&oacute; ai mỗi khi xa qu&ecirc; kh&ocirc;ng ngo&aacute;i nh&igrave;n lại, nh&igrave;n lần cuối tam quan l&agrave;ng m&igrave;nh. Những người con xa xứ, khi về lại qu&ecirc; nh&agrave;, bước qua cổng l&agrave;ng l&agrave; biết m&igrave;nh đ&atilde; về tới m&aacute;i nh&agrave; th&acirc;n y&ecirc;u, về được lại mảnh đất ch&ocirc;n rau cắt rốn, chợt thấy l&ograve;ng ấm lại. C&oacute; cả niềm h&acirc;n hoan, nỗi bịn rịn ươn ướt tr&ecirc;n mi, quệt v&agrave;o v&aacute;ch cổng, sướt &nbsp;</span>tr&ecirc;n cột cổng. V&agrave; c&oacute; ai mỗi khi xa qu&ecirc; kh&ocirc;ng ngo&aacute;i nh&igrave;n lại, nh&igrave;n lần cuối tam quan l&agrave;ng m&igrave;nh. Với vẻ trầm mặc ghi dấu ấn văn h&oacute;a một thời được x&acirc;y dựng v&agrave; phương trưởng của v&ugrave;ng miền theo thời gian. Kiến tr&uacute;c cổng l&agrave;ng ch&acirc;u thổ Bắc bộ Kiến tr&uacute;c cổng l&agrave;ng xưa kh&ocirc;ng cầu kỳ, ph&ocirc; trương, m&agrave; chỉ nhằm khẳng định chỗ đứng của m&igrave;nh trong khoảng kh&ocirc;ng gian l&agrave;ng qu&ecirc; v&ugrave;ng đồng bằng Bắc Bộ, vốn &quot;nửa k&iacute;n, nửa hở&quot;, trong đ&oacute; giới hạn của n&oacute; chỉ mang t&iacute;nh chất tượng trưng. Mỗi một cổng l&agrave;ng Việt Nam đều c&oacute; những n&eacute;t kiến tr&uacute;c ri&ecirc;ng l&agrave;m t&acirc;m điểm trong c&aacute;i bố cục h&agrave;i h&ograve;a với kh&ocirc;ng gian của con đường l&agrave;ng, lũy tre xanh, c&acirc;y đa, giếng nước, ao l&agrave;ng v&agrave; những c&aacute;nh đồng l&uacute;a ch&iacute;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.635884001397193039.jpg\" /></p>\r\n<p>\r\n	Cổng l&agrave;ng thường được đặt ở vị tr&iacute; trang trọng nhất, dễ nh&igrave;n thấy nhất v&agrave; thường th&igrave; chỉ c&oacute; một cửa ch&iacute;nh nhưng ở nhiều nơi, liền với cửa ch&iacute;nh c&ograve;n c&oacute; hai cửa phụ thấp v&agrave; nhỏ hơn, được x&acirc;y dựng trang tr&iacute; h&agrave;i ho&agrave; với cổng ch&iacute;nh, tạo th&agrave;nh một thể kiến tr&uacute;c thống nhất tựa như những ng&ocirc;i tam quan của ch&ugrave;a, hay như những bức cửa m&atilde; ở đ&igrave;nh l&agrave;ng. Ng&agrave;y thường, người lạ kể cả d&acirc;n l&agrave;ng chỉ được qua lại ở hai cửa ng&aacute;ch, khi l&agrave;ng c&oacute; việc trọng đại như vua đến thăm, lễ rước quan trạng vinh qui, hội h&egrave;&hellip; cửa ch&iacute;nh mới được mở ra. Hai b&ecirc;n cổng l&agrave;ng thường gắn đ&ocirc;i vế đối chữ nho, c&acirc;u đối c&oacute; thể do vua ban nhưng phần lớn l&agrave; những c&acirc;u đối do l&agrave;ng lập n&ecirc;n. Cổng l&agrave;ng thường l&agrave; một tam quan x&acirc;y gạch, kh&ocirc;ng to lắm cũng kh&ocirc;ng lớn lắm. Cầu kỳ th&igrave; đắp ng&oacute;i với một v&agrave;i họa tiết d&acirc;n gian. Ở những chốn qu&ecirc; ngh&egrave;o, cổng l&agrave;ng mộc mạc. Hai b&ecirc;n trụ gạch thấp nhỏ, khi&ecirc;m nhường đỡ một tấm x&agrave; cũng bằng gạch, thế l&agrave; th&agrave;nh cổng l&agrave;ng. Kh&ocirc;ng một n&eacute;t vẽ trang điểm, kh&ocirc;ng m&agrave;u m&egrave;, thậm ch&iacute; kh&ocirc;ng một n&eacute;t chữ t&ecirc;n l&agrave;ng, vậy m&agrave; ch&iacute;nh những chiếc cổng v&ocirc; danh như thế lại trở n&ecirc;n th&acirc;n thiết gắn b&oacute; v&ocirc; chừng. L&agrave;ng kh&aacute; giả th&igrave; cổng lớn, rồng chầu, hổ phục, n&oacute;c m&aacute;i v&agrave; đầu đao c&oacute; khi kết đ&ocirc;i chim phượng. Thời phong kiến, nhiều khi danh gi&aacute; của l&agrave;ng quyết định tầm cỡ cổng l&agrave;ng. Ng&agrave;y ấy, l&agrave;ng n&agrave;o c&oacute; nhiều người đỗ đạt, l&agrave;m quan thường được vua ban cho quyền x&acirc;y cổng lớn nhưng cũng kh&ocirc;ng được vượt qu&aacute; những qui định của luật lệ. Cũng kh&ocirc;ng &iacute;t l&agrave;ng dựng hai cổng, gồm cổng tiền v&agrave; cổng hậu. Cổng tiền, thường hướng về ph&iacute;a Đ&ocirc;ng Nam, hướng của gi&oacute; l&agrave;nh, hướng của mặt trời mọc, để đ&oacute;n những niềm vui sinh s&ocirc;i trong lao động v&agrave; hạnh ph&uacute;c, c&ograve;n cổng hậu, thường hướng ra ph&iacute;a T&acirc;y, hướng mặt trời lặn, để tiễn đưa những vướng bận buồn rầu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/70/700_0.699777001397193039.jpg\" /></p>\r\n<p>\r\n	Cổng l&agrave;ng trước đ&acirc;y c&ograve;n c&oacute; c&aacute;nh cửa, do b&agrave; con địa phương chung tiền dựng bằng gỗ hoặc bằng tre v&agrave; thường trổ về hướng Đ&ocirc;ng Nam, đ&oacute;n gi&oacute; m&aacute;t v&agrave; mặt trời mọc. C&aacute;nh cửa ấy mở ra mỗi sớm, với hy vọng mang v&agrave;o ph&uacute;c lộc v&agrave; niềm vui. Cổng c&oacute; người thay nhau canh g&aacute;c để bảo vệ b&igrave;nh an cho b&agrave; con, khi c&oacute; biến động. Cổng l&agrave;ng, đối với người xưa, c&oacute; sự gắn b&oacute; s&acirc;u sắc, thể hiện t&igrave;nh cảm thi&ecirc;ng li&ecirc;ng với qu&ecirc; hương xứ sở. Xưa, Lương Văn Can, người s&aacute;ng lập ra phong tr&agrave;o Đ&ocirc;ng Kinh nghĩa thục (1907), qu&ecirc; ở l&agrave;ng Nhị Kh&ecirc;, Thường T&iacute;n, mỗi khi về qu&ecirc;, qua cổng l&agrave;ng l&agrave; cổng Quốc, c&oacute; đề bốn chữ &ldquo;Như kiến đại t&acirc;n&rdquo; (Như được đ&oacute;n kh&aacute;ch qu&yacute;), nếu đi xe tay, đến cổng Quốc, &ocirc;ng đều xuống xe, đi bộ về nh&agrave;. Mặc d&ugrave; theo lệ l&agrave;ng phải đến gần đền thờ Nguyễn Tr&atilde;i, gọi l&agrave; đền &ocirc;ng Khai Quốc, c&oacute; tấm bia hạ m&atilde;, người đi xe mới phải xuống xe đi bộ qua đền, rồi qua đền lại được l&ecirc;n xe đi tiếp. T&igrave;nh cảm s&acirc;u nặng với qu&ecirc; hương, tr&acirc;n trọng c&aacute;c bậc ti&ecirc;n liệt của người qu&ecirc;, mỗi khi qua cổng l&agrave;ng đ&aacute;ng qu&yacute;, đ&aacute;ng trọng biết bao. Trải qua bao đời, những t&igrave;nh cảm ấy, gắn với c&acirc;y đa, giếng nước, ao l&agrave;ng, gắn với những ng&ocirc;i đ&igrave;nh, ng&ocirc;i ch&ugrave;a cổ, đ&atilde; trở th&agrave;nh hồn qu&ecirc;, mang sức sống v&agrave; bản lĩnh của l&agrave;ng, trở th&agrave;nh sức mạnh vật chất, chống lại kẻ th&ugrave;, mỗi khi đất nước bị x&acirc;m lược. Nhờ vậy m&agrave; ở những thời kỳ d&acirc;n tộc ta bị mất nước nhưng l&agrave;ng th&igrave; kh&ocirc;ng bao giờ mất. V&agrave; lẽ sống của người l&agrave;ng được ghi tạc ở cổng l&agrave;ng, lu&ocirc;n s&aacute;ng ngời những gi&aacute; trị ch&acirc;n, thiện, mỹ. Đ&oacute; ch&iacute;nh l&agrave; một diện mạo mang bản sắc độc đ&aacute;o của văn ho&aacute; l&agrave;ng. Trải qua thăng trầm của thời gian, ng&agrave;y nay cổng l&agrave;ng - Biểu tượng văn ho&aacute; của l&agrave;ng qu&ecirc; ch&acirc;u thổ Bắc bộ đang dần đi v&agrave;o qu&ecirc;n l&atilde;ng bởi tốc độ ch&oacute;ng mặt của sự đ&ocirc; thị h&oacute;a.</p>\r\n<p>\r\n	Nh&agrave; tầng với nhiều kiểu c&aacute;ch x&acirc;y dựng pha tạp đ&atilde; mọc l&ecirc;n giữa những l&agrave;ng qu&ecirc; bao đời mộc mạc, b&igrave;nh dị, ph&aacute; vỡ &iacute;t nhiều c&aacute;i cảnh quan đ&atilde; l&agrave; đặc trưng của n&ocirc;ng th&ocirc;n Bắc bộ suốt mấy trăm năm. Dẫu vậy, vẫn c&ograve;n ở đ&acirc;u đ&oacute; tại c&aacute;c l&agrave;ng qu&ecirc; miền Bắc bộ vẻ đẹp của những kiến tr&uacute;c cổ của những chiếc cổng l&agrave;ng đa dạng. Cổng l&agrave;ng Bắc bộ như một lời mời ch&agrave;o th&agrave;nh thật nhất để ta bước v&agrave;o với đời sống l&agrave;ng qu&ecirc;. Trong một g&oacute;c t&acirc;m thức n&agrave;o đ&oacute; của người d&acirc;n, cổng l&agrave;ng vẫn tồn tại như một biểu tượng th&acirc;n thương v&agrave; đặc trưng của mỗi l&agrave;ng qu&ecirc; truyền thống. Hiện nay, c&aacute;c l&agrave;ng qu&ecirc; Việt Nam v&agrave; c&aacute;c l&agrave;ng x&atilde; ngoại &ocirc; th&agrave;nh phố đang nở rộ &ldquo;phong tr&agrave;o&rdquo; phục chế lại hay x&acirc;y dựng mới c&aacute;c cổng l&agrave;ng theo kiểu truyền thống. Đ&oacute; l&agrave; một n&eacute;t đ&aacute;ng mừng. Nhưng c&oacute; lẽ, niềm vui sẽ trọn vẹn hơn nếu sau mỗi cổng l&agrave;ng ấy l&agrave; cuộc sống người d&acirc;n lu&ocirc;n no ấm, y&ecirc;n b&igrave;nh v&agrave; đậm đ&agrave; bản sắc &ldquo;Mỹ tục khả phong&rdquo; (Phong tục tốt đẹp) ri&ecirc;ng. Du lịch, GO! -</p>\r\n<p>\r\n	Theo mạng Cinet, internet</p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1398665244', '0', '1397193038', '0', '0');
INSERT INTO `slm_news` VALUES ('100', '0.298999001399976735.jpg', 'a:20:{i:0;s:24:\"0.298999001399976735.jpg\";i:1;s:24:\"0.605406001399976735.jpg\";i:2;s:24:\"0.891374001399976735.jpg\";i:3;s:24:\"0.193450001399976736.jpg\";i:4;s:24:\"0.495698001399976736.jpg\";i:5;s:24:\"0.759000001399976736.jpg\";i:6;s:24:\"0.076565001399976737.jpg\";i:7;s:24:\"0.361204001399976737.jpg\";i:8;s:24:\"0.666456001399976737.jpg\";i:9;s:24:\"0.984065001399976737.jpg\";i:10;s:24:\"0.263173001399976738.jpg\";i:11;s:24:\"0.583163001399976738.jpg\";i:12;s:24:\"0.874662001399976738.jpg\";i:13;s:24:\"0.134722001399976739.jpg\";i:14;s:24:\"0.404861001399976739.jpg\";i:15;s:24:\"0.726014001399976739.jpg\";i:16;s:24:\"0.108828001399976740.jpg\";i:17;s:24:\"0.328223001399976740.jpg\";i:18;s:24:\"0.631387001399976740.jpg\";i:19;s:24:\"0.919400001399976740.jpg\";}', null, 'Hà Nội vào top 20 thành phố phải tới trong đời', null, null, '12', '', '\r\n	Thủ đ&ocirc; Việt Nam vinh dự đứng thứ 8 trong bảng xếp hạng do Trip Advisor thực hiện, vượt qua cả Thượng Hải, New York hay Dubai .', '<p itemprop=\"description\" style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">Thủ đ&ocirc; Việt Nam vinh dự đứng thứ 8 trong bảng xếp hạng do Trip Advisor thực hiện, vượt qua cả Thượng Hải, New York hay Dubai .</strong></p>\r\n<p itemprop=\"description\" style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.605406001399976735.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">1.&nbsp;Istanbul, Thổ Nhĩ Kỳ.&nbsp;</strong>Istanbul l&agrave; một th&agrave;nh phố s&ocirc;i động, c&oacute; nền văn h&oacute;a giao thoa giữa Kit&ocirc; gi&aacute;o ch&acirc;u &Acirc;u v&agrave; Hồi gi&aacute;o ch&acirc;u &Aacute; trong h&agrave;ng ng&agrave;n năm.&nbsp;Nơi đ&acirc;y để lại nhiều ấn tượng đẹp trong l&ograve;ng du kh&aacute;ch thập phương bởi thức ăn thơm ngon, hương vị c&agrave; ph&ecirc; đậm đ&agrave;, những khu chợ sắc m&agrave;u rực rỡ c&ugrave;ng kiến ​​tr&uacute;c t&ocirc;n gi&aacute;o cổ k&iacute;nh.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.891374001399976735.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">2.&nbsp;Rome, &Yacute;</strong>. L&agrave; một trong những th&agrave;nh phố c&oacute; tầm quan trọng trong lịch sử văn minh phương T&acirc;y, Rome&nbsp;nổi tiếng bởi h&agrave;ng loạt di sản c&oacute; tuổi đời hơn 2.500 năm.&nbsp;Nghệ sĩ đường phố v&agrave; những nh&agrave; h&agrave;ng, qu&aacute;n x&aacute; nằm san s&aacute;t nhau trong kh&ocirc;ng kh&iacute; nhộn nhịp, sầm uất ch&iacute;nh l&agrave; n&eacute;t ri&ecirc;ng độc đ&aacute;o của th&agrave;nh phố l&acirc;u đời n&agrave;y. &nbsp;Ostia Antica, biệt thự cổ của Ho&agrave;ng đế Commodus l&agrave; những điểm tham quan được du kh&aacute;ch y&ecirc;u th&iacute;ch khi đặt ch&acirc;n đến&nbsp;Rome.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.193450001399976736.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">3. London, Anh Quốc</strong>.&nbsp;London l&agrave; một trong những trung t&acirc;m giao thoa của nhiều nền văn h&oacute;a tr&ecirc;n thế giới với hơn 300 ng&ocirc;n ngữ được n&oacute;i tại đ&acirc;y. Đến th&agrave;nh phố hoa lệ n&agrave;y, bạn c&oacute; &nbsp;thể t&igrave;m thấy thức ăn, c&aacute;c h&igrave;nh thức nghệ thuật, trung t&acirc;m vui chơi giải tr&iacute; đặc trưng &nbsp;từ bất cứ nơi n&agrave;o tr&ecirc;n thế giới.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.495698001399976736.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">4. Bắc Kinh, Trung Quốc.</strong>&nbsp;Bắc Kinh l&agrave; một trong những khu d&acirc;n cư lớn v&agrave; l&acirc;u đời nhất tr&ecirc;n thế giới với lịch sử hơn 3.000 năm khai ph&aacute; v&agrave; x&acirc;y dựng, nơi đ&acirc;y gắn liền với nhiều di t&iacute;ch lịch sử quan trọng của Trung Quốc như Vạn L&yacute; Trường Th&agrave;nh v&agrave; Tử Cấm Th&agrave;nh.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.759000001399976736.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">5. Prague, Cộng h&ograve;a S&eacute;c</strong>. &nbsp;Prague được ghi nhận l&agrave; trung t&acirc;m ch&iacute;nh trị, văn h&oacute;a v&agrave; kinh tế lớn nhất thời trung cổ của ch&acirc;u &Acirc;u. Giờ đ&acirc;y, th&agrave;nh phố đang bắt đầu h&ograve;a nhịp với những gi&aacute; trị mới mẻ.&nbsp;D&ugrave; bạn bước v&agrave;o t&ograve;a nh&agrave; Dancing hiện đại hay L&acirc;u đ&agrave;i Praha cổ k&iacute;nh,&nbsp;th&igrave; vẫn sẽ cảm nhận được sự c&acirc;n bằng ho&agrave;n hảo giữa c&aacute;i mới v&agrave; cũ ở th&agrave;nh phố n&agrave;y.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.076565001399976737.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	&nbsp;</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">6.&nbsp;Th&agrave;nh phố Marrakech, Ma-rốc</strong>.&nbsp;Với c&aacute;c khu chợ tấp nập v&agrave; đầy m&agrave;u sắc, th&agrave;nh phố Marrakech&nbsp;l&agrave; nơi l&yacute; tưởng cho những ai muốn trải nghi&ecirc;m hương vị của cuộc sống Ma-rốc truyền thống. Taj Palace l&agrave; điểm đến sang trọng cho du kh&aacute;ch c&aacute;i nh&igrave;n to&agrave;n cảnh về v&ugrave;ng đất n&agrave;y từ d&atilde;y n&uacute;i Atlas.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.361204001399976737.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">7.&nbsp;Paris, Ph&aacute;p</strong>.&nbsp;Paris l&agrave; một kho t&agrave;ng di sản văn h&oacute;a v&ocirc; gi&aacute; của nh&acirc;n loại.&nbsp;Th&agrave;nh phố l&agrave; nơi lưu trữ nhiều t&aacute;c phẩm nghệ thuật gi&aacute; trị v&agrave; tinh t&uacute;y kiến ​​tr&uacute;c từ khắp nơi tr&ecirc;n thế giới. Bảo t&agrave;ng Louvre v&agrave; Orangerie l&agrave; một trong những địa điểm đắt gi&aacute; bạn n&ecirc;n gh&eacute; thăm khi đến Paris&nbsp;.&nbsp;Một tuần chỉ đủ để bạn kh&aacute;m ph&aacute; những g&igrave; thuộc về bề nổi của th&agrave;nh phố n&agrave;y. H&atilde;y c&ugrave;ng bạn b&egrave; thưởng thức m&oacute;n ăn ngon tuyệt trong c&aacute;c nh&agrave; h&agrave;ng &nbsp;Le Fumoir.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.298999001399976735.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">8. H&agrave; Nội, Việt Nam</strong>.&nbsp;H&agrave; Nội l&agrave; sự pha trộn hấp dẫn của Đ&ocirc;ng v&agrave; T&acirc;y, kết hợp văn h&oacute;a truyền thống Việt Nam với n&eacute;t tinh hoa của lối kiến tr&uacute;c Ph&aacute;p.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.984065001399976737.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	May mắn, th&agrave;nh phố bị t&agrave;n ph&aacute; bởi nhiều thập kỷ chiến tranh nay đ&atilde; phục hồi nhanh v&agrave; v&agrave; trở n&ecirc;n s&ocirc;i động hơn bao giờ hết.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.263173001399976738.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">9. Siem Reap, Campuchia</strong>. Tỉnh&nbsp;Siem Reap nơi c&oacute; quần thể kiến tr&uacute;c &nbsp;Angkor Wat, th&agrave;nh phố nổi tiếng với ng&ocirc;i ch&ugrave;a v&agrave; những tượng đ&agrave;i t&ocirc;n gi&aacute;o lớn nhất tr&ecirc;n thế giới. Nếu như Angkor Wat cho du kh&aacute;ch gi&aacute;c phi&ecirc;u lưu, kh&aacute;m ph&aacute; những trải nghiệm văn h&oacute;a mới mẻ th&igrave; Siem Reap sẽ l&agrave; nơi để kh&aacute;ch tham quan nghỉ dưỡng với những địa điểm vui chơi, thư gi&atilde;n l&yacute; th&uacute;.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.583163001399976738.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">10. Thượng Hải, Trung Quốc</strong>. Cuộc sống ở Thương Hải l&uacute;c n&agrave;o cũng nhộn nhịp, bạn sẽ t&igrave;m thấy niềm vui tr&ecirc;n từng con phố. &nbsp;Bảo t&agrave;ng Thượng Hải, Ch&ugrave;a Phật Ngọc, v&agrave; quảng trường Đ&ocirc;ng Đ&agrave;i l&agrave; những nơi kh&aacute;ch du lịch hay gh&eacute; đến khi viếng thăm th&agrave;nh phố s&ocirc;i động n&agrave;y.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.874662001399976738.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">11. Berlin, Đức</strong>.&nbsp;Cũng giống như London v&agrave; Paris, Berlin l&agrave; trung t&acirc;m văn h&oacute;a, nghệ thuật v&agrave; kiến ​​tr&uacute;c độc đ&aacute;o, c&oacute; gi&aacute; trị của nh&acirc;n loại. Berlin l&agrave; chiếc cầu nối giữa qu&aacute; khứ v&agrave; hiện tại với những c&ocirc;ng tr&igrave;nh vĩ đại được x&acirc;y dựng từ thời trung cổ như Cổng Brandenburg, bức tường Berlin.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.134722001399976739.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">12. Th&agrave;nh phố New York, Mỹ</strong>.&nbsp;Th&agrave;nh phố New York l&agrave; trung t&acirc;m của văn h&oacute;a, ch&iacute;nh trị của phương T&acirc;y hiện đại. Cho d&ugrave; bạn đang tham gia một chuyến đi xe đạp nh&agrave;n nh&atilde; qua C&ocirc;ng vi&ecirc;n Trung t&acirc;m, kh&aacute;m ph&aacute; những đỉnh cao bất tận của c&aacute;c trung t&acirc;m mua sắm, hoặc tản bộ đến một tiệm b&aacute;nh&nbsp;pizza tr&ecirc;n phố&hellip; th&igrave; cũng vẫn sẽ cảm nhận được sự phồn hoa rất ri&ecirc;ng của th&agrave;nh phố hiện đại bậc nhất h&agrave;nh trinh n&agrave;y.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.404861001399976739.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">13. Firenze, &Yacute;</strong>.&nbsp;Từ thời trung cổ Firenze đ&atilde; l&agrave; trung t&acirc;m thương mại v&agrave; văn ho&aacute; của ch&acirc;u &Acirc;u, được xem l&agrave; nơi ph&aacute;t động tr&agrave;o lưu thời kỳ phục hưng của &Yacute; &ndash; gia đ&igrave;nh Medici nổi tiếng từ khi cai trị Firenze. Firenze nổi tiếng về nghệ thuật v&agrave; kiến tr&uacute;c. Th&agrave;nh phố c&oacute; những&nbsp;bảo t&agrave;ng tốt nhất, ph&ograve;ng trưng b&agrave;y, nh&agrave; thờ, quảng trường, v&agrave; c&aacute;c cửa h&agrave;ng chất lượng tốt. Nh&agrave; thờ Santa Maria del Fiore v&agrave; Piazza della Signoria l&agrave; hai nơi nổi tiếng bạn n&ecirc;n đặt ch&acirc;n đến khi viếng thăm th&agrave;nh phố n&agrave;y.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.726014001399976739.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">14.&nbsp;Buenos Aires, Argentina</strong>. Buenos Aires được gọi l&agrave; &ldquo;Paris của Nam Mỹ&rdquo; bởi h&agrave;ng loạt di sản, kiến tr&uacute;c ch&acirc;u &Acirc;u được x&acirc;y dựng tại th&agrave;nh phố n&agrave;y từ nhiều thế kỉ trước. Mặc d&ugrave; vậy, th&agrave;nh phố vẫn giữ được n&eacute;t đặc trưng của nền văn h&oacute;a Mỹ Latinh, với c&aacute;c qu&aacute;n c&agrave; ph&ecirc; vỉa h&egrave; v&agrave; c&ocirc;ng vi&ecirc;n đẹp.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.108828001399976740.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">15.&nbsp;Barcelona, ​​T&acirc;y Ban Nha</strong>.&nbsp;Barcelona l&agrave; th&agrave;nh phố được x&acirc;y dựng từ nhiều thế kỷ trước. Những d&atilde;y nh&agrave; nằm san s&aacute;t nhau tr&ecirc;n c&aacute;c con phố nhỏ l&agrave; đặc trưng nơi đ&acirc;y. Bạn chỉ sẽ nh&igrave;n thấy sự xa hoa v&agrave; gi&agrave;u c&oacute; của th&agrave;nh phố n&agrave;y khi đến với c&aacute;c khu nghỉ dưỡng tr&ecirc;n biển.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.328223001399976740.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">16.&nbsp;St. Petersburg, Nga</strong>.&nbsp;St. Petersburg&nbsp;đ&atilde; được coi l&agrave; cầu nối văn h&oacute;a giữa Nga với phương T&acirc;y. Kh&ocirc;ng giống như Moscow, gắn liền với h&igrave;nh ảnh gạch đỏ v&agrave; m&aacute;i v&ograve;m, th&agrave;nh phố St. Petersburg c&oacute; lối kiến ​​tr&uacute;c t&acirc;n cổ điển được thiết kế bởi người &Yacute;, mang hơi thở ch&acirc;u &Acirc;u giữa l&ograve;ng nước nga.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.631387001399976740.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">17. Dubai, tiểu vương quốc Ả Rập</strong>. Đến với Dubai, bạn sẽ c&oacute; cơ hội&nbsp;chi&ecirc;m ngưỡng t&ograve;a nh&agrave; cao nhất thế giới Burj Khalifa, &nbsp;cũng như Dubai Mall, trung t&acirc;m mua sắm lớn nhất h&agrave;nh tinh. Ngo&agrave;i ra, những h&ograve;n đảo nh&acirc;n tạo tuyệt vời v&agrave; c&aacute;c b&atilde;i tắm đẹp cũng l&agrave; những địa điểm vui chơi l&yacute; tưởng thu h&uacute;t du kh&aacute;ch gần xa. C&oacute; thể n&oacute;i, Dubai ng&agrave;y nay đ&atilde; trải qua một cuộc đại lột x&aacute;c bởi trong những năm 1970, nơi đ&acirc;y vẫn c&ograve;n l&agrave; những bộ lạc hoang sơ.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/100/700_0.919400001399976740.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">18. Chicago, Mỹ</strong>.&nbsp;Chicago l&agrave; th&agrave;nh tr&igrave; miền Trung nằm ở ph&iacute;aT&acirc;y Hoa Kỳ, qu&ecirc; hương của hai d&ograve;ng nhạc nổi tiếng thế giới blues v&agrave; jazz. Đa số du kh&aacute;ch đến kh&aacute;m ph&aacute; v&ugrave;ng đất n&agrave;y đều bị hấp dẫn bởi đường ch&acirc;n trời đẹp như tranh vẽ , những t&ograve;a nh&agrave; cao chọc trời được x&acirc;y dựng đầu ti&ecirc;n tr&ecirc;n thế giới, nơi quy tụ những c&acirc;u lạc bộ h&agrave;i kịch tốt nhất của Hoa Kỳ.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img alt=\"Nhập mô tả cho ảnh\" src=\"http://img.v3.news.zdn.vn/w660/Uploaded/qjyyf/2014_05_08/19.jpg\" style=\"text-align: justify; margin: 10px auto; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; -webkit-transition: all 0.3s linear; transition: all 0.3s linear; display: block; width: 670px; max-width: 670px;\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">19.&nbsp;Cape Town, Nam Phi</strong>.&nbsp;Cape Town (l&agrave; th&agrave;nh phố đ&ocirc;ng d&acirc;n thứ nh&igrave; Nam Phi, nơi được bao bọc bởi một b&ecirc;n l&agrave; n&uacute;i đ&aacute; cao cheo leo, một b&ecirc;n l&agrave; biển rộng hoang sơ. Th&agrave;nh phố n&agrave;y l&agrave; một trong những địa điểm ph&aacute;t triển nhất Ch&acirc;u Phi ng&agrave;y nay. Du kh&aacute;ch vẫn c&oacute; thể bắt gặp lối kiến tr&uacute;c phương T&acirc;y khi dạo quanh&nbsp;&nbsp;Cape Town bởi v&ugrave;ng đất n&agrave;y từng l&agrave; thuộc địa của c&aacute;c nước t&acirc;y &Acirc;u.<img alt=\"Nhập mô tả cho ảnh\" src=\"http://img.v3.news.zdn.vn/w660/Uploaded/qjyyf/2014_05_08/20.jpg\" style=\"margin: 10px auto; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; -webkit-transition: all 0.3s linear; transition: all 0.3s linear; display: block; width: 670px; max-width: 670px;\" /><strong style=\"margin: 0px; padding: 0px; border: 0px; outline: rgb(0, 0, 0); vertical-align: baseline; background-color: transparent; color: rgb(28, 28, 28);\">20.&nbsp;Bangkok, Th&aacute;i Lan</strong>.&nbsp;Bangkok l&agrave; v&ugrave;ng đất nổi danh bởi những ng&ocirc;i đền Phật gi&aacute;o c&oacute; kiến tr&uacute;c đẹp v&agrave; cuộc sống về đ&ecirc;m nhộn nhịp. Đ&acirc;y l&agrave; một trong những điểm đến l&yacute; tưởng cho những du kh&aacute;ch chưa c&oacute; hầu bao rủng rỉnh.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399977355', '0', '1399976735', '0', '0');
INSERT INTO `slm_news` VALUES ('96', '0.878544001399961876.jpg', 'a:5:{i:0;s:24:\"0.878544001399961876.jpg\";i:1;s:24:\"0.044072001399961877.jpg\";i:2;s:24:\"0.187226001399961877.jpg\";i:3;s:24:\"0.372433001399961877.jpg\";i:4;s:24:\"0.552584001399961877.jpg\";}', null, 'Hiện tượng thiên nhiên kỳ thú trên bầu trời Việt Nam', null, null, '12', '', '\r\n	Ng&agrave;y biến th&agrave;nh đ&ecirc;m ở Quảng Ninh, cầu vồng đ&ocirc;i ở H&agrave; Nội, m&acirc;y ngũ sắc tại Vĩnh Ph&uacute;c&hellip; l&agrave; những hiện tượng lạ g&acirc;y x&ocirc;n xao cộng đồng. &nbsp;&nbsp;', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/96/700_0.044072001399961877.jpg\" /><br />\r\n	9h s&aacute;ng 3/4, người d&acirc;n ở khu vực H&ograve;n Gai, TP Hạ Long (Quảng Ninh) phải bật đ&egrave;n để sinh hoạt v&agrave; kinh doanh do&nbsp;bầu trời&nbsp;tối sầm&nbsp;xuống. Nguy&ecirc;n nh&acirc;n được x&aacute;c định l&agrave; do cơn gi&ocirc;ng lớn, m&acirc;y đen d&agrave;y đặc đ&atilde; bao phủ mặt trời.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/96/700_0.187226001399961877.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mặt trời tỏa h&agrave;o quang xuất hiện nhiều ở một số địa phương những năm qua như Đ&agrave; Nẵng, B&agrave; Rịa- Vũng T&agrave;u, L&agrave;o Cai, Đ&agrave; Lạt, B&igrave;nh Dương. Quầng mặt trời l&agrave; hiện tượng quang học trong kh&iacute; quyển do những đ&aacute;m m&acirc;y hoặc những lớp kh&ocirc;ng kh&iacute; tr&ecirc;n cao c&oacute; hạt băng, hạt bụi nước kh&uacute;c xạ với &aacute;nh s&aacute;ng mặt trời tạo n&ecirc;n. Quầng mặt trời tương tự như quầng mặt trăng, khi quầng mặt trời xuất hiện l&agrave; l&uacute;c thời tiết kh&ocirc; r&aacute;o, kh&ocirc;ng mưa. Tr&ecirc;n h&igrave;nh l&agrave; hiện tượng h&agrave;o quang mặt trời được ghi nhận ở&nbsp;Bắc Kạn&nbsp;v&agrave;o th&aacute;ng 8 năm ngo&aacute;i.<em>&nbsp;Ảnh do độc giả H&agrave; Triệu Ngọc Quỳnh cung cấp.</em></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/96/700_0.372433001399961877.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đ&aacute;m m&acirc;y kỳ th&uacute; tr&ecirc;n bầu trời H&agrave; Nội v&agrave;o buổi chiều cuối&nbsp;th&aacute;ng 8/2009 sau cơn&nbsp;mưa lớn.&nbsp;C&aacute;c nh&agrave; khoa học cho biết, hiện tượng n&agrave;y kh&ocirc;ng li&ecirc;n quan đến c&aacute;c diễn biến kh&iacute; tượng lớn, nhưng tần suất lặp lại của c&aacute;c đ&aacute;m m&acirc;y rất thấp n&ecirc;n hiếm gặp. Đ&oacute;&nbsp;l&agrave; m&acirc;y mammatus, Việt Nam gọi l&agrave;&nbsp;&rdquo;m&acirc;y vảy rồng&rdquo;.&nbsp;Ảnh:<em>Long H&agrave;.</em></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/96/700_0.552584001399961877.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Buổi chiều&nbsp;cuối th&aacute;ng 7/2012, những đ&aacute;m m&acirc;y ngũ sắc xuất hiện tr&ecirc;n bầu trời H&agrave; Nội v&agrave; Vĩnh Ph&uacute;c. Hiện&nbsp;tượng n&agrave;y trước đ&oacute; được người&nbsp;Quy Nhơn chứng kiến v&agrave;o&nbsp;th&aacute;ng 6/2012.&nbsp;&nbsp;M&acirc;y ngũ sắc l&agrave; sự xuất hiện của m&agrave;u sắc trong một&nbsp;đ&aacute;m m&acirc;y, kh&ocirc;ng qu&aacute; hiếm gặp.&nbsp;Đ&aacute;m m&acirc;y ngũ sắc được tạo ra nhờ nhiễu xạ, c&aacute;c giọt nước nhỏ&nbsp;hoặc c&aacute;c tinh thể nước đ&aacute; nhỏ trong đ&aacute;m m&acirc;y t&aacute;n xạ &aacute;nh s&aacute;ng trắng.<em>&nbsp;Ảnh do độc giả&nbsp;Đặng Ho&agrave;ng Long &ndash; Vũ Đ&igrave;nh Kh&aacute;nh cung cấp.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Cũng trong th&aacute;ng 7/2012, người H&agrave; Nội được chứng kiến&nbsp;cầu vồng đ&ocirc;i v&agrave;&nbsp;những &aacute;ng m&acirc;y rực rỡ sắc m&agrave;u.&nbsp;Cầu vồng l&agrave; hiện tượng t&aacute;n sắc của &aacute;nh s&aacute;ng mặt trời khi tương t&aacute;c với những hạt nước trong kh&ocirc;ng kh&iacute;. Khi &aacute;nh s&aacute;ng vừa bị phản xạ, vừa bị kh&uacute;c xạ qua hạt nước, n&oacute; sẽ t&aacute;ch th&agrave;nh c&aacute;c m&agrave;u theo thứ tự đỏ, cam, v&agrave;ng, lục, lam, ch&agrave;m, t&iacute;m. Thường thấy nhất l&agrave; cầu vồng đơn, c&ograve;n&nbsp;cầu vồng &ldquo;sinh đ&ocirc;i&rdquo; rất hiếm gặp. Theo c&aacute;c nh&agrave; khoa học quốc tế, b&iacute; ẩn của loại cầu vồng n&agrave;y nằm ở sự kết hợp của c&aacute;c giọt nước với k&iacute;ch cỡ kh&aacute;c nhau. Đ&ocirc;i khi, hai cơn mưa r&agrave;o xảy ra c&ugrave;ng một l&uacute;c, c&aacute;c hạt mưa sẽ c&oacute; k&iacute;ch cỡ kh&aacute;c nhau v&agrave; tạo ra cầu vồng hơi biến dạng. Những cầu vồng n&agrave;y kết hợp sẽ l&agrave;m th&agrave;nh cầu vồng sinh đ&ocirc;i.&nbsp;Ảnh do độc giả Đỗ Duy Trường cung cấp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/96/700_0.878544001399961876.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	S&aacute;ng&nbsp;1/9/1989, nhiều người thấy đ&aacute;m m&acirc;y h&igrave;nh rồng Đền Đ&ocirc; (Bắc Ninh). Ảnh:<em>Nguyễn Đức Th&igrave;n.</em></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399978142', '0', '1399440615', '0', '0');
INSERT INTO `slm_news` VALUES ('74', '0.563948001397465172.jpg', 'a:10:{i:0;s:24:\"0.563948001397465172.jpg\";i:1;s:24:\"0.064614001397465244.jpg\";i:2;s:24:\"0.280968001397465244.jpg\";i:3;s:24:\"0.478677001397465244.jpg\";i:4;s:24:\"0.646751001397465244.jpg\";i:5;s:24:\"0.845598001397465244.jpg\";i:6;s:24:\"0.061572001397465245.jpg\";i:7;s:24:\"0.261608001397465245.jpg\";i:8;s:24:\"0.443103001397465245.jpg\";i:9;s:24:\"0.733449001397465245.jpg\";}', null, 'Làng dệt lụa nghìn năm tuổi ở Hà Nội', null, null, '12', '', '\r\n	Nằm c&aacute;ch hồ Gươm khoảng 10 km, nhưng l&agrave;ng Vạn Ph&uacute;c vẫn giữ được n&eacute;t cổ k&iacute;nh của l&agrave;ng qu&ecirc; xưa với c&acirc;y đa, giếng nước, m&aacute;i đ&igrave;nh.', '<div class=\"width_common space_bottom_20\">\r\n	<div class=\"block_content_slide_showdetail\">\r\n		<div id=\"article_content\">\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.064614001397465244.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Nhắc đến l&agrave;ng Vạn Ph&uacute;c, mọi người sẽ nhớ đến nghề dệt lụa tơ tằm nổi tiếng khắp trong v&agrave; ngo&agrave;i nước, từng được xuất hiện trong c&aacute;c b&agrave;i thơ, b&agrave;i h&aacute;t, phim ảnh.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.280968001397465244.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Khu giới thiệu quy tr&igrave;nh dệt lụa v&agrave; trưng b&agrave;y sản phẩm. Mỗi th&aacute;ng, l&agrave;ng đ&oacute;n 3.000-5.000 lượt kh&aacute;ch tham quan.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.478677001397465244.jpg\" />&nbsp;\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						Kh&aacute;ch du lịch sẽ được xem c&aacute;c c&ocirc;ng đoạn dệt lụa, lắng nghe &acirc;m thanh rộn r&agrave;ng từ khung cửi, tiếng thoi đ&atilde; trở th&agrave;nh nhịp điệu cuộc sống nơi đ&acirc;y.</p>\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.563948001397465172.jpg\" />&nbsp;\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						Khung cửi cổ của l&agrave;ng, c&oacute; từ đầu thế kỷ 19, l&agrave; c&ocirc;ng cụ lao động đem lại sự phồn thịnh của l&agrave;ng nghề Vạn Ph&uacute;c ng&agrave;y nay.</p>\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.646751001397465244.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Cổng l&agrave;ng nay đ&atilde; được tu bổ bề thế. Vạn Ph&uacute;c được c&ocirc;ng nhận kỷ lục &#39;L&agrave;ng nghề dệt Lụa tơ tằm l&acirc;u đời nhất c&ograve;n duy tr&igrave; hoạt động đến ng&agrave;y nay&#39; do Trung t&acirc;m s&aacute;ch kỷ lục Việt Nam trao tặng.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.845598001397465244.jpg\" />&nbsp;<a class=\"btn_icon_show_slide_show\" href=\"http://doisong.vnexpress.net/photo/khong-gian-song/lang-det-lua-nghin-nam-tuoi-o-ha-noi-2975923.html#\"> </a><br />\r\n					<p style=\"text-align: center;\">\r\n						Đ&igrave;nh l&agrave;ng lụa Vạn Ph&uacute;c, nơi thờ th&agrave;nh ho&agrave;ng l&agrave;ng, được x&acirc;y dựng từ thế kỷ 17.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.061572001397465245.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Những nơi thờ c&uacute;ng ở l&agrave;ng vẫn được giữ nguy&ecirc;n vẹn.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.261608001397465245.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Do kinh tế ph&aacute;t triển, khắp l&agrave;ng l&agrave; c&aacute;c t&ograve;a nh&agrave; cao tầng mọc san s&aacute;t nhau. Nếu chịu kh&oacute; đi quanh l&agrave;ng, bạn vẫn thấy được nhiều ng&ocirc;i nh&agrave; cổ, những m&aacute;i v&ograve;m, cổng v&agrave;o c&oacute; tuổi đời gần trăm năm.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.443103001397465245.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						C&acirc;y đa trong miếu l&agrave;ng Vạn Ph&uacute;c, một trong s&aacute;u c&acirc;y được phong tặng &#39;Cụm c&acirc;y di sản Việt Nam&#39;, c&oacute; tuổi đời gần 1.000 năm.</p>\r\n					<p style=\"text-align: center;\">\r\n						&nbsp;</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/74/700_0.733449001397465245.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Ngo&agrave;i c&aacute;c sản phẩm của l&agrave;ng, trong chợ l&agrave;ng c&ograve;n b&aacute;n một số đồ nhập từ nơi kh&aacute;c.</p>\r\n				</div>\r\n			</div>\r\n			<div class=\"fck_detail width_common\">\r\n				<p style=\"text-align: center;\">\r\n					&nbsp;</p>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1397465514', '0', '1397465172', '0', '0');
INSERT INTO `slm_news` VALUES ('90', '0.448158001399263068.jpg', 'a:15:{i:0;s:24:\"0.448158001399263068.jpg\";i:1;s:24:\"0.419658001399263069.jpg\";i:2;s:24:\"0.671479001399263069.jpg\";i:3;s:24:\"0.899708001399263069.jpg\";i:4;s:24:\"0.188963001399263070.jpg\";i:5;s:24:\"0.381622001399263070.jpg\";i:6;s:24:\"0.554169001399263070.jpg\";i:7;s:24:\"0.741104001399263070.jpg\";i:8;s:24:\"0.156525001399263071.jpg\";i:9;s:24:\"0.839254001399263072.jpg\";i:10;s:24:\"0.169770001399263073.jpg\";i:11;s:24:\"0.510203001399263073.jpg\";i:12;s:24:\"0.814603001399263073.jpg\";i:13;s:24:\"0.154967001399263074.jpg\";i:14;s:24:\"0.513168001399263074.jpg\";}', null, 'Múa Rồng độc đáo trên sông Sào Khê', null, null, '12', '', '\r\n	Ng&agrave;y 17/4, lễ hội Th&aacute;nh Qu&yacute; Minh Đại Vương thường ni&ecirc;n diễn ra tại đền Trần, khu du lịch Tr&agrave;ng An - B&aacute;i Đ&iacute;nh, Ninh B&igrave;nh với hoạt động m&uacute;a rồng tr&ecirc;n s&ocirc;ng.', '<div id=\"article_content\">\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.419658001399263069.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Khu du lịch sinh th&aacute;i Tr&agrave;ng An đ&oacute;n h&agrave;ng ngh&igrave;n lượt kh&aacute;ch du lịch về rước Th&aacute;nh Qu&yacute; Minh Đại Vương h&agrave;ng năm. Ban Tổ chức bố tr&iacute; khoảng hơn 1.000 thuyền để phục vụ du kh&aacute;ch tới đền Nội L&acirc;m (đền Trần) v&agrave; tham quan c&aacute;c hang động tr&ecirc;n s&ocirc;ng S&agrave;o Kh&ecirc;, với tổng chiều d&agrave;i đoạn đường l&ecirc;n tới hơn 15 km.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.448158001399263068.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Du kh&aacute;ch đổ về Tr&agrave;ng An đ&ocirc;ng nhất dịp đầu năm. Đ&acirc;y cũng l&agrave; m&ugrave;a nước cạn, thuyền b&egrave; di chuyển tr&ecirc;n s&ocirc;ng dễ d&agrave;ng v&agrave; thuận tiện hơn. Một trong những n&eacute;t nổi bật của lễ hội Th&aacute;nh Qu&yacute; Minh Đại Vương năm nay l&agrave; tiết mục m&uacute;a rồng tr&ecirc;n s&ocirc;ng.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.671479001399263069.jpg\" />&nbsp;\r\n			<p class=\"Normal\" style=\"margin-top:1em;margin-right:0px;margin-bottom:1em;margin-left:0px;line-height:20px;\">\r\n				Năm chiếc thuyền được cột chặt tạo th&agrave;nh s&acirc;n khấu tr&igrave;nh diễn cho c&aacute;c nghệ sĩ. Hai thuyền rồng đi đầu, theo sau l&agrave; c&aacute;c đội trống, kiệu, cờ....</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img height=\"682\" src=\"http://langquetoi.com/images/news/90/700_0.899708001399263069.jpg\" width=\"551\" />&nbsp;<a class=\"btn_icon_show_slide_show\" href=\"http://dulich.vnexpress.net/photo/anh-video/mua-rong-doc-dao-tren-song-sao-khe-2979499.html#\"> </a><br />\r\n			<p>\r\n				Người điều khiển đầu rồng phải rất tập trung để diễn m&agrave;n m&uacute;a tr&ecirc;n s&ocirc;ng.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.188963001399263070.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 15px 0px;\">\r\n				H&igrave;nh ảnh hai con rồng đầy m&agrave;u sắc vươn m&igrave;nh giữa nước non h&ugrave;ng vĩ để lại ấn tượng mạnh cho du kh&aacute;ch nước ngo&agrave;i.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.381622001399263070.jpg\" />&nbsp;\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Tiếng trống v&agrave; tiếng h&ograve; reo vang vộng cả v&ugrave;ng s&ocirc;ng nước S&agrave;o Kh&ecirc; y&ecirc;n b&igrave;nh.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.554169001399263070.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Tr&ecirc;n h&agrave;nh tr&igrave;nh rước Th&aacute;nh Qu&yacute; Minh Đại Vương, lễ rước nước cũng được tiến h&agrave;nh.&nbsp;<span style=\"color:rgb(0,0,0);\">Ph&oacute; chủ tịch nước Nguyễn Thị Doan về dự v&agrave; l&agrave;m chủ lễ, cầu quốc th&aacute;i d&acirc;n an, mưa thuận gi&oacute; h&ograve;a.&nbsp;</span></p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.741104001399263070.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"color:rgb(34,34,34);background-color:rgb(255,255,255);margin-top:4px;margin-right:4px;margin-bottom:4px;margin-left:4px;\">\r\n				Xuy&ecirc;n suốt h&agrave;nh tr&igrave;nh hơn một giờ đồng hồ ch&egrave;o thuyền qua đoạn đường d&agrave;i 6 km để tới đền Nội L&acirc;m. Du kh&aacute;ch c&oacute; thể tham quan Phủ Đột (Đền Tr&igrave;nh) hay cổng Tam Quan nằm giữa n&uacute;i xanh h&ugrave;ng vĩ. Từ Cổng Tam Quan, du kh&aacute;ch c&oacute; thể di chuyển ra đền Nội L&acirc;m bằng c&aacute;ch đi bộ.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.156525001399263071.jpg\" />&nbsp;\r\n			<p class=\"Normal\" style=\"margin-top:1em;margin-right:0px;margin-bottom:1em;margin-left:0px;line-height:20px;\">\r\n				Ở những đoạn dễ di chuyển, du kh&aacute;ch c&oacute; thể thử sức ch&egrave;o qua sự hướng dẫn của người l&aacute;i.&nbsp;</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.839254001399263072.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				C&aacute;c thuyền hoạt động trong khu du lịch đều được đ&aacute;nh số thứ tự, người l&aacute;i chỉ được chở tối đa 5 kh&aacute;ch/ chuyến v&agrave; phải đ&oacute;n trả kh&aacute;ch theo sự điều phối của ban quản l&yacute;. Điều n&agrave;y gi&uacute;p gi&aacute; thuyền ổn định v&agrave; tr&aacute;nh t&igrave;nh trạng chen lấn, gi&agrave;nh giật kh&aacute;ch.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.169770001399263073.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Hiện nay, khu du lịch Tr&agrave;ng An khai th&aacute;c du lịch 12 hang động tr&ecirc;n tổng số 54 hang. T&ecirc;n mỗi hang động đều gắn với những điển t&iacute;ch th&uacute; vị.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/90/700_0.510203001399263073.jpg\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Đường đến Đền Trần trai qua 3 hang động l&agrave; hang S&aacute;ng, hang Tối v&agrave; hang Nấu Rượu. Những nhũ đ&aacute; kết tủa từ h&agrave;ng triệu năm với h&igrave;nh th&ugrave; lạ mắt l&agrave; điểm ấn tượng của cuộc h&agrave;nh tr&igrave;nh.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img height=\"644\" src=\"http://langquetoi.com/images/news/90/700_0.814603001399263073.jpg\" width=\"537\" /><br />\r\n			&nbsp;</div>\r\n		<div class=\"desc_cation\">\r\n			<p class=\"Normal\" style=\"margin:0px 0px 16px 0px;color:#232323;\">\r\n				Hang Tối d&agrave;i 320 m , ngoằn ngo&egrave;o v&agrave; kh&oacute; di chuyển chỉ cho ph&eacute;p thuyền đi một chiều. C&oacute; những điểm cao hơn mặt nước 1,5 m n&ecirc;n v&agrave;o m&ugrave;a nước nổi việc đi lại qua hang gặp nhiều kh&oacute; khăn.</p>\r\n		</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img height=\"443\" src=\"http://langquetoi.com/images/news/90/700_0.154967001399263074.jpg\" width=\"664\" />&nbsp;\r\n			<p class=\"Normal\" style=\"margin-top:1em;margin-right:0px;margin-bottom:1em;margin-left:0px;line-height:20px;\">\r\n				Sau khi cập bến, du kh&aacute;ch phải chinh phục 175 bậc thang dốc để tới được đền Nội L&acirc;m.</p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n	<div class=\"item_slide_show\">\r\n		<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n			<img height=\"473\" src=\"http://langquetoi.com/images/news/90/700_0.513168001399263074.jpg\" width=\"671\" />&nbsp;\r\n			<p class=\"Normal\" style=\"margin-top:1em;margin-right:0px;margin-bottom:1em;margin-left:0px;line-height:20px;color:rgb(0,0,0);\">\r\n				Nghi lễ rước kiệu l&ecirc;n đền Nội L&acirc;m được người d&acirc;n v&agrave; du kh&aacute;ch quan t&acirc;m, tập trung theo d&otilde;i.&nbsp;<span>UNESCO đang xem x&eacute;t việc c&ocirc;ng nhận khu du lịch sinh th&aacute;i Tr&agrave;ng An l&agrave; di sản, nếu được th&ocirc;ng qua đ&acirc;y sẽ l&agrave; di sản văn h&oacute;a v&agrave; thi&ecirc;n nhi&ecirc;n đầu ti&ecirc;n ở Việt Nam.</span></p>\r\n		</div>\r\n		<div class=\"clear\">\r\n			&nbsp;</div>\r\n	</div>\r\n</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399365778', '0', '1399263068', '0', '0');
INSERT INTO `slm_news` VALUES ('89', '0.021524001399260373.jpg', 'a:1:{i:0;s:24:\"0.021524001399260373.jpg\";}', null, 'Hạt sen tốt cho ăn kiêng', null, null, '12', '', '\r\n	C&oacute; h&agrave;m lượng dinh dưỡng phong ph&uacute; nhưng &iacute;t chất b&eacute;o b&atilde;o h&ograve;a, hạt sen được xem l&agrave; thực phẩm ăn ki&ecirc;ng h&agrave;ng đầu.', '<p class=\"Normal\">\r\n	Khoa học hiện đại đ&atilde; chứng minh hạt sen l&agrave; một th&agrave;nh phần kh&ocirc;ng thể thiếu trong chế độ ăn ki&ecirc;ng tốt cho sức khỏe.</p>\r\n<p class=\"subtitle\">\r\n	<strong>Dinh dưỡng trong hạt sen</strong></p>\r\n<p class=\"Normal\">\r\n	Hạt sen l&agrave; nguồn thực phẩm l&yacute; tưởng cung cấp protein, kali, magi&ecirc; v&agrave; phốt pho. Đ&acirc;y l&agrave; những chất c&oacute; chức năng ch&iacute;nh x&uacute;c t&aacute;c enzyme v&agrave; cấu tạo tế b&agrave;o. Hạt sen chứa &iacute;t chất b&eacute;o b&atilde;o h&ograve;a (th&agrave;nh phần l&agrave;m tăng cholesterol m&aacute;u), cholesterol v&agrave; muối natri.</p>\r\n<p class=\"Normal\" style=\"text-align: center;\">\r\n	<img height=\"499\" src=\"http://langquetoi.com/images/news/89/700_0.021524001399260373.jpg\" width=\"700\" /></p>\r\n<p class=\"Normal\">\r\n	100 g hạt sen cung cấp cho cơ thể 350 calo, 18 g protein, nhưng chỉ c&oacute; khoảng 2,2 g chất b&eacute;o. Ngo&agrave;i ra, hạt sen c&ograve;n chứa một lượng kẽm v&agrave; sắt vừa phải gi&uacute;p tăng cường sức đề kh&aacute;ng cho cơ thể v&agrave; h&igrave;nh th&agrave;nh tế b&agrave;o m&aacute;u.</p>\r\n<p class=\"Normal\">\r\n	Ngo&agrave;i ra, hạt sen chứa chất L- isoaspartyl methyltransferase, một loại enzyme chống l&atilde;o h&oacute;a. Enzyme n&agrave;y gi&uacute;p sửa chữa protein bị tổn thương. Do đ&oacute;, nhiều c&ocirc;ng ty mỹ phẩm đang cố gắng kh&aacute;m ph&aacute; để đưa loại enzyme n&agrave;y v&agrave;o c&aacute;c sản phẩm chống l&atilde;o h&oacute;a. Hạt sen c&oacute; đặc t&iacute;nh gi&uacute;p giảm r&ograve; rỉ dịch của c&aacute;c bộ phận nội tạng, như l&aacute; l&aacute;ch, thận v&agrave; tim. Hạt sen rất tốt cho tinh thần những người mắc chứng mất ngủ, bồn chồn lo lắng.</p>\r\n<p class=\"subtitle\">\r\n	<strong>Hạt sen trong chế độ ăn ki&ecirc;ng</strong></p>\r\n<p class=\"Normal\">\r\n	Hạt sen l&agrave; một loại thực phẩm ăn ki&ecirc;ng tốt h&agrave;ng đầu. Với lượng calo cung cấp v&agrave;o cơ thể thấp nhưng hạt sen lại chứa nhiều chất dinh dưỡng, vi lượng đ&aacute;p ứng nhiều nhu cầu kh&aacute;c nhau của cơ thể. Điều đ&oacute; gi&uacute;p bạn ăn ki&ecirc;ng m&agrave; vẫn kh&ocirc;ng bị thiếu chất, cơ thể vẫn cường tr&aacute;ng, khỏe mạnh. Một số nghi&ecirc;n cứu đ&atilde; chứng minh ăn hạt sen gi&uacute;p loại bỏ độc tố, đặc biệt l&agrave; chất b&eacute;o dư thừa trong cơ thể.</p>\r\n<p class=\"Normal\">\r\n	Hạt sen ăn sống hoặc nấu ch&iacute;n đều tốt. Trong ẩm thực, hạt sen l&agrave; một nguy&ecirc;n liệu hữu dụng c&oacute; thể chế biến với nhiều phương thức kh&aacute;c nhau như chi&ecirc;n, x&agrave;o, s&uacute;p, ch&egrave;, ch&aacute;o, b&aacute;nh kẹo...</p>\r\n<p class=\"Normal\">\r\n	Do đ&oacute;, nếu chọn hạt sen l&agrave; thực phẩm ch&iacute;nh trong chương tr&igrave;nh ăn ki&ecirc;ng, bạn c&oacute; thể kết hợp hạt sen với c&aacute;c loại thực phẩm kh&aacute;c chế biến th&agrave;nh nhiều m&oacute;n ăn ngon kh&aacute;c nhau c&oacute; thể ăn trong thời gian d&agrave;i m&agrave; kh&ocirc;ng sợ ng&aacute;n.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399260739', '0', '1399260372', '0', '0');
INSERT INTO `slm_news` VALUES ('85', '0.908220001399082777.jpg', 'a:10:{i:0;s:24:\"0.908220001399082777.jpg\";i:1;s:24:\"0.979125001399082777.jpg\";i:2;s:24:\"0.000141001399082778.jpg\";i:3;s:24:\"0.144868001399082778.jpg\";i:4;s:24:\"0.280444001399082778.jpg\";i:5;s:24:\"0.370983001399082778.jpg\";i:6;s:24:\"0.646854001399082778.jpg\";i:7;s:24:\"0.798841001399082778.jpg\";i:8;s:24:\"0.892058001399082778.jpg\";i:9;s:24:\"0.913729001399082778.jpg\";}', null, '10 loài vật cực độc cần phải tránh xa ngay khi có thể', null, null, '12', '', '\r\n	Thế giới hoang d&atilde; l&agrave; một v&ugrave;ng đất tuyệt đẹp v&agrave; th&uacute; vị. Nhưng cũng thật kh&ocirc;ng may l&agrave; nơi đ&oacute; lu&ocirc;n tiềm ẩn nguy cơ giết chết ch&uacute;ng ta chỉ trong v&agrave;i gi&acirc;y nếu bất cẩn. Bởi nơi đ&oacute; thường đi k&egrave;m với nhiều lo&agrave;i vật nguy hiểm c&oacute; nọc độc, hung &aacute;c hoặc cả hai. H&atilde;y tr&aacute;nh xa 10 lo&agrave;i vật dưới đ&acirc;y nếu bạn tr&acirc;n trọng gi&aacute; trị cuộc sống của m&igrave;nh. Hầu', '<div itemprop=\"articleBody\" style=\"text-align: justify;\">\r\n	<div>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"331\" src=\"http://langquetoi.com/images/news/85/700_0.979125001399082777.jpg\" width=\"526\" /></p>\r\n		<p>\r\n			1. C&aacute; đ&aacute; T&ecirc;n khoa học Synanceia, C&aacute; Đ&aacute; l&agrave; một trong những lo&agrave;i c&aacute; độc nhất tr&ecirc;n thế giới. Ch&uacute;ng được t&igrave;m thấy ở c&aacute;c v&ugrave;ng ven biển Ấn Độ- Th&aacute;i B&igrave;nh Dương, ngo&agrave;i khơi bờ biển Florida v&agrave; trong v&ugrave;ng biển Caribbean. Chất độc của ch&uacute;ng t&aacute;c động mạnh v&agrave;o thần kinh v&agrave; được tiết ra từ c&aacute;c tuyến v&acirc;y gai lưng. Khi bị đe dọa ch&uacute;ng sẽ tiết ra một lượng nọc độc tỷ lệ thuận với sự &aacute;p lực bị đe dọa m&agrave; ch&uacute;ng cảm nhận được. C&aacute; Đ&aacute; ngụy trang bản th&acirc;n bằng m&agrave;u x&aacute;m v&agrave; đốm giống m&agrave;u sắc của đ&aacute;, khi ch&uacute;ng ta bơi l&ocirc;i v&agrave; chẳng mai bước v&agrave;o ch&uacute;ng v&agrave; bị cắn th&igrave; chỉ sau 20 gi&acirc;y t&iacute;nh mạng của người đ&oacute; chắc chắn bị đe dọa.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"544\" src=\"http://langquetoi.com/images/news/85/700_0.000141001399082778.jpg\" width=\"625\" /></p>\r\n		<p>\r\n			2. Ếch độc-ếch phi ti&ecirc;u Lo&agrave;i ếch n&agrave;y c&oacute; m&agrave;u sắc rất sặc sỡ. Độc tố của ch&uacute;ng trong tuyến da để bảo vệ bản th&acirc;n chống lại kể săn mồi. Mức độ độc tố t&ugrave;y thuộc v&agrave;o k&iacute;ch thước v&agrave; m&agrave;u sắc của ch&uacute;ng. Lo&agrave;i độc nhất của lo&agrave;i ếch n&agrave;y l&agrave; Ếch độc v&agrave;ng- t&ecirc;n khoa học Phyllobates terribilis, độc tố của ch&uacute;ng trung b&igrave;nh giết đến 12 người đ&agrave;n &ocirc;ng v&agrave; 10.000 con chuột.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/85/700_0.144868001399082778.jpg\" /></p>\r\n		<p>\r\n			3. Sứa &ldquo;ong bắc c&agrave;y&rdquo; Đặc điểm nhận dạng l&agrave; ch&uacute;ng c&oacute; h&igrave;nh &ocirc; hộp, chứ kh&ocirc;ng phải l&agrave; m&aacute;i v&ograve;m hoặc h&igrave;nh vương miện. Mặt dưới của &ocirc; bao gồm một nắp để tập trung v&agrave; tăng lưu lượng nước kh&ocirc;ng bị tho&aacute;t khỏi &ocirc;. Kết quả l&agrave;, sứa hộp c&oacute; thể di chuyển nhanh hơn so với những lo&agrave;i sứa kh&aacute;c, tốc độ đ&atilde; được ghi nhận l&ecirc;n 6m/ ph&uacute;t. Ch&uacute;ng sản xuất nọc độc cực kỳ mạnh, l&agrave; một trong những sinh vật độc nhất tr&ecirc;n thế giới. Những vết cắn từ lo&agrave;i n&agrave;y cực kỳ đau đớn v&agrave; đ&ocirc;i khi g&acirc;y tử vong đối với con người.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"394\" src=\"http://langquetoi.com/images/news/85/700_0.908220001399082777.jpg\" width=\"500\" /></p>\r\n		<p>\r\n			4. Rắn hổ mang ch&uacute;a L&agrave; một trong những lo&agrave;i độc nhất tr&ecirc;n thế giới với chiều d&agrave;i l&ecirc;n đến 5,7 m. Ch&uacute;ng được t&igrave;m thấy trong c&aacute;c khu rừng từ Ấn Độ đến Đ&ocirc;ng Nam &Aacute;. Rắn hổ mang ch&uacute;a cũng được biết đến như l&agrave; biểu tượng văn h&oacute;a của một số nước tr&ecirc;n thế giới. Nọc độc của ch&uacute;ng t&aacute;c động trực tiếp đến hệ thần kinh. Độc tố bắt đầu tấn c&ocirc;ng v&agrave;o hệ thống thần kinh trung ương. C&aacute;c triệu chứng bao gồm đau nặng, mắt mờ, ch&oacute;ng mặt, buồn ngủ v&agrave; t&ecirc; liệt, nếu nặng hơn nữa l&agrave; suy tim, suy h&ocirc; hấp v&agrave; tử vong.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"464\" src=\"http://langquetoi.com/images/news/85/700_0.280444001399082778.jpg\" width=\"594\" /></p>\r\n		<p>\r\n			5. Ốc n&oacute;n cẩm thạch L&agrave; lo&agrave;i ốc biển ăn thịt. Thức ăn chủ yếu l&agrave; c&aacute;c động vật th&acirc;n mềm kh&aacute;c, ch&uacute;ng ti&ecirc;m nộc độc v&agrave;o con mồi sau đ&oacute; con mồi bị ph&acirc;n hủy v&agrave; h&uacute;t dịch từ đ&oacute;. Lo&agrave;i ốc n&agrave;y được t&igrave;m thấy ở Ấn Độ Dương v&agrave; ph&iacute;a t&acirc;y Th&aacute;i B&igrave;nh Dương. Nhưng nếu bị đe dọa ch&uacute;ng sẽ d&ugrave;ng v&ograve;i của m&igrave;nh ch&acirc;m nhiều lần n&ecirc;n kẻ th&ugrave; v&agrave; với lượng độc đ&oacute; sẽ g&acirc;y nguy hiểm đến nạn nh&acirc;n.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"519\" src=\"http://langquetoi.com/images/news/85/700_0.370983001399082778.jpg\" width=\"606\" /></p>\r\n		<p>\r\n			6. Bạch tuộc đốm xanh Ch&uacute;ng sống trong v&ugrave;ng thủy triều v&agrave; c&aacute;c rạn san h&ocirc; ở Th&aacute;i B&igrave;nh Dương v&agrave; Ấn Độ Dương. Lo&agrave;i n&agrave;y được c&ocirc;ng nhận l&agrave; một những lo&agrave;i độc nhất của thế giới biển. Mặc d&ugrave; k&iacute;ch thước nhỏ v&agrave; tương đối dễ chịu, nhưng đ&atilde; c&oacute; bằng chứng về mối nguy hiểm cho con người. Khi bị k&iacute;ch động những xanh v&agrave; đen tr&ecirc;n cơ thể ch&uacute;ng to ra v&agrave; chất độc được sản xuất bởi c&aacute;c vi khuẩn trong tuyến nước bọt của bạch tuộc. Nọc độc của ch&uacute;ng dẫn đến buồn n&ocirc;n , ngừng h&ocirc; hấp , suy tim , nghi&ecirc;m trọng hơn l&agrave; dẫn đến t&ecirc; liệt, m&ugrave; l&ograve;a v&agrave; tử vong trong v&ograve;ng v&agrave;i ph&uacute;t nếu kh&ocirc;ng được điều trị.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"493\" src=\"http://langquetoi.com/images/news/85/700_0.646854001399082778.jpg\" width=\"580\" /></p>\r\n		<p>\r\n			7. C&aacute; n&oacute;c Phần lớn c&aacute; N&oacute;c l&agrave; c&aacute; nước mặn v&agrave; sinh sống xung quanh c&aacute;c b&atilde;i đ&aacute; san h&ocirc; ngầm v&ugrave;ng nhiệt đới, nhưng c&oacute; v&agrave;i lo&agrave;i l&agrave; c&aacute;c nước ngọt, sinh sống trong s&ocirc;ng suối hay cửa s&ocirc;ng. C&aacute; n&oacute;c c&oacute; thể g&acirc;y chết người nếu kh&ocirc;ng được sơ chế đ&uacute;ng c&aacute;ch. C&aacute;c bộ phận của c&aacute; n&oacute;c c&oacute; độc t&iacute;nh với mức độ rất kh&aacute;c nhau như sau: trứng, tinh ho&agrave;n, gan, ruột, da, thịt. Biểu hiện của sự ngộ độc l&agrave; ch&oacute;ng mặt v&agrave; n&ocirc;n mửa, tiếp theo l&agrave; t&ecirc; liệt cơ thể, nhịp tim nhanh, giảm huyết &aacute;p, c&oacute; thể dẫn đến tử vong.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/85/700_0.798841001399082778.jpg\" /></p>\r\n		<p>\r\n			8. Nhện lang thang Brazil Hay c&ograve;n được biết đến với c&aacute;i t&ecirc;n nhện &ldquo;vũ trang&rdquo;. Lo&agrave;i n&agrave;y chủ yếu được t&igrave;m thấy ở v&ugrave;ng nhiệt đới Nam, Trung Mỹ. Nhện lang thang Brazil xuất hiện trong s&aacute;ch kỷ lục Guinness từ năm 2010 l&agrave; lo&agrave;i độc nhất trong thế giới nhện độc. Ở mức độ chết người, chất độc t&aacute;c động v&agrave;o thần kinh g&acirc;y ra mất kiểm so&aacute;t cơ bắp v&agrave; c&aacute;c vấn đề h&ocirc; hấp, dẫn đến t&ecirc; liệt v&agrave; cuối c&ugrave;ng ngạt thở . Ngo&agrave;i ra, nọc độc g&acirc;y đau đớn v&agrave; vi&ecirc;m sau một vết cắn do một hiệu ứng k&iacute;ch th&iacute;ch nọc độc với hệ thần kinh cảm gi&aacute;c.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"437\" src=\"http://langquetoi.com/images/news/85/700_0.892058001399082778.jpg\" width=\"507\" /></p>\r\n		<p>\r\n			9. Rắn nội địa- Taipans L&agrave; lo&agrave;i đặc hữu ở v&ugrave;ng kh&ocirc; hạn khu vực trung t&acirc;m ph&iacute;a đ&ocirc;ng &Uacute;c . C&aacute;c con rắn nội địa được coi l&agrave; lo&agrave;i độc nhất tr&ecirc;n thế giới. Nọc độc của n&oacute; ph&aacute;t t&aacute;c cực mạnh đối với lo&agrave;i m&aacute;u n&oacute;ng. Theo ước t&iacute;nh, một vết cắn đủ để giết chết &iacute;t nhất 100 người đ&agrave;n &ocirc;ng trưởng. T&ugrave;y theo t&iacute;nh chất của vết cắn, n&oacute; c&oacute; thể giết một ai đ&oacute; trong &iacute;t nhất l&agrave; 30 đến 45 ph&uacute;t nếu kh&ocirc;ng được điều trị. Lo&agrave;i rắn n&agrave;y cực kỳ nhanh nhẹn, c&oacute; thể tấn c&ocirc;ng ngay lập tức với độ ch&iacute;nh x&aacute;c tuyệt đối. Từ &ldquo;khốc liệt&rdquo; từ t&ecirc;n thay thế để m&ocirc; tả nọc độc của n&oacute;.</p>\r\n		<p style=\"text-align: center;\">\r\n			<img height=\"487\" src=\"http://langquetoi.com/images/news/85/700_0.913729001399082778.jpg\" width=\"557\" /></p>\r\n		<p>\r\n			10. S&acirc;u bướm đ&ecirc;m khổng lồ Được t&igrave;m thấy ở ph&iacute;a Nam Mỹ, l&agrave; một trong những lo&agrave;i s&acirc;u bướm nổi tiếng nhất. S&acirc;u bướm d&ugrave;ng những chiếc t&oacute;c mọc đan ch&eacute;o của m&igrave;nh đ&acirc;m v&agrave;o nạn nh&acirc;n nếu kh&ocirc;ng may chạm v&agrave;o. Sau đ&oacute;, ch&uacute;ng sản xuất 1 lượng độc tố chết người. Khi đ&atilde; bị ch&iacute;ch, lượng độc nhanh ch&oacute;ng x&acirc;m nhập v&agrave;o cơ thể, đẫn đến số người tử vong do hậu quả của vết ch&iacute;ch. Ngay khi những vết vi&ecirc;m bắt đầu xuất hiện, theo sau bởi những triệu chứng như đau đầu, sốt, n&ocirc;n mửa, suy nhược. Nếu chất độc ngấm v&agrave;o m&aacute;u, g&acirc;y ra hiện tượng chảy m&aacute;u trong v&agrave; tử vong.</p>\r\n	</div>\r\n</div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969308', '0', '1399082777', '0', '0');
INSERT INTO `slm_news` VALUES ('86', '0.508866001399083999.jpg', 'a:2:{i:0;s:24:\"0.508866001399083999.jpg\";i:1;s:24:\"0.610638001399083999.jpg\";}', null, 'Rắn 2 đầu xuất hiện', null, null, '12', '', '\r\n	Mặc d&ugrave; chỉ xuất hiện trong v&agrave;i gi&acirc;y nhưng người d&acirc;n vẫn c&oacute; thể nhận ra được con rắn hai đầu n&agrave;y thuộc lo&agrave;i rắn nước', '<div>\r\n	<p>\r\n		Từ l&acirc;u, rắn hai đầu đ&atilde; kh&ocirc;ng c&ograve;n l&agrave; một sinh vật huyền thoại chỉ tồn tại trong truyền thuyết m&agrave; đ&atilde; được khoa học thế giới ghi nhận một v&agrave;i trường hợp. Mới đ&acirc;y, người ta lại ph&aacute;t hiện ra một con rắn c&oacute; hai đầu đang trườn b&ograve; tr&ecirc;n đường tại một ng&ocirc;i l&agrave;ng thuộc th&agrave;nh phố Burdwan, bang Bengal, Ấn Độ.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/86/700_0.508866001399083999.jpg\" /></p>\r\n	<div style=\"text-align: center;\">\r\n		<img height=\"400\" src=\"http://langquetoi.com/images/news/86/700_0.610638001399083999.jpg\" width=\"608\" /></div>\r\n	<p style=\"text-align: center;\">\r\n		Những h&igrave;nh ảnh hiếm hoi m&agrave; anh Apurba chụp lại được.</p>\r\n	<p>\r\n		Anh Apurba Ghosh l&agrave; người đ&atilde; nhanh tay d&ugrave;ng điện thoại chụp lại bức ảnh con rắn hai đầu n&agrave;y. Dựa tr&ecirc;n h&igrave;nh d&aacute;ng b&ecirc;n ngo&agrave;i, người ta cho rằng, đ&acirc;y l&agrave; lo&agrave;i rắn nước kh&ocirc;ng c&oacute; độc.</p>\r\n	<p>\r\n		Được biết, ban đầu, con rắn hai đầu xuất hiện trước cửa một ng&ocirc;i nh&agrave; trong l&agrave;ng. Sau đ&oacute; chỉ chớp nho&aacute;ng, n&oacute; đ&atilde; nhanh ch&oacute;ng lẩn mất v&agrave;o trong bụi rặm trước khi bị người d&acirc;n bắt được.</p>\r\n	<p>\r\n		Cũng theo Apurba, ng&ocirc;i l&agrave;ng nơi anh sinh sống l&agrave; nơi thường xuy&ecirc;n xuất hiện rắn v&igrave; xung quanh l&agrave;ng c&oacute; nhiều rừng rậm. Tuy nhi&ecirc;n, rắn ở đ&acirc;y kh&ocirc;ng tấn c&ocirc;ng con người. D&acirc;n l&agrave;ng thậm ch&iacute; c&ograve;n lu&ocirc;n tr&acirc;n trọng ch&uacute;ng v&igrave; theo quan điểm của đạo Hin-đu, lo&agrave;i rắn tượng trưng cho điềm l&agrave;nh.</p>\r\n	<p>\r\n		Tr&ecirc;n thực tế, rắn hai đầu chỉ l&agrave; một hiện tượng đột biến nhiều đầu ở động vật c&oacute; tỷ lệ xảy ra rất hiếm. Th&ocirc;ng thường, những sinh vật đột biến n&agrave;y chỉ sống được v&agrave;i th&aacute;ng l&agrave; tối đa.</p>\r\n</div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969281', '0', '1399083999', '0', '0');
INSERT INTO `slm_news` VALUES ('87', '0.502899001399084143.jpg', 'a:2:{i:0;s:24:\"0.502899001399084143.jpg\";i:1;s:24:\"0.679839001399084143.jpg\";}', null, 'Hoang mang bướm ‘khủng’ hình đầu rắn về đậu ở tường nhà', null, null, '12', '', '\r\n	Ở Nghệ An ph&aacute;t hiện lo&agrave;i bớm lạ, khổng lồ, th&acirc;n c&oacute; nhiều hoa văn mềm mại, đẹp như tranh vẽ. Hai ch&oacute;p c&aacute;nh c&oacute; h&igrave;nh giống với đầu rắn.', '<p style=\"text-align: justify;\">\r\n	S&aacute;ng ng&agrave;y 9/4 anh Tạ Văn Hiền ở x&atilde; Thọ Th&agrave;nh, huyện Y&ecirc;n Th&agrave;nh, Nghệ An trong l&uacute;c chặt tre l&agrave;m nh&agrave; đ&atilde; ph&aacute;t hiện một con bướm khổng lồ.</p>\r\n<p style=\"text-align: center;\">\r\n	<em><img height=\"419\" src=\"http://langquetoi.com/images/news/87/700_0.502899001399084143.jpg\" width=\"635\" /></em></p>\r\n<p style=\"text-align: center;\">\r\n	<em>Bướm lạ h&igrave;nh đầu rắn g&acirc;y x&ocirc;n xao dư luận ở Nghệ An.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Theo m&ocirc; tả, th&acirc;n bướm chỉ bằng hai ng&oacute;n tay nhưng c&oacute; sải cảnh d&agrave;i khoảng 30-35cm, th&acirc;n c&oacute; nhiều hoa văn mềm mại, đẹp như tranh vẽ. Tr&ecirc;n m&igrave;nh bướm c&oacute; m&agrave;u n&acirc;u c&aacute;nh gi&aacute;n v&agrave; nhiều vằn vện kh&aacute;c. Hai ch&oacute;p c&aacute;nh c&oacute; h&igrave;nh giống với đầu rắn.</p>\r\n<p style=\"text-align: justify;\">\r\n	Biết tin, d&acirc;n l&agrave;ng liền đổ x&ocirc; đến xem để tận mắt chứng kiến, chi&ecirc;m ngưỡng vẻ đẹp của lo&agrave;i bướm n&agrave;y.</p>\r\n<p style=\"text-align: justify;\">\r\n	Trước đ&oacute;, s&aacute;ng 3/4, &ocirc;ng Vũ Văn Chu&nbsp;ở x&oacute;m Phố Chợ, x&atilde; T&acirc;n Sơn, huyện Lục Ngạn, tỉnh Bắc Giang ph&aacute;t hiện c&oacute; một con bướm lớn c&oacute; m&agrave;u sắc sặc sỡ, sải c&aacute;nh rộng gần 30 cm đậu tr&ecirc;n tường&nbsp;nh&agrave;. Điều kỳ lạ l&agrave; tr&ecirc;n hai c&aacute;nh con bướm lại c&oacute; h&igrave;nh giống h&igrave;nh đầu rắn.</p>\r\n<p style=\"text-align: center;\">\r\n	<em><img height=\"708\" src=\"http://langquetoi.com/images/news/87/700_0.679839001399084143.jpg\" width=\"605\" /></em></p>\r\n<p style=\"text-align: center;\">\r\n	<em>Bướm Khổng lồ m&agrave; &ocirc;ng Chu đ&atilde; ph&aacute;t hiện được.</em></p>\r\n<p style=\"text-align: justify;\">\r\n	&Ocirc;ng Chu cho biết: &ldquo;Từ nhỏ tới giờ, t&ocirc;i chưa từng thấy con bướm n&agrave;o c&oacute; k&iacute;ch thước lớn như vậy&rdquo;. Nhiều người d&acirc;n c&ograve;n hoang mang cho rằng con bướm n&agrave;y c&oacute; lẽ mang một yếu tố&nbsp;t&acirc;m linh&nbsp;n&agrave;o đ&oacute;.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tuy nhi&ecirc;n, theo căn cứ khoa học, đ&acirc;y l&agrave; bướm Khế &ndash; lo&agrave;i bướm đ&ecirc;m c&oacute; k&iacute;ch thước lớn nhất Việt Nam v&agrave; tr&ecirc;n&nbsp;thế giới. T&ecirc;n khoa học của&nbsp;bướm Khế&nbsp;l&agrave; Attacus atlas Linnaeus 1758, Saturnia atlas Linnaeus, họ bướm ma Saturniidae thuộc bộ c&aacute;nh vẩy Lepidoptera. Lo&agrave;i bướm n&agrave;y hiện được đưa v&agrave;o s&aacute;ch đỏ Việt Nam, danh s&aacute;ch động vật bị đe dọa bậc nguy cấp cần được bảo vệ.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399084197', '0', '1399084143', '0', '0');
INSERT INTO `slm_news` VALUES ('88', '0.870267001399260037.jpg', 'a:4:{i:0;s:24:\"0.870267001399260037.jpg\";i:1;s:24:\"0.109618001399260038.jpg\";i:2;s:24:\"0.213305001399260038.jpg\";i:3;s:24:\"0.444429001399260038.jpg\";}', null, 'Kỳ lạ loài bướm có ngoại hình giống rắn', null, null, '12', '', '\r\n	Ấu tr&ugrave;ng của lo&agrave;i bướm đ&ecirc;m Hemeroplanes triptolemus được biết đến với ngoại h&igrave;nh giống như một con rắn.', '<div style=\"text-align: justify;\">\r\n	<p>\r\n		Với ngoại h&igrave;nh giống như lo&agrave;i rắn, ấu tr&ugrave;ng bướm Hemeroplanes triptolemus thường khiến nhiều người nhầm lẫn đ&acirc;y l&agrave; một lo&agrave;i b&ograve; s&aacute;t mới m&agrave; c&aacute;c nh&agrave; khoa học chưa kh&aacute;m ph&aacute;.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/88/700_0.109618001399260038.jpg\" /></p>\r\n	<div style=\"text-align: center;\">\r\n		<em>&nbsp;Ấu tr&ugrave;ng bướm Hemeroplanes triptolemus c&oacute; ngoại h&igrave;nh giống lo&agrave;i rắn.</em></div>\r\n	<div style=\"text-align: center;\">\r\n		&nbsp;</div>\r\n	<div style=\"text-align: center;\">\r\n		<img height=\"636\" src=\"http://langquetoi.com/images/news/88/700_0.870267001399260037.jpg\" width=\"516\" /></div>\r\n	<p style=\"text-align: center;\">\r\n		<img height=\"597\" src=\"http://langquetoi.com/images/news/88/700_0.213305001399260038.jpg\" width=\"522\" /></p>\r\n	<div style=\"text-align: center;\">\r\n		<em>Phần đầu h&igrave;nh th&agrave;nh đ&ocirc;i mắt ho&agrave;n chỉnh.</em></div>\r\n	<div>\r\n		Tuy nhi&ecirc;n, tr&ecirc;n thực tế Hemeroplanes triptolemus l&agrave; một lo&agrave;i bướm đ&ecirc;m thuộc họ Sphingidae. Lo&agrave;i n&agrave;y sống phổ biến ở Costa Rica, Belize, Mexico, Guatemala&hellip;</div>\r\n	<p>\r\n		Khi ở giai đoạn ấu tr&ugrave;ng, bướm Hemeroplanes triptolemus thường ph&aacute;t triển phần th&acirc;n trước giống ngoại h&igrave;nh của lo&agrave;i rắn h&igrave;nh th&agrave;nh 2 mắt đầy đủ.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/88/700_0.444429001399260038.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		<em>Bướm Hemeroplanes triptolemus trưởng th&agrave;nh</em>.</p>\r\n	<p>\r\n		Được biết, c&aacute;ch h&oacute;a th&acirc;n đặc biệt n&agrave;y của ấu tr&ugrave;ng bướm Hemeroplanes triptolemus l&agrave; một h&igrave;nh thức tự bảo vệ m&igrave;nh khỏi những lo&agrave;i th&uacute; săn mồi c&oacute; nguy cơ tấn c&ocirc;ng n&oacute;.</p>\r\n</div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969402', '0', '1399260037', '0', '0');
INSERT INTO `slm_news` VALUES ('75', '0.559012001397546027.jpg', 'a:6:{i:0;s:24:\"0.559012001397546027.jpg\";i:1;s:24:\"0.699964001397546027.jpg\";i:2;s:24:\"0.795082001397546027.jpg\";i:3;s:24:\"0.832530001397546027.jpg\";i:4;s:24:\"0.940496001397546027.jpg\";i:5;s:24:\"0.035783001397546028.jpg\";}', null, 'Bữa tẩm bổ cuối tuần tiền tỷ của đại gia', null, null, '12', '', '\r\n	Ăn m&ocirc;i c&aacute; Anh Vũ, phở b&ograve; Kobe, c&aacute;c m&oacute;n trộn v&agrave;ng &hellip; l&agrave; những m&oacute;n ăn độc v&agrave; đắ tại Việt Nam, đ&atilde; được c&aacute;c đại gia kh&ocirc;ng tiếc tay chi tiền.', '<div style=\"text-align: justify;\">\r\n	<p>\r\n		<strong>Tổ Yến &ndash; &ldquo;trứng c&aacute; muối của phương Đ&ocirc;ng&rdquo;</strong></p>\r\n	<p>\r\n		Giới trẻ tuổi v&agrave; trung lưu Việt Nam đang rất ưa chuộng m&oacute;n ăn đắt đỏ nhất nh&igrave; thế giới n&agrave;y v&igrave; họ cho rằng đ&acirc;y l&agrave; thực phẩm qu&yacute; c&oacute; lơi cho sức khỏe v&agrave; c&oacute; khả năng chống lại nhiều bệnh tật.</p>\r\n	<p>\r\n		Tổ yến trước đ&acirc;y chỉ d&agrave;nh cho giới ho&agrave;ng tộc. Tuy nhi&ecirc;n, nhu cầu hiện nay đ&atilde; thổi b&ugrave;ng ng&agrave;nh c&ocirc;ng nghiệp tổ yến to&agrave;n cầu với doanh thu 5 tỉ USD mỗi năm, chủ yếu phục vụ người d&ugrave;ng lắm tiền ch&acirc;u &Aacute;, trong đ&oacute; c&oacute; Việt Nam.&rdquo; &ndash; &Ocirc;ng Tok Teng Sai &ndash; Chủ tịch Hiệp hội Kinh doanh tổ yến Malaysia cho biết.</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.699964001397546027.jpg\" /></div>\r\n		<div style=\"text-align: center;\">\r\n			Tổ yến c&oacute; gi&aacute; 2.500 USD/kg b&aacute;n lẻ.&rdquo;</div>\r\n	</div>\r\n	<p>\r\n		Anh L&ecirc; Danh Ho&agrave;ng &ndash; người s&aacute;ng lập c&ocirc;ng ty chuy&ecirc;n kinh doanh sản phẩm từ tổ yến &ndash; NutriNest cho biết: &ldquo;Được coi l&agrave; &ldquo;trứng c&aacute; muối của phương Đ&ocirc;ng&rdquo;, tổ yến c&oacute; gi&aacute; gần 1.000 USD &ndash; 1.500 USD/kg b&aacute;n bu&ocirc;n v&agrave; 2.500 USD/kg b&aacute;n lẻ.&rdquo;</p>\r\n	<p>\r\n		Giữa năm 2011, VinaCapital đ&atilde; đầu tư 7,5 triệu USD v&agrave;o nh&agrave; yến ở miền Trung Việt Nam với khoảng 100.000 con, theo b&agrave; Đặng Phạm Minh Loan &ndash; Ph&oacute; gi&aacute;m đốc VinaCapital.</p>\r\n	<p>\r\n		B&agrave; cho biết: &ldquo;Trung Quốc v&agrave; Việt Nam l&agrave; những nước ti&ecirc;u thụ tổ yến mạnh nhất. Họ tin rằng tổ yến c&oacute; rất nhiều t&aacute;c dụng với sức khỏe, đặc biệt l&agrave; chống l&atilde;o h&oacute;a v&agrave; n&acirc;ng cao hệ miễn dịch&rdquo;. Ng&agrave;nh c&ocirc;ng nghiệp n&agrave;y c&oacute; doanh thu ước t&iacute;nh 200 triệu USD mỗi năm tại Việt Nam, với tốc độ tăng trưởng h&agrave;ng năm đạt 25%.</p>\r\n	<p>\r\n		<strong>Ăn m&ocirc;i&nbsp;c&aacute; Anh Vũ&nbsp;sẽ gặp may mắn&hellip;.</strong></p>\r\n	<p>\r\n		Đ&atilde; c&oacute; kh&ocirc;ng &iacute;t c&aacute;c đại gia, thương gia sẵn s&agrave;ng chi v&agrave;i ba chục triệu để mua c&aacute; Anh Vũ v&igrave; họ tin rằng ăn c&aacute; Anh Vũ sẽ gặp may mắn, thuận lợi.</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.795082001397546027.jpg\" /></div>\r\n		<p style=\"text-align: center;\">\r\n			Thịt c&aacute; Anh Vũ thơm ngon v&agrave; cực kỳ gi&agrave;u đạm.</p>\r\n	</div>\r\n	<p>\r\n		Nhiều &ldquo;đại gia&rdquo; từ TP.HCM, H&agrave; Nội hay c&aacute;c tỉnh th&agrave;nh kh&aacute;c đ&atilde; t&igrave;m về v&ugrave;ng Bạch Hạc, TP Việt Tr&igrave; (Ph&uacute; Thọ) &ndash; v&ugrave;ng sinh sống chủ yếu của c&aacute; Anh Vũ để t&igrave;m cho được một con c&aacute; Anh Vũ để thưởng thức.</p>\r\n	<div>\r\n		<p>\r\n			C&oacute; chủ qu&aacute;n đ&atilde; h&eacute;t gi&aacute; 20 triệu đồng/con c&aacute; nặng gần 3kg nhưng một đại gia vẫn đồng &yacute; mua ngay v&igrave; muốn thưởng thức cho ng&agrave;y đẹp trời.</p>\r\n		<p>\r\n			Điểm &ldquo;độc&rdquo; của lo&agrave;i c&aacute; n&agrave;y được ghi điểm trong mắt đại gia l&agrave; khối sụn m&ocirc;i mỏng ở phần m&otilde;m của lo&agrave;i c&aacute; n&agrave;y. Sụn gi&ograve;n, thơm v&agrave; rất ngon. Ch&uacute;ng c&oacute; khả năng chữa bệnh rất tốt, nhưng lo&agrave;i c&aacute; n&agrave;y chỉ sống ở nơi nước trong, trong những hang đ&aacute; s&acirc;u v&agrave; khi nước lạnh mới m&ograve; ra t&igrave;m mồi, v&igrave; vậy, việc bắt được một con Anh Vũ l&agrave; cả một kỳ c&ocirc;ng.</p>\r\n		<p>\r\n			Tại nh&agrave; h&agrave;ng lớn tr&ecirc;n địa b&agrave;n H&agrave; nội, cụ thể l&agrave; khu vực Kim M&atilde;, Ba Đ&igrave;nh cho biết, mỗi th&aacute;ng c&ocirc;ng ty xuất ra thị trường khoảng 400 đến 500 con, chủ yếu l&agrave; cung cấp cho c&aacute;c nh&agrave; h&agrave;ng sang trọng, b&aacute;n v&agrave;o S&agrave;i G&ograve;n, v&agrave; xuất khẩu ra nước ngo&agrave;i. C&aacute; n&agrave;y hầu như to&agrave;n c&aacute;c quan sếp ăn để t&igrave;m đến sự may mắn n&ecirc;n lượng ti&ecirc;u thụ rất ổn định.</p>\r\n	</div>\r\n	<p>\r\n		Phần th&acirc;n c&aacute; c&ograve;n lại để hấp sẽ giữ được nguy&ecirc;n c&aacute;c chất bổ v&agrave; thơm ngon hơn bất cứ kiểu c&aacute;ch chế biến kh&aacute;c, bởi vậy, cũng l&agrave; m&oacute;n được ưa th&iacute;ch hơn cả. Thịt c&aacute; Anh Vũ thơm ngon v&agrave; cực kỳ gi&agrave;u đạm.</p>\r\n	<p>\r\n		<strong>Một con cua bằng một tấn th&oacute;c</strong></p>\r\n	<p>\r\n		Một con cua bằng một tấn th&oacute;c Nếu như ăn c&aacute; anh vũ để lấy may, th&igrave; tr&agrave;o lưu ăn cua ho&agrave;ng đế mới l&agrave; đẳng cấp. V&agrave; đẳng cấp ấy c&oacute; gi&aacute; bằng cả một tấn th&oacute;c của người n&ocirc;ng d&acirc;n.</p>\r\n	<p>\r\n		Loại cua khổng lồ nặng gần 2kg, với gi&aacute; khoảng 5 triệu đồng một con, mức gi&aacute; n&agrave;y đắt gấp 10 lần so với cua bể Việt Nam.</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.832530001397546027.jpg\" /></div>\r\n		<p style=\"text-align: center;\">\r\n			Loại cua khổng lồ nặng gần 2kg, với gi&aacute; khoảng 5 triệu đồng một con</p>\r\n	</div>\r\n	<p>\r\n		T&ecirc;n của loại cua n&agrave;y l&agrave; king crab hay c&ograve;n gọi l&agrave; cua ho&agrave;ng đế. Đ&acirc;y l&agrave; lo&agrave;i chỉ sống ở những v&ugrave;ng biển nước rất lạnh v&agrave; s&acirc;u từ 200-400 m như Alaska (Mỹ), Nga, Nhật Bản, H&agrave;n Quốc&hellip; Cua ho&agrave;ng đế hiếm c&oacute; kh&oacute; t&igrave;m bởi để đ&aacute;nh bắt n&oacute;, những người thợ săn phải ra khơi v&agrave;o những ng&agrave;y b&atilde;o biển. Điều đặc biệt l&agrave; loại cua n&agrave;y c&oacute; k&iacute;ch thước v&agrave; trọng lượng lớn hơn rất nhiều so với cua biển th&ocirc;ng thường.</p>\r\n	<p>\r\n		<strong>B&agrave;o thai rắn</strong></p>\r\n	<p>\r\n		Kh&ocirc;ng g&igrave; qu&yacute; hơn sức khỏe, sức khỏe l&agrave; v&agrave;ng, vậy n&ecirc;n chuyện ăn b&agrave;o thai rắn cũng chỉ l&agrave;&hellip; chuyện nhỏ. Nhất l&agrave; b&agrave;o thai rắn lục đang đến kỳ &ldquo;khai hoa nở nhụy&rdquo; l&agrave; &ldquo;đẳng cấp&rdquo; số một.</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.559012001397546027.jpg\" /></div>\r\n		<p style=\"text-align: center;\">\r\n			T&uacute;i mật xanh l&eacute;t được đ&aacute;nh gi&aacute; l&agrave; tinh t&uacute;y nhất của con rắn.</p>\r\n	</div>\r\n	<p>\r\n		Chỉ một nh&aacute;t dao, m&aacute;u của con rắn được &ldquo;chắt&rdquo; v&agrave;o từng ly rượu c&ograve;n bộ l&ograve;ng tung ra, tr&aacute;i tim con rắn thoi th&oacute;p đập tr&ecirc;n đĩa, t&uacute;i mật xanh l&eacute;t được đ&aacute;nh gi&aacute; l&agrave; tinh t&uacute;y nhất của con rắn được lấy ra cho v&agrave;o một cốc rượu to.</p>\r\n	<p>\r\n		<strong>50 &ndash; 150 triệu, một bữa r&ugrave;a v&agrave;ng</strong></p>\r\n	<p>\r\n		Trong cơn b&atilde;o gi&aacute;, nhiều người phải thắt chặt chi ti&ecirc;u nhưng kh&ocirc;ng &iacute;t đại gia sẵn tay chi tiền cho một bữa ăn l&ecirc;n tới 150 triệu đồng chỉ c&oacute; 3 con r&ugrave;a v&agrave;ng m&agrave; theo người ta đồn nhau rằng &ldquo;r&ugrave;a v&agrave;ng cực qu&yacute; hiếm, n&oacute; sống tr&ecirc;n đại ng&agrave;n, hấp thụ linh kh&iacute; của trời đất n&ecirc;n m&aacute;u thịt tinh khiết, đại bổ, ăn v&agrave;o tăng cường sinh lực, đặc biệt ai c&oacute; bệnh tim nếu uống rượu pha mật r&ugrave;a, huyết r&ugrave;a sẽ hết ngay&rdquo;.</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.940496001397546027.jpg\" /></div>\r\n		<p style=\"text-align: center;\">\r\n			Mức gi&aacute; th&igrave; kh&ocirc;ng hề rẻ ch&uacute;t n&agrave;o, 50 triệu đồng một con.</p>\r\n	</div>\r\n	<p>\r\n		Mức gi&aacute; th&igrave; kh&ocirc;ng hề rẻ ch&uacute;t n&agrave;o, 50 triệu đồng một con. Để bồi bổ thậm ch&iacute; c&oacute; người c&ograve;n ăn m&oacute;n n&agrave;y thường xuy&ecirc;n. &ldquo;Thường th&ocirc;i, mỗi th&aacute;ng c&oacute; khi anh ch&eacute;n v&agrave;i ba con r&ugrave;a v&agrave;ng&rdquo; &ndash; một đại gia cho biết.</p>\r\n	<p>\r\n		<strong>Đại gia Việt &ldquo;sốt&rdquo; với m&oacute;n trộn v&agrave;ng</strong></p>\r\n	<p>\r\n		Đ&oacute; l&agrave; những loại rượu trộn v&agrave;ng để phục vụ c&aacute;c đại gia. Phổ biến nhất l&agrave; rượu trắng c&oacute; trộn v&agrave;ng c&aacute;m hiệu ORO c&oacute; dung t&iacute;ch 0,75 ml được mang từ Ấn Độ về, c&oacute; gi&aacute; hơn 5 triệu đồng/chai; hay rượu với độ cồn 43,5%, thể t&iacute;ch 750ml, xuất xứ Italia với những miếng v&agrave;ng l&aacute; nhỏ li ti nổi long lanh trong chai, gi&aacute; khoảng 7 triệu đồng/chai. Thậm ch&iacute;, trong m&ugrave;a trung thu vừa qua, tr&ecirc;n thị trường c&ograve;n xuất hiện những hộp b&aacute;nh c&oacute; nh&acirc;n v&agrave;ng c&aacute;m, b&aacute;n khoảng 3 triệu đồng/chiếc&hellip;.</p>\r\n	<p>\r\n		<strong>Phở b&ograve; Kobe</strong></p>\r\n	<p>\r\n		B&aacute;t phở thịt b&ograve; Kobe tại Kh&aacute;ch sạn vườn Thủ đ&ocirc; (H&agrave; Nội) được cho l&agrave; c&oacute; gi&aacute; đắt nhất Việt Nam, với hai mức gi&aacute; hiện c&oacute; l&agrave; 650.000 đồng v&agrave; 850.000 đồng mỗi b&aacute;t. D&ugrave; &ldquo;b&atilde;o gi&aacute;&rdquo; đang ho&agrave;nh h&agrave;nh c&ugrave;ng lo ngại về nhiễm ph&oacute;ng xạ Nhật Bản nhưng phở b&ograve; Kobe vẫn h&uacute;t hồn nhiều thực kh&aacute;ch H&agrave; Nội. Ngo&agrave;i ra c&ograve;n c&oacute; những m&oacute;n khai vị như: s&uacute;p tổ yến gạch cua 48 USD, tổ yến tiềm hạnh nh&acirc;n 33 USD, s&uacute;p v&acirc;y c&aacute; tổ yến thượng hạng hồng x&iacute;u 72 USD, s&uacute;p b&agrave;o ngư Nam Phi sốt dầu h&agrave;o 96 USD&hellip;</p>\r\n	<div>\r\n		<div style=\"text-align: center;\">\r\n			<img src=\"http://langquetoi.com/images/news/75/700_0.035783001397546028.jpg\" /></div>\r\n		<p style=\"text-align: center;\">\r\n			Hai mức gi&aacute; hiện c&oacute; l&agrave; 650.000 đồng v&agrave; 850.000 đồng mỗi b&aacute;t.</p>\r\n	</div>\r\n</div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969335', '0', '1397546027', '0', '0');
INSERT INTO `slm_news` VALUES ('76', '0.510114001397807131.jpeg', 'a:10:{i:0;s:25:\"0.510114001397807131.jpeg\";i:1;s:25:\"0.766965001397807131.jpeg\";i:2;s:25:\"0.990306001397807131.jpeg\";i:3;s:25:\"0.182135001397807132.jpeg\";i:4;s:25:\"0.347190001397807132.jpeg\";i:5;s:25:\"0.516484001397807132.jpeg\";i:6;s:25:\"0.381940001397807135.jpeg\";i:7;s:25:\"0.663870001397807135.jpeg\";i:8;s:25:\"0.975413001397807135.jpeg\";i:9;s:25:\"0.273086001397807136.jpeg\";}', null, 'Đột nhập địa điểm xử lí rắn lớn nhất Indonesia', null, null, '12', '', '\r\n	Những h&igrave;nh ảnh sau sẽ khiến người xem phải r&ugrave;ng m&igrave;nh khi chứng kiến họ h&agrave;ng nh&agrave; rắn bị lột da, xẻ thịt dưới b&agrave;n tay của c&aacute;c người thợ.', '<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.766965001397807131.jpeg\" /><br />\r\n	Đủ c&aacute;c loại rắn được thu gom lại sau đ&oacute; chuyển đến một cơ sở chuy&ecirc;n l&agrave;m rắn tại l&agrave;ng <strong>Kertasura, Cirebon, Indonesia</strong>. Tại đ&acirc;y ch&uacute;ng sẽ được ph&acirc;n loại th&agrave;nh những sản phẩm ri&ecirc;ng v&agrave; b&aacute;n cho c&aacute;c địa điểm c&oacute; nhu cầu. Chẳng hạn da rắn sẽ được b&aacute;n cho c&aacute;c nh&agrave; m&aacute;y sản xuất t&uacute;i ở c&aacute;c tỉnh miền T&acirc;y v&agrave; Trung <strong>Java</strong>.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\">l<img src=\"http://langquetoi.com/images/news/76/700_0.510114001397807131.jpeg\" /><br />\r\n	Khu giết mổ rộng h&agrave;ng trăm m&eacute;t n&ecirc;n c&oacute; thể tiếp nhận số lượng lớn rắn được chuyển đến.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.990306001397807131.jpeg\" /><br />\r\n	C&aacute;c c&ocirc;ng nh&acirc;n sẽ bắt tay v&agrave;o việc lột da sau khi đ&atilde; lấy hết phần nội tạng b&ecirc;n trong.&nbsp;</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.182135001397807132.jpeg\" /><br />\r\n	Đ&acirc;y l&agrave; phần thịt rắn được ph&acirc;n loại ri&ecirc;ng v&agrave; xử l&yacute; với c&aacute;c phương ph&aacute;p th&iacute;ch hợp.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.347190001397807132.jpeg\" /><br />\r\n	C&ocirc;ng việc lột da, xẻ thịt rắn n&agrave;y tưởng chừng chỉ c&oacute; đ&agrave;n &ocirc;ng mới d&aacute;m l&agrave;m, nhưng tại đ&acirc;y vẫn c&oacute; c&aacute;c c&ocirc;ng nh&acirc;n l&agrave; phụ nữ.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.516484001397807132.jpeg\" /><br />\r\n	Một c&ocirc;ng nh&acirc;n đang lột da rắn một c&aacute;ch th&agrave;nh thạo. Đ&acirc;y l&agrave; c&ocirc;ng việc quen thuộc của những người l&agrave;m c&ocirc;ng tại cơ sở nổi tiếng ở l&agrave;ng <strong>Kertasura</strong>.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.381940001397807135.jpeg\" /><br />\r\n	Da rắn sau khi rửa sạch v&agrave; qua xử l&yacute; sẽ được đem phơi kh&ocirc; ngo&agrave;i trời nắng.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.663870001397807135.jpeg\" /><br />\r\n	Tất cả sẽ được căng đều bằng c&aacute;ch quấn chặt v&agrave;o c&aacute;c thanh gỗ nhỏ v&agrave; để nơi kh&ocirc; r&aacute;o.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.975413001397807135.jpeg\" /><br />\r\n	Sau khi được xử l&yacute; cẩn thận, sản phẩm sẽ được b&aacute;n cho c&aacute;c nh&agrave; m&aacute;y sản xuất t&uacute;i. Những mặt h&agrave;ng đạt chất lượng tốt sẽ c&oacute; gi&aacute; rất cao.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/76/700_0.273086001397807136.jpeg\" /><br />\r\n	Để c&oacute; được những tấm da đẹp như thế n&agrave;y l&agrave; cả một qu&aacute; tr&igrave;nh xử l&yacute; c&ocirc;ng phu của c&aacute;c c&ocirc;ng nh&acirc;n. V&igrave; thế những sản phẩm chế tạo từ da rắn cũng c&oacute; gi&aacute; rất cao ngo&agrave;i thị trường.</span></p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399972162', '0', '1397807131', '0', '0');
INSERT INTO `slm_news` VALUES ('77', '0.631256001397807927.jpg', 'a:19:{i:0;s:24:\"0.631256001397807927.jpg\";i:1;s:24:\"0.740993001397807927.jpg\";i:2;s:24:\"0.885184001397807927.jpg\";i:3;s:24:\"0.015671001397807928.jpg\";i:4;s:24:\"0.294821001397807929.jpg\";i:5;s:24:\"0.737345001397807929.jpg\";i:6;s:24:\"0.179613001397807930.jpg\";i:7;s:24:\"0.569354001397807930.jpg\";i:8;s:24:\"0.966217001397807930.jpg\";i:9;s:24:\"0.396243001397807931.jpg\";i:10;s:24:\"0.676939001397807931.jpg\";i:11;s:24:\"0.101132001397807932.jpg\";i:12;s:24:\"0.527331001397807932.jpg\";i:13;s:24:\"0.981025001397807932.jpg\";i:14;s:24:\"0.209574001397807933.jpg\";i:15;s:24:\"0.664547001397807933.jpg\";i:16;s:24:\"0.135150001397807934.jpg\";i:17;s:24:\"0.657390001397807934.jpg\";i:18;s:24:\"0.131951001397807935.jpg\";}', null, 'Rùng mình xâm nhập hang động nhung nhúc rắn độc', null, null, '12', '', '\r\n	H&agrave;ng trăm ng&agrave;n con rắn độc chen ch&uacute;c nhau, l&uacute;c nh&uacute;c như những h&igrave;nh ảnh trong thần thoại về địa ngục g&acirc;y cảm gi&aacute;c r&ugrave;ng m&igrave;nh cho du kh&aacute;ch.', '<div style=\"width: 160px !important; margin: 0 auto;\">\r\n	&nbsp;</div>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.740993001397807927.jpg\" /><br />\r\n	Hang rắn Garter, lo&agrave;i rắn c&oacute; lượng nọc độc &iacute;t nhưng vẫn c&oacute; thể g&acirc;y nguy hiểm cho con người, ch&uacute;ng l&agrave; lo&agrave;i rắn đặc trưng của khu vực Bắc Mỹ</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.885184001397807927.jpg\" /><br />\r\n	Khu vực tr&uacute; ẩn của lo&agrave;i rắn n&agrave;y l&agrave; ở Narcisse, Manitoba, Canada</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.015671001397807928.jpg\" /><br />\r\n	Thamnophis sirtalis hay c&ograve;n gọi với t&ecirc;n l&agrave; Garter đỏ l&agrave; lo&agrave;i rắn c&oacute; dải v&agrave;ng tr&ecirc;n nền n&acirc;u hoặc xanh l&aacute; c&acirc;y</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.294821001397807929.jpg\" /><br />\r\n	Ch&uacute;ng c&oacute; chiều d&agrave;i từ khoảng 55 - 140cm v&agrave; trọng lượng kh&aacute; nhẹ, chỉ khoảng 150g</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.737345001397807929.jpg\" /><br />\r\n	M&ugrave;a đ&ocirc;ng ch&uacute;ng nằm ngủ trong c&aacute;c hang s&acirc;u v&agrave; gần như bị đ&ocirc;ng đ&aacute;</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.179613001397807930.jpg\" /><br />\r\n	Khi m&ugrave;a xu&acirc;n về, những con rắn đực sẽ b&ograve; l&ecirc;n mặt đất trước v&agrave; chờ đợi lũ rắn c&aacute;i</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.569354001397807930.jpg\" /><br />\r\n	Khi rắn c&aacute;i b&ograve; ra, ch&uacute;ng tiết ra c&aacute;c chất thu h&uacute;t bạn t&igrave;nh khiến lũ rắn đực lao đến giao phối</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.966217001397807930.jpg\" /><br />\r\n	Hoạt động n&agrave;y của lũ rắn tạo n&ecirc;n những &#39;quả cầu rắn&#39; l&uacute;c nh&uacute;c, bầy nhầy trong c&aacute;c hang động</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.396243001397807931.jpg\" /><br />\r\n	Điều th&uacute; vị l&agrave; c&oacute; những con rắn lưỡng t&iacute;nh cũng c&oacute; thể tiết ra m&ugrave;i để thu h&uacute;t lũ rắn đực đến sưởi ấm cho ch&uacute;ng</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.676939001397807931.jpg\" /><br />\r\n	Sau m&ugrave;a sinh sản, trứng rắn nhan nhản ở khắp mọi nơi</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.101132001397807932.jpg\" /><br />\r\n	Theo c&aacute;c nh&agrave; khoa học, mỗi hố rắn ở khu vực n&agrave;y c&oacute; khoảng 35.000 con trong khi số lượng ở c&aacute;c địa điểm xung quanh l&ecirc;n đến 250.000 con</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.527331001397807932.jpg\" /><br />\r\n	Cơ quan thẩm quyền đ&atilde; th&agrave;nh lập khu vực bảo tồn rắn hoang d&atilde; để giữ an to&agrave;n cho h&agrave;ng chục ng&agrave;n con rắn ở Narcisse</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.981025001397807932.jpg\" /><br />\r\n	Cận cảnh đầu v&agrave; lưỡi của một con rắn garter đỏ</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/77/700_0.131951001397807935.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/77/700_0.631256001397807927.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\">Cận cảnh một con rắn garter đỏ</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/77/700_0.209574001397807933.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/77/700_0.664547001397807933.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><strong><img alt=\"Nổi da gà xâm nhập hang động nhung nhúc rắn độc\" src=\"http://s1.img.yan.vn//YanNews/2167221/201401/20140115-0509-hoa_ran_canada-15.jpg\" /></strong><br />\r\n	Nhung nh&uacute;c rắn độc dưới c&aacute;c hang động</span></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"ctl00_MainContent_lbContent\"><img src=\"http://langquetoi.com/images/news/77/700_0.657390001397807934.jpg\" /><br />\r\n	Lũ rắn trườn l&ecirc;n nhau tạo cảm r&aacute;c rợn người cho du kh&aacute;ch</span></p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969387', '0', '1397807927', '0', '0');
INSERT INTO `slm_news` VALUES ('78', '0.733463001397809164.jpg', 'a:9:{i:0;s:24:\"0.733463001397809164.jpg\";i:1;s:24:\"0.905055001397809164.jpg\";i:2;s:24:\"0.994024001397809164.jpg\";i:3;s:24:\"0.141778001397809165.jpg\";i:4;s:24:\"0.280576001397809165.jpg\";i:5;s:24:\"0.368803001397809165.jpg\";i:6;s:24:\"0.510347001397809165.jpg\";i:7;s:24:\"0.655734001397809165.jpg\";i:8;s:24:\"0.739814001397809165.jpg\";}', null, '9 loài rắn độc nhất thế giới', null, null, '12', '', '\r\n	Thế giới c&oacute; rất nhiều lo&agrave;i rắn, trong đ&oacute; c&oacute; lo&agrave;i v&ocirc; hại nhưng c&oacute; những loại cực độc, c&oacute; thể khiến nạn nh&acirc;n chết ngay tức khắc khi bị tấn c&ocirc;ng: Rắn Taipan nội địa,Rắn đu&ocirc;i chu&ocirc;ng,Rắn độc Australia,Hổ mang b&agrave;nh,Rắn độc đen ch&acirc;u Phi,Rắn cạp nong xanh', '<p style=\"text-align: center;\">\r\n	<strong>Rắn đu&ocirc;i chu&ocirc;ng</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.733463001397809164.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&acirc;y l&agrave; lo&agrave;i rắn duy nhất của ch&acirc;u Mỹ nằm trong danh s&aacute;ch n&agrave;y. Rắn đu&ocirc;i chu&ocirc;ng rất dễ nhận biết bởi chiếc đu&ocirc;i đặc biệt của n&oacute;. Rắn đu&ocirc;i chu&ocirc;ng chưa trưởng th&agrave;nh thường độc hơn những con đ&atilde; trưởng th&agrave;nh. Nọc độc của lo&agrave;i rắn n&agrave;y c&oacute; thể nhanh ch&oacute;ng l&agrave;m t&ecirc; liệt hệ thần kinh của c&aacute;c con mồi như chuột, chim v&agrave; một số loại động vật nhỏ kh&aacute;c, khiến tim con mồi ngừng đập chỉ một v&agrave;i ph&uacute;t sau khi bị cắn. Ảnh: Listverse.</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn độc Australia (Death Adder)</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Đ&acirc;y l&agrave; lo&agrave;i rắn duy nhất của ch&acirc;u Mỹ nằm trong danh s&aacute;ch n&agrave;y. Rắn đu&ocirc;i chu&ocirc;ng rất dễ nhận biết bởi chiếc đu&ocirc;i đặc biệt của n&oacute;. Rắn đu&ocirc;i chu&ocirc;ng chưa trưởng th&agrave;nh thường độc hơn những con đ&atilde; trưởng th&agrave;nh. Nọc độc của lo&agrave;i rắn n&agrave;y c&oacute; thể nhanh ch&oacute;ng l&agrave;m t&ecirc; liệt hệ thần kinh của c&aacute;c con mồi như chuột, chim v&agrave; một số loại động vật nhỏ kh&aacute;c, khiến tim con mồi ngừng đập chỉ một v&agrave;i ph&uacute;t sau khi bị cắn. Ảnh: Listverse.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	&nbsp;</div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Đ&acirc;y l&agrave; lo&agrave;i rắn duy nhất của ch&acirc;u Mỹ nằm trong danh s&aacute;ch n&agrave;y. Rắn đu&ocirc;i chu&ocirc;ng rất dễ nhận biết bởi chiếc đu&ocirc;i đặc biệt của n&oacute;. Rắn đu&ocirc;i chu&ocirc;ng chưa trưởng th&agrave;nh thường độc hơn những con đ&atilde; trưởng th&agrave;nh. Nọc độc của lo&agrave;i rắn n&agrave;y c&oacute; thể nhanh ch&oacute;ng l&agrave;m t&ecirc; liệt hệ thần kinh của c&aacute;c con mồi như chuột, chim v&agrave; một số loại động vật nhỏ kh&aacute;c, khiến tim con mồi ngừng đập chỉ một v&agrave;i ph&uacute;t sau khi bị cắn. Ảnh: Listverse.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	&nbsp;</div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.905055001397809164.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Lo&agrave;i rắn n&agrave;y ph&acirc;n bổ chủ yếu ở Australia v&agrave; New Guinea. Ch&uacute;ng thường săn v&agrave; giết chết những lo&agrave;i rắn kh&aacute;c th&ocirc;ng qua phục k&iacute;ch. Rắn độc Australia l&agrave; một trong những lo&agrave;i rắn nguy hiểm nhất thế giới với nọc độc g&acirc;y hại rất lớn cho hệ thần kinh, khiến con mồi v&agrave; kẻ th&ugrave; c&oacute; thể tử vong trong 6 tiếng đồng hồ. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn hổ lục (Viper)</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Lo&agrave;i rắn n&agrave;y ph&acirc;n bổ chủ yếu ở Australia v&agrave; New Guinea. Ch&uacute;ng thường săn v&agrave; giết chết những lo&agrave;i rắn kh&aacute;c th&ocirc;ng qua phục k&iacute;ch. Rắn độc Australia l&agrave; một trong những lo&agrave;i rắn nguy hiểm nhất thế giới với nọc độc g&acirc;y hại rất lớn cho hệ thần kinh, khiến con mồi v&agrave; kẻ th&ugrave; c&oacute; thể tử vong trong 6 tiếng đồng hồ. Ảnh: Listverse<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.994024001397809164.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Hổ mang b&agrave;nh Philippines&nbsp;</strong></p>\r\n<p style=\"text-align: center;\">\r\n	Lo&agrave;i rắn n&agrave;y xuất hiện ở hầu hết mọi khu vực tr&ecirc;n thế giới. Tuy nhi&ecirc;n, hai lo&agrave;i độc nhất l&agrave; rắn lục hoa c&acirc;n (Saw Scaled Viper) v&agrave; Chain Viper chủ yếu ph&acirc;n bổ ở khu vực Trung Đ&ocirc;ng v&agrave; Trung &Aacute;, đặc biệt l&agrave; Ấn Độ, Trung Quốc v&agrave; Đ&ocirc;ng Nam &Aacute;. Lo&agrave;i rắn n&agrave;y rất n&oacute;ng t&iacute;nh v&agrave; thường kiếm ăn v&agrave;o ban đ&ecirc;m, đặc biệt l&agrave; sau khi trời mưa. Ảnh: Listverse</p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.141778001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hầu hết c&aacute;c loại rắn hổ mang kh&ocirc;ng nằm trong danh s&aacute;ch n&agrave;y. Tuy nhi&ecirc;n, hổ mang b&agrave;nh Philippines l&agrave; một ngoại lệ. Nọc độc của lo&agrave;i rắn n&agrave;y nguy hiểm nhất trong c&aacute;c lo&agrave;i hổ mang, g&acirc;y hại cho thần kinh, l&agrave;m rối loạn hoạt động của tim v&agrave; hệ h&ocirc; hấp, khiến con mồi tử vong trong v&ograve;ng 30 ph&uacute;t. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn hổ ch&acirc;u &Uacute;c</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.280576001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Rắn hổ ch&acirc;u &Uacute;c ph&acirc;n bổ chủ yếu ở Australia. Nọc độc của lo&agrave;i rắn n&agrave;y ảnh hưởng rất lớn tới thần kinh, c&oacute; thể khiến con mồi v&agrave; kẻ th&ugrave; tử vong trong v&ograve;ng 30 ph&uacute;t. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn độc đen ch&acirc;u Phi</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.368803001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Cơ thể c&oacute; m&agrave;u x&aacute;m n&acirc;u, nhưng b&ecirc;n trong miệng lại c&oacute; m&agrave;u đen, lo&agrave;i rắn n&agrave;y xuất hiện ở nhiều nơi tr&ecirc;n lục địa đen. Rắn độc đen ch&acirc;u Phi thường hung hăng v&agrave; c&oacute; đ&ograve;n tấn c&ocirc;ng quyết định. Đ&acirc;y l&agrave; lo&agrave;i rắn di chuyển nhanh nhất tr&ecirc;n đất liền, c&oacute; thể đạt tốc độ 20 km/h. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn cạp nong xanh</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.510347001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Rắn cạp nong xanh hay c&ograve;n gọi l&agrave; rắn Malayan, xuất hiện ở Đ&ocirc;ng Nam &Aacute; v&agrave; Indonesia. Lo&agrave;i rắn n&agrave;y cũng săn v&agrave; giết c&aacute;c lo&agrave;i rắn kh&aacute;c. Th&ocirc;ng thường, rắn cạp nong xanh c&oacute; xu hướng lẩn trốn hơn l&agrave; tấn c&ocirc;ng. Nọc độ của rắn cạp nong xanh mạnh hơn hổ mang 16 lần. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn Eastern Brown&nbsp;</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.655734001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Eastern Brown lo&agrave;i rắn phổ biến ở Australia, xếp loại cực độc thứ 2 thế giới. Khi trưởng th&agrave;nh, lo&agrave;i rắn n&agrave;y kh&aacute;c nhau về m&agrave;u sắc. B&ecirc;n cạnh m&agrave;u n&acirc;u b&oacute;ng, lo&agrave;i rắn n&agrave;y cũng c&oacute; c&aacute;c m&agrave;u sắc kh&aacute;c như v&agrave;ng, x&aacute;m đen. Rắn Eastern Brown ăn động vật c&oacute; xương sống, chẳng hạn ếch, thằn lằn, rắn, chim v&agrave; c&aacute;c lo&agrave;i gặm nhấm. Ảnh: Listverse</p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn Taipan nội địa</strong></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/78/700_0.739814001397809165.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&acirc;y l&agrave; lo&agrave;i rắn c&oacute; nọc độc mạnh nhất ở tr&ecirc;n cạn. Lượng nọc tối đa của một vết cắn l&agrave; 110 mg, đủ để giết chết 100 người, hay 250.000 con chuột. Mức độ độc của nọc rắn n&agrave;y gấp 50 lần rắn hổ mang thường. Ảnh: Listverse</p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html\" target=\"_blank\">http://news.zing.vn/9-loai-ran-doc-nhat-the-gioi-post364821.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399972144', '0', '1397809164', '0', '0');
INSERT INTO `slm_news` VALUES ('79', '0.877185001398148278.jpg', 'a:15:{i:0;s:24:\"0.877185001398148278.jpg\";i:1;s:24:\"0.399617001398148279.jpg\";i:2;s:24:\"0.590988001398148279.jpg\";i:3;s:24:\"0.933273001398148279.jpg\";i:4;s:24:\"0.264212001398148280.jpg\";i:5;s:24:\"0.583498001398148280.jpg\";i:6;s:24:\"0.064880001398148281.jpg\";i:7;s:24:\"0.204944001398148281.jpg\";i:8;s:24:\"0.522520001398148281.jpg\";i:9;s:24:\"0.881464001398148281.jpg\";i:10;s:24:\"0.201477001398148282.jpg\";i:11;s:24:\"0.515616001398148282.jpg\";i:12;s:24:\"0.763595001398148282.jpg\";i:13;s:24:\"0.026258001398148283.jpg\";i:14;s:24:\"0.347644001398148283.jpg\";}', null, 'Lễ hội làng Lệ Mật năm Giáp Ngọ 2014', null, null, '25', '', '\r\n	Hội l&agrave;ng Lệ Mật ở phường Việt Hưng, quận Long Bi&ecirc;n (H&agrave; Nội) nổi tiếng với c&aacute;c m&agrave;n bắt c&aacute;, rước v&agrave; ch&eacute;m rắn s&ocirc;i động đ&igrave;nh l&agrave;ng.', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.399617001398148279.jpg\" /></p>\r\n<p>\r\n	Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật.&nbsp;Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.</p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật. Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html\" target=\"_blank\">http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật. Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html\" target=\"_blank\">http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật. Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html\" target=\"_blank\">http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật. Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html\" target=\"_blank\">http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a>\r\n	<div style=\"position: absolute; left: -99999px;\">\r\n		Lễ đả ngư diễn ra tại giếng Ngọc trước đ&igrave;nh Lệ Mật. Hội được tổ chức v&agrave;o dịp 23/3 &acirc;m lịch h&agrave;ng năm. Từ s&aacute;ng sớm đại diện con ch&aacute;u của 13 trại ph&iacute;a T&acirc;y th&agrave;nh Thăng Long xưa đội 13 m&acirc;m lễ vật mang từ Kinh đ&ocirc; về đ&igrave;nh l&agrave;ng.<br />\r\n		<br />\r\n		B&agrave;i viết: <a href=\"http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html\" target=\"_blank\">http://news.zing.vn/Man-chem-ran-o-hoi-lang-Le-Mat-post410361.html</a><br />\r\n		<br />\r\n		<a href=\"http://news.zing.vn\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n</div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.590988001398148279.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c trai đinh trong l&agrave;ng c&oacute; sức khỏe được cử ra l&agrave;m lễ đả ngư v&agrave; chuẩn bị quăng lưới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.933273001398148279.jpg\" /></p>\r\n<p>\r\n	Lễ &quot;đả ngư&quot; l&agrave; một nghi thức t&acirc;m linh quan trọng v&agrave; kh&ocirc;ng thể thiếu được trong lễ hội truyền thống Lệ Mật. Lễ tục n&agrave;y được bắt nguồn từ sự t&iacute;ch ch&agrave;ng trai họ Ho&agrave;ng đ&atilde; xả th&acirc;n đ&aacute;nh Giảo Long, cứu x&aacute;c c&ocirc;ng ch&uacute;a nh&agrave; L&yacute;. Đả ngư (tức đ&aacute;nh c&aacute;) như một lời ước nguyện tri &acirc;n của c&ocirc;ng ch&uacute;a đối với vị th&agrave;nh ho&agrave;ng l&agrave;ng. Trong ảnh, c&aacute;c trai đinh dồn c&aacute; về một nơi để đ&aacute;nh bắt .</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.204944001398148281.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.064880001398148281.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Th&agrave;nh quả sau khi đ&aacute;nh bắt</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.881464001398148281.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.201477001398148282.jpg\" /></p>\r\n<p>\r\n	C&aacute; đ&aacute;nh được đều đặt tr&ecirc;n chiếc m&acirc;m đồng, rước c&aacute; v&agrave;o miếu cạnh giếng để tr&igrave;nh c&ocirc;ng ch&uacute;a, rồi mới rước c&aacute; v&agrave;o đ&igrave;nh để c&uacute;ng Thần. Khi rước c&aacute; v&agrave;o đ&igrave;nh cũng đi theo nghi thức của một đ&aacute;m rước rất trang trọng, uy nghi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.347644001398148283.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tướng nh&agrave; L&yacute; đi t&igrave;m thủy qu&aacute;i, cứu C&ocirc;ng ch&uacute;a</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.264212001398148280.jpg\" /></p>\r\n<p>\r\n	Tr&ograve; m&uacute;a rắn trong hội . Con rắn (được l&agrave;m bằng nan tre lợp vải) tượng trưng cho lo&agrave;i thuỷ qu&aacute;i đ&atilde; bị ch&agrave;ng trai họ Ho&agrave;ng d&ugrave;ng sức mạnh v&agrave; &yacute; ch&iacute; của m&igrave;nh hạ gục. Nhạc m&uacute;a l&agrave; d&agrave;n b&aacute;t &acirc;m v&agrave; tiếng trống nhịp đ&ocirc;i kết hợp dồn dập, n&aacute;o nức.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.026258001398148283.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.522520001398148281.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ch&agrave;ng trai họ Ho&agrave;ng chiến thắng lo&agrave;i thủy qu&aacute;i</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.515616001398148282.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	M&uacute;a sinh tiền g&oacute;p vui trong hội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.763595001398148282.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ngo&agrave;i ra, hội l&agrave;ng Lệ Mật c&ograve;n c&oacute; nhiều hoạt động kh&aacute;c như thi đấu b&oacute;ng chuyền...</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.583498001398148280.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/79/700_0.877185001398148278.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	H&aacute;t quan họ....</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969027', '0', '1398148278', '0', '0');
INSERT INTO `slm_news` VALUES ('80', '0.865632001398232141.jpg', 'a:17:{i:0;s:24:\"0.865632001398232141.jpg\";i:1;s:24:\"0.254908001398232142.jpg\";i:2;s:24:\"0.526869001398232142.jpg\";i:3;s:24:\"0.845222001398232142.jpg\";i:4;s:24:\"0.126216001398232143.jpg\";i:5;s:24:\"0.388937001398232143.jpg\";i:6;s:24:\"0.648487001398232143.jpg\";i:7;s:24:\"0.905357001398232143.jpg\";i:8;s:24:\"0.154985001398232144.jpg\";i:9;s:24:\"0.432158001398232144.jpg\";i:10;s:24:\"0.765900001398232144.jpg\";i:11;s:24:\"0.016390001398232145.jpg\";i:12;s:24:\"0.269500001398232145.jpg\";i:13;s:24:\"0.574575001398232145.jpg\";i:14;s:24:\"0.847456001398232145.jpg\";i:15;s:24:\"0.731783001398232146.jpg\";i:16;s:24:\"0.094490001398232147.jpg\";}', null, 'Thập Tam Trại trẩy hội làng Lệ Mật 2014', null, null, '25', '', '\r\n	Thập tam trại l&agrave; một t&ecirc;n gọi chung chỉ 13 l&agrave;ng nghề ở ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long. Tương truyền, c&aacute;c l&agrave;ng n&agrave;y được lập n&ecirc;n thời vua L&yacute; Nh&acirc;n T&ocirc;ng, bởi c&ocirc;ng của dũng sỹ họ Ho&agrave;ng v&agrave; d&acirc;n l&agrave;ng Lệ Mật. Từ thời nh&agrave; L&yacute; đ&atilde; c&oacute; t&ecirc;n c&aacute;c th&ocirc;n trại: Vạn Bảo, Đại Y&ecirc;n (Đại Bi), Ngọc H&agrave;, Hữu Tiệp, Giảng V&otilde;, Thủ Lệ, Liễu Nha', '<p>\r\n	<b>Thập tam trại</b> l&agrave; một t&ecirc;n gọi chung chỉ 13 l&agrave;ng nghề ở ph&iacute;a T&acirc;y kinh th&agrave;nh Thăng Long. Tương truyền, c&aacute;c l&agrave;ng n&agrave;y được lập n&ecirc;n thời vua L&yacute; Nh&acirc;n T&ocirc;ng, bởi c&ocirc;ng của dũng sỹ họ Ho&agrave;ng v&agrave; d&acirc;n l&agrave;ng Lệ Mật.</p>\r\n<p>\r\n	Từ thời nh&agrave; L&yacute; đ&atilde; c&oacute; t&ecirc;n c&aacute;c th&ocirc;n trại: Vạn Bảo, Đại Y&ecirc;n (Đại Bi), Ngọc H&agrave;, Hữu Tiệp, Giảng V&otilde;, Thủ Lệ, Liễu Nhai (Liễu Giai), Cống Y&ecirc;n .C&aacute;c l&agrave;ng n&agrave;y đ&oacute;ng vai tr&ograve; cung cấp c&aacute;c nhu yếu phẩm cho kinh th&agrave;nh Thăng long.</p>\r\n<p>\r\n	Năm n&agrave;o cũng vậy, v&agrave;o ng&agrave;y hội ch&iacute;nh (23-3) người d&acirc;n Thập Tam Trại lại n&ocirc; nức trẩy hội, quay về qu&ecirc; hương Lệ Mật.</p>\r\n<p>\r\n	Dưới đ&acirc;y l&agrave; một số h&igrave;nh ảnh của Thập Tam Trại v&agrave;o ng&agrave;y hội l&agrave;ng Lệ Mật 2014 (23-3-2014 &Acirc;m lịch)</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.865632001398232141.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mở đầu l&agrave; đo&agrave;n cựu chiến binh, c&aacute;c b&ocirc; l&atilde;o, d&acirc;n l&agrave;ng Lệ Mật đ&oacute;n c&aacute;c Trại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.254908001398232142.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.526869001398232142.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Vĩnh Ph&uacute;c 2</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.845222001398232142.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Đại Y&ecirc;n</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.126216001398232143.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.388937001398232143.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Vạn Bảo</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.648487001398232143.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.905357001398232143.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Ngọc H&agrave;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.154985001398232144.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Hữu Tiệp</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.432158001398232144.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Cống Vị</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.765900001398232144.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Giảng V&otilde;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.016390001398232145.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.269500001398232145.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Thủ Lệ</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.574575001398232145.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.847456001398232145.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Ngọc Kh&aacute;nh</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.731783001398232146.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Trại Vĩnh Ph&uacute;c</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/80/700_0.094490001398232147.jpg\" /></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399969008', '0', '1398232141', '0', '0');
INSERT INTO `slm_news` VALUES ('81', '0.798488001398664599.jpg', 'a:10:{i:0;s:24:\"0.798488001398664599.jpg\";i:1;s:24:\"0.777008001398664602.jpg\";i:2;s:24:\"0.073436001398664603.jpg\";i:3;s:24:\"0.392351001398664603.jpg\";i:4;s:24:\"0.738659001398664603.jpg\";i:5;s:24:\"0.094666001398664604.jpg\";i:6;s:24:\"0.503701001398664604.jpg\";i:7;s:24:\"0.907634001398664604.jpg\";i:8;s:24:\"0.348694001398664605.jpg\";i:9;s:24:\"0.675101001398664605.jpg\";}', null, 'Ông lão 15 năm vớt rác làm sạch giếng làng', null, null, '12', '', '\r\n	Ở tuổi 81, &ocirc;ng Nguyễn Duy Y&ecirc;n ở l&agrave;ng Ho&agrave;ng Mai, phường Ho&agrave;ng Văn Thụ (Ho&agrave;ng Mai, H&agrave; Nội), vẫn đều đặn ng&agrave;y hai lần ra giếng vớt r&aacute;c.', '<p>\r\n	Ở tuổi 81, &ocirc;ng Nguyễn Duy Y&ecirc;n ở l&agrave;ng Ho&agrave;ng Mai, phường Ho&agrave;ng Văn Thụ (Ho&agrave;ng Mai, H&agrave; Nội), vẫn đều đặn ng&agrave;y hai lần ra giếng vớt r&aacute;c.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.777008001398664602.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&atilde; l&ecirc;n phố nhưng Ho&agrave;ng Mai vẫn giữ được những n&eacute;t xưa của một ng&ocirc;i l&agrave;ng. Ngo&agrave;i cổng x&oacute;m, đ&igrave;nh l&agrave;ng, ở đ&acirc;y c&ograve;n tới 4 chiếc giếng lớn c&oacute; h&igrave;nh d&aacute;ng v&agrave; k&iacute;ch thước giống nhau.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.073436001398664603.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	D&acirc;n l&agrave;ng khi n&oacute;i về giếng xưa, ai cũng nhắc tới cụ &ocirc;ng Nguyễn Duy Y&ecirc;n (81 tuổi) ng&agrave;y ng&agrave;y cặm cụi đi vớt r&aacute;c ở giếng t&acirc;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.392351001398664603.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&Ocirc;ng Y&ecirc;n l&agrave; người sống ở l&agrave;ng Ho&agrave;ng Mai từ nhỏ. Đời cha của &ocirc;ng Y&ecirc;n đ&atilde; c&oacute; giếng nước n&agrave;y. Từ khi c&oacute; nước m&aacute;y, d&acirc;n l&agrave;ng kh&ocirc;ng d&ugrave;ng nước giếng nữa. Giếng bỏ kh&ocirc;ng trở n&ecirc;n mất vệ sinh v&igrave; c&oacute; nhiều người vứt r&aacute;c, thậm ch&iacute; cả x&aacute;c động vật chết xuống.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.738659001398664603.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tổ d&acirc;n phố sau đ&oacute; họp đề xuất giao cho &ocirc;ng Y&ecirc;n tr&ocirc;ng nom giếng, v&igrave; l&uacute;c đ&oacute; &ocirc;ng Y&ecirc;n đ&atilde; nghỉ hưu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.094666001398664604.jpg\" /><img src=\"http://langquetoi.com/images/news/81/700_0.503701001398664604.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nhận tr&aacute;ch nhiệm n&agrave;y 15 năm qua, &ocirc;ng Y&ecirc;n vẫn ng&agrave;y ng&agrave;y cặm cụi hớt r&aacute;c, l&aacute; c&acirc;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"497\" src=\"http://langquetoi.com/images/news/81/700_0.907634001398664604.jpg\" width=\"358\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&Ocirc;ng cũng nhắc nhở mọi người xung quanh kh&ocirc;ng vứt r&aacute;c, đổ chất thải xuống giếng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.348694001398664605.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&Ocirc;ng cũng nu&ocirc;i c&aacute; r&ocirc; dưới giếng. Những l&uacute;c rảnh rỗi, &ocirc;ng đem cần ra c&acirc;u. &Ocirc;ng t&acirc;m sự: &quot;C&aacute; nhiều khi chỉ để cho m&egrave;o ăn nhưng t&ocirc;i thấy vui. Vui hơn l&agrave; c&ograve;n c&oacute; bạn c&acirc;u để n&oacute;i chuyện&quot;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.675101001398664605.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&Ocirc;ng Y&ecirc;n kể: &quot;Mỗi lần thau giếng, t&ocirc;i d&ugrave;ng m&aacute;y bơm h&uacute;t cạn nước rồi thu&ecirc; th&ecirc;m 3 người dọn. L&uacute;c giếng cạn, thấy dưới giếng nhiều nhất l&agrave; lốp xe, b&aacute;t hương v&agrave; gạch ng&oacute;i... Lần thau vừa rồi, t&ocirc;i c&ograve;n thấy cả một chiếc xe m&aacute;y hỏng. Dưới giếng c&oacute; một lớp b&ugrave;n, khi dọn sạch th&igrave; mạch nước từ giữa giếng mới chảy ra&quot;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/81/700_0.798488001398664599.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nhờ c&oacute; &ocirc;ng Y&ecirc;n, giếng nước của l&agrave;ng bao năm qua l&uacute;c n&agrave;o cũng xanh trong.Những phần thưởng nhỏ động vi&ecirc;n khiến &ocirc;ng Y&ecirc;n rất phấn khởi. &quot;T&ocirc;i mới được phường động vi&ecirc;n thưởng 10 c&aacute;i khăn mặt&quot;, &ocirc;ng Y&ecirc;n khoe.</p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969371', '0', '1398664599', '0', '0');
INSERT INTO `slm_news` VALUES ('82', '0.496317001398664698.jpg', 'a:10:{i:0;s:24:\"0.496317001398664698.jpg\";i:1;s:24:\"0.575283001398664698.jpg\";i:2;s:24:\"0.687250001398664698.jpg\";i:3;s:24:\"0.812360001398664698.jpg\";i:4;s:24:\"0.901689001398664698.jpg\";i:5;s:24:\"0.981636001398664698.jpg\";i:6;s:24:\"0.066721001398664699.jpg\";i:7;s:24:\"0.191603001398664699.jpg\";i:8;s:24:\"0.275444001398664699.jpg\";i:9;s:24:\"0.404366001398664699.jpg\";}', null, 'Hà Nội mùa hoa bưởi', null, null, '12', '', '\r\n	Th&aacute;ng ba, tr&ecirc;n nhiều con phố ở H&agrave; th&agrave;nh lại thơm nh&egrave; nhẹ m&ugrave;i hoa bưởi từ những quầy, g&aacute;nh hoa của c&aacute;c b&agrave;, c&aacute;c chị.', '<p style=\"text-align: center;\">\r\n	Th&aacute;ng ba, tr&ecirc;n nhiều con phố ở H&agrave; th&agrave;nh lại thơm nh&egrave; nhẹ m&ugrave;i hoa bưởi từ những quầy, g&aacute;nh hoa của c&aacute;c b&agrave;, c&aacute;c chị.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.575283001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Những ng&agrave;y n&agrave;y, b&agrave; Thu (phố H&agrave;ng Khoai) lại b&agrave;y b&aacute;n th&ecirc;m rổ hoa bưởi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.687250001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hoa bưởi giản dị, mộc mạc nhưng hương th&igrave; nổi trội,&nbsp;chưa thấy hoa nhưng đ&atilde; c&oacute; thể cảm nhận được m&ugrave;i thơm thanh khiết.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.812360001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&agrave; Thu b&aacute;n hoa theo b&oacute;, cũng c&oacute; nhiều nơi lại b&aacute;n hoa theo lạng, gi&aacute; khoảng 10.000-15.000 đồng một lạng. H&agrave; Nội mưa ph&ugrave;n k&eacute;o d&agrave;i, hoa dễ dập nhưng nhiều gia đ&igrave;nh vẫn mua hoa về cắm bởi hương thơm dịu như xua tan đi c&aacute;i nồm kh&oacute; chịu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.901689001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Người H&agrave; Nội&nbsp;c&oacute; th&oacute;i quen mua hoa bưởi về đặt&nbsp;l&ecirc;n đĩa thắp hương c&uacute;ng gia ti&ecirc;n, cắm trong lọ nhỏ, ướp tr&agrave;, nấu ch&egrave; hoặc gội đầu.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.981636001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tr&ecirc;n phố Giảng V&otilde; v&agrave; C&aacute;t Linh, c&aacute;c c&ocirc;, c&aacute;c chị g&aacute;nh hoa bưởi đi b&aacute;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.066721001398664699.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hoa bưởi b&aacute;n tr&ecirc;n phố đa số xuất ph&aacute;t từ c&aacute;c l&agrave;ng ven đ&ocirc; H&agrave; Nội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.191603001398664699.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	V&ugrave;ng Diễn vốn nổi tiếng l&agrave; đất trồng bưởi của H&agrave; th&agrave;nh. Đến đ&acirc;y v&agrave;o th&aacute;ng 3, chưa v&agrave;o tới vườn bạn đ&atilde; ngửi&nbsp;ng&agrave;o ngạt hương thơm. Khi ra về, hương bưởi vẫn c&ograve;n vương theo.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.275444001398664699.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hoa bưởi thường nở đồng loạt cả ch&ugrave;m, cả c&acirc;y. Những b&ocirc;ng hoa trắng tinh kh&ocirc;i nổi bật tr&ecirc;n nền l&aacute; xanh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.404366001398664699.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mỗi dịp th&aacute;ng ba về, người H&agrave; Nội xa qu&ecirc; đều nhớ về m&ugrave;i hương thơm ng&aacute;t của hoa bưởi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/82/700_0.496317001398664698.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Rất nhiều thế hệ kh&ocirc;ng thể qu&ecirc;n những c&acirc;u thơ của Phan Thị Thanh Nh&agrave;n: &quot;N&agrave;o ai đ&atilde; một lần d&aacute;m n&oacute;i/ Hương bưởi thơm cho l&ograve;ng bối rối/ C&ocirc; b&eacute; như ch&ugrave;m hoa lặng lẽ/ Nhờ hương thơm n&oacute;i hộ t&igrave;nh y&ecirc;u&quot;.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1398668607', '0', '1398664698', '0', '0');
INSERT INTO `slm_news` VALUES ('84', '0.598731001399000754.jpg', 'a:17:{i:0;s:24:\"0.598731001399000754.jpg\";i:1;s:24:\"0.792428001399000754.jpg\";i:2;s:24:\"0.993787001399000754.jpg\";i:3;s:24:\"0.245883001399000755.jpg\";i:4;s:24:\"0.478682001399000755.jpg\";i:5;s:24:\"0.710530001399000755.jpg\";i:6;s:24:\"0.885466001399000755.jpg\";i:7;s:24:\"0.141759001399000756.jpg\";i:8;s:24:\"0.382001001399000756.jpg\";i:9;s:24:\"0.595978001399000756.jpg\";i:10;s:24:\"0.780336001399000756.jpg\";i:11;s:24:\"0.008930001399000757.jpg\";i:12;s:24:\"0.186080001399000757.jpg\";i:13;s:24:\"0.491341001399000757.jpg\";i:14;s:24:\"0.737010001399000757.jpg\";i:15;s:24:\"0.973740001399000757.jpg\";i:16;s:24:\"0.149446001399000758.jpg\";}', null, 'Đêm hội Carnaval Hạ Long tối 30/4/2014', null, null, '12', '', '\r\n	Trong m&agrave;n diễu h&agrave;nh v&agrave; tr&igrave;nh diễn tại đ&ecirc;m hội Carnaval Hạ Long tối 30/4, hơn 600 vũ c&ocirc;ng v&agrave; người mẫu đ&atilde; thể hiện những vũ điệu đẹp mắt c&ugrave;ng trang phục nhiều m&agrave;u sắc, khoe th&acirc;n.', '<p>\r\n	Trong m&agrave;n diễu h&agrave;nh v&agrave; tr&igrave;nh diễn tại đ&ecirc;m hội Carnaval Hạ Long tối 30/4, hơn 600 vũ c&ocirc;ng v&agrave; người mẫu đ&atilde; thể hiện những vũ điệu đẹp mắt c&ugrave;ng trang phục nhiều m&agrave;u sắc, khoe th&acirc;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.792428001399000754.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&ecirc;m hội Carnaval Hạ Long 2014 diễn ra v&agrave;o tối 30/04 thu h&uacute;t h&agrave;ng vạn du kh&aacute;ch đến với s&acirc;u khấu bờ biển lớn nhất miền Bắc.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.598731001399000754.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Gần 4.000&nbsp;diễn vi&ecirc;n chuy&ecirc;n nghiệp v&agrave; nghiệp dư c&ugrave;ng nhau tạo n&ecirc;n một cuộc đại tr&igrave;nh diễn tr&ecirc;n một &ldquo;s&acirc;n khấu&rdquo; lớn c&oacute; chiều d&agrave;i tr&ecirc;n hơn 1km dọc đường Ho&agrave;ng Quốc Việt, phường H&ugrave;ng Thắng, TP Hạ Long.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.993787001399000754.jpg\" /><img src=\"http://langquetoi.com/images/news/84/700_0.245883001399000755.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Thu h&uacute;t nhiều &aacute;nh mắt du kh&aacute;ch, kh&aacute;n giả nhất l&agrave; d&agrave;n người mẫu, vũ c&ocirc;ng xinh đẹp trong c&aacute;c trang phục sắc m&agrave;u gợi cảm, đậm chất latin.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.478682001399000755.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nhiều c&ocirc; g&aacute;i c&oacute; th&acirc;n h&igrave;nh gợi cảm, nụ cười rạng rỡ, vũ điệu uyển chuyển li&ecirc;n tục.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.710530001399000755.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	613 diễn vi&ecirc;n n&agrave;y gồm cả chuy&ecirc;n v&agrave; kh&ocirc;ng chuy&ecirc;n nghiệp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.885466001399000755.jpg\" /><img src=\"http://langquetoi.com/images/news/84/700_0.141759001399000756.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c vũ c&ocirc;ng kh&ocirc;ng ngừng uyển chuyển nhảy m&uacute;a theo điệu nhạc latin dọc khắp hơn 1.000m đường diễu h&agrave;nh.Để c&oacute; được c&aacute;c điệu nhảy, m&uacute;a đẹp mắt họ đ&atilde; phải tập luyện trong nhiều ng&agrave;y trước đ&oacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.382001001399000756.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đo&agrave;n diễn vi&ecirc;n Bellydance (m&uacute;a bụng) với những đ&ocirc;i c&aacute;nh sắc m&agrave;u.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.595978001399000756.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&oacute; những l&uacute;c c&aacute;c người đẹp h&oacute;a th&acirc;n th&agrave;nh những thi&ecirc;n thần trong bộ trang phục trắng muốt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.780336001399000756.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Kể cả c&aacute;c thiếu nữ d&acirc;n tộc thiểu số. Quảng Ninh cũng l&agrave; địa phương c&oacute; nhiều d&acirc;n tộc sinh sống rải r&aacute;c tr&ecirc;n khắp v&ugrave;ng..</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.008930001399000757.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	D&agrave;n người mẫu chuy&ecirc;n nghiệp, nổi tiếng th&igrave; được đứng hai b&ecirc;n tr&ecirc;n xe diễu h&agrave;nh. C&aacute;c c&ocirc; diện &aacute;o d&agrave;i truyền thống vẫy tay ch&agrave;o kh&aacute;n giả.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.186080001399000757.jpg\" /><img src=\"http://langquetoi.com/images/news/84/700_0.491341001399000757.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.737010001399000757.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c thiếu nữ đo&agrave;n nghệ thuật đến từ Trung Quốc cũng kh&ocirc;ng thua k&eacute;m sắc đẹp c&aacute;c c&ocirc; g&aacute;i Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.973740001399000757.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đo&agrave;n nữ diễn vi&ecirc;n nước bạn L&agrave;o v&agrave; Th&aacute;i Lan trong trang phục d&acirc;n tộc truyền thống.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/84/700_0.149446001399000758.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&acirc;y cũng l&agrave; dịp gặp gỡ giao lưu, trao đổi v&agrave; s&acirc;n chơi, s&acirc;n khấu lớn để thể hiện hết m&igrave;nh của c&aacute;c đo&agrave;n nghệ thuật trong v&agrave; ngo&agrave;i tỉnh Quảng Ninh.</p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969324', '0', '1399000754', '0', '0');
INSERT INTO `slm_news` VALUES ('93', '0.502715001399264263.jpg', 'a:12:{i:0;s:24:\"0.502715001399264263.jpg\";i:1;s:24:\"0.747948001399264263.jpg\";i:2;s:24:\"0.929272001399264263.jpg\";i:3;s:24:\"0.137337001399264264.jpg\";i:4;s:24:\"0.343384001399264264.jpg\";i:5;s:24:\"0.541871001399264264.jpg\";i:6;s:24:\"0.742268001399264264.jpg\";i:7;s:24:\"0.162799001399264265.jpg\";i:8;s:24:\"0.559734001399264265.jpg\";i:9;s:24:\"0.864922001399264265.jpg\";i:10;s:24:\"0.241605001399264266.jpg\";i:11;s:24:\"0.569940001399264266.jpg\";}', null, 'Trên đất Phật Tây Thiên', null, null, '12', '', '\r\n	Leo bộ l&ecirc;n đỉnh T&acirc;y Thi&ecirc;n (Tam Đảo, Vĩnh Ph&uacute;c) kh&aacute; kh&oacute; nhọc, nhưng b&ugrave; lại du kh&aacute;ch được h&ograve;a m&igrave;nh v&agrave;o thi&ecirc;n cảnh hữu t&igrave;nh, thỏa t&acirc;m hướng Phật.', '<div class=\"width_common space_bottom_20\">\r\n	<div class=\"block_content_slide_showdetail\">\r\n		<div id=\"article_content\">\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.747948001399264263.jpg\" /></div>\r\n				<div class=\"desc_cation\">\r\n					<p style=\"text-align: center;\">\r\n						Đại Bảo th&aacute;p Kim cương thừa giữa n&uacute;i trời T&acirc;y Thi&ecirc;n.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.929272001399264263.jpg\" /></div>\r\n				<div class=\"desc_cation\">\r\n					<p style=\"text-align: center;\">\r\n						Trước khi đến được Đại Bảo th&aacute;p, cuộc h&agrave;nh hương tr&ecirc;n đất Phật T&acirc;y Thi&ecirc;n bắt đầu bằng chuyến xe điện đưa du kh&aacute;ch từ b&atilde;i đậu xe tới ch&acirc;n n&uacute;i.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.137337001399264264.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Du kh&aacute;ch c&oacute; thể đi bộ tr&ecirc;n con đường bậc thang uốn lượn trong l&ograve;ng rừng n&uacute;i.&nbsp;</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.343384001399264264.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Tr&ecirc;n đường đi, c&oacute; nhiều đoạn du kh&aacute;ch bắt gặp suối nước lớn b&ecirc;n cạnh với hệ thống c&aacute;p treo chạy ph&iacute;a tr&ecirc;n. Đi c&aacute;p treo hiện đại l&ecirc;n đỉnh T&acirc;y Thi&ecirc;n l&agrave; c&aacute;ch dễ d&agrave;ng nhất v&agrave; được ngắm rừng n&uacute;i từ tr&ecirc;n cao.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.541871001399264264.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Đường l&ecirc;n đỉnh T&acirc;y Thi&ecirc;n c&oacute; nhiều đoạn bậc thang dốc đứng, khiến du kh&aacute;ch kh&aacute; vất vả, phải dừng ch&acirc;n tại những qu&aacute;n nghỉ dọc đường để lấy sức. Đối với những người g&aacute;nh h&agrave;ng hay gặp tr&ecirc;n đường, việc leo dốc n&uacute;i đ&atilde; trở n&ecirc;n quen thuộc.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.742268001399264264.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Du kh&aacute;ch c&oacute; thể gặp nhiều bất ngờ th&uacute; vị tr&ecirc;n đường đi, v&iacute; dụ như pho tượng Phật theo trường ph&aacute;i Mật t&ocirc;ng trong một nh&agrave; lưu niệm b&ecirc;n đường.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.162799001399264265.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						V&agrave;o những ng&agrave;y mưa, nước suối c&oacute; thể chảy tr&agrave;n qua đường đi.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.502715001399264263.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Sau khoảng 4 km đường n&uacute;i, du kh&aacute;ch sẽ tới khu quần thể c&aacute;c c&ocirc;ng tr&igrave;nh đền ch&ugrave;a đặc sắc.&nbsp;</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.559734001399264265.jpg\" />&nbsp;\r\n					<p style=\"text-align: center;\">\r\n						Phật tử l&agrave;m nghi lễ nhiễu th&aacute;p trong Đại Bảo th&aacute;p Kim Cương thừa.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.864922001399264265.jpg\" />&nbsp;\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						C&aacute;c hoa văn đặc trưng của d&ograve;ng Phật gi&aacute;o Mật t&ocirc;ng b&ecirc;n trong Đại Bảo th&aacute;p Kim Cương thừa.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.241605001399264266.jpg\" />&nbsp;\r\n					<p class=\"Normal\" style=\"text-align: center;\">\r\n						C&aacute;c vị sư đi bộ quanh Thiền đường của Thiền viện Tr&uacute;c L&acirc;m.</p>\r\n				</div>\r\n				<div class=\"clear\" style=\"text-align: center;\">\r\n					&nbsp;</div>\r\n			</div>\r\n			<div class=\"item_slide_show\">\r\n				<div class=\"block_thumb_slide_show\" style=\"text-align: center;\">\r\n					<img src=\"http://langquetoi.com/images/news/93/700_0.569940001399264266.jpg\" />&nbsp;<a class=\"btn_icon_show_slide_show\" href=\"http://dulich.vnexpress.net/photo/anh-video/vung-dat-phat-tay-thien-2976752.html#\"> </a></div>\r\n				<div class=\"desc_cation\">\r\n					<p style=\"text-align: center;\">\r\n						Du kh&aacute;ch cũng c&oacute; thể chọn mua một số sản vật của rừng n&uacute;i như măng rừng để mang về ăn hay l&agrave;m qu&agrave;.</p>\r\n					<div style=\"text-align: center;\">\r\n						&nbsp;</div>\r\n				</div>\r\n			</div>\r\n		</div>\r\n	</div>\r\n</div>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1399969294', '0', '1399264263', '0', '0');
INSERT INTO `slm_news` VALUES ('94', '0.573217001399961444.jpg', 'a:11:{i:0;s:24:\"0.573217001399961444.jpg\";i:1;s:24:\"0.744779001399961444.jpg\";i:2;s:24:\"0.916314001399961444.jpg\";i:3;s:24:\"0.081488001399961445.jpg\";i:4;s:24:\"0.247653001399961445.jpg\";i:5;s:24:\"0.445695001399961445.jpg\";i:6;s:24:\"0.638180001399961445.jpg\";i:7;s:24:\"0.805945001399961445.jpg\";i:8;s:24:\"0.973314001399961445.jpg\";i:9;s:24:\"0.150244001399961446.jpg\";i:10;s:24:\"0.351778001399961446.jpg\";}', null, 'Những món đồ mua bằng cả cây vàng giờ ra sao?', null, null, '12', '', '\r\n	Theo thời gian, những m&oacute;n đồ được mua bằng cả c&acirc;y v&agrave;ng cũng dần mất đi gi&aacute; trị. Một số vẫn được c&aacute;c gia đ&igrave;nh sử dụng, số c&ograve;n lại bị đem b&aacute;n đồng n&aacute;t, l&agrave;m đồ trưng b&agrave;y.&nbsp;', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.744779001399961444.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	&Ocirc;ng B&ugrave;i C&ocirc;ng B&ocirc;ng ở Quỳnh Phụ, Th&aacute;i B&igrave;nh từng chi v&agrave;i c&acirc;y v&agrave;ng chỉ để mua những đồ vật trong nh&agrave;. Chiếc xe Super Cub 70 n&agrave;y được &ocirc;ng Quỳnh mua v&agrave;o năm 1995 trị gi&aacute; 5 c&acirc;y v&agrave;ng, tương đương với 25 triệu đồng l&uacute;c bấy giờ. &Ocirc;ng cho biết, hai vợ chồng &ocirc;ng l&agrave;m đủ nghề như nấu rượu, h&agrave;ng s&aacute;o, chăn nu&ocirc;i, l&agrave;m đậu&hellip;mới c&oacute; thể mua được. &ldquo; Xe m&aacute;y thời ấy qu&yacute; hiếm lắm, cả l&agrave;ng duy nhất nh&agrave; t&ocirc;i c&oacute;&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.916314001399961444.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ngo&agrave;i sở hữu chiếc xe m&aacute;y thời thượng l&uacute;c bấy giờ, gia đ&igrave;nh &ocirc;ng B&ocirc;ng cũng c&oacute; ti vi m&agrave;u đầu ti&ecirc;n trong l&agrave;ng. Chiếc ti vi m&agrave;u &ldquo;vỏ đỏ&rdquo; &ocirc;ng mua v&agrave;o năm 1992 bằng 7 chỉ v&agrave;ng. &Ocirc;ng Quỳnh cho biết, cả hai đồ vật đều của Nhật n&ecirc;n độ bền cao, hiện tại chạy vẫn tốt. Nhiều người bu&ocirc;n b&aacute;n đồ cổ đến trả gi&aacute; 5 triệu đồng nhưng &ocirc;ng kh&ocirc;ng b&aacute;n.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.081488001399961445.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Chiếc tivi Daewoo trị gi&aacute; 7,5 chỉ v&agrave;ng, được &ocirc;ng Trần C&ocirc;ng Thơm (Th&aacute;i B&igrave;nh) mua năm 1995. &ldquo;Khi đ&oacute; ti vi qu&yacute; hiếm lắm, cứ chập tối l&agrave; cả l&agrave;ng đến xem chật nh&agrave;&rdquo;, &ocirc;ng n&oacute;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.247653001399961445.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	B&agrave;n ủi con g&agrave;, lư hương đồng, đ&agrave;i Sharp&hellip; được &ocirc;ng Kh&iacute;nh (Kinh M&ocirc;n, Hải Dương) mua mất hơn 2 c&acirc;y v&agrave;ng năm 1994.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.445695001399961445.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&Ocirc;ng cho biết, năm ấy chưa c&oacute; xe đạp n&ecirc;n đ&atilde; phải đi bộ hơn 40 km l&ecirc;n th&agrave;nh phố mới mua được.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.638180001399961445.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Chiếc đ&agrave;i trị gi&aacute; 3 chỉ v&agrave;ng hiện tại vẫn hoạt động tốt, được &ocirc;ng Kh&iacute;nh thường xuy&ecirc;n sử dụng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.805945001399961445.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Cặp m&acirc;m đồng c&oacute; tuổi đời hơn 50 năm của nh&agrave; &ocirc;ng Nguyễn T&aacute; Hợi (Ho&agrave;i Đức, H&agrave; Nội) từng được c&aacute;c cụ mua với hơn 500 đồng thời đ&oacute;. &Ocirc;ng cho biết, đ&acirc;y l&agrave; m&oacute;n qu&agrave; &ocirc;ng được bố mẹ vợ tặng v&agrave;o ng&agrave;y cưới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.573217001399961444.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Xe đạp thống nhất, phượng ho&agrave;ng&hellip;từng nổi danh đắt đỏ một thời, với gi&aacute; trị l&ecirc;n đến nửa c&acirc;y v&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.973314001399961445.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tuy nhi&ecirc;n, giờ ch&uacute;ng trở th&agrave;nh đồ đồng n&aacute;t hoặc vật trang tr&iacute; trong cửa h&agrave;ng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.150244001399961446.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&igrave;nh lọc nước c&oacute; tuổi đời 70 năm vẫn xuất hiện trong một số gia đ&igrave;nh tại n&ocirc;ng th&ocirc;n&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/94/700_0.351778001399961446.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Chiếc đ&egrave;n dầu n&agrave;y cũng c&oacute; tuổi đời kh&aacute; l&acirc;u, được truyền từ đời nọ sang đời kia n&ecirc;n kh&ocirc;ng ai biết gi&aacute; thực của n&oacute;.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399961708', '0', '1399440470', '0', '0');
INSERT INTO `slm_news` VALUES ('97', '0.619568001399963469.jpg', 'a:8:{i:0;s:24:\"0.619568001399963469.jpg\";i:1;s:24:\"0.725676001399963469.jpg\";i:2;s:24:\"0.794812001399963469.jpg\";i:3;s:24:\"0.886611001399963469.jpg\";i:4;s:24:\"0.976852001399963469.jpg\";i:5;s:24:\"0.165747001399963470.jpg\";i:6;s:24:\"0.205996001399963470.jpg\";i:7;s:24:\"0.391924001399963470.jpg\";}', null, 'Kinh hoàng rắn hỗn chiến săn mồi', null, null, '12', '', '\r\n	Hai qu&aacute;i&nbsp;rắn&nbsp;tử chiến bằng miệng, rắn hạ độc tắc k&egrave; hoa, c&aacute; sấu chết thảm bởi rắn, rắn &ldquo;bay&rdquo; từ tr&ecirc;n cao t&oacute;m gọn vẹt&hellip; &nbsp;&nbsp;', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.725676001399963469.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Rắn lục v&agrave; rắn đu&ocirc;i chu&ocirc;ng tử chiến. Cả hai&nbsp;qu&aacute;i rắn&nbsp;đều cố gắng d&ugrave;ng miệng c&oacute; nanh độc của m&igrave;nh hạ gục đối thủ, cắn x&eacute;, b&ecirc;n n&agrave;o đuối sức sẽ &ldquo;mất mạng&rdquo;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.794812001399963469.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hỗn chiến căng thẳng giữa&nbsp;rắn&nbsp;v&agrave; tắc k&egrave; hoa. Sau khi t&oacute;m gọn tắc k&egrave; ph&iacute;a dưới nanh độc, con rắn ti&ecirc;m chất độc v&agrave;o con mồi v&agrave; từ từ nuốt chửng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.886611001399963469.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Con mồi to lớn bị trăn cuộn chặt, chết v&igrave; ngạt, sau đ&oacute; nuốt chửng v&agrave;o bụng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.976852001399963469.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute; sấu chết thảm bởi&nbsp;rắn. Con rắn hạ gục v&agrave; nuốt gọn s&aacute;t thủ đầm lầy chỉ trong 5 tiếng đồng hồ vật lộn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.165747001399963470.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;ch thức m&agrave; một con rắn nuốt chửng mồi l&agrave; một con chuột đen như thế n&agrave;y khiến người ta phải r&ugrave;ng m&igrave;nh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.205996001399963470.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Th&uacute; c&oacute; t&uacute;i dường như &ldquo;mất h&uacute;t&rdquo; v&igrave; bị rắn cuốn chặt cho kh&ocirc;ng c&ograve;n chỗ hở. Khiến con mồi chết ngạt l&agrave; &ldquo;chi&ecirc;u&rdquo; hay d&ugrave;ng khi rắn muốn hạ gục con mồi.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.619568001399963469.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Rắn vươn m&igrave;nh từ tr&ecirc;n tường cao xuống t&oacute;m gọn con vẹt đang bay, con vật ngỡ ng&agrave;ng khi rơi v&agrave;o &ldquo;cửa tử&rdquo;, t&igrave;m mọi c&aacute;ch v&ugrave;ng vẫy.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/97/700_0.391924001399963470.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	M&agrave;n vật lộn giữa rắn v&agrave; con mồi rời khỏi hồi gay cấn khi con rắn &ldquo;thao t&uacute;ng&rdquo; bữa ăn của n&oacute; trong miệng.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399963629', '0', '1399440717', '0', '0');
INSERT INTO `slm_news` VALUES ('98', '0.504856001399451165.jpg', 'a:11:{i:0;s:24:\"0.504856001399451165.jpg\";i:1;s:24:\"0.589825001399451165.jpg\";i:2;s:24:\"0.674392001399451165.jpg\";i:3;s:24:\"0.756337001399451165.jpg\";i:4;s:24:\"0.840003001399451165.jpg\";i:5;s:24:\"0.919956001399451165.jpg\";i:6;s:24:\"0.002052001399451166.jpg\";i:7;s:24:\"0.090642001399451166.jpg\";i:8;s:24:\"0.177123001399451166.jpg\";i:9;s:24:\"0.259030001399451166.jpg\";i:10;s:24:\"0.336014001399451166.jpg\";}', null, 'Các món ăn về rắn của làng nghề Lệ Mật', null, null, '25', '', '\r\n	Về l&agrave;ng Lệ Mật, c&aacute;c qu&yacute; kh&aacute;ch sẽ được thưởng thức c&aacute;c m&oacute;n ăn về rắn rất độc đ&aacute;o v&agrave; lạ miệng...', '<p>\r\n	<strong><u><span new=\"\" times=\"\">1. S&uacute;p rắn:</span></u></strong></p>\r\n<p>\r\n	<span new=\"\" times=\"\">L&agrave; m&oacute;n ăn khai vị trong bữa ăn. S&uacute;p rắn tuy giản dị, c&aacute;ch nấu kh&ocirc;ng cầu kỳ nh&shy;ưng lại n&iacute;u ch&acirc;n thực kh&aacute;c bởi ch&iacute;nh sự mộc mạc, thanh đạm như&shy;ng v&ocirc; c&ugrave;ng tinh tế, đặc tr&shy;ưng cho c&aacute;c m&oacute;n ăn miền Bắc. Tất cả đ&shy;ược thể hiện rất r&otilde; qua vị ngọt thanh từ nư&shy;ớc d&ugrave;ng (ninh từ x&shy;ương rắn) h&shy;ương thơm tự nhi&ecirc;n đặc trư&shy;ng của nấm h&shy;ương, vị b&eacute;o ngậy của trứng g&agrave; ta v&agrave; vị ngọt thơm, gi&ograve;n, m&aacute;t của ng&ocirc; non.</span></p>\r\n<div>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.504856001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">2. Rắn x&agrave;o lăn:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">Rắn chặt kh&uacute;c, hấp kỹ r&uacute;t x&shy;ương x&agrave;o c&ugrave;ng h&agrave;nh t&acirc;y, sả, ớt, rau răm... Đ&acirc;y l&agrave; m&oacute;n ăn đ&shy;ược coi l&agrave; ngon nhất trong c&aacute;c m&oacute;n ăn về rắn, nhưng c&aacute;ch chế biến lại đơn giản, kh&ocirc;ng cầu kỳ, nguy&ecirc;n liệu dễ tim, dễ kiếm, như&shy; thế mới giữ đ&shy;ợc vị ngọt ngon, bổ thơm của thịt rắn.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.589825001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">3. Chả cuốn l&aacute; Lốt:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">L&aacute; Lốt c&oacute; vị nồng, hơi cay c&oacute; t&iacute;nh ấm chống phong h&agrave;n, giảm đau đầu như&shy;ng khi kết hợp với thịt rắn đ&atilde; tạo n&ecirc;n một m&oacute;n ăn ngon, bổ d&shy;ưỡng như&shy; một m&oacute;n ăn chữa bệnh. Để l&agrave;m m&oacute;n ăn n&agrave;y hết sức đơn giản, thịt rắn t&shy;ơi băm nhỏ c&ugrave;ng sả, ớt, rau răm v&agrave; c&aacute;c loại gia vị ti&ecirc;u, muối, m&igrave; ch&iacute;nh...sau đ&oacute; đ&shy;ược cuốn tr&ograve;n với l&aacute; Lốt v&agrave; đem chi&ecirc;n gi&ograve;n đều. M&oacute;n ăn n&agrave;y đư&shy;ợc chấm với nư&shy;ớc mắm chanh ớt, vị ngọt đậm đ&agrave; của thịt rắn kết hợp với m&ugrave;i thơm hấp dẫn của l&aacute; Lốt thực sự đem lại cảm gi&aacute;c ngon miệng cho thực kh&aacute;ch.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.674392001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">4. Nem rắn:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">Nem l&agrave; m&oacute;n ăn dễ chế biến đ&atilde; trở th&agrave;nh m&oacute;n ăn quen thuộc trong c&aacute;c gia đ&igrave;nh Việt Nam. Nem c&oacute; mặt trong những bữa tiệc quan trọng đến c&aacute;c bữa cơm b&igrave;nh d&acirc;n. Nguy&ecirc;n liệu để l&agrave;m nh&acirc;n nem rắn gồm: Thịt rắn băm nhỏ, mộc nhĩ, nấm h&shy;ương, h&agrave;nh kh&ocirc;, gi&aacute; sống, trứng, hạt ti&ecirc;u, muối gia vị. Tất cả trộn đều rồi d&ugrave;ng b&aacute;nh đa nem cuốn tr&ograve;n v&agrave; r&aacute;n ch&iacute;n v&agrave;ng trong chảo ngập mỡ. N&shy;ước chấm nem rắn phải pha chế thật ngon, kh&eacute;o điều h&ograve;a giữa c&aacute;c loại gia vị: Nư&shy;ớc mắm, ti&ecirc;u, ớt, chanh...</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.756337001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">5. S&shy;ườn rắn b&aacute;nh đa:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">V&agrave;ng ngon, c&oacute; m&agrave;u c&aacute;nh r&aacute;n, thơm lừng m&ugrave;i tỏi, sả, ớt, X&shy;ương rắn đư&shy;ợc băm nhỏ, đư&shy;ợc x&agrave;o tr&ecirc;n lửa lớn c&ugrave;ng tỏi, sả, ớt, l&aacute; chanh. Ăn k&egrave;m với m&oacute;n n&agrave;y c&oacute; b&aacute;nh đa vừng hoặc b&aacute;nh phồng t&ocirc;m chi&ecirc;n gi&ograve;n. S&shy;ờn rắn b&aacute;nh đa l&agrave; một m&oacute;n ăn tuy đơn giản nh&shy;ưng lại đủ những vị cay, đắng ngọt, b&ugrave;i gi&ograve;n thơm, b&eacute;o ngậy...</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.840003001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">6. Da rắn chi&ecirc;n gi&ograve;n:</span></u></strong></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.919956001399451165.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">7. Rắn n&shy;ướng:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">Trư&shy;ớc đ&acirc;y bắt đư&shy;ợc con rắn tr&ecirc;n đồng, kh&ocirc;ng c&oacute; gia vị n&ecirc;n ng&shy;ười d&acirc;n n&shy;ướng rắn trong lửa rơm, cạo cho tr&oacute;c hết vẩy ngo&agrave;i rồi n&shy;ướng cho đến khi mỡ rắn chảy x&egrave;o x&egrave;o, da v&agrave;ng h&shy;ợm, nứt nở lộ ra lớp thịt trắng n&otilde;n, thơm lừng, hơi n&oacute;ng c&ograve;n bay nghi ng&uacute;t, bẻ chia nhau mỗi ng&shy;ười một kh&uacute;c chấm c&ugrave;ng muối ti&ecirc;u ớt. C&ograve;n ng&agrave;y nay, tại c&aacute;c nh&agrave; h&agrave;ng đặc sản thịt rắn th&igrave; m&oacute;n rắn n&shy;ướng đư&shy;ợc l&agrave;m b&agrave;i bản hơn. Rắn đư&shy;ợc chặt kh&uacute;c, dần x&shy;ương thật mềm, tẩm &shy;ớp đầy đủ gia vị n&shy;ướng tr&ecirc;n than hoa v&agrave; thỉnh thoảng t&shy;ới th&ecirc;m mỡ h&agrave;nh cho đến khu kh&uacute;c rắn ch&iacute;n v&agrave;ng đều.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.002052001399451166.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">8. Rắn hầm sả gừng:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">Rắn đư&shy;ợc hầm mềm chung với sả c&acirc;y đập dập v&agrave; gừng cắt l&aacute;. Rắn hầm sả gừng thơm ngon hơn c&aacute;c loại thịt th&shy;ờng ăn h&agrave;ng ng&agrave;y rất nhiều v&agrave; l&agrave; m&oacute;n ăn bổ, m&aacute;t, ngủ khỏe. Rắn sau khi đ&shy;ược xơ chế xong chặt kh&uacute;c 7 - 8cm cho v&agrave;o xoong n&shy;ước, bắc xoong l&ecirc;n bếp, th&ecirc;m sả gừng c&agrave;ng nhiều c&agrave;ng ngon. Nư&shy;ớc cốt hầm sả gừng bổ v&agrave; ngọt lự, h&shy;ương sả gừng v&agrave; m&ugrave;i thơm của thịt rắn k&iacute;ch th&iacute;ch cả khứu gi&aacute;c v&agrave; vị gi&aacute;c của thực kh&aacute;ch. Đ&acirc;y đ&shy;ược coi l&agrave; một m&oacute;n ăn ngon, đơn giản v&agrave; cổ truyền nhất của ng&shy;ười d&acirc;n Lệ Mật x&shy;a.</span></p>\r\n	<p>\r\n		&nbsp;</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.090642001399451166.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">9. Rắn hầm thuốc băc:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">L&agrave; m&oacute;n ăn bổ nhất trong c&aacute;c m&oacute;n ăn về rắn. Rắn đư&shy;ợc tiềm với 9 vị thuốc bắc c&oacute; c&ocirc;ng dụng chống đau nhức v&agrave; l&agrave; vị thuốc m&aacute;t. Rắn tiềm đư&shy;ợc để trong nồi lẩu v&agrave; đặt tr&ecirc;n bếp lửa nhỏ, s&ocirc;i liu riu. H&shy;ương thuốc bắc tỏa nghi ng&uacute;t, vị ngọt đậm nhừ của thịt rắn.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.177123001399451166.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">10. Ch&aacute;o rắn đậu xanh:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">L&agrave; m&oacute;n ăn thư&shy;ờng đư&shy;ợc &shy;ưa th&iacute;ch nhất trong thực đơn rắn. Ch&agrave;o thơm c&oacute; vị b&ugrave;i của đậu xanh, dẻo của gạo nếp v&agrave; vị ngọt của thịt rắn. Rắn nấu ch&aacute;o đậu xanh đ&shy;ợc coi l&agrave; m&oacute;n ăn ngon, bổ hơn cả. Th&acirc;n rắn l&agrave;m sạch chặt kh&uacute;c bỏ v&agrave;o nồi ch&aacute;o đậu xanh đang s&ocirc;i s&ugrave;ng sục. Nấu ch&aacute;o đến nhừ v&agrave; thịt rắn cũng&nbsp; vừa mềm. Ch&aacute;o ăn n&oacute;ng mới ngon, ng&shy;ười bị cảm c&uacute;m ăn b&aacute;t ch&aacute;o rắn đậu xanh sẽ ra nhiều mồ h&ocirc;i c&oacute; khi khỏi hết bệnh.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.259030001399451166.jpg\" /></p>\r\n	<p>\r\n		<strong><u><span new=\"\" times=\"\">11. X&ocirc;i mỡ rắn:</span></u></strong></p>\r\n	<p>\r\n		<span new=\"\" times=\"\">L&agrave; m&oacute;n ăn rất quen thuộc nh&shy;ưng lại l&agrave;m cho thực kh&aacute;ch cảm thấy nhớ nhất mỗi khi đến với l&agrave;ng Lệ Mật. Hạt x&ocirc;i đỗ v&agrave;ng &oacute;ng mỡ rắn, thơm dẻo, b&eacute;o ngậy, h&uacute;t mắt với sắc v&agrave;ng rộm của h&agrave;nh ph&iacute;. M&oacute;n ăn n&agrave;y tuy quen m&agrave; lạ, kh&ocirc;ng qu&aacute; sang trọng nh&shy;ưng với c&aacute;c chế biến theo kiểu ri&ecirc;ng của ng&shy;ười Lệ Mật, x&ocirc;i mỡ rắn đem lại cho ng&shy;ười thư&shy;ởng thức m&ugrave;i vị quen quen, lạ lạ, thấm đẫm h&shy;ương qu&ecirc; v&agrave; sẽ c&ograve;n nhớ m&atilde;i.</span></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/98/700_0.336014001399451166.jpg\" /></p>\r\n	<p>\r\n		&nbsp;</p>\r\n</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399966728', '0', '1399451165', '0', '0');
INSERT INTO `slm_news` VALUES ('99', '0.318732001399962271.jpg', 'a:11:{i:0;s:24:\"0.318732001399962271.jpg\";i:1;s:24:\"0.499465001399962271.jpg\";i:2;s:24:\"0.683860001399962271.jpg\";i:3;s:24:\"0.904639001399962271.jpg\";i:4;s:24:\"0.073864001399962272.jpg\";i:5;s:24:\"0.246453001399962272.jpg\";i:6;s:24:\"0.411608001399962272.jpg\";i:7;s:24:\"0.591718001399962272.jpg\";i:8;s:24:\"0.783352001399962272.jpg\";i:9;s:24:\"0.995126001399962272.jpg\";i:10;s:24:\"0.355259001399962273.jpg\";}', null, 'Giếng ngọc Trọng Thủy – Mỵ Châu, Cổ Loa', null, null, '12', '', '\r\n	T&ograve;a th&agrave;nh x&acirc;y dựng kiểu v&ograve;ng ốc ở ngoại th&agrave;nh H&agrave; Nội l&agrave; một di sản văn h&oacute;a qu&yacute; b&aacute;u với những dấu t&iacute;ch cổ c&ograve;n s&oacute;t lại cho đến nay.', '<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	<strong>T&ograve;a th&agrave;nh x&acirc;y dựng kiểu v&ograve;ng ốc ở ngoại th&agrave;nh H&agrave; Nội l&agrave; một di sản văn h&oacute;a qu&yacute; b&aacute;u với những dấu t&iacute;ch cổ c&ograve;n s&oacute;t lại cho đến nay.</strong></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.318732001399962271.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	C&acirc;u chuyện cổ t&iacute;ch về Mỵ Ch&acirc;u Trọng Thủy vẫn được lưu truyền m&atilde;i cho đến ng&agrave;y nay. Sau khi c&ocirc;ng ch&uacute;a bị vua An Dương Vương ch&eacute;m, Trọng Thủy đuổi theo đến nơi, thấy Mỵ Ch&acirc;u đ&atilde; chết, thương kh&oacute;c &ocirc;m x&aacute;c đem về ch&ocirc;n ở Loa Th&agrave;nh, h&oacute;a l&agrave;m đ&aacute; ngọc. Trọng Thủy nhớ tiếc Mỵ Ch&acirc;u, trở lại nơi vợ tắm gội trang điểm thương nhớ kh&ocirc;ng ngu&ocirc;i, cuối c&ugrave;ng nhảy xuống giếng chết. Hiện nay giếng Trọng Thủy &ndash; Mỵ Ch&acirc;u nằm ngay cửa đền An Dương Vương, x&atilde; Cổ Loa, huyện Đ&ocirc;ng Anh (H&agrave; Nội).</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.499465001399962271.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Khu di t&iacute;ch Cổ Loa nằm tr&ecirc;n địa phận 3 x&atilde; Cổ Loa, Dục T&uacute; v&agrave; Việt H&ugrave;ng thuộc huyện Đ&ocirc;ng Anh (H&agrave; Nội), c&aacute;ch trung t&acirc;m thủ đ&ocirc; H&agrave; Nội khoảng 17km về ph&iacute;a bắc, Kh&aacute;c với c&aacute;c di t&iacute;ch lịch sử kh&aacute;c, Cổ Loa l&agrave; một quần thể di t&iacute;ch c&oacute; diện t&iacute;ch trải rộng tr&ecirc;n một địa bản rộng lớn, c&oacute; diện t&iacute;ch bảo tồn gần 500ha. Đ&acirc;y được coi l&agrave; địa chỉ văn h&oacute;a đặc biệt của thủ đ&ocirc; v&agrave; cả nước.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.683860001399962271.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Cổ Loa ng&agrave;y nay kh&ocirc;ng chỉ l&agrave; một di sản văn h&oacute;a, một bằng chứng về sự s&aacute;ng tạo, về tr&igrave;nh độ kỹ thuật cũng như văn h&oacute;a của người Việt cổ trong c&ocirc;ng cuộc giữ nước v&agrave; chống ngoại x&acirc;m m&agrave; c&ograve;n l&agrave; một điểm đến l&yacute; tưởng cho c&aacute;c du kh&aacute;ch thập phương muốn kh&aacute;m ph&aacute; những gi&aacute; trị văn h&oacute;a, những h&igrave;nh ảnh quen thuộc của một l&agrave;ng qu&ecirc; Bắc Bộ thanh b&igrave;nh.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.904639001399962271.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Hằng năm v&agrave;o ng&agrave;y 6 th&aacute;ng gi&ecirc;ng &acirc;m lịch, cư d&acirc;n Cổ Loa tổ chức một lễ hội trang trọng để tưởng nhớ những người đ&atilde; c&oacute; c&ocirc;ng x&acirc;y th&agrave;nh, nhất l&agrave; để ghi ơn An Dương Vương, người khai sinh ra nh&agrave; nước phong kiến &Acirc;u Lạc.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.073864001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	Năm 1962, th&agrave;nh Cổ Loa được Nh&agrave; nước xếp hạng di t&iacute;ch lịch sử văn h&oacute;a cấp quốc gia.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.246453001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Nơi đ&acirc;y l&agrave; một khu đất đồi cao r&aacute;o nằm ở tả ngạn s&ocirc;ng Ho&agrave;ng (tức s&ocirc;ng Thiếp), l&agrave; một con s&ocirc;ng nh&aacute;nh lớn quan trọng của s&ocirc;ng Hồng, nối liền s&ocirc;ng Hồng với s&ocirc;ng Cầu trong hệ thống s&ocirc;ng Th&aacute;i B&igrave;nh.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.411608001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Về mặt qu&acirc;n sự, th&agrave;nh Cổ Loa thể hiện sự s&aacute;ng tạo độc đ&aacute;o của người Việt cổ trong c&ocirc;ng cuộc giữ nước v&agrave; chống ngoại x&acirc;m. Với c&aacute;c bức th&agrave;nh ki&ecirc;n cố, với h&agrave;o s&acirc;u rộng c&ugrave;ng c&aacute;c ụ, lũy, đ&acirc;y l&agrave; một căn cứ ph&ograve;ng thủ vững chắc để bảo vệ nh&agrave; vua, triều đ&igrave;nh v&agrave; kinh đ&ocirc;.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.591718001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Th&agrave;nh Cổ Loa được x&acirc;y dựng kiểu v&ograve;ng ốc (n&ecirc;n gọi l&agrave; Loa th&agrave;nh). Tương truyền c&oacute; tới 9 v&ograve;ng, dưới th&agrave;nh ngo&agrave;i l&agrave; h&agrave;o s&acirc;u ngập nước thuyền b&egrave; đi lại được. Ng&agrave;y nay ở Cổ Loa c&ograve;n lại 3 v&ograve;ng th&agrave;nh đất, d&agrave;i tổng cộng 16 km: V&ograve;ng ngo&agrave;i (Th&agrave;nh ngoại) chu vi 8km, v&ograve;ng giữa (Th&agrave;nh trung) h&igrave;nh đa gi&aacute;c c&oacute; chu vi 6,5km v&agrave; v&ograve;ng trong c&ugrave;ng (Th&agrave;nh nội, h&igrave;nh chữ nhật, chu vi 1,6km). Mỗi v&ograve;ng th&agrave;nh đều c&oacute; h&agrave;o nước bao quanh b&ecirc;n ngo&agrave;i, h&agrave;o rộng trung b&igrave;nh từ 10m đến 30m. C&aacute;c v&ograve;ng h&agrave;o đều th&ocirc;ng với nhau v&agrave; th&ocirc;ng với s&ocirc;ng Ho&agrave;ng.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.783352001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Sự kết hợp của s&ocirc;ng, h&agrave;o v&agrave; tường th&agrave;nh kh&ocirc;ng c&oacute; h&igrave;nh dạng nhất định, khiến th&agrave;nh như một m&ecirc; cung, l&agrave; một khu qu&acirc;n sự vừa thuận lợi cho tấn c&ocirc;ng vừa tốt cho ph&ograve;ng thủ. Th&agrave;nh Trung l&agrave; một v&ograve;ng th&agrave;nh kh&ocirc;ng c&oacute; khu&ocirc;n h&igrave;nh c&acirc;n xứng, chu vi 6.500m, nơi cao nhất l&agrave; 10m, mặt th&agrave;nh rộng trung b&igrave;nh 10m, c&oacute; năm cửa ở c&aacute;c hướng Đ&ocirc;ng, Nam, Bắc, T&acirc;y Bắc v&agrave; T&acirc;y Nam, trong đ&oacute; cửa đ&ocirc;ng ăn th&ocirc;ng với s&ocirc;ng Ho&agrave;ng. Th&agrave;nh ngoại kh&ocirc;ng c&ograve;n h&igrave;nh d&aacute;ng r&otilde; r&agrave;ng, chu vi hơn 8.000m, cao trung b&igrave;nh 3m-4m (c&oacute; chỗ tới hơn 8m).</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.995126001399962272.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Pho tượng c&ocirc;ng ch&uacute;a Mỵ Ch&acirc;u bị chặt đầu được đặt b&ecirc;n trong đền thờ. Trong c&acirc;u chuyện kh&ocirc;ng biết c&oacute; bao nhi&ecirc;u phần trăm l&agrave; sự thật nhưng d&acirc;n gian vẫn xem bức tượng l&agrave; hiện th&acirc;n v&agrave; l&agrave; minh chứng sắt đ&aacute; cho l&ograve;ng trung nghĩa.</p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/99/700_0.355259001399962273.jpg\" /></p>\r\n<p style=\"margin: 0px; padding: 0px 0px 10px; border: 0px; outline: rgb(0, 0, 0); font-size: 16px; vertical-align: baseline; background-color: rgb(244, 245, 246); line-height: 26px; color: rgb(34, 34, 34); font-family: Arial, Helvetica, sans-serif; text-align: justify;\">\r\n	Về mặt văn h&oacute;a, Cổ Loa l&agrave; một t&ograve;a th&agrave;nh cổ nhất c&ograve;n để lại dấu t&iacute;ch, đ&acirc;y c&ograve;n l&agrave; một di sản văn h&oacute;a qu&yacute; b&aacute;u, một bằng chứng về sự s&aacute;ng tạo, về tr&igrave;nh độ kỹ thuật cũng như văn h&oacute;a của người Việt Cổ. Đ&aacute; k&egrave; ch&acirc;n th&agrave;nh, gốm rải r&igrave;a th&agrave;nh, h&agrave;o nước quanh co, ụ lũy phức tạp, hỏa mai chắc chắn v&agrave; nhất l&agrave; địa h&igrave;nh hiểm trở ngoằn ngo&egrave;o, tất cả l&agrave;m n&ecirc;n nghệ thuật kiến tr&uacute;c v&agrave; văn h&oacute;a thời An Dương Vương.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1399962528', '0', '1399962217', '0', '0');
INSERT INTO `slm_news` VALUES ('101', '0.759871001400118068.jpg', 'a:8:{i:0;s:24:\"0.759871001400118068.jpg\";i:1;s:24:\"0.117013001400118069.jpg\";i:2;s:24:\"0.366006001400118072.jpg\";i:3;s:24:\"0.138607001400118073.jpg\";i:4;s:24:\"0.753828001400118073.jpg\";i:5;s:24:\"0.196444001400118074.jpg\";i:6;s:24:\"0.685670001400118074.jpg\";i:7;s:24:\"0.100531001400118075.jpg\";}', null, 'Chùm ảnh: Những tòa nhà phủ kín cờ đỏ sao vàng', null, null, '12', '', '\r\n	S&aacute;ng 14/5, người đi đường ở H&agrave; Nội tỏ ra lạ lẫm v&agrave; th&iacute;ch th&uacute; khi nh&igrave;n thấy h&agrave;ng chục l&aacute; cờ đỏ sao v&agrave;ng được phủ k&iacute;n mặt tiền hai t&ograve;a nh&agrave; cao tầng.', '<p>\r\n	<font color=\"616161\"><strong>S&aacute;ng 14/5, người đi đường ở H&agrave; Nội tỏ ra lạ lẫm v&agrave; th&iacute;ch th&uacute; khi nh&igrave;n thấy h&agrave;ng chục l&aacute; cờ đỏ sao v&agrave;ng được phủ k&iacute;n mặt tiền hai t&ograve;a nh&agrave; cao tầng.</strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"780\" src=\"http://langquetoi.com/images/news/101/700_0.117013001400118069.jpg\" width=\"666\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Một t&ograve;a nh&agrave; 8 tầng ở số 100 Trần Duy Hưng treo 48 l&aacute; cờ dọc 6 tầng xuy&ecirc;n suốt từ tầng 2 l&ecirc;n tầng 7, mỗi tầng treo 8 l&aacute; cờ.<font color=\"616161\">Diện t&iacute;ch phủ k&iacute;n cờ tổ quốc khoảng 160 m2. C&aacute;c nh&acirc;n vi&ecirc;n l&agrave;m việc tại t&ograve;a nh&agrave; cho biết, người chủ muốn thể hiện t&igrave;nh y&ecirc;u nước nh&acirc;n sự kiện Biển Đ&ocirc;ng.</font></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"503\" src=\"http://langquetoi.com/images/news/101/700_0.138607001400118073.jpg\" width=\"703\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Mỗi l&aacute; cờ c&oacute; k&iacute;ch cỡ 150 x 220 cm được đặt may ri&ecirc;ng theo khổ của khung cửa k&iacute;nh.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"602\" src=\"http://langquetoi.com/images/news/101/700_0.753828001400118073.jpg\" width=\"702\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Người d&acirc;n đi qua kh&ocirc;ng khỏi ngạc nhi&ecirc;n v&agrave; th&iacute;ch th&uacute;.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"538\" src=\"http://langquetoi.com/images/news/101/700_0.196444001400118074.jpg\" width=\"664\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Một to&agrave; nh&agrave; 8 tầng kh&aacute;c tr&ecirc;n phố B&agrave; Triệu cũng treo 24 l&aacute; cờ dọc 3 tầng nh&agrave;.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"400\" src=\"http://langquetoi.com/images/news/101/700_0.759871001400118068.jpg\" width=\"659\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Cả hai to&agrave; nh&agrave; ở hai con phố kh&aacute;c nhau tr&ecirc;n đều đang kinh doanh chung một nh&atilde;n hiệu thời trang ở tầng 1.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"400\" src=\"http://langquetoi.com/images/news/101/700_0.685670001400118074.jpg\" width=\"665\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Một v&agrave;i cửa h&agrave;ng kh&aacute;c ở gần đ&oacute;, c&aacute;c nh&acirc;n vi&ecirc;n b&aacute;n h&agrave;ng thời trang cũng mặc &aacute;o ph&ocirc;ng in h&igrave;nh cờ đỏ sao v&agrave;ng đứng l&agrave;m việc.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img height=\"400\" src=\"http://langquetoi.com/images/news/101/700_0.100531001400118075.jpg\" width=\"666\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><font color=\"616161\">Hoặc b&agrave;i tr&iacute; treo cờ ở vị tr&iacute; đẹp trong showroom.</font></font></p>', '0', '1', '0', '0', '1', '0', null, null, null, null, null, '1400297070', '0', '1400118068', '0', '0');
INSERT INTO `slm_news` VALUES ('102', '0.363841001400120224.jpg', 'a:16:{i:0;s:24:\"0.363841001400120224.jpg\";i:1;s:24:\"0.654049001400120224.jpg\";i:2;s:24:\"0.940831001400120224.jpg\";i:3;s:24:\"0.560033001400120226.jpg\";i:4;s:24:\"0.166549001400120227.jpg\";i:5;s:24:\"0.669645001400120227.jpg\";i:6;s:24:\"0.125278001400120228.jpg\";i:7;s:24:\"0.637990001400120228.jpg\";i:8;s:24:\"0.114615001400120229.jpg\";i:9;s:24:\"0.635265001400120229.jpg\";i:10;s:24:\"0.167162001400120230.jpg\";i:11;s:24:\"0.729724001400120230.jpg\";i:12;s:24:\"0.284461001400120231.jpg\";i:13;s:24:\"0.881151001400120231.jpg\";i:14;s:24:\"0.342448001400120232.jpg\";i:15;s:24:\"0.837170001400120232.jpg\";}', null, 'Cảnh đẹp Hà Nội qua góc máy đặc biệt', null, null, '12', '', '\r\n	Khi được quan s&aacute;t từ độ cao hơn 200 m, lăng Chủ tịch Hồ Ch&iacute; Minh, t&ograve;a nh&agrave; Quốc hội, cột cờ H&agrave; Nội, cầu Th&ecirc; H&uacute;c, Văn Miếu - Quốc Tử Gi&aacute;m hay c&aacute;c quảng trường ở thủ đ&ocirc; đẹp rực rỡ.', '<p>\r\n	<font color=\"616161\"><strong>Khi được quan s&aacute;t từ độ cao hơn 200 m, lăng Chủ tịch Hồ Ch&iacute; Minh, t&ograve;a nh&agrave; Quốc hội, cột cờ H&agrave; Nội, cầu Th&ecirc; H&uacute;c, Văn Miếu - Quốc Tử Gi&aacute;m hay c&aacute;c quảng trường ở thủ đ&ocirc; đẹp rực rỡ.</strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.654049001400120224.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\"><font color=\"616161\">Lăng Chủ tịch Hồ Ch&iacute; Minh v&agrave; quảng trường Ba Đ&igrave;nh nh&igrave;n từ độ cao 200m. Kh&aacute;nh th&agrave;nh năm 1975, lăng gồm 3 lớp với chiều cao 21,6m, lớp dưới tạo d&aacute;ng bậc thềm tam cấp, lớp giữa l&agrave; kết cấu trung t&acirc;m gồm ph&ograve;ng thi h&agrave;i v&agrave; c&aacute;c h&agrave;nh lang, cầu thang. Quanh bốn mặt l&agrave; những h&agrave;ng cột vu&ocirc;ng bằng đ&aacute; hoa cương, m&aacute;i lăng h&igrave;nh tam cấp ở lớp tr&ecirc;n c&ugrave;ng. Quảng trường Ba Đ&igrave;nh d&agrave;i 320m v&agrave; rộng 100m, c&oacute; 240 &ocirc; cỏ. Ở giữa l&agrave; cột cờ cao 25m. Nơi đ&acirc;y từng diễn ra c&aacute;c cuộc diễu binh, diễu h&agrave;nh nh&acirc;n dịp c&aacute;c ng&agrave;y lễ lớn tầm quốc gia, v&agrave; c&ograve;n l&agrave; địa điểm tham quan, vui chơi, dạo m&aacute;t của du kh&aacute;ch v&agrave; người d&acirc;n.</font></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.940831001400120224.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">T&ograve;a nh&agrave; Quốc hội cao 39m, c&oacute; kiến tr&uacute;c h&igrave;nh vu&ocirc;ng, ph&ograve;ng họp h&igrave;nh tr&ograve;n ở giữa, gồm 2 tầng hầm v&agrave; 5 tầng nổi, k&iacute;ch thước mặt bằng 102m x 102m, tổng diện t&iacute;ch s&agrave;n tr&ecirc;n 60.000m2. T&ograve;a nh&agrave; c&oacute; khu vực đỗ xe ngầm quy m&ocirc; 3 tầng hầm với sức chứa 500 &ocirc; t&ocirc; với diện t&iacute;ch tr&ecirc;n 17.000m2, đường hầm nối Nh&agrave; Quốc hội v&agrave; Bộ Ngoại giao d&agrave;i 60m. Nh&agrave; Quốc hội sẽ l&agrave; trụ sở l&agrave;m việc của Quốc hội, nơi tổ chức c&aacute;c kỳ họp v&agrave; đ&oacute;n tiếp kh&aacute;ch quốc tế cấp cao của Đảng v&agrave; Nh&agrave; nước</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.363841001400120224.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Cột cờ H&agrave; Nội nằm tr&ecirc;n đường Điện Bi&ecirc;n Phủ (quận Ba Đ&igrave;nh), được x&acirc;y dựng năm 1812 tr&ecirc;n phần đất ph&iacute;a nam của Ho&agrave;ng th&agrave;nh Thăng Long dưới thời Vua Gia Long triều Nguyễn. Đ&acirc;y l&agrave; một trong số &iacute;t những c&ocirc;ng tr&igrave;nh kiến tr&uacute;c thuộc khu vực th&agrave;nh H&agrave; Nội may mắn tho&aacute;t khỏi sự ph&aacute; hủy do ch&iacute;nh quyền đ&ocirc; hộ Ph&aacute;p tiến h&agrave;nh trong ba năm 1894 -1897. To&agrave;n bộ cột cờ cao hơn 33m. T&iacute;nh cả trụ treo cờ th&igrave; cao hơn 41m gồm ba tầng đế v&agrave; một th&acirc;n cột. C&aacute;c tầng đế h&igrave;nh ch&oacute;p vu&ocirc;ng cụt, nhỏ dần, chồng l&ecirc;n nhau, xung quanh x&acirc;y ốp gạch. Từ ng&agrave;y x&acirc;y dựng đến nay, cột cờ H&agrave; Nội đ&atilde; tr&ecirc;n 200 tuổi.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.560033001400120226.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Cầu Long Bi&ecirc;n l&agrave; c&acirc;y cầu th&eacute;p đầu ti&ecirc;n bắc qua s&ocirc;ng Hồng do Ph&aacute;p x&acirc;y dựng từ năm 1898 - 1902. Cầu từng bị m&aacute;y bay Mỹ n&eacute;m bom 10 lần, hỏng 7 nhịp v&agrave; 4 trụ lớn. Gần đ&acirc;y c&acirc;y cầu trăm tuổi n&agrave;y từng bị đặt l&ecirc;n b&agrave;n c&acirc;n tranh luận cho việc ph&aacute; dỡ để x&acirc;y l&ecirc;n một chiếc cầu mới c&aacute;ch vị tr&iacute; n&agrave;y 30m.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.166549001400120227.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Tượng đ&agrave;i vua L&yacute; Th&aacute;i Tổ nằm giữa c&aacute;c tuyến phố Đinh Ti&ecirc;n Ho&agrave;ng, Ng&ocirc; Quyền, L&ecirc; Lai, L&ecirc; Thạch. Tượng nằm trong c&ocirc;ng vi&ecirc;n c&ugrave;ng t&ecirc;n, kh&aacute;nh th&agrave;nh năm 2004, l&agrave;m bằng đồng liền khối, nặng 14 tấn, cao 3,3m. Đ&acirc;y kh&ocirc;ng chỉ l&agrave; nơi vui chơi của người d&acirc;n m&agrave; c&ograve;n l&agrave; địa điểm tổ chức nhiều sự kiện của th&agrave;nh phố.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.669645001400120227.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Cầu Th&ecirc; H&uacute;c cong cong nối v&agrave;o đền Ngọc Sơn giữa hồ Ho&agrave;n Kiếm. Cầu được x&acirc;y dựng v&agrave;o năm 1865 do Nguyễn Văn Si&ecirc;u, một nh&agrave; thơ v&agrave; nh&agrave; văn h&oacute;a lớn của H&agrave; Nội thời bấy giờ thiết kế, cải tạo. Cầu gồm 15 nhịp, 32 ch&acirc;n cột gỗ tr&ograve;n xếp th&agrave;nh 16 đ&ocirc;i. B&ecirc;n tr&ecirc;n l&aacute;t v&aacute;n gỗ v&agrave; sơn m&agrave;u đỏ sẫm, chữ Th&ecirc; H&uacute;c thếp v&agrave;ng.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.125278001400120228.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Ga H&agrave; Nội (trước kia gọi l&agrave; Ga H&agrave;ng Cỏ) l&agrave; nh&agrave; ga đường sắt ch&iacute;nh của thủ đ&ocirc;. Ga gồm hai khu ở hai cửa kh&aacute;c nhau. Để đi từ H&agrave; Nội v&agrave;o c&aacute;c tỉnh, th&agrave;nh phố ph&iacute;a Nam h&agrave;nh kh&aacute;ch sẽ đi cửa hướng đường L&ecirc; Duẩn, c&ograve;n ph&iacute;a Bắc kh&aacute;ch sẽ đi từ cửa khu B tr&ecirc;n phố Trần Qu&yacute; C&aacute;p.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.637990001400120228.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">C&ocirc;ng tr&igrave;nh Nh&agrave; h&aacute;t Lớn v&agrave; kh&aacute;ch sạn Hilton. Nh&agrave; h&aacute;t Lớn do Ph&aacute;p x&acirc;y dựng năm 1911 theo mẫu nh&agrave; h&aacute;t Opera Garnier ở Paris. Tuy nhi&ecirc;n, tầm v&oacute;c nhỏ hơn v&agrave; sử dụng c&aacute;c vật liệu ph&ugrave; hợp với điều kiện kh&iacute; hậu địa phương. Nh&agrave; h&aacute;t c&oacute; mặt bằng, kh&ocirc;ng gian biểu diễn, cầu thang, lối v&agrave;o sảnh... giống với c&aacute;c nh&agrave; h&aacute;t ở ch&acirc;u &Acirc;u đầu thế kỷ 20. Kết cấu kiến tr&uacute;c kiểu m&aacute;i hai mảng lợp ng&oacute;i đ&aacute; đen c&ugrave;ng c&aacute;c họa tiết trang tr&iacute; b&ecirc;n trong. Ra đời muộn hơn c&aacute;c nh&agrave; h&aacute;t ở S&agrave;i G&ograve;n v&agrave; Hải Ph&ograve;ng n&ecirc;n Nh&agrave; h&aacute;t Lớn H&agrave; Nội c&oacute; kiến tr&uacute;c ho&agrave;n chỉnh nhất v&agrave; trở th&agrave;nh một h&igrave;nh ảnh quen thuộc, đặc trưng của thủ đ&ocirc;.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.114615001400120229.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Quảng trường C&aacute;ch mạng th&aacute;ng T&aacute;m nằm ở trước cửa Nh&agrave; h&aacute;t Lớn thuộc phường Tr&agrave;ng Tiền, quận Ho&agrave;n Kiếm. Nơi đ&acirc;y ng&agrave;y 19/8/1945 từng diễn ra cuộc m&iacute;t tinh lớn biến th&agrave;nh cuộc biểu dương lực lượng v&agrave; hoạt động vũ trang cướp ch&iacute;nh quyền, mở đầu cuộc tổng khởi nghĩa C&aacute;ch mạng th&aacute;ng T&aacute;m tr&ecirc;n cả nước.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.635265001400120229.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Văn Miếu - Quốc tử Gi&aacute;m nh&igrave;n từ độ cao hơn 200m. Bao bọc xung quanh di t&iacute;ch n&agrave;y l&agrave; c&aacute;c con phố Quốc Tử Gi&aacute;m, T&ocirc;n Đức Thắng, Nguyễn Th&aacute;i Học v&agrave; Văn Miếu. Đ&acirc;y l&agrave; một trong những di t&iacute;ch quốc gia đặc biệt. Quần thể kiến tr&uacute;c Văn Miếu - Quốc Tử Gi&aacute;m bao gồm hồ Văn, khu Văn Miếu - Quốc Tử Gi&aacute;m v&agrave; vườn Gi&aacute;m. Kiến tr&uacute;c chủ thể l&agrave; Văn Miếu - nơi thờ Khổng Tử v&agrave; Quốc Tử Gi&aacute;m. To&agrave;n khu c&oacute; tường gạch vồ bao quanh, ph&iacute;a trong chia th&agrave;nh 5 lớp kh&ocirc;ng gian với c&aacute;c kiến tr&uacute;c kh&aacute;c nhau. Mỗi lớp kh&ocirc;ng gian được giới hạn bởi c&aacute;c tường gạch c&oacute; 3 cửa để th&ocirc;ng với nhau (gồm cửa ch&iacute;nh giữa v&agrave; hai cửa phụ hai b&ecirc;n). Từ ngo&agrave;i v&agrave;o trong phải lần lượt đi qua c&aacute;c cổng Văn Miếu, Đại Trung, Khu&ecirc; Văn C&aacute;c, Đại Th&agrave;nh v&agrave; Th&aacute;i Học. Đ&acirc;y c&ograve;n l&agrave; nơi tham quan của du kh&aacute;ch trong v&agrave; ngo&agrave;i nước, nơi khen tặng cho sinh vi&ecirc;n, học sinh xuất sắc. V&agrave;o mỗi kỳ thi quan trọng của ng&agrave;nh gi&aacute;o dục, đ&acirc;y c&ograve;n l&agrave; nơi c&aacute;c sĩ tử đến cầu may.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.167162001400120230.jpg\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Hồ Văn trước cửa Văn Miếu. Đ&acirc;y cũng l&agrave; di t&iacute;ch nằm trong quần thể kiến tr&uacute;c Văn Miếu - Quốc Tử Gi&aacute;m.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.729724001400120230.jpg\" /></strong></font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Vườn hoa Văn Miếu với khu nh&agrave; b&aacute;t gi&aacute;c, b&ecirc;n cạnh l&agrave; tuyến phố T&ocirc;n Đức Thắng.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.284461001400120231.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Quảng trường Đ&ocirc;ng Kinh Nghĩa Thục nằm ở ph&iacute;a Đ&ocirc;ng Bắc quận Ho&agrave;n Kiếm, gần hồ Gươm v&agrave; c&aacute;c tuyến phố H&agrave;ng Ngang, Cầu Gỗ, H&agrave;ng Gai v&agrave; Đinh Ti&ecirc;n Ho&agrave;ng. Thời Ph&aacute;p thuộc quảng trường n&agrave;y c&oacute; t&ecirc;n l&agrave; <em>Place N&eacute;grier</em>. Điểm đặc biệt của khu vực n&agrave;y l&agrave; một đ&agrave;i phun nước nằm ch&iacute;nh giữa. Nơi đ&acirc;y thường tổ chức c&aacute;c sự kiện đường phố v&agrave;o mỗi dịp lễ, Tết.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.881151001400120231.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\">Một g&oacute;c phố cổ H&agrave; Nội nh&igrave;n từ tr&ecirc;n cao. Khu vực n&agrave;y thuộc địa b&agrave;n c&aacute;c phường H&agrave;ng B&ocirc;ng, H&agrave;ng Đ&agrave;o (quận Ho&agrave;n Kiếm). Khu đ&ocirc; thị n&agrave;y c&oacute; từ thời L&yacute; - Trần, tập trung d&acirc;n cư hoạt động tiểu thủ c&ocirc;ng nghiệp v&agrave; bu&ocirc;n b&aacute;n giao thương, h&igrave;nh th&agrave;nh l&ecirc;n những phố nghề đặc trưng, mang những n&eacute;t truyền thống ri&ecirc;ng biệt của cư d&acirc;n th&agrave;nh thị, kinh đ&ocirc;.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.342448001400120232.jpg\" /></strong></font></p>\r\n<p>\r\n	<font color=\"616161\">Đường tr&ecirc;n cao đầu ti&ecirc;n ở thủ đ&ocirc; đoạn c&oacute; t&ograve;a nh&agrave; 72 tầng, cao nhất Việt Nam - Keangnam. Đường c&oacute; tổng chiều d&agrave;i to&agrave;n tuyến 15km, 4 l&agrave;n xe, &ocirc; t&ocirc; được ph&eacute;p chạy tốc độ tối đa 100km/h.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\"><strong><img src=\"http://langquetoi.com/images/news/102/700_0.837170001400120232.jpg\" /></strong></font></p>\r\n<p style=\"text-align: justify;\">\r\n	<font color=\"616161\"><font color=\"616161\">S&acirc;n vận động quốc gia Mỹ Đ&igrave;nh từ tr&ecirc;n cao nh&igrave;n như một l&ograve;ng chảo. Đ&acirc;y l&agrave; s&acirc;n vận động đa năng với hơn 40.000 chỗ ngồi, kết hợp cả b&oacute;ng đ&aacute; với thi đấu điền kinh 8 đường chạy v&ograve;ng 400m, 10 đường chạy thẳng 110m. Tổng diện t&iacute;ch 15,5ha. S&acirc;n c&oacute; 4 kh&aacute;n đ&agrave;i cao từ 8m đến hơn 25m, 419 ph&ograve;ng chức năng, hệ thống chiếu s&aacute;ng 355 b&oacute;ng ở 4 cột cao 54m. Nơi đ&acirc;y thường tổ chức c&aacute;c trận đấu của đội tuyển b&oacute;ng đ&aacute; quốc gia v&agrave; c&aacute;c sự kiện lớn hay c&aacute;c chương tr&igrave;nh ca nhạc đặc biệt.</font></font></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400120688', '0', '1400120224', '0', '0');
INSERT INTO `slm_news` VALUES ('103', '0.248648001400134987.jpg', 'a:6:{i:0;s:24:\"0.248648001400134987.jpg\";i:1;s:24:\"0.396974001400134987.JPG\";i:2;s:24:\"0.620660001400134987.jpg\";i:3;s:24:\"0.702767001400134987.jpg\";i:4;s:24:\"0.037158001400134989.jpg\";i:5;s:24:\"0.511719001400134990.jpg\";}', null, 'Hồ Quê – nét chấm phá cho một ngôi làng ẩm thực lâu đời', null, null, '25', '', '\r\n	Nh&agrave; h&agrave;ng Hồ Qu&ecirc; mới được th&agrave;nh lập chưa l&acirc;u, kh&ocirc;ng nằm trong hệ thống c&aacute;c cửa h&agrave;ng đặc sản gia truyền, song đ&atilde; kịp in dấu trong l&ograve;ng rất nhiều thực kh&aacute;ch bởi kh&ocirc;ng chỉ hương vị lạ của c&aacute;c m&oacute;n ăn từ rắn m&agrave; đặc biệt bởi c&aacute;c m&oacute;n ăn chế biến từ thủy hải sản tươi sống.&nbsp;', '<p style=\"text-align: justify;\">\r\n	Thương hiệu &ldquo;<strong>l&agrave;ng rắn</strong>&rdquo; từ l&acirc;u đ&atilde; được c&aacute;c du kh&aacute;ch thập phương d&agrave;nh tặng ri&ecirc;ng cho <strong>Lệ Mật</strong>.</p>\r\n<p style=\"text-align: justify;\">\r\n	L&agrave;ng<strong> Lệ Mật</strong> xưa gắn liền với truyền thuyết ch&agrave;ng trai <strong>họ Ho&agrave;ng</strong> đ&aacute;nh thắng giảo long cứu thi h&agrave;i c&ocirc;ng ch&uacute;a, người Lệ Mật xưa lam lũ với c&aacute;i nghiệp bắt rắn được cha truyền con nối qua nhiều đời. <strong>Lệ Mật</strong> ng&agrave;y nay đ&atilde; l&ecirc;n phố nhưng đ&acirc;u đ&oacute; vẫn giữ được c&aacute;i hồn của qu&ecirc; hương, nổi tiếng khắp bốn phương l&agrave; l&agrave;ng ẩm thực với c&aacute;c nh&agrave; h&agrave;ng đặc sản chuy&ecirc;n c&aacute;c m&oacute;n ăn được chế biến từ thịt rắn.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.396974001400134987.JPG\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Nh&agrave; h&agrave;ng Hồ Qu&ecirc;</strong> mới được th&agrave;nh lập chưa l&acirc;u, kh&ocirc;ng nằm trong hệ thống c&aacute;c cửa h&agrave;ng đặc sản gia truyền, song đ&atilde; kịp in dấu trong l&ograve;ng rất nhiều thực kh&aacute;ch bởi kh&ocirc;ng chỉ hương vị lạ của c&aacute;c m&oacute;n ăn từ rắn m&agrave; đặc biệt bởi c&aacute;c m&oacute;n ăn chế biến từ thủy hải sản tươi sống.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.620660001400134987.jpg\" /></p>\r\n<p align=\"center\">\r\n	<em>C&aacute;c m&oacute;n ăn từ thủy hải sản l&agrave; bản sắc của nh&agrave; h&agrave;ng</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Hướng tới đối tượng thực kh&aacute;ch b&igrave;nh d&acirc;n, chi ph&iacute; cho mỗi bữa ăn tại <strong>Hồ Qu&ecirc;</strong> lu&ocirc;n được ch&uacute;ng t&ocirc;i coi l&agrave; ưu thế. Kh&ocirc;ng cầu k&igrave; về trang tr&iacute; v&agrave; h&igrave;nh thức, nh&agrave; h&agrave;ng đặt ti&ecirc;u ch&iacute; chất lượng v&agrave; số lượng của mỗi m&oacute;n ăn l&ecirc;n h&agrave;ng đầu. Thực đơn phong ph&uacute; v&agrave; thay đổi linh hoạt theo m&ugrave;a. Đầu bếp cũng ch&iacute;nh l&agrave; &ocirc;ng chủ của nh&agrave; h&agrave;ng, lu&ocirc;n sẵn s&agrave;ng tiếp nhận mọi g&oacute;p &yacute; từ nhiều ph&iacute;a để kh&ocirc;ng ngừng ho&agrave;n thiện chất lượng m&oacute;n ăn, cũng như chất lượng phục vụ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.248648001400134987.jpg\" /></p>\r\n<p align=\"center\">\r\n	<em>Một m&acirc;m rắn đủ m&oacute;n với gi&aacute; cả rất phải chăng</em></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.702767001400134987.jpg\" /></p>\r\n<p align=\"center\">\r\n	<em>B&ecirc;n cạnh đ&oacute; c&ograve;n c&oacute; c&aacute;c m&oacute;n chế biến từ gia cầm l&agrave;m phong ph&uacute; th&ecirc;m sự lựa chọn của kh&aacute;ch h&agrave;ng</em></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.037158001400134989.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Kh&ocirc;ng gian rộng r&atilde;i với sức chứa khoảng 150 người, hệ thống &acirc;m thanh được trang bị đầy đủ v&agrave; ph&ograve;ng h&aacute;t karaoke tự chọn cho những kh&aacute;ch h&agrave;ng c&oacute; nhu cầu cũng khiến cho <strong>Hồ Qu&ecirc;</strong> hiện tại l&agrave; địa điểm tin cậy để tổ chức c&aacute;c buổi party, li&ecirc;n hoan, họp lớp, gặp mặt&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/103/700_0.511719001400134990.jpg\" /></p>\r\n<p align=\"center\">\r\n	<em>Một g&oacute;c tầng 3 của nh&agrave; h&agrave;ng</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Chưa một ai &ldquo;cau c&oacute;&rdquo; khi rời <strong>Hồ Qu&ecirc;</strong>, sau mỗi bữa ăn của kh&aacute;ch, điều hạnh ph&uacute;c nhất ch&uacute;ng t&ocirc;i lu&ocirc;n lu&ocirc;n nhận được đ&oacute; l&agrave; những phản hồi t&iacute;ch cực từ kh&aacute;ch h&agrave;ng. Đến l&agrave;ng <strong>Lệ Mật</strong>, kh&ocirc;ng bao giờ bạn thiếu sự lựa chọn, nhưng nếu bạn l&agrave; &ldquo;t&iacute;n đồ&rdquo; y&ecirc;u th&iacute;ch những điều giản dị th&igrave; h&atilde;y đến với ch&uacute;ng t&ocirc;i.</p>\r\n<p style=\"text-align: justify;\">\r\n	Sẽ thật l&agrave; vinh hạnh nếu ch&uacute;ng t&ocirc;i được đ&oacute;n tiếp c&aacute;c bạn v&agrave;o một ng&agrave;y đẹp trời!&nbsp;</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400742673', '0', '1400134987', '0', '0');
INSERT INTO `slm_news` VALUES ('104', '0.054838001400295324.jpg', 'a:11:{i:0;s:24:\"0.054838001400295324.jpg\";i:1;s:24:\"0.227001001400295324.jpg\";i:2;s:24:\"0.321880001400295324.jpg\";i:3;s:24:\"0.497128001400295324.jpg\";i:4;s:24:\"0.670807001400295324.jpg\";i:5;s:24:\"0.847881001400295324.jpg\";i:6;s:24:\"0.025787001400295325.jpg\";i:7;s:24:\"0.200836001400295325.jpg\";i:8;s:24:\"0.422672001400295325.jpg\";i:9;s:24:\"0.313705001400295326.jpg\";i:10;s:24:\"0.198268001400295329.jpg\";}', null, 'Bộ bàn ghế gỗ mun nặng 2 tấn giá 1,5 tỷ đồng', null, null, '12', '', '\r\n	Sau gần một năm chế t&aacute;c, bộ b&agrave;n ghế mun hoa 10 m&oacute;n chạm khắc kỳ l&acirc;n vừa được một đại gia mua với gi&aacute; 1,5 tỷ.', '<p>\r\n	<strong>Sau gần một năm chế t&aacute;c, bộ b&agrave;n ghế mun hoa 10 m&oacute;n chạm khắc kỳ l&acirc;n vừa được một đại gia mua với gi&aacute; 1,5 tỷ.</strong></p>\r\n<div style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/104/700_0.054838001400295324.jpg\" /></div>\r\n<p>\r\n	<span style=\"background-color: rgb(253, 253, 253); color: rgb(51, 51, 51); font-family: arial; font-size: 14px; line-height: 20px; text-align: justify;\">Bộ ghế salon gỗ mun hoa với k&iacute;ch thước cột 22, chạm khắc kỳ l&acirc;n gồm 10 m&oacute;n: 1 băng ghế d&agrave;i 3 m, cao 1,72 m, nặng gần 2 tấn; 4 ghế đơn, mỗi chiếc cao 1,72 m v&agrave; nặng gần nửa tấn; 2 b&agrave;n tr&agrave;, 2 ghế đ&ocirc;n nặng hơn 100 kg, v&agrave; 1 b&agrave;n tr&agrave; ch&iacute;nh với chiều d&agrave;i hơn 2 m, mặt gỗ đặc dầy 6 cm, nặng hơn 300 kg.</span></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/104/700_0.227001001400295324.jpg\" /></p>\r\n<div>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: justify;\">\r\n		Bộ sản phẩm được chạm kỳ l&acirc;n l&agrave; một trong những kiểu chạm khắc đ&ograve;i hỏi sự tinh tế v&agrave; sắc xảo. Khổ gỗ d&ugrave;ng để chạm khắc rất lớn. Sản phẩm được giới chuy&ecirc;n m&ocirc;n đ&aacute;nh gi&aacute; cao v&agrave; hiếm c&oacute; tr&ecirc;n thị trường nội thất hiện nay.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img alt=\"Theo tiết lộ của chủ cơ sở nội thất tại TP HCM, để chế tác được bộ bàn ghế “khủng” này, chủ cơ sở nội thất đã phải sử dụng đến 9 khối gỗ mun sọc loại đẹp và kích thước bản gỗ to gần 60cm, trong đó có những súc gỗ nguyên khối dài 3m50, được sẻ ra để đóng băng ghế dài. Trong khi đó, 15 thợ mộc bậc cao và công nhân lành nghề thực hiện để hoàn thành bộ sản phẩm, bao gồm các khâu: sẻ gỗ, lựa gỗ, ra phôi, đục chạm, ráp, làm nguội và sơn.\" src=\"http://img.v3.news.zdn.vn/w660/Uploaded/pgi_xvauqbnau/2014_04_22/b3_1.jpg\" style=\"border: 0px; font-size: 0px; vertical-align: baseline; margin: 10px auto; padding: 0px; line-height: 0; max-width: 660px; transition: all 0.3s linear; -webkit-transition: all 0.3s linear; display: block; width: auto;\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Ghế đơn cao hơn 1,7 m, nặng gần nửa tấn.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.497128001400295324.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Ghế d&agrave;i được chế t&aacute;c từ những c&acirc;y gỗ nguy&ecirc;n khối d&agrave;i 2,7 m, đầu ghế được chạm trổ kỳ l&acirc;n từ nguy&ecirc;n kh&uacute;c gỗ lớn.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.670807001400295324.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Tay ghế được chạm kỳ l&acirc;n mang &yacute; nghĩa sung t&uacute;c, t&agrave;i lộc v&agrave; may mắn.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.847881001400295324.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<span style=\"text-align: justify;\">Theo giới s&agrave;nh chơi đồ gỗ, mun hoa l&agrave; loại gỗ qu&yacute; hiếm, c&oacute; gi&aacute; trị được xếp hạng gỗ nh&oacute;m một, c&ugrave;ng nh&oacute;m với một số loại gỗ qu&yacute; kh&aacute;c như sưa, trắc, ho&agrave;ng đ&agrave;n. Gỗ mun c&oacute; chất lượng rất tốt với độ bền cơ học cao, khả năng chống mối mọt tự nhi&ecirc;n, gỗ c&oacute; đặc t&iacute;nh rất nặng v&agrave; sớ gỗ mịn, đanh cứng, v&acirc;n c&oacute; những hoa văn sọc trắng v&agrave;ng v&agrave; đen h&ograve;a quyện lẫn nhau rất đẹp mắt.</span></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.025787001400295325.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Ghế lớn c&oacute; trọng lượng gần 2 tấn, được cẩu v&agrave; vận chuyển kh&eacute;o l&eacute;o.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.200836001400295325.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		K&iacute;ch thước một chiếc ghế lớn hơn cả người.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.422672001400295325.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<span style=\"text-align: justify;\">Ngo&agrave;i ra, c&aacute;c nghệ nh&acirc;n đang chế t&aacute;c bộ b&agrave;n ghế Rồng Đỉnh Cửu Long (9 rồng) bằng nhiều chất liệu gỗ qu&yacute; kh&aacute;c như cẩm lai, mun sọc, gi&aacute; dao động từ 1,3 tỷ đến 1,5 tỷ đồng.</span></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.313705001400295326.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Bộ 9 rồng được chạm khắc tinh xảo với hoa văn h&igrave;nh rồng.</p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/104/700_0.198268001400295329.jpg\" /></p>\r\n	<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n		Gi&aacute; mỗi bộ sản phẩm như vậy l&ecirc;n tới 1,3-1,5 tỷ đồng.</p>\r\n</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400296228', '0', '1400295324', '0', '0');
INSERT INTO `slm_news` VALUES ('105', '0.837187001400295703.png', 'a:10:{i:0;s:24:\"0.837187001400295703.png\";i:1;s:24:\"0.309285001400295704.png\";i:2;s:24:\"0.681046001400295704.jpg\";i:3;s:24:\"0.833977001400295704.png\";i:4;s:24:\"0.345577001400295705.jpg\";i:5;s:24:\"0.471762001400295705.png\";i:6;s:24:\"0.018041001400295706.jpg\";i:7;s:24:\"0.171782001400295706.jpg\";i:8;s:24:\"0.319035001400295706.png\";i:9;s:24:\"0.756079001400295706.jpg\";}', null, 'Những loài rắn sát thủ được cưng chiều nhất thế giới', null, null, '12', '', '\r\n	Rất nhiều người nu&ocirc;i&nbsp;rắn&nbsp;như th&uacute; cưng trong nh&agrave; mặc d&ugrave; loại th&uacute; cưng n&agrave;y c&oacute; thể l&agrave;m người kh&aacute;c chết khiếp.', '<div class=\"post-text\">\r\n	<p>\r\n		<strong>Rất nhiều người nu&ocirc;i&nbsp;rắn&nbsp;như th&uacute; cưng trong nh&agrave; mặc d&ugrave; loại th&uacute; cưng n&agrave;y c&oacute; thể l&agrave;m người kh&aacute;c chết khiếp.</strong></p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.309285001400295704.png\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Rắn&nbsp;ng&ocirc; c&oacute; thể được bắt gặp kh&aacute; thường xuy&ecirc;n trong c&aacute;c gia đ&igrave;nh ở nhiều bang ph&iacute;a đ&ocirc;ng nước Mỹ, từ New Jersey tới Florida Keys.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.681046001400295704.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Kh&ocirc;ng chỉ ngoan ngo&atilde;n, mạnh khỏe, rắn ng&ocirc; c&ograve;n sở hữu nguồn gen đa dạng gi&uacute;p ch&uacute;ng kho&aacute;c tr&ecirc;n m&igrave;nh những &ldquo;bộ c&aacute;nh&rdquo; lộng lẫy, đầy m&agrave;u sắc.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.833977001400295704.png\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Khi gặp nguy hiểm, trăn ho&agrave;ng gia cuộn chặt cơ thể lại th&agrave;nh h&igrave;nh tr&ograve;n n&ecirc;n c&ograve;n được gọi l&agrave; trăn quả b&oacute;ng.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.345577001400295705.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Mặc d&ugrave; sở hữu sức mạnh t&agrave;n bạo, c&oacute; thể siết chết con mồi trong chưa đầy một ph&uacute;t, nhưng trăn ho&agrave;ng gia lại l&agrave; một giống vật nu&ocirc;i kh&aacute; ngoan ngo&atilde;n.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.471762001400295705.png\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Trong điều kiện tự nhi&ecirc;n, rắn vua California được biết đến l&agrave; một kẻ ăn thịt t&agrave;n bạo &ndash; thường xuy&ecirc;n nuốt chửng rắn chu&ocirc;ng.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.018041001400295706.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Nhưng nếu được huấn luyện tốt, rắn vua sẽ trở th&agrave;nh một con th&uacute; cưng dễ bảo v&agrave; c&oacute; thể thay m&oacute;n kho&aacute;i khẩu rắn chu&ocirc;ng bằng chuột.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.837187001400295703.png\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Rắn sữa nổi tiếng với bộ c&aacute;nh 3 m&agrave;u đỏ, đen, trắng rất ấn tượng. Hơn nữa, ch&uacute;ng c&ograve;n ho&agrave;n to&agrave;n v&ocirc; hại với con người v&igrave; kh&ocirc;ng c&oacute; nọc độc.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.171782001400295706.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Tuy nhi&ecirc;n, người ta rất dễ nhầm lẫn giữa rắn sữa v&agrave; rắn san h&ocirc; &ndash; một lo&agrave;i rắn cực độc bởi diện mạo gần giống nhau.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.319035001400295706.png\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Tương tự như trăn ho&agrave;ng gia, trăn boa c&oacute; khả năng siết chặt con mồi c&ugrave;ng đ&ocirc;i răng nanh sắc v&agrave; cơ h&agrave;m cực khỏe.</p>\r\n	<p style=\"text-align: center;\">\r\n		<img src=\"http://langquetoi.com/images/news/105/700_0.756079001400295706.jpg\" /></p>\r\n	<p style=\"text-align: center;\">\r\n		Tuy nhi&ecirc;n, lo&agrave;i n&agrave;y chỉ d&agrave;nh ri&ecirc;ng cho những tay nu&ocirc;i rắn chuy&ecirc;n nghiệp chứ kh&ocirc;ng phải những người mới bắt đầu.</p>\r\n</div>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400296726', '0', '1400295703', '0', '0');
INSERT INTO `slm_news` VALUES ('106', '0.028196001400296003.jpg', 'a:10:{i:0;s:24:\"0.028196001400296003.jpg\";i:1;s:24:\"0.125403001400296003.jpg\";i:2;s:24:\"0.222803001400296003.jpg\";i:3;s:24:\"0.303142001400296003.jpg\";i:4;s:24:\"0.390423001400296003.jpg\";i:5;s:24:\"0.482556001400296003.jpg\";i:6;s:24:\"0.569425001400296003.jpg\";i:7;s:24:\"0.610394001400296003.jpg\";i:8;s:24:\"0.797892001400296003.jpg\";i:9;s:24:\"0.991249001400296003.jpg\";}', null, 'Đặc sản độc lạ của dân tộc Thái Tây Bắc', null, null, '12', '', '\r\n	Đặc sản d&acirc;n tộc, c&aacute;c m&oacute;n ăn độc đ&aacute;o của d&acirc;n tộc Th&aacute;i T&acirc;y Bắc', '<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253);\">\r\n	<strong style=\"border: 0px; vertical-align: baseline; margin: 0px; padding: 0px;\">Những m&oacute;n ngon của đồng b&agrave;o ăn một lần nhớ m&atilde;i.</strong></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.125403001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	C&aacute; pỉnh tộp l&agrave; m&oacute;n ăn độc đ&aacute;o nhất của người Th&aacute;i với đặc trưng c&aacute; xẻ đ&ocirc;i, nhồi c&aacute;c c&aacute;c gia vị v&agrave;o giữa rồi gập c&aacute; lại, cho l&ecirc;n nướng.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.222803001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<span style=\"text-align: justify;\">Chuột n&uacute;i được thui tr&ecirc;n than hồng cho da chuột v&agrave;ng rộm, mới đem dao mổ bỏ phần ruột, chỉ giữ lại l&aacute; gan. Sau đ&oacute;, những gia vị cần thiết được chế biến rất tỉ mỉ từ rau m&ugrave;i, rau răm, thảo dược, đậu xanh&hellip; được gi&atilde; nhuyễn v&agrave; nhồi v&agrave;o ph&iacute;a trong con chuột giống như đầu bếp nhồi thịt v&agrave;o trong quả mướp đắng. Chuột được thui một lần nữa cho ra mỡ, sau đ&oacute; mới đưa v&agrave;o một nồi đất để đồ x&ocirc;i, v&agrave; người d&acirc;n tộc Th&aacute;i gọi đ&oacute; l&agrave; &ldquo;x&ocirc;i chuột&rdquo;</span></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.303142001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	M&oacute;n nướng rất được người d&acirc;n tộc Th&aacute;i ưa th&iacute;ch, c&aacute;c loại thịt tr&acirc;u hoặc b&ograve;, c&aacute;, g&agrave; nướng được người Th&aacute;i tẩm, ướp gia vị rất cầu kỳ.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.390423001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<span style=\"text-align: justify;\">Gia vị để ướp l&agrave; ti&ecirc;u rừng hay c&ograve;n gọi l&agrave; &ldquo;mắc kh&eacute;n&rdquo;, ớt, tỏi, gừng, muối&hellip; Trước khi đem ướp với thịt, c&aacute;c gia vị cũng được nướng l&ecirc;n cho ch&iacute;n, tỏa m&ugrave;i thơm.</span></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.028196001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	M&oacute;n ếch sữa nấu chuối đậu.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.482556001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	M&oacute;n thịt thối đứng ng&ocirc;i vị h&agrave;ng đầu trong tứ đại đặc sản của người d&acirc;n tộc Th&aacute;i ở Mường La.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.569425001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	N&ograve;ng nọc nấu sả ớt, một m&oacute;n ăn đặc trưng của đồng b&agrave;o Th&aacute;i trong lễ hội&nbsp;Pu Nhạ Thầu.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.610394001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	Người Th&aacute;i c&ograve;n một đặc sản kh&aacute;c l&agrave; m&oacute;n nậm pịa.</p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.797892001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<span style=\"text-align: justify;\">Nậm pịa l&agrave; một m&oacute;n ăn sền sền, c&oacute; m&agrave;u xanh r&ecirc;u đặc trưng , c&oacute; thể d&ugrave;ng để l&agrave;m nước chấm v&agrave; c&oacute; thể d&ugrave;ng trực tiếp l&agrave;m m&oacute;n ăn như một loại canh, c&oacute; t&aacute;c dụng giải rượu rất tốt</span></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/106/700_0.991249001400296003.jpg\" /></p>\r\n<p style=\"border: 0px; font-size: 14px; vertical-align: baseline; margin: 0px; padding: 0px 0px 10px; line-height: 20px; color: rgb(51, 51, 51); font-family: arial; background-color: rgb(253, 253, 253); text-align: center;\">\r\n	Pa p&iacute;nh tộp đ&acirc;y l&agrave; một m&oacute;n ăn được chế biến từ c&aacute; suối.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400297006', '0', '1400296002', '0', '0');
INSERT INTO `slm_news` VALUES ('107', '0.855282001400481770.jpg', 'a:10:{i:0;s:24:\"0.855282001400481770.jpg\";i:1;s:24:\"0.094453001400481771.jpg\";i:2;s:24:\"0.263582001400481771.jpg\";i:3;s:24:\"0.414833001400481771.jpg\";i:4;s:24:\"0.582126001400481771.jpg\";i:5;s:24:\"0.748482001400481771.jpg\";i:6;s:24:\"0.018517001400481772.jpg\";i:7;s:24:\"0.167025001400481772.jpg\";i:8;s:24:\"0.350023001400481772.jpg\";i:9;s:24:\"0.591597001400481772.jpg\";}', null, 'Những hình ảnh khủng khiếp trên bầu trời Hà Nội chiều tối 18/5', null, null, '12', '', '\r\n	Khoảng 17h30 đến 18h h&ocirc;m nay, trời H&agrave; Nội bỗng nổi gi&oacute; sau một ng&agrave;y nắng n&oacute;ng kỷ lục. M&acirc;y đen &ugrave;n &ugrave;n k&eacute;o đến từng mảng, từng mảng khiến trời đen kịt.', '<p>\r\n	<strong>Khoảng 17h30 đến 18h h&ocirc;m nay, trời H&agrave; Nội bỗng nổi gi&oacute; sau một ng&agrave;y nắng n&oacute;ng kỷ lục. M&acirc;y đen &ugrave;n &ugrave;n k&eacute;o đến từng mảng, từng mảng khiến trời đen kịt.</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	Khoảng 18h30, trời bắt đầu mưa, sấm chớp đ&ugrave;ng đ&ugrave;ng.</p>\r\n<p style=\"text-align: justify;\">\r\n	Tới 19h45 ng&agrave;y 18/5, trời H&agrave; Nội vẫn mưa to. Theo th&ocirc;ng tin ch&uacute;ng t&ocirc;i cập nhật qua điện thoại, một số nơi ở H&agrave; Nội c&oacute; c&acirc;y đổ, g&acirc;y sự cố tạm thời mất điện hoặc internet.</p>\r\n<p style=\"text-align: justify;\">\r\n	Dưới đ&acirc;y l&agrave; h&igrave;nh ảnh đ&aacute;ng sợ tr&ecirc;n bầu trời H&agrave; Nội chiều tối ng&agrave;y 18/5. Ảnh ch&uacute;ng t&ocirc;i tổng hợp nguồn CTV v&agrave; tr&ecirc;n trang facebook c&aacute; nh&acirc;n của Nguyen Ba Ngoc, Ngọc L&acirc;n, L&ecirc; Tuyết&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/107/700_0.855282001400481770.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	M&acirc;y đen, k&eacute;o từng mảng bao phủ c&aacute;c t&ograve;a nh&agrave; cao tầng</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/107/700_0.094453001400481771.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"553\" src=\"http://langquetoi.com/images/news/107/700_0.263582001400481771.jpg\" width=\"529\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"602\" src=\"http://langquetoi.com/images/news/107/700_0.414833001400481771.jpg\" width=\"531\" /></p>\r\n<p style=\"text-align: center;\">\r\n	H&agrave; Nội đang nắng gay gắt, trời bắt đầu sầm tối v&agrave;o ho&agrave;ng 17h45</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/107/700_0.582126001400481771.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/107/700_0.748482001400481771.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	M&acirc;y đen vần vũ tr&ecirc;n nền trời</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"629\" src=\"http://langquetoi.com/images/news/107/700_0.018517001400481772.jpg\" width=\"558\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"446\" src=\"http://langquetoi.com/images/news/107/700_0.167025001400481772.jpg\" width=\"579\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/107/700_0.350023001400481772.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"590\" src=\"http://langquetoi.com/images/news/107/700_0.591597001400481772.jpg\" width=\"598\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Mưa to, giật đổ c&acirc;y cối</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400482059', '0', '1400481770', '0', '0');
INSERT INTO `slm_news` VALUES ('108', '0.592043001400742126.jpg', 'a:15:{i:0;s:24:\"0.592043001400742126.jpg\";i:1;s:24:\"0.102553001400742127.jpg\";i:2;s:24:\"0.602555001400742127.jpg\";i:3;s:24:\"0.060921001400742128.jpg\";i:4;s:24:\"0.486844001400742128.jpg\";i:5;s:24:\"0.905873001400742128.jpg\";i:6;s:24:\"0.382106001400742129.jpg\";i:7;s:24:\"0.857860001400742129.jpg\";i:8;s:24:\"0.300344001400742130.jpg\";i:9;s:24:\"0.802106001400742130.jpg\";i:10;s:24:\"0.256431001400742131.jpg\";i:11;s:24:\"0.718630001400742131.jpg\";i:12;s:24:\"0.272894001400742132.jpg\";i:13;s:24:\"0.710879001400742132.jpg\";i:14;s:24:\"0.204988001400742133.jpg\";}', null, 'Nông dân Bắc Bộ đổi đời nhờ nghề săn chuột', null, null, '12', '', '\r\n	Với người d&acirc;n Kim Trung, Hưng H&agrave;, Th&aacute;i B&igrave;nh, &ldquo;họ nh&agrave; t&yacute;&rdquo; l&agrave; cần c&acirc;u cơm nu&ocirc;i sống cả gia đ&igrave;nh, bởi họ c&oacute; thể kiếm được h&agrave;ng chục triệu đồng nhờ nghề n&agrave;y v&agrave;o m&ugrave;a vụ.', '<p>\r\n	<strong>Với người d&acirc;n Kim Trung, Hưng H&agrave;, Th&aacute;i B&igrave;nh, &ldquo;họ nh&agrave; t&yacute;&rdquo; l&agrave; cần c&acirc;u cơm nu&ocirc;i sống cả gia đ&igrave;nh, bởi họ c&oacute; thể kiếm được h&agrave;ng chục triệu đồng nhờ nghề n&agrave;y v&agrave;o m&ugrave;a vụ.</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.102553001400742127.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Trước đ&acirc;y, người d&acirc;n x&atilde; Kim Trung bắt chuột quanh năm, nhưng do nhu cầu ti&ecirc;u thụ tăng mạnh khiến việc săn bắt trở n&ecirc;n khan hiếm. Hiện nay, nghề bắt chuột chỉ diễn ra v&agrave;o dịp cuối năm, đ&oacute; l&agrave; sau vụ gặt l&uacute;a m&ugrave;a chi&ecirc;m.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.602555001400742127.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">L&agrave; &ldquo;cao thủ&rdquo; trong nghề, &ocirc;ng H&agrave; Văn Dũng tr&uacute; tại th&ocirc;n Nghĩa Th&ocirc;n, x&atilde; Kim Trung cho biết: giống chuột đất c&oacute; thịt thơm v&agrave; ngọt hơn chuột đ&agrave;n n&ecirc;n gi&aacute; kh&aacute; cao, từ 70.000 đồng đến 100.000 đồng/kg. C&ograve;n chuột đ&agrave;n c&oacute; gi&aacute; 50.000 đồng/kg, nếu bắt tr&uacute;ng một tổ thường thu được hơn 20 con. &ldquo;Nhưng bắt chuột đất kh&oacute; lắm, kh&ocirc;ng phải chuyện dễ, m&igrave;nh d&ugrave;ng nước đổ v&agrave;o lỗ th&igrave; kh&ocirc;ng ăn thua, ch&uacute;ng nhất quyết chịu chết trương b&ecirc;n trong chứ kh&ocirc;ng chui ra đ&acirc;u. Phải đ&agrave;o tận hang bắt sống được n&oacute;&rdquo;, &ocirc;ng Dũng n&oacute;i.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.060921001400742128.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">C&oacute; nhiều c&aacute;ch bắt chuột. Người d&acirc;n ở đ&acirc;y chỉ cần d&ugrave;ng những trang bị th&ocirc; sơ sẵn c&oacute; như cuốc xẻng để đ&agrave;o ổ, đổ nước hoặc hun kh&oacute;i v&agrave;o hang khiến ch&uacute;ng ngoi ra.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.486844001400742128.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Sau khi bắt được chuột, c&aacute;c thợ mổ nh&uacute;ng chuột v&agrave;o nồi nước s&ocirc;i, sau đ&oacute; cạo sạch l&ocirc;ng, lấy ruột.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.905873001400742128.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Trải chuột tr&ecirc;n rơm v&agrave;ng để chuẩn bị hun.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.592043001400742126.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">&ldquo;Họ nh&agrave; t&yacute;&rdquo; được thui v&agrave;ng tr&ecirc;n đống rơm.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.382106001400742129.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Qu&aacute; tr&igrave;nh hun chuột cũng phải ch&uacute; &yacute; để tr&aacute;nh bị ch&aacute;y, kh&eacute;t hoặc ch&iacute;n kh&ocirc;ng đều.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.857860001400742129.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Trung b&igrave;nh mỗi ng&agrave;y bắt v&agrave; l&agrave;m thịt chuột, mỗi người kiếm được 300.000 &ndash; 400.000 đồng.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.300344001400742130.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Với những hộ cả nh&agrave; bắt chuột, thu nhập cả gia đ&igrave;nh v&agrave;o m&ugrave;a vụ l&ecirc;n đến v&agrave;i chục triệu đồng. C&ocirc;ng việc n&agrave;y gi&uacute;p nhiều người l&agrave;ng Gi&aacute;p gi&agrave;u l&ecirc;n tr&ocirc;ng thấy.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.802106001400742130.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Việc bắt chuột thu h&uacute;t cả trẻ nhỏ tham gia.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.256431001400742131.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Đ&aacute;m trẻ thi nhau bắt chuột.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.718630001400742131.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Đặc sản thịt chuột c&ograve;n l&agrave;m gi&agrave;u cho c&aacute;c tiểu thương.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.272894001400742132.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Người b&aacute;n thịt chuột tại Cầu Gi&aacute;c - Kim Trung cho biết, mỗi ng&agrave;y, đứng b&aacute;n từ 16h đến 18h l&agrave; &ldquo;ch&aacute;y h&agrave;ng&rdquo; 30-40kg thịt chuột, thu nhập 300.000 - 350.000 đồng.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.710879001400742132.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">M&oacute;n thịt chuột &ldquo;h&uacute;t&rdquo; kh&aacute;ch.</font></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/108/700_0.204988001400742133.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<font color=\"616161\">Thịt chuột được b&aacute;n k&egrave;m c&aacute;c loại gia vị như: mắm, muối, hạt ti&ecirc;u bắc, riềng, gừng, sả&hellip; Người mua về c&oacute; thể chế biến ra nhiều m&oacute;n như chuột gi&aacute;n &eacute;p, luộc , giả cầy&hellip;</font></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400743753', '0', '1400742126', '0', '0');
INSERT INTO `slm_news` VALUES ('109', '0.174954001400743301.jpg', 'a:7:{i:0;s:24:\"0.174954001400743301.jpg\";i:1;s:24:\"0.471066001400743301.jpg\";i:2;s:24:\"0.757928001400743301.jpg\";i:3;s:24:\"0.026755001400743302.jpg\";i:4;s:24:\"0.331048001400743302.jpg\";i:5;s:24:\"0.801141001400743302.jpg\";i:6;s:24:\"0.343031001400743303.jpg\";}', null, '1.200 học sinh xếp hình bản đồ Việt Nam', null, null, '12', '', '\r\n	Hơn 1.000 học sinh trường Phan Huy Ch&uacute; ( H&agrave; Nội) đ&atilde; xếp h&igrave;nh bản đồ Việt Nam nhằm khơi dậy t&igrave;nh y&ecirc;u đất nước, phản đối việc Trung Quốc tr&aacute;i ph&eacute;p đưa gi&agrave;n khoan v&agrave;o v&ugrave;ng biển Việt Nam.', '<p>\r\n	Hơn 1.000 học sinh trường Phan Huy Ch&uacute; ( H&agrave; Nội) đ&atilde; xếp h&igrave;nh bản đồ Việt Nam nhằm khơi dậy t&igrave;nh y&ecirc;u đất nước, phản đối việc Trung Quốc tr&aacute;i ph&eacute;p đưa gi&agrave;n khoan v&agrave;o v&ugrave;ng biển Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.471066001400743301.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	S&aacute;ng 16/5, học sinh THPT Phan Huy Ch&uacute; (Đống Đa, H&agrave; Nội) đ&atilde; c&oacute; một buổi ch&agrave;o cờ &yacute; nghĩa với việc xếp h&igrave;nh bản đồ Việt Nam. C&ocirc; hiệu ph&oacute; Cao Thanh Nga cho biết, Ban gi&aacute;m hiệu v&agrave; học sinh to&agrave;n trường đ&atilde; c&oacute; &yacute; tưởng tr&ecirc;n sau những tiết học v&agrave; c&aacute;c buổi sinh hoạt thực tế về Biển đảo trong tuần qua</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.757928001400743301.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	1.200 bạn trẻ đ&atilde; xếp h&igrave;nh Tổ quốc m&agrave;u đỏ c&ugrave;ng hai quần đảo Ho&agrave;ng Sa v&agrave; Trường Sa, bao quanh l&agrave; tr&aacute;i tim m&agrave;u xanh của thanh ni&ecirc;n t&igrave;nh nguyện. Hoạt động diễn ra tr&ecirc;n s&acirc;n trường rộng gần 2.000 m2</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.174954001400743301.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sau đ&oacute;, to&agrave;n trường đ&atilde; l&agrave;m lễ ch&agrave;o cờ, h&aacute;t quốc ca v&agrave; h&ocirc; vang khẩu hiệu &quot;Tổ Quốc l&agrave; tr&ecirc;n hết, Ho&agrave;ng Sa - Trường Sa l&agrave; m&aacute;u thịt Việt Nam. Ch&uacute;ng ta y&ecirc;u h&ograve;a b&igrave;nh&quot;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.026755001400743302.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Với sự nhiệt t&igrave;nh của tập thể gi&aacute;o vi&ecirc;n v&agrave; học sinh, trường chỉ mất đ&uacute;ng một buổi chiều sau giờ học ng&agrave;y 15/5 để tập dượt cho sự kiện s&aacute;ng nay.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.331048001400743302.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hoạt động n&agrave;y nhằm truyền tải th&ocirc;ng điệp mạnh mẽ khẳng định chủ quyền biển đảo của Việt Nam đối với hai quần đảo Ho&agrave;ng Sa, Trường Sa v&agrave; phản đối việc Trung Quốc đưa gi&agrave;n khoan v&agrave;o v&ugrave;ng biển thuộc chủ quyền Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.801141001400743302.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Bản đồ Tổ quốc chuyển m&agrave;u xanh, thể hiện tinh thần y&ecirc;u chuộng h&ograve;a b&igrave;nh của d&acirc;n tộc Việt Nam. Kết th&uacute;c buổi ch&agrave;o cờ, c&aacute;c thầy c&ocirc; v&agrave; học sinh trường Phan Huy Ch&uacute; đ&atilde; quy&ecirc;n g&oacute;p &quot;Tấm l&ograve;ng gửi ra biển đảo th&acirc;n thương!&quot; để tặng c&aacute;c chiến sĩ đang chiến đấu bảo vệ chủ quyền biển đảo quy&ecirc; hương.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/109/700_0.343031001400743303.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Sau sự kiện xếp h&igrave;nh bản đồ Việt Nam, học sinh trường Phan Huy Ch&uacute; sẽ tham gia chương tr&igrave;nh chữ k&yacute; &quot;H&ograve;a b&igrave;nh cho Biển Đ&ocirc;ng&quot; do Ủy ban H&ograve;a B&igrave;nh TP H&agrave; Nội v&agrave; Li&ecirc;n hiệp c&aacute;c Tổ chức Hữu Nghị H&agrave; Nội ph&aacute;t động. Đ&acirc;y l&agrave; chương tr&igrave;nh h&agrave;nh động v&igrave; h&ograve;a b&igrave;nh, lấy chữ k&yacute; v&agrave; &yacute; kiến của cộng đồng v&agrave;o thư thỉnh nguyện &quot;H&ograve;a b&igrave;nh cho Biển Đ&ocirc;ng&quot; nhằm thu h&uacute;t sự quan t&acirc;m của c&ocirc;ng luận trong nước v&agrave; quốc tế. Qua đ&oacute; gửi đến bạn b&egrave; tr&ecirc;n to&agrave;n thế giới th&ocirc;ng điệp h&ograve;a b&igrave;nh từ Việt Nam: Đo&agrave;n kết, mong muốn g&igrave;n giữ h&ograve;a b&igrave;nh tr&ecirc;n Biển Đ&ocirc;ng v&agrave; khu vực ASEAN.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1400743661', '0', '1400743301', '0', '0');
INSERT INTO `slm_news` VALUES ('110', '0.557077001400840894.jpg', 'a:16:{i:0;s:24:\"0.557077001400840894.jpg\";i:1;s:24:\"0.794251001400840894.jpg\";i:2;s:24:\"0.996342001400840894.jpg\";i:3;s:24:\"0.722638001400840899.jpg\";i:4;s:24:\"0.208747001400840900.jpg\";i:5;s:24:\"0.634812001400840900.jpg\";i:6;s:24:\"0.101194001400840901.jpg\";i:7;s:24:\"0.534097001400840901.jpg\";i:8;s:24:\"0.138666001400840902.jpg\";i:9;s:24:\"0.500003001400840902.jpg\";i:10;s:24:\"0.195796001400840903.jpg\";i:11;s:24:\"0.633755001400840903.jpg\";i:12;s:24:\"0.981160001400840903.jpg\";i:13;s:24:\"0.489588001400840904.jpg\";i:14;s:24:\"0.757814001400840904.jpg\";i:15;s:24:\"0.100233001400840905.jpg\";}', null, 'Bằng lăng nở tím trời Hà Nội', null, null, '12', '', '\r\n	Th&aacute;ng 5 hạ về, m&agrave;u bằng lăng nhuộm t&iacute;m mọi nẻo đường thủ đ&ocirc;.', '<p>\r\n	<strong>Th&aacute;ng 5 hạ về, m&agrave;u bằng lăng nhuộm t&iacute;m mọi nẻo đường thủ đ&ocirc;.</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.794251001400840894.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Thời điểm n&agrave;y nhiều con phố của thủ đ&ocirc; trở n&ecirc;n thơ mộng hơn bởi sắc t&iacute;m bằng lăng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.557077001400840894.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hồ Gươm lu&ocirc;n đẹp v&agrave;o bất cứ thời khắc n&agrave;o. Trong m&ugrave;a hoa bằng lăng, m&acirc;y trời, mặt nước &aacute;nh l&ecirc;n sắc t&iacute;m mộng mơ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.996342001400840894.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tr&ecirc;n &quot;con đường thơ&quot; Kim M&atilde;, bằng lăng đang v&agrave;o m&ugrave;a nở.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.722638001400840899.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đường Văn Cao tr&agrave;n sắc m&agrave;u. Những c&acirc;y bằng lăng hai b&ecirc;n đường nở bung, d&agrave;y đặc.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.208747001400840900.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đường Giải Ph&oacute;ng đột nhi&ecirc;n trở n&ecirc;n xinh đẹp. Những c&acirc;y bằng lăng trải d&agrave;i hai b&ecirc;n đường trổ hoa, dường như l&agrave;m giảm bớt c&aacute;i nắng gay gắt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.634812001400840900.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Những con đường ở Cầu Giấy trồng nhiều bằng lăng như Trần Quốc Ho&agrave;n, Trần Th&aacute;i T&ocirc;ng, Duy T&acirc;n... phủ m&agrave;u t&iacute;m.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.101194001400840901.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	T&iacute;m bằng lăng, rực đỏ phượng vĩ, hai m&agrave;u sắc song h&agrave;nh gọi hạ về với H&agrave; Nội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"526\" src=\"http://langquetoi.com/images/news/110/700_0.534097001400840901.jpg\" width=\"537\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Những m&ugrave;a thi, m&ugrave;a tan trường cũng về theo sắc t&iacute;m.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.138666001400840902.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Sắc t&iacute;m tượng trưng cho sự chung thủy, ng&acirc;y thơ v&agrave; nhiều người xem bằng lăng l&agrave; lo&agrave;i hoa của mối t&igrave;nh học tr&ograve;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img height=\"561\" src=\"http://langquetoi.com/images/news/110/700_0.500003001400840902.jpg\" width=\"499\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Ng&agrave;y ban đầu quen nhau/ &Aacute;o em trong m&agrave;u hoa t&iacute;m/ M&agrave;u hoa m&agrave; em y&ecirc;u/ C&oacute; t&ecirc;n hoa bằng lăng t&iacute;m/ L&agrave; m&agrave;u t&iacute;m của nhớ/ L&agrave; giọt nắng chiều t&iacute;m/ H&ograve;a v&agrave;o tim của gi&oacute; h&aacute;t vi vu... (<span class=\"title\">B&agrave;i h&aacute;t <em>Hoa t&iacute;m bằng lăng</em></span>).</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.195796001400840903.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Hoa bằng lăng c&oacute; nguồn gốc từ Ấn Độ. Do c&oacute; m&agrave;u đẹp n&ecirc;n c&aacute;c tỉnh th&agrave;nh ở Việt Nam hay trồng nhiều l&agrave;m cảnh quan đ&ocirc; thị. Lo&agrave;i hoa n&agrave;y c&oacute; nhiều cung bậc m&agrave;u sắc kh&aacute;c nhau. Phổ biến nhất l&agrave; m&agrave;u t&iacute;m hồng, t&iacute;m biếc. Một số c&acirc;y c&oacute; m&agrave;u t&iacute;m pha trắng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.633755001400840903.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Bằng lăng khoe sắc t&iacute;m biếc dưới bầu trời trong xanh của H&agrave; Nội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.981160001400840903.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&acirc;y bằng lăng t&iacute;m nhạt, c&aacute;nh nhỏ, hoa rất d&agrave;y.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.489588001400840904.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Tr&ecirc;n đường Trần Th&aacute;i T&ocirc;ng (Cầu Giấy) c&oacute; một c&acirc;y bằng lăng m&agrave;u hồng. Nhiều người d&acirc;n sống xung quanh cho biết, năm n&agrave;o c&acirc;y n&agrave;y cũng nở hoa m&agrave;u n&agrave;y. Nhiều người th&iacute;ch th&uacute; bởi vẻ đẹp của n&oacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/110/700_0.100233001400840905.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	M&agrave;u sắc kh&aacute;c biệt của c&acirc;y bằng lăng độc đ&aacute;o n&agrave;y so với c&aacute;c c&acirc;y m&agrave;u t&iacute;m xung quanh.M&ugrave;a bằng lăng nhanh đến v&agrave; nhanh đi. Bỗng chốc một sớm mai thức dậy, bỗng chốc sau cơn mưa, bỗng một ng&agrave;y nh&agrave;n tản, mộng mơ... ai đ&oacute; chợt nhận ra những con phố ngang qua đều m&agrave;u t&iacute;m, m&agrave;u hồng của bằng lăng, đừng ph&acirc;n v&acirc;n ngừng lại cảm nhận vẻ đẹp của th&agrave;nh phố th&aacute;ng 5.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1401073321', '0', '1400840894', '0', '0');
INSERT INTO `slm_news` VALUES ('111', '0.340211001401508113.jpg', 'a:10:{i:0;s:24:\"0.340211001401508113.jpg\";i:1;s:24:\"0.599936001401508113.jpg\";i:2;s:24:\"0.876280001401508113.jpg\";i:3;s:24:\"0.072357001401508114.jpg\";i:4;s:24:\"0.258729001401508114.jpg\";i:5;s:24:\"0.472495001401508114.jpg\";i:6;s:24:\"0.662000001401508114.jpg\";i:7;s:24:\"0.705072001401508117.jpg\";i:8;s:24:\"0.995251001401508117.jpg\";i:9;s:24:\"0.356716001401508118.jpg\";}', null, '10 điều du khách Mỹ thích làm ở Việt Nam', null, null, '12', '', '\r\n	Trang Huffington Post liệt k&ecirc; 10 điều du kh&aacute;ch Mỹ n&ecirc;n l&agrave;m khi đến Việt Nam, trong đ&oacute; c&oacute; đi chợ v&agrave; h&aacute;t karaoke l&uacute;c tối muộn.', '<p>\r\n	<strong>Trang Huffington Post liệt k&ecirc; 10 điều du kh&aacute;ch Mỹ n&ecirc;n l&agrave;m khi đến Việt Nam, trong đ&oacute; c&oacute; đi chợ v&agrave; h&aacute;t karaoke l&uacute;c tối muộn.</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.599936001401508113.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Một trong những điểm đến đầu ti&ecirc;n m&agrave; trang <em>Huffington Post </em>gợi &yacute;&nbsp;lựa chọn khi đến Việt Nam ch&iacute;nh l&agrave; dạo chơi ở khu phố cổ, khu vực bờ Hồ H&agrave; Nội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.876280001401508113.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đến thăm quan Vịnh Hạ Long, di sản thi&ecirc;n nhi&ecirc;n của thế giới, cũng l&agrave; lời khuy&ecirc;n đưa ra cho du kh&aacute;ch Mỹ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.072357001401508114.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Sa Pa l&agrave; những điểm tiếp theo cần thăm th&uacute; tại dải đất h&igrave;nh chữ S.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.258729001401508114.jpg\" /></p>\r\n<p style=\"text-align: justify;\">\r\n	Đến Việt Nam, du kh&aacute;ch dễ bị quyến rũ khi thưởng thức những cốc c&agrave; ph&ecirc; theo phong c&aacute;ch Việt v&agrave; nhấm nh&aacute;p c&ugrave;ng hạt hướng dương ngo&agrave;i vỉa h&egrave;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.472495001401508114.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Phở Việt cũng l&agrave; một m&oacute;n kh&ocirc;ng thể bỏ qua.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.340211001401508113.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c khu chợ cũng l&agrave; n&eacute;t văn h&oacute;a độc đ&aacute;o của người Việt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.662000001401508114.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hội An nằm trong top những điểm đến của Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.705072001401508117.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Du lịch ở đồng bằng s&ocirc;ng Cửu Long v&agrave; đi chợ nổi ở Miền T&acirc;y s&ocirc;ng nước.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.995251001401508117.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ở nh&agrave; người d&acirc;n bản địa cũng l&agrave; một trong những hoạt động th&uacute; vị khi đến thăm Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/111/700_0.356716001401508118.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Điều cuối c&ugrave;ng m&agrave; du kh&aacute;ch nước ngo&agrave;i th&iacute;ch th&uacute; ch&iacute;nh l&agrave; h&aacute;t karaoke ở c&aacute;c qu&aacute;n b&ecirc;n đường v&agrave;o l&uacute;c tối muộn.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1401508379', '0', '1401508113', '0', '0');
INSERT INTO `slm_news` VALUES ('112', '0.621710001406791543.jpg', 'a:9:{i:0;s:24:\"0.621710001406791543.jpg\";i:1;s:24:\"0.806246001406791543.jpg\";i:2;s:24:\"0.941412001406791543.jpg\";i:3;s:24:\"0.064090001406791544.jpg\";i:4;s:24:\"0.200754001406791544.jpg\";i:5;s:24:\"0.303314001406791544.jpg\";i:6;s:24:\"0.399894001406791544.jpg\";i:7;s:24:\"0.502166001406791544.jpg\";i:8;s:24:\"0.603163001406791544.jpg\";}', null, 'Cầu Rồng được CNN hết lời ca ngợi', null, null, '12', '', '\r\n	K&ecirc;nh truyền h&igrave;nh Mỹ d&agrave;nh những lời t&ocirc;n vinh cho c&ocirc;ng tr&igrave;nh Cầu Rồng của Việt Nam v&agrave; nhấn mạnh đ&acirc;y l&agrave; biểu tượng th&agrave;nh c&ocirc;ng mới của th&agrave;nh phố biển Đ&agrave; Nẵng.', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.621710001406791543.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Cầu Rồng được ph&oacute;ng vi&ecirc;n của CNN mi&ecirc;u tả l&agrave; giống như biểu tượng của một quốc gia đang trỗi dậy.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.806246001406791543.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Cầu Rồng c&oacute; thể phun lửa v&agrave; nước rất ngoạn mục. Khi đ&ecirc;m về, cầu được chiếu s&aacute;ng lung linh. Nhờ thiết kế độc đ&aacute;o m&agrave; c&ocirc;ng tr&igrave;nh đ&atilde; nhận được giải Special Citation (Biểu dương đặc biệt) do Hiệp hội c&aacute;c nh&agrave; thiết kế chiếu s&aacute;ng chuy&ecirc;n nghiệp thế giới (IALD) trao tặng tại Mỹ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.941412001406791543.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<span id=\"result_box\" lang=\"vi\" xml:lang=\"vi\">Cũng theo k&ecirc;nh tin tức n&agrave;y, Cầu Rồng trở th&agrave;nh một biểu tượng được người d&acirc;n Việt Nam y&ecirc;u th&iacute;ch, tượng trưng cho tương lai thịnh vượng của đất nước</span>.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.064090001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Được khởi c&ocirc;ng từ th&aacute;ng 7/2009 v&agrave; kh&aacute;nh th&agrave;nh 29/3/2013. Cầu d&agrave;i 666 m, rộng 37,5 m với 6 l&agrave;n xe, phần lề d&agrave;nh cho người đi bộ mỗi b&ecirc;n rộng 2,5 m. Tổng vốn đầu tư tr&ecirc;n 1.700 tỷ đồng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.200754001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Theo thiết kế, phần h&igrave;nh d&aacute;ng của th&acirc;n rồng bằng th&eacute;p d&agrave;i khoảng 560 m, nặng hơn 9.000 tấn, đầu rồng cao 10 m so với mặt cầu, nặng 40 tấn. Mắt rồng được thiết kế h&igrave;nh tr&aacute;i tim gắn với hệ thống đ&egrave;n chiếu hiện đại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.303314001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Phần đầu rồng sẽ phun lửa v&agrave;o ban đ&ecirc;m, phun nước v&agrave;o ban ng&agrave;y dịp cuối tuần, lễ hội.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.399894001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Nh&igrave;n từ xa Cầu Rồng giống như một ch&uacute; rồng đang chuẩn bị vươn m&igrave;nh bay l&ecirc;n trời xanh. Đ&acirc;y l&agrave; một thiết kế độc đ&aacute;o, được coi như một trong những biểu tượng mới, thể hiện sự trỗi dậy mạnh mẽ của th&agrave;nh phố Đ&agrave; Nẵng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.502166001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Kh&ocirc;ng chỉ ri&ecirc;ng cầu Rồng, ch&iacute;nh quyền Đ&agrave; Nẵng c&ograve;n tập trung tu sửa, x&acirc;y dựng cơ sở hạ tầng để tập trung ph&aacute;t triển du lịch.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/112/700_0.603163001406791544.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Sở Văn h&oacute;a, Thể thao v&agrave; Du lịch của Đ&agrave; Nẵng hy vọng năm nay sẽ c&oacute; khoảng 3 triệu lượt kh&aacute;ch gh&eacute; thăm th&agrave;nh phố ven biển xinh đẹp n&agrave;y.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1406791958', '0', '1406791543', '0', '0');
INSERT INTO `slm_news` VALUES ('113', '0.329309001406792450.jpg', 'a:6:{i:0;s:24:\"0.329309001406792450.jpg\";i:1;s:24:\"0.454540001406792450.jpg\";i:2;s:24:\"0.553619001406792450.jpg\";i:3;s:24:\"0.645526001406792450.jpg\";i:4;s:24:\"0.743022001406792450.jpg\";i:5;s:24:\"0.834976001406792450.jpg\";}', null, 'Đặc sản miền Trung giữa lòng Hà Nội', null, null, '12', '', '\r\n	Tuy c&oacute; rất nhiều m&oacute;n ngon miền Trung nhưng đặc sản Huế v&agrave; Đ&agrave; Nẵng vẫn l&agrave; phổ biến nhất ở H&agrave; Nội với b&uacute;n b&ograve;, nem lụi v&agrave; b&aacute;nh tr&aacute;ng cuốn thịt heo.', '<p>\r\n	&nbsp; Dưới đ&acirc;y l&agrave; những m&oacute;n ăn nổi tiếng miền Trung bạn c&oacute; thể thưởng thức ngay tại H&agrave; Nội.</p>\r\n<p class=\"subtitle\">\r\n	<strong>M&igrave; Quảng</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.454540001406792450.jpg\" /></p>\r\n<p class=\"Normal\">\r\n	&nbsp; M&oacute;n ăn c&oacute; xuất xứ từ Quảng Nam nhưng đ&atilde; dần phổ biến v&agrave; trở th&agrave;nh m&oacute;n đặc trưng của dải đất miền Trung. So với c&aacute;c loại m&igrave;, phở, b&uacute;n, miến... của người d&acirc;n miền Bắc, m&igrave; Quảng c&oacute; n&eacute;t rất ri&ecirc;ng khi kh&ocirc;ng hề chan nước d&ugrave;ng ngập miệng b&aacute;t, m&agrave; chỉ đủ trộn để c&aacute;c nguy&ecirc;n liệu thấm đều gia vị. Kh&ocirc;ng chỉ đậm đ&agrave;, m&oacute;n ăn c&ograve;n rất bắt mắt với sự h&ograve;a quyện hấp dẫn giữa m&agrave;u đỏ của t&ocirc;m, m&agrave;u trắng của thịt heo, m&agrave;u v&agrave;ng của trứng c&ugrave;ng m&agrave;u xanh rau củ. Khi ăn, bỏ th&ecirc;m mấy miếng b&aacute;nh đa vừng c&ugrave;ng ch&uacute;t lạc rang khiến b&aacute;t m&igrave; Quảng gi&ograve;n tan lạ miệng.</p>\r\n<p class=\"Normal\">\r\n	Bạn c&oacute; thể t&igrave;m đến c&aacute;c qu&aacute;n m&igrave; Quảng tr&ecirc;n phố Quang Trung, Nguy&ecirc;n Hồng, Ngọc Kh&aacute;nh... để thưởng thức m&oacute;n ăn ngon, độc đ&aacute;o n&agrave;y.</p>\r\n<p class=\"Normal\">\r\n	<strong>B&aacute;nh tr&aacute;ng cuốn thịt heo</strong></p>\r\n<p class=\"Normal\" style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.329309001406792450.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	&nbsp; Chỉ gồm c&aacute;c nguy&ecirc;n liệu đơn giản như thịt heo, b&aacute;nh tr&aacute;ng, rau sống... nhưng người Đ&agrave; Nẵng đ&atilde; l&agrave;m n&ecirc;n m&oacute;n ăn khiến thực kh&aacute;ch kh&oacute; c&oacute; thể chối từ.C&oacute; mặt ở H&agrave; Nội với ch&uacute;t biến tấu nhưng hương vị đặc trưng của mắm n&ecirc;m th&igrave; kh&ocirc;ng hề thay đổi. Bạn c&oacute; thể chọn thịt luộc hoặc thịt quay để cuốn c&ugrave;ng rau xanh, dứa, c&agrave; rốt v&agrave; dưa chuột th&aacute;i sợi, g&oacute;i chặt b&ecirc;n ngo&agrave;i l&agrave; lớp b&aacute;nh tr&aacute;ng mỏng tang.&nbsp;</p>\r\n<p class=\"Normal\">\r\n	Chấm một &iacute;t mắm n&ecirc;m rồi nhẹ nh&agrave;ng đưa cuốn thịt heo v&agrave;o miệng, bạn sẽ cảm nhận được vị ngọt của thịt, tươi m&aacute;t của rau xanh, mềm dai của b&aacute;nh tr&aacute;ng h&ograve;a quyện trong hương thơm quyến rũ của vị mắm đặc trưng. Phạm Ngọc Thạch, Tr&uacute;c Kh&ecirc;, Duy T&acirc;n... l&agrave; những địa chỉ d&agrave;nh cho thực kh&aacute;ch &quot;nghiền&quot; m&oacute;n cuốn của Đ&agrave; Nẵng.</p>\r\n<p class=\"Normal\">\r\n	<strong>B&uacute;n mắm n&ecirc;m</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.553619001406792450.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&ecirc;n cạnh b&aacute;nh tr&aacute;ng cuốn thịt heo, b&uacute;n mắm n&ecirc;m l&agrave; m&oacute;n ăn kh&ocirc;ng thể thiếu của hầu hết c&aacute;c qu&aacute;n b&aacute;n đặc sản Đ&agrave; Nẵng ở H&agrave; Nội. Giống như b&uacute;n trộn, m&oacute;n ăn n&agrave;y c&oacute; th&agrave;nh phần chủ yếu gồm b&uacute;n, thịt heo quay, rau th&aacute;i nhỏ, ch&uacute;t lạc rang v&agrave; mắm n&ecirc;m. Trong đ&oacute;, thịt heo quay v&agrave; mắm n&ecirc;m l&agrave; hai yếu tố quyết định l&agrave;m n&ecirc;n sức hấp dẫn ri&ecirc;ng cho m&oacute;n. Thịt phải gồm cả nạc v&agrave; mỡ, quay gi&ograve;n, v&agrave;ng b&igrave;, c&ograve;n mắm n&ecirc;m phải dậy m&ugrave;i thơm, đậm đ&agrave; v&agrave; dịu hơn chấm cuốn. Tuy nhi&ecirc;n ở một số qu&aacute;n bạn cũng c&oacute; thể y&ecirc;u cầu thịt luộc thay cho thịt quay để ăn c&ugrave;ng b&uacute;n mắm n&ecirc;m cho đổi vị.</p>\r\n<p>\r\n	<strong>B&uacute;n b&ograve; Huế</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.645526001406792450.jpg\" /></p>\r\n<p class=\"Normal\">\r\n	&nbsp; So với c&aacute;c m&oacute;n ăn miền Trung ở H&agrave; Nội th&igrave; b&uacute;n b&ograve; Huế c&oacute; vẻ nổi tiếng hơn cả. Thực kh&aacute;ch c&oacute; thể lựa chọn m&oacute;n ăn n&agrave;y cho bất kỳ bữa ăn n&agrave;o trong ng&agrave;y như s&aacute;ng, trưa, chiều, tối. Ngo&agrave;i những sợi b&uacute;n to, tr&ograve;n, trắng n&otilde;n, một b&aacute;t b&uacute;n b&ograve; Huế đ&uacute;ng điệu kh&ocirc;ng thể thiếu m&oacute;ng gi&ograve;, thịt b&ograve; th&aacute;i l&aacute;t mỏng, một v&agrave;i miếng tiết cắt nhỏ v&agrave; một miếng gi&ograve;... Tuy nhi&ecirc;n, điểm l&agrave;m n&ecirc;n kh&aacute;c biệt cho m&oacute;n b&uacute;n b&ograve; Huế với c&aacute;c loại b&uacute;n ở H&agrave; Nội ch&iacute;nh l&agrave; vị nước d&ugrave;ng ngọt, cay nồng.&nbsp;Với hương vị đặc trưng của nắng gi&oacute; miền Trung, m&oacute;n ăn n&agrave;y đ&atilde; g&oacute;p phần tạo n&ecirc;n sự phong ph&uacute; cho ẩm thực H&agrave; Th&agrave;nh.</p>\r\n<p class=\"Normal\">\r\n	Tại H&agrave; Nội c&oacute; rất nhiều qu&aacute;n chuy&ecirc;n b&aacute;n b&uacute;n b&ograve; Huế, do đ&oacute; bạn c&oacute; thể dễ d&agrave;ng t&igrave;m một nơi để thưởng thức như tr&ecirc;n phố Nguyễn Thượng Hiền, T&ocirc; Hiệu, Kh&acirc;m Thi&ecirc;n, Quang Trung...</p>\r\n<p class=\"Normal\">\r\n	<strong>Nem lụi</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.743022001406792450.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	L&agrave; m&oacute;n ăn đặc sản Huế, nem lụi cũng nhanh ch&oacute;ng chiếm được cảm t&igrave;nh của c&aacute;c thực kh&aacute;ch H&agrave; Th&agrave;nh bởi hương vị đậm đ&agrave; rất miền Trung. Kh&aacute;c với nem cuốn hay nem chua r&aacute;n, nem lụi&nbsp;được l&agrave;m từ thịt lợn băm nhuyễn, trộn với b&igrave;, mỡ ướp với muối, ti&ecirc;u, đường, th&iacute;nh rồi bọc xung quanh c&acirc;y sả hoặc một chiếc đũa tre, đem nướng tr&ecirc;n bếp than đỏ rực. Ngo&agrave;i ăn với&nbsp;rau sống, gi&aacute; sống, chuối xanh.., nem lụi c&ograve;n được chấm với nước l&egrave;o sền sệt. Bạn c&oacute; thể t&igrave;m thấy c&aacute;c qu&aacute;n nem lụi tr&ecirc;n phố Thụy Khu&ecirc;, Đội Cấn, Phan Huy &Iacute;ch hoặc ng&otilde; chợ Kim Li&ecirc;n.</p>\r\n<p>\r\n	<strong>C&aacute;c loại b&aacute;nh Huế</strong></p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/113/700_0.834976001406792450.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Sẽ l&agrave; một thiếu s&oacute;t nếu điểm danh đến c&aacute;c m&oacute;n ăn miền Trung nổi tiếng ở H&agrave; Nội m&agrave; kh&ocirc;ng nhắc đến c&aacute;c m&oacute;n b&aacute;nh Huế. N&agrave;o l&agrave; b&aacute;nh bột lọc, b&aacute;nh x&egrave;o,&nbsp;b&aacute;nh nậm, b&aacute;nh kho&aacute;i, b&aacute;nh ram &iacute;t, b&aacute;nh b&egrave;o, b&aacute;nh ướt... &nbsp;V&igrave; l&agrave; c&aacute;c m&oacute;n ăn chơi n&ecirc;n ch&uacute;ng &iacute;t khi được b&aacute;n ch&iacute;nh v&agrave; ri&ecirc;ng ở c&aacute;c qu&aacute;n m&agrave; thường nằm chung trong thực đơn đồ ăn Huế. Mỗi m&oacute;n c&oacute; hương vị ri&ecirc;ng, tuy nhỏ nhưng được chế biến rất c&ocirc;ng phu v&agrave; ăn k&egrave;m với một loại nước chấm ri&ecirc;ng. C&aacute;c qu&aacute;n Huế ở H&agrave; Nội cho bạn tham khảo nằm tr&ecirc;n đường L&aacute;ng Hạ, H&agrave;ng B&ocirc;ng, Th&aacute;i Thịnh...</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1406792999', '0', '1406792450', '0', '0');
INSERT INTO `slm_news` VALUES ('114', '0.002358001406868501.jpg', 'a:11:{i:0;s:24:\"0.002358001406868501.jpg\";i:1;s:24:\"0.300521001406868862.jpg\";i:2;s:24:\"0.712078001406868502.jpg\";i:3;s:24:\"0.711251001406868503.jpg\";i:4;s:24:\"0.783758001406868504.jpg\";i:5;s:24:\"0.827829001406868505.jpg\";i:6;s:24:\"0.270655001406868580.jpg\";i:7;s:24:\"0.736190001406868581.jpg\";i:8;s:24:\"0.344040001406868583.jpg\";i:9;s:24:\"0.096614001406868585.jpg\";i:10;s:24:\"0.254402001406868586.jpg\";}', null, 'Ảnh hiếm về Hà Nội 1975 qua góc nhìn người Mỹ', null, null, '12', '', '\r\n	Th&aacute;ng 12/1975, ph&aacute;i đo&agrave;n đầu ti&ecirc;n của Mỹ đ&atilde; đến Việt Nam l&agrave;m việc sau chiến tranh. &Ocirc;ng Henry J. Kenny, một th&agrave;nh vi&ecirc;n của ph&aacute;i đo&agrave;n Mỹ đ&atilde; chụp lại nhiều bức ảnh về H&agrave; Nội nh&acirc;n dịp n&agrave;y.', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.300521001406868862.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ng&atilde; 5 bờ hồ với nh&agrave; h&agrave;ng Thủy Tạ ở b&ecirc;n tr&aacute;i, đ&agrave;i phun nước ở b&ecirc;n phải c&ugrave;ng đường t&agrave;u điện ph&iacute;a dưới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.002358001406868501.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đường Nguyễn Văn Cừ (Gia L&acirc;m) gần đoạn giao với Quốc lộ 5 - Cầu Chui, Long Bi&ecirc;n b&acirc;y giờ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.712078001406868502.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ng&ocirc;i nh&agrave; biển số 59 với t&acirc;m biển đề: &quot;Nhận may, v&aacute;, chữa quần &aacute;o nam nữ&quot;. Ng&ocirc;i nh&agrave; ph&iacute;a tr&aacute;i th&igrave; trưng biển &quot;Nhận xay bột trẻ em&quot;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.711251001406868503.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Một quầy b&aacute;n rau tr&ecirc;n vỉa h&egrave; phố cổ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.783758001406868504.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Một v&ograve;i nước c&ocirc;ng cộng với tấm biển nhỏ gần đ&oacute; ghi: &quot;M&Aacute;Y NƯỚC - Chỉ để lấy nước, cấm tắm, giặt, rửa&quot;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.827829001406868505.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tuyến phố H&agrave;ng Ngang - H&agrave;ng Đ&agrave;o.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.270655001406868580.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	C&aacute;c th&agrave;nh vi&ecirc;n Ủy ban về người mất t&iacute;ch ở Đ&ocirc;ng Nam &Aacute; của Mỹ gặp Thủ tướng Phạm Văn Đồng. Đ&acirc;y l&agrave; ph&aacute;i đo&agrave;n đầu ti&ecirc;n của Mỹ đến thăm Việt Nam sau chiến tranh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.736190001406868581.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ph&aacute;i đo&agrave;n của Mỹ v&agrave; c&aacute;c c&aacute;n bộ Việt Nam l&agrave;m việc tại s&acirc;n bay Nội B&agrave;i.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.344040001406868583.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Tiếp nhận h&agrave;i cốt l&iacute;nh Mỹ thiệt mạng trong chiến tranh Việt Nam.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.096614001406868585.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Thực hiện c&aacute;c thủ tục tiếp nhận h&agrave;i cốt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/114/700_0.254402001406868586.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	H&agrave;i cốt l&iacute;nh Mỹ được chuyển l&ecirc;n m&aacute;y bay của h&atilde;ng H&agrave;ng kh&ocirc;ng d&acirc;n dụng Việt Nam để đưa về nước.&nbsp;</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1406869466', '0', '1406868413', '0', '0');
INSERT INTO `slm_news` VALUES ('115', '0.249802001406950870.jpg', 'a:12:{i:0;s:24:\"0.249802001406950870.jpg\";i:1;s:24:\"0.472299001406950870.jpg\";i:2;s:24:\"0.662980001406950870.jpg\";i:3;s:24:\"0.872676001406950870.jpg\";i:4;s:24:\"0.114974001406950871.jpg\";i:5;s:24:\"0.474007001406950871.jpg\";i:6;s:24:\"0.674659001406950871.jpg\";i:7;s:24:\"0.989313001406950871.jpg\";i:8;s:24:\"0.127845001406950872.jpg\";i:9;s:24:\"0.345858001406950872.jpg\";i:10;s:24:\"0.513837001406950872.jpg\";i:11;s:24:\"0.675841001406950872.jpg\";}', null, 'Vẻ đẹp vịnh Hạ Long nhìn từ trên cao', null, null, '12', '', '\r\n	L&agrave; t&aacute;c giả chụp vịnh Hạ Long từ tr&ecirc;n cao với số lượng ảnh nhiều nhất do trung t&acirc;m kỷ lục Việt Nam x&aacute;c nhận, Đỗ Kh&aacute;nh Giang đ&atilde; mang đến những g&oacute;c nh&igrave;n độc đ&aacute;o về di sản thi&ecirc;n nhi&ecirc;n thế giới n&agrave;y.', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.472299001406950870.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đỗ Kh&aacute;nh Giang l&agrave; một nhiếp ảnh trẻ (sinh năm 1974) người Quảng Ninh. Sau th&agrave;nh c&ocirc;ng của triển l&atilde;m ảnh nghệ thuật c&aacute; nh&acirc;n đầu ti&ecirc;n mang t&ecirc;n &quot;Hạ Long - đ&aacute; v&agrave; nước vĩnh cửu&quot;năm 2010 v&agrave; xuất bản bộ s&aacute;ch ảnh c&ugrave;ng chủ đề, anh tiếp tục cuộc h&agrave;nh tr&igrave;nh kh&aacute;m ph&aacute; thế giới kỳ quan.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.249802001406950870.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Bằng g&oacute;c nh&igrave;n độc đ&aacute;o của m&igrave;nh, Đỗ Kh&aacute;nh Giang&nbsp;kh&ocirc;ng ngừng cống hiến cho c&ocirc;ng ch&uacute;ng y&ecirc;u nghệ thuật những khoảnh khắc ấn tượng.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.662980001406950870.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ảnh Hạ Long của Đỗ Giang hầu hết chụp từ tr&ecirc;n cao. Bởi vậy, h&igrave;nh khối, đường n&eacute;t, mầu sắc v&agrave; kh&ocirc;ng gian đều mạch lạc, th&ecirc;nh thang, kh&ocirc;ng hỗn tạp. V&agrave; lu&ocirc;n gợi cho người xem những x&uacute;c cảm về sự diễm lệ, thuần khiết.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.872676001406950870.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	B&atilde;i tắm nhỏ tr&ecirc;n vịnh Hạ Long.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.114974001406950871.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Cửa hang.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.474007001406950871.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Đ&aacute; nghĩ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.674659001406950871.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Kh&ocirc;ng h&igrave;nh tự nhi&ecirc;n độc đ&aacute;o do cửa hang tạo ra.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.989313001406950871.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	H&ograve;n Khủng Long.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.127845001406950872.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	M&agrave;nh đ&aacute;.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.345858001406950872.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	H&ograve;n cối xay.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.513837001406950872.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Ho&agrave;ng h&ocirc;n tr&ecirc;n vịnh Hạ Long.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/115/700_0.675841001406950872.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	Hạ Long kỳ ảo trong &aacute;nh dương cuối ng&agrave;y.</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1406951871', '0', '1406950870', '0', '0');
INSERT INTO `slm_news` VALUES ('118', '0.238359001409544326.JPG', 'a:15:{i:0;s:24:\"0.238359001409544326.JPG\";i:1;s:24:\"0.987126001409544624.JPG\";i:2;s:24:\"0.698856001409544326.JPG\";i:3;s:24:\"0.963856001409544326.JPG\";i:4;s:24:\"0.400653001409544329.JPG\";i:5;s:24:\"0.702972001409544329.JPG\";i:6;s:24:\"0.033329001409544330.JPG\";i:7;s:24:\"0.415906001409544330.JPG\";i:8;s:24:\"0.882254001409544400.JPG\";i:9;s:24:\"0.061111001409544401.JPG\";i:10;s:24:\"0.272552001409544401.JPG\";i:11;s:24:\"0.436534001409544401.JPG\";i:12;s:24:\"0.600316001409544401.jpg\";i:13;s:24:\"0.791091001409544401.jpg\";i:14;s:24:\"0.919243001409544402.jpg\";}', null, 'Tái hiện cố đô Huế thu nhỏ ở Sài Gòn', null, null, '12', '', '\r\n	Với mong muốn ba mẹ m&igrave;nh được thấy Huế mỗi ng&agrave;y, anh Nguyễn Thanh T&ugrave;ng đ&atilde; t&aacute;i hiện lại to&agrave;n bộ quần thể di t&iacute;ch cố đ&ocirc; Huế cổ k&iacute;nh một c&aacute;ch sinh động giữa l&ograve;ng S&agrave;i G&ograve;n n&aacute;o nhiệt.', '<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.987126001409544624.JPG\" /></p>\r\n<p>\r\n	Trong khu Ngự L&atilde;m Vi&ecirc;n rộng hơn 1.000 m2&nbsp;tr&ecirc;n&nbsp;đường Ho&agrave;ng Hữu Nam, phường Long B&igrave;nh,&nbsp;quận 9, TP HCM, anh Nguyễn Thanh T&ugrave;ng phục dựng lại một&nbsp;kiến tr&uacute;c nghệ thuật cố đ&ocirc; Huế ho&agrave;nh tr&aacute;ng khiến nhiều du kh&aacute;ch trầm trồ th&iacute;ch th&uacute;,&nbsp;kh&ocirc;ng khỏi sững sờ v&igrave; c&oacute; cảm gi&aacute;c kinh th&agrave;nh Huế cổ k&iacute;nh đang hiện ra trước mắt.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.238359001409544326.JPG\" /></p>\r\n<p>\r\n	Ho&agrave;ng th&agrave;nh Huế bao quanh Tử cấm th&agrave;nh&nbsp;với cổng Ngọ M&ocirc;n, điện Cần Ch&aacute;nh, điện Th&aacute;i H&ograve;a, cung Trường Sanh, phủ Nội Vụ, vườn Cơ Hạ, Duyệt Thị Đường, điện Phụng Ti&ecirc;n...&nbsp;được anh T&ugrave;ng t&aacute;i hiện đầy đủ kiến tr&uacute;c, những đường n&eacute;t chạm trổ tinh tế, những đường cong uốn lượn nhẹ nh&agrave;ng thanh tho&aacute;t.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.718492001409544400.JPG\" /></p>\r\n<p>\r\n	C&aacute;c d&atilde;y s&uacute;ng thần c&ocirc;ng trong Ho&agrave;ng th&agrave;nh được đ&uacute;c đ&uacute;ng khu&ocirc;n mẫu, d&atilde;y cột của m&aacute;i che d&ugrave; nhỏ nhưng cũng được đi&ecirc;u khắc tinh xảo với nhiều đường n&eacute;t phức tạp.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.698856001409544326.JPG\" /></p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	To&agrave;n bộ c&ocirc;ng tr&igrave;nh &ldquo;Huế thu nhỏ&rdquo; của anh c&oacute; 151 kiến tr&uacute;c. N&eacute;t cổ k&iacute;nh, thanh tịnh của v&ugrave;ng đất cố đ&ocirc; được t&aacute;i hiện một c&aacute;ch sinh động với những c&ocirc;ng tr&igrave;nh mang đậm bản sắc văn h&oacute;a, lịch sử như: Ho&agrave;ng th&agrave;nh Huế, lăng c&aacute;c vua triều Nguyễn, ch&ugrave;a Thi&ecirc;n Mụ, cầu Trường Tiền tr&ecirc;n s&ocirc;ng Hương thơ mộng... Nổi bật nhất l&agrave; ho&agrave;ng th&agrave;nh Huế, một di t&iacute;ch lịch sử cổ k&iacute;nh của đất kinh kỳ h&agrave;ng trăm năm được t&aacute;i hiện tinh xảo y như thật. B&ecirc;n ngo&agrave;i kinh th&agrave;nh Huế, nằm ven con s&ocirc;ng Hương thơ mộng l&agrave; bến V&acirc;n L&acirc;u v&agrave; đ&igrave;nh Thương Bạc, cầu Trường Tiền c&ugrave;ng cồn Gi&atilde; Vi&ecirc;n v&agrave; cồn Hến ngự hai b&ecirc;n Ho&agrave;ng th&agrave;nh.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Tai-hien-co-do-Hue-o-Sai-Gon-de-bao-hieu-cha-me-post444412.html\" target=\"_blank\">http://news.zing.vn/Tai-hien-co-do-Hue-o-Sai-Gon-de-bao-hieu-cha-me-post444412.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.963856001409544326.JPG\" /></p>\r\n<p>\r\n	Anh T&ugrave;ng đ&atilde; m&agrave;y m&ograve; t&igrave;m hiểu, gặp gỡ c&aacute;c nh&agrave; nghi&ecirc;n cứu Huế, c&aacute;c chuy&ecirc;n gia về kiến tr&uacute;c cố đ&ocirc;, c&aacute;c nghệ nh&acirc;n xưa c&ograve;n s&oacute;t lại của Huế. Từ năm 2002, sau&nbsp;khi thiết kế xong, việc x&acirc;y dựng cũng gặp kh&ocirc;ng &iacute;t kh&oacute; khăn. Ban đầu anh thử nghiệm l&agrave;m những m&ocirc; h&igrave;nh lăng tẩm, th&agrave;nh qu&aacute;ch Huế bằng gỗ tr&aacute;t xi măng b&ecirc;n ngo&agrave;i. Tuy nhi&ecirc;n, khi đưa v&agrave;o sử dụng thử nghiệm do trời nắng gỗ bị gi&atilde;n nở n&ecirc;n th&agrave;nh quả của anh v&agrave; h&agrave;ng chục c&ocirc;ng nh&acirc;n đều trở th&agrave;nh c&ocirc;ng cốc.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.400653001409544329.JPG\" /></p>\r\n<p>\r\n	Anh t&acirc;m nguyện, đầu ti&ecirc;n l&agrave;m l&agrave; cho ba mẹ m&igrave;nh, kế đến l&agrave; cho con ch&aacute;u trong gia đ&igrave;nh g&igrave;n giữ gia phong, văn h&oacute;a cội nguồn n&ecirc;n d&ugrave; gặp nhiều kh&oacute; khăn, anh cũng kh&ocirc;ng nản l&ograve;ng... May mắn l&agrave; anh gặp được một nghệ nh&acirc;n gi&uacute;p chế tạo khu&ocirc;n silicon để l&agrave;m c&ocirc;ng tr&igrave;nh kiến tr&uacute;c bằng bột đ&aacute; Bửu Long, c&oacute; thể chống chịu thời tiết khắc nghiệt.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.702972001409544329.JPG\" /></p>\r\n<p>\r\n	Quần thể di t&iacute;ch cố đ&ocirc; Huế l&agrave;m trong 7 năm mới xong, tốn nhiều c&ocirc;ng sức v&agrave; tiền của. Sau hơn chục năm từ khi được x&acirc;y dựng, c&aacute;c kiến tr&uacute;c bằng đ&aacute; được phủ ri&ecirc;u phong c&agrave;ng l&agrave;m cho c&aacute;c c&ocirc;ng tr&igrave;nh trở n&ecirc;n cổ k&iacute;nh hơn m&agrave; kh&ocirc;ng bị hư hại.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.033329001409544330.JPG\" /></p>\r\n<p>\r\n	Ho&agrave;ng Th&agrave;nh được thiết kế đầy đủ với c&aacute;c cung điện của vua, cung ho&agrave;ng hậu, c&aacute;c phi tần kết hợp với hồ, vườn, bờ tường, lối đi san s&aacute;t nhau v&agrave; rất chắc chắn.&nbsp;Những c&acirc;y cảnh nhỏ được trồng ngay trong c&ocirc;ng tr&igrave;nh c&agrave;ng l&agrave;m cho c&aacute;c khối đ&aacute; trở n&ecirc;n mềm mại v&agrave; đẹp mắt hơn</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.415906001409544330.JPG\" /></p>\r\n<p>\r\n	&quot;C&oacute; nhiều chi tiết chạm trổ qu&aacute; tinh vi, nhiều đường n&eacute;t phức tạp khiến c&aacute;c nghệ nh&acirc;n lắc đầu v&igrave; ước chừng phải mất 10 năm mới chạm trổ xong chừng ấy kiến tr&uacute;c&quot;, anh T&ugrave;ng chia sẻ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.882254001409544400.JPG\" /></p>\r\n<p>\r\n	Ngo&agrave;i Ho&agrave;ng th&agrave;nh Huế, ph&iacute;a xa xa trong Ngự L&atilde;m vi&ecirc;n, anh T&ugrave;ng c&ograve;n l&agrave;m cả ch&ugrave;a Thi&ecirc;n Mụ, điện H&ograve;n Ch&eacute;n c&ugrave;ng lăng tẩm của c&aacute;c vua Gia Long, Minh Mạng, Tự Đức v&agrave; Khải Định - 4 &ocirc;ng vua anh k&iacute;nh nể nhất. C&aacute;c c&ocirc;ng tr&igrave;nh n&agrave;y cũng được thiết kế v&agrave; đi&ecirc;u khắc y như c&aacute;c c&ocirc;ng tr&igrave;nh đang hiện hữu ở cố đ&ocirc; Huế. Trong ảnh,&nbsp;Lăng Khải Định được chạm khắc tinh tế v&agrave; đẹp mắt.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.061111001409544401.JPG\" /></p>\r\n<p>\r\n	&quot;Một nghệ nh&acirc;n b&igrave;nh thường kh&oacute; c&oacute; thể l&agrave;m nổi c&ocirc;ng tr&igrave;nh kiến tr&uacute;c n&agrave;y m&agrave; phải l&agrave; một người am hiểu tường tận về văn h&oacute;a v&agrave; lịch sử Huế mới c&oacute; thể đảm đương. Nhưng như thế cũng chưa đủ. Một người con xứ Huế phải c&oacute; l&ograve;ng y&ecirc;u Huế đến da diết v&agrave; muốn l&agrave;m một điều g&igrave; đ&oacute; cho Huế mới c&oacute; thể l&agrave;m được n&oacute;&rdquo;, anh T&ugrave;ng chia sẻ. Trong ảnh, Lăng vua Minh Mạng được thiết kế đậm chất xứ Huế.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.272552001409544401.JPG\" /></p>\r\n<p>\r\n	Ch&ugrave;a Thi&ecirc;n Mụ - một danh lam thắng cảnh nổi tiếng b&ecirc;n d&ograve;ng s&ocirc;ng Hương được x&acirc;y dựng từ triều Nguyễn lu&ocirc;n được đ&ocirc;ng đảo kh&aacute;ch du lịch gh&eacute; thăm khi đến Huế. C&ocirc;ng tr&igrave;nh được thiết kế đầy đủ ch&ugrave;a, th&aacute;p, ph&iacute;a dưới cổng ch&ugrave;a l&agrave; d&ograve;ng s&ocirc;ng Hương.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.436534001409544401.JPG\" /></p>\r\n<p>\r\n	Tuy được t&aacute;i tạo lại với b&agrave;n tay con người với k&iacute;ch thước thu nhỏ, nhưng hệ thống kinh th&agrave;nh Huế xưa vẫn giữ được n&eacute;t kiến tr&uacute;c mẫu mực cổ k&iacute;nh.&nbsp;&nbsp;Kh&oacute; ai c&oacute; thể ngờ rằng, một người l&agrave;m trong lĩnh vực viễn th&ocirc;ng như anh T&ugrave;ng lại am hiểu về văn h&oacute;a, lịch sử v&agrave; kiến tr&uacute;c xứ Huế như một nh&agrave; nghi&ecirc;n cứu Huế thực thụ.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.600316001409544401.jpg\" /></p>\r\n<p>\r\n	Điều th&ocirc;i th&uacute;c anh l&agrave;m c&ocirc;ng tr&igrave;nh &ldquo;Huế thu nhỏ&rdquo; n&agrave;y&nbsp;một phần v&igrave; gia đ&igrave;nh. Ba mẹ anh thường kể về xứ Huế với niềm say sưa quyện lẫn nỗi nhớ qu&ecirc; hương da diết khiến anh kh&ocirc;ng khỏi chạnh l&ograve;ng. &quot;Ba mạ ng&agrave;y c&agrave;ng gi&agrave; yếu, kh&ocirc;ng thể mỗi lần nhớ Huế l&agrave; đi về thăm qu&ecirc; được n&ecirc;n t&ocirc;i quyết ch&iacute; phải l&agrave;m sao để mỗi sớm mai thức dậy l&agrave; ba mạ thấy Huế trước mắt m&igrave;nh rồi&quot;, anh T&ugrave;ng t&acirc;m sự.&nbsp;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.791091001409544401.jpg\" /></p>\r\n<p>\r\n	Rồi tiếng l&agrave;nh đồn xa, c&aacute;c trường học bắt đầu gửi học sinh đến học ngoại kh&oacute;a để t&igrave;m hiểu về văn h&oacute;a, lịch sử Huế. Anh T&ugrave;ng t&acirc;m niệm, anh l&agrave;m c&ocirc;ng tr&igrave;nh Ngự L&atilde;m Vi&ecirc;n kh&ocirc;ng phải để kinh doanh du lịch n&ecirc;n tất cả mọi người đến đ&acirc;y tham quan đều được miễn ph&iacute;. Kh&ocirc;ng những vậy, ngo&agrave;i thời gian đi l&agrave;m, kh&aacute;ch v&agrave; c&aacute;c đo&agrave;n đến tham quan c&ograve;n được anh T&ugrave;ng giảng về lịch sử, văn h&oacute;a, kiến tr&uacute;c Huế.&nbsp;</p>\r\n<div style=\"position: absolute; left: -99999px;\">\r\n	B&ecirc;n cạnh đ&oacute; anh muốn l&agrave;m điều g&igrave; đ&oacute; cho qu&ecirc; hương, anh muốn Huế thu nhỏ c&ograve;n được xem l&agrave; gi&aacute;o cụ học tập trực quan đầy sinh động cho nhiều học sinh, sinh vi&ecirc;n. Khi c&ocirc;ng tr&igrave;nh ho&agrave;n th&agrave;nh, b&agrave; con họ h&agrave;ng k&eacute;o đến tham quan ng&agrave;y c&agrave;ng đ&ocirc;ng, ba mẹ anh rất vui mừng. Từ năm 2007 đến nay, Ngự L&atilde;m Vi&ecirc;n đ&atilde; đ&oacute;n h&agrave;ng trăm ng&agrave;n kh&aacute;ch từ nhiều nơi đến tham quan, đa phần l&agrave; học sinh v&agrave; sinh vi&ecirc;n. Với anh, đ&oacute;n th&ecirc;m một người kh&aacute;ch l&agrave; anh đ&oacute;n th&ecirc;m một niềm vui, bởi ước nguyện của anh đ&atilde; được th&ecirc;m một người hiểu v&agrave; chia sẻ.<br />\r\n	<br />\r\n	B&agrave;i viết: <a href=\"http://news.zing.vn/Tai-hien-co-do-Hue-o-Sai-Gon-de-bao-hieu-cha-me-post444412.html\" target=\"_blank\">http://news.zing.vn/Tai-hien-co-do-Hue-o-Sai-Gon-de-bao-hieu-cha-me-post444412.html</a><br />\r\n	<br />\r\n	<a href=\"http://news.zing.vn/\" style=\"display:block;font-size:0;line-height:0;\" target=\"_blank\" title=\"Zing News\">Nguồn Zing News</a></div>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/118/700_0.919243001409544402.jpg\" /></p>', '0', '0', '0', '0', '0', '0', null, null, null, null, null, '1456125575', '0', '1409544326', '0', '0');
INSERT INTO `slm_news` VALUES ('119', '0.672340001413953458.jpg', 'a:5:{i:0;s:24:\"0.672340001413953458.jpg\";i:1;s:24:\"0.913166001413953458.jpg\";i:2;s:24:\"0.944538001413953458.jpg\";i:3;s:24:\"0.991629001413953458.JPG\";i:4;s:24:\"0.108564001413953459.JPG\";}', null, 'Tác dụng chữa bệnh từ rắn', null, null, '1', '', '\r\n	Rắn l&agrave;m thuốc gồm nhiều loại, thường l&agrave; những lo&agrave;i rắn độc, người ta hay d&ugrave;ng 3 lo&agrave;i rắn độc mang t&ecirc;n rắn hổ mang, rắn cạp nong, rắn cạp nia, nhưng những t&ecirc;n đ&oacute; nhiều khi lại được d&ugrave;ng để chỉ nhiều lo&agrave;i rắn kh&aacute;c nhau, cần ch&uacute; &yacute; ph&acirc;n biệt.', '<p>\r\n	Ở Việt Nam, c&aacute;c nước Đ&ocirc;ng nam &Aacute; v&agrave; ch&acirc;u Đại dương c&oacute; tập qu&aacute;n bắt rắn, nu&ocirc;i rắn. Rắn với nhiều bộ phận được d&ugrave;ng l&agrave;m thuốc: Thịt rắn, mật rắn, nọc rắn (đặc biệt nọc rắn của họ rắn hổ), x&aacute;c rắn lột (x&agrave; tho&aacute;i).</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">Những con rắn hay d&ugrave;ng ở Việt Nam:</strong></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">1. Rắn hổ mang:&nbsp;</strong>t&ecirc;n hổ mang c&ograve;n gọi l&agrave; hổ lửa, hổ ph&igrave;, con ph&igrave;, hổ đất, rắn mang k&iacute;nh. C&oacute; hai chi rắn kh&aacute;c nhau ở nước ta mang t&ecirc;n hổ mang c&oacute; t&ecirc;n l&agrave; chi Naji v&agrave; chi Agkistrodon.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">2. Rắn cạp nong:&nbsp;</strong>c&ograve;n c&oacute; t&ecirc;n l&agrave; rắn mai gầm, c&oacute; nơi gọi l&agrave; rắn mai gầm v&agrave;ng, rắn đen v&agrave;ng, rắn v&ograve;ng v&agrave;ng. Rắn cạp nong thường sống ở cả đồng bằng v&agrave; miền n&uacute;i, con rắn n&agrave;y rất đặc biệt ở chỗ th&acirc;n n&oacute; hơi h&igrave;nh ba cạnh, gồm những khoanh đen v&agrave; v&agrave;ng v&ograve;ng quanh cả bụng.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">3. Rắn cạp nia:&nbsp;</strong>Hay rắn mai gầm bạc, c&ograve;n gọi l&agrave; rắn đen trắng, rắn hổ khoang, rắn v&ograve;ng bạc. Lo&agrave;i rắn n&agrave;y thường ngắn hơn lo&agrave;i cạp nong. M&agrave;u rắn đen xanh hay n&acirc;u sẫm c&oacute; những khoanh m&agrave;u trắng hay trắng v&agrave;ng, khoanh m&agrave;u trắng hẹp hơn khoanh m&agrave;u đen,.</p>\r\n<p>\r\n	Ph&acirc;n bố: những con rắn d&ugrave;ng l&agrave;m thuốc được ph&acirc;n bổ rộng r&atilde;i ở miền Bắc v&agrave; miền Nam, đồng bằng hay rừng n&uacute;i đều c&oacute;.</p>\r\n<p>\r\n	Y dược học cổ truyền phương Đ&ocirc;ng d&ugrave;ng thịt, mật v&agrave; x&aacute;c rắn lột; Dược học hiện đại d&ugrave;ng nọc rắn để b&agrave;o chế thuốc với biểu tượng con rắn nhả nọc v&agrave;o cốc thuốc. Với c&aacute;c lo&agrave;i rắn c&oacute; nọc độc, nếu cắn người rất dễ g&acirc;y tử vong, v&igrave; vậy cần c&oacute; biện ph&aacute;p ph&ograve;ng ngừa v&agrave; điều trị t&iacute;ch cực, đặc biệt ở những nơi chăn nu&ocirc;i rắn v&agrave; ở c&aacute;c v&ugrave;ng đầm hồ s&ocirc;ng nước c&oacute; nhiều rắn độc.</p>\r\n<p>\r\n	<img src=\"http://langquetoi.com/images/news/119/700_0.913166001413953458.jpg\" style=\"height: 344px; width: 700px;\" /></p>\r\n<p>\r\n	Bộ phận d&ugrave;ng v&agrave; th&agrave;nh phần h&oacute;a học:</p>\r\n<p>\r\n	Thịt rắn (x&agrave; nhục): c&oacute; protein v&agrave; acid amin, chất mỡ, saponoid<br style=\"box-sizing: border-box;\" />\r\n	Mật rắn (x&agrave; đởm): c&oacute; cholesterin, acid palmitic, a. stearic, a. cholic&hellip;<br style=\"box-sizing: border-box;\" />\r\n	X&aacute;c rắn lột (x&agrave; tho&aacute;i) c&oacute; oxyt titan v&agrave; oxyt kẽm<br style=\"box-sizing: border-box;\" />\r\n	Nọc rắn: chất độc thuộc nh&oacute;m zootoxin, protein, albumin, kẽm, Ca, Mg, enzym</p>\r\n<p>\r\n	Theo Đ&ocirc;ng y, c&aacute;c loại rắn cạn c&oacute; vị ngọt, mặn, t&iacute;nh &ocirc;n, c&oacute; độc; v&agrave;o can tỳ.<br style=\"box-sizing: border-box;\" />\r\n	X&agrave; đởm vị ngọt hơI cay v&agrave; kh&ocirc;ng đắng; v&agrave;o phế. T&aacute;c dụng giảm ho giảm đau<br style=\"box-sizing: border-box;\" />\r\n	X&agrave; tho&aacute;I vị ngọt mặn, t&iacute;nh b&igrave;nh, hơI tanh; v&agrave; kinh can. T&aacute;c dụng kh&aacute;ng khuẩn, chống vi&ecirc;m&nbsp;<a href=\"http://2giadinh.vn/tag/giai-doc/\" style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; transition: all 0.1s ease-in-out; -webkit-transition: all 0.1s ease-in-out; color: rgb(51, 51, 51); text-decoration: none;\" target=\"_bank\" title=\"giải độc\"><strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">giảI độc</strong></a>.</p>\r\n<p>\r\n	<img src=\"http://langquetoi.com/images/news/119/700_0.672340001413953458.jpg\" style=\"height: 557px; width: 700px;\" /></p>\r\n<p>\r\n	Nọc rắn c&oacute; t&aacute;c dụng giảm đau chống vi&ecirc;m, chống h&igrave;nh th&agrave;nh huyết khối n&ecirc;n d&ugrave;ng chữa đau thần kinh,&nbsp;<a href=\"http://2giadinh.vn/tag/ung-thu/\" style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; transition: all 0.1s ease-in-out; -webkit-transition: all 0.1s ease-in-out; color: rgb(51, 51, 51); text-decoration: none;\" target=\"_bank\" title=\"ung thư\"><strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">ung thư</strong></a>&nbsp;m&agrave; kh&ocirc;ng g&acirc;y nghiện</p>\r\n<p>\r\n	C&ocirc;ng năng chủ trị: X&agrave; nhục c&oacute; t&aacute;c dụng khu phong trừ thấp, trấn kinh, trừ co giật. D&ugrave;ng cho c&aacute;c trường hợp bại liệt, đau nhức xương khớp, vi&ecirc;m khớp, phong h&agrave;n thấp t&yacute;, trẻ em sốt cao co giật, lở ngứa, dị ứng, co giật uốn v&aacute;n.<br style=\"box-sizing: border-box;\" />\r\n	Liều d&ugrave;ng c&aacute;ch d&ugrave;ng: 1 con rắn; d&ugrave;ng kết hợp với c&aacute;c dược liệu hoặc nấu, hầm, ng&acirc;m, ướp.<br style=\"box-sizing: border-box;\" />\r\n	Ki&ecirc;ng kỵ: Kh&ocirc;ng d&ugrave;ng cho người &acirc;m hư hoả vượng, người huyết hư.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">C&aacute;ch d&ugrave;ng rắn l&agrave;m thuốc:</strong></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">1.&nbsp;Hoạt lạc, giảm đau:</strong></p>\r\n<p>\r\n	+ Rượu Rắn Hổ mang: Thịt rắn Hổ mang 6g, quy th&acirc;n 12g, ngũ gia b&igrave; 12g, khương hoạt 12g, thi&ecirc;n ma 12g, tần giao 12g, ph&ograve;ng phong 12g. Ng&acirc;m rượu uống hoặc sắc uống. D&ugrave;ng trong chứng bệnh phong thấp l&agrave;m trở ngại kinh lạc, khớp xương đau nhức l&acirc;u ng&agrave;y kh&ocirc;ng khỏi. Ngo&agrave;i ra c&ograve;n trị phong thấp t&ecirc; bại.</p>\r\n<p>\r\n	+ Rượu rắn: Rắn hổ mang 1 con, rắn cạp nong hay cạp nia 1 con, rắn r&aacute;o 1 con, cẩu t&iacute;ch 50g, tiểu hồi 30g, h&agrave; thủ &ocirc; đỏ 80g, trần b&igrave; 30g, k&ecirc; huyết đằng 120g, ngũ gia b&igrave; 80g, thi&ecirc;n ni&ecirc;n kiện 80g, đường k&iacute;nh 660g, rượu ethanol 600 4 l&iacute;t, rượu ethanol 400 vừa đủ 10 l&iacute;t. Chữa phong t&ecirc; thấp, đau xương nhức cơ, b&aacute;n th&acirc;n bất toại, b&agrave;n tay b&agrave;n ch&acirc;n đổ mồ h&ocirc;i. Mỗi lần d&ugrave;ng 15 &ndash; 20 ml, uống trước khi đi ngủ.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">2.&nbsp;Trừ phong, hết kinh giật:</strong>&nbsp;D&ugrave;ng cho trẻ em bị kinh phong, s&agrave;i uốn v&aacute;n, co r&uacute;t g&acirc;n.</p>\r\n<p>\r\n	+ Rắn Hổ mang 63g (ng&acirc;m rượu bỏ da v&agrave; xương), rết 63g (tẩm rượu nướng), rắn đen (&ocirc; ti&ecirc;u x&agrave;) ng&acirc;m rượu bỏ da v&agrave; xương. Nghiền chung th&agrave;nh bột. Ng&agrave;y uống 2 &ndash; 3 lần, mỗi lần uống 8g, chi&ecirc;u với rượu h&acirc;m n&oacute;ng. Trị s&agrave;i uốn v&aacute;n, cổ ưỡn cong, th&acirc;n thẳng cứng.</p>\r\n<p style=\"box-sizing: border-box; border: 0px; margin: 10px 0px; padding: 0px; vertical-align: baseline; color: rgb(51, 51, 51); font-family: \\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\'Helvetica Neue\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\\', Helvetica, Arial, sans-serif; font-size: 14px; line-height: 20px; text-align: justify; background-color: rgb(255, 255, 255);\">\r\n	<img src=\"http://langquetoi.com/images/news/119/700_0.108564001413953459.JPG\" style=\"width: 700px; height: 525px;\" /></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">3.&nbsp;Ti&ecirc;u phong, khỏi ngứa:</strong></p>\r\n<p>\r\n	+ Cao khu phong: Rắn hổ mang 8g, bạc h&agrave; 8g, thi&ecirc;n ma 12g, kinh giới 12g. Nghiền th&agrave;nh bột, trộn đều với rượu v&agrave; mật, l&agrave;m th&agrave;nh cao. Chia uống 2 lần trong ng&agrave;y. Trị phong t&ecirc; liệt, ngứa khắp người.</p>\r\n<p>\r\n	+ Rắn hổ mang 80g, thuyền tho&aacute;i 80g; nghiền th&agrave;nh bột mịn để ri&ecirc;ng. Lấy hy thi&ecirc;m 20g, k&eacute; đầu ngựa 20g; sắc lấy nước v&agrave; uống với 4g bột thuốc tr&ecirc;n. Uống liền trong 2 tuần lễ. Trị ngứa v&agrave;ng da.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">4.&nbsp;Người ta nghi&ecirc;n cứu nọc rắn để b&agrave;o chế thuốc c&oacute; t&aacute;c dụng giảm đau k&eacute;o d&agrave;i:</strong>&nbsp;kem b&ocirc;i tại chỗ (Vipratox- Đức, Viprosalum &ndash; Nga, Najatox &ndash; Việt Nam) chữa thấp khớp, vi&ecirc;m cơ, vi&ecirc;m d&acirc;y thần kinh&hellip;; dạng thuốc ti&ecirc;m (Viperalgin &ndash; Tiệp khắc) t&aacute;c dụng giảm đau, nhất l&agrave; đau do ung thư.</p>\r\n<p>\r\n	<img src=\"http://langquetoi.com/images/news/119/700_0.991629001413953458.JPG\" style=\"height: 525px; width: 700px;\" /></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">5.&nbsp;Mật rắn được d&ugrave;ng l&agrave;m thuốc trị ho do phong, đau lưng, nhức đầu kinh ni&ecirc;n:</strong>&nbsp;Thuốc &ldquo;Tam x&agrave; đởm&rdquo; gồm mật của rắn hổ mang, rắn cạp nong hoặc cạp nia, rắn r&aacute;o, trần b&igrave; v&agrave; 1 số dược liệu kh&aacute;c; chữa ho, đau bụng, ti&ecirc;u chảy do phong h&agrave;n. Thuốc cũng c&oacute; t&aacute;c dụng với người vi&ecirc;m đa khớp, đau nhiều về m&ugrave;a r&eacute;t, đau c&oacute; sốt nhẹ.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">6.&nbsp;X&aacute;c rắn đốt th&agrave;nh tro</strong>, trộn với dầu r&aacute;n củ r&aacute;y dại v&agrave; nghệ v&agrave;ng, chữa&nbsp;<a href=\"http://2giadinh.vn/tag/phu-nu/\" style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline; transition: all 0.1s ease-in-out; -webkit-transition: all 0.1s ease-in-out; color: rgb(51, 51, 51); text-decoration: none;\" target=\"_bank\" title=\"phụ nữ\"><strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">phụ nữ</strong></a>&nbsp;bị nứt đầu v&uacute;, chữa mụn nhọt.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">M&oacute;n ăn b&agrave;i thuốc từ con rắn:</strong></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">- Thịt rắn nướng:</strong>&nbsp;Thịt rắn hổ (Bạch hoa x&agrave; nhục) đ&atilde; lột da bỏ ruột th&aacute;i l&aacute;t để sẵn. Chảo đất nung đặt tr&ecirc;n bếp tro n&oacute;ng đỏ, vẩy &iacute;t nước dấm, cho thịt rắn v&agrave;o nướng, đậy k&iacute;n, đảo c&aacute;c l&aacute;t thịt 3 &ndash; 5 lần cho ch&iacute;n. Chấm với nước chấm v&agrave; gia vị khi ăn. D&ugrave;ng cho c&aacute;c trường hợp bại liệt, lở ngứa nổi ban dị ứng.</p>\r\n<p>\r\n	<img src=\"http://langquetoi.com/images/news/119/700_0.944538001413953458.jpg\" style=\"height: 467px; width: 700px;\" /></p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">- Chả rắn xương s&ocirc;ng l&aacute; lốt:</strong>&nbsp;Rắn 1 &ndash; 2 con. Lột da bỏ ruột, băm nhỏ với xương s&ocirc;ng, l&aacute; lốt, th&ecirc;m muối mắm gia vị, nặn th&agrave;nh vi&ecirc;n, r&aacute;n ch&iacute;n. D&ugrave;ng cho c&aacute;c trường hợp đau nhức xương khớp, lở ngứa, ban chấn dị ứng. Tuần ăn 2 &ndash; 3 lần.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">- Ch&aacute;o rắn:</strong>&nbsp;Rắn 1 con, gạo tẻ 150g. Lột da bỏ ruột, chặt bỏ đầu, chặt th&agrave;nh đoạn, nấu với gạo tẻ th&agrave;nh ch&aacute;o, th&ecirc;m gia vị, rau thơm, ti&ecirc;u ớt. D&ugrave;ng cho c&aacute;c trường hợp bại liệt, đau nhức chi thể, kinh giật, lở ngứa. Tuần ăn 1 &ndash; 2 lần.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">- Thịt rắn hầm rễ ti&ecirc;u:</strong>&nbsp;Thịt rắn 250g, rễ c&acirc;y hạt ti&ecirc;u 50g, Rắn bỏ đầu ruột, l&agrave;m sạch chặt miếng; rễ c&acirc;y hạt ti&ecirc;u rửa sạch cắt đoạn, th&ecirc;m gừng, h&agrave;nh, dấm, gia vị, nước lượng th&iacute;ch hợp. Đun to lửa cho s&ocirc;i, đun nhỏ lửa cho ch&iacute;n nhừ. Chia ăn 2 &ndash; 3 lần trong ng&agrave;y. D&ugrave;ng cho c&aacute;c trường hợp vi&ecirc;m sưng khớp, lở ngứa, bại liệt.</p>\r\n<p>\r\n	<strong style=\"box-sizing: border-box; border: 0px; margin: 0px; padding: 0px; vertical-align: baseline;\">Lưu &yacute;:</strong></p>\r\n<p>\r\n	Những người hay bị dị ứng, người kh&ocirc;ng được uống rượu như: bị bệnh đường ti&ecirc;u ho&aacute;, tăng huyết &aacute;p, phụ nữ c&oacute; thai&hellip;.. Rượu rắn c&oacute; t&iacute;nh ấm n&oacute;ng, những người thuộc nhiệt chứng (tạng nhiệt, m&aacute;u n&oacute;ng) kh&ocirc;ng n&ecirc;n sử dụng. Rượu rắn tuy tốt với người bị phong thấp, tuy nhi&ecirc;n kh&ocirc;ng n&ecirc;n lạm dụng, chỉ n&ecirc;n d&ugrave;ng 10 ng&agrave;y cho 1 đợt v&agrave; mỗi ng&agrave;y chỉ uống khoảng 25ml v&agrave;o bữa cơm tối. Về thịt rắn c&oacute; s&aacute;ch khuy&ecirc;n người ti&ecirc;u ho&aacute; kh&ocirc;ng tốt kh&ocirc;ng n&ecirc;n d&ugrave;ng&hellip;</p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1428059887', '0', '1413953458', '0', '0');
INSERT INTO `slm_news` VALUES ('117', '0.208015001409543641.jpg', 'a:10:{i:0;s:24:\"0.208015001409543641.jpg\";i:1;s:24:\"0.355217001409543641.jpg\";i:2;s:24:\"0.449734001409543641.jpg\";i:3;s:24:\"0.588136001409543641.jpg\";i:4;s:24:\"0.693813001409543641.jpg\";i:5;s:24:\"0.802984001409543641.jpg\";i:6;s:24:\"0.904536001409543641.jpg\";i:7;s:24:\"0.045973001409543642.jpg\";i:8;s:24:\"0.243832001409543642.jpg\";i:9;s:24:\"0.358645001409543642.jpg\";}', null, '10 loài rắn siêu độc nguy hiểm nhất thế giới', null, null, '12', '', '\r\n	Trong tự nhi&ecirc;n tồn tại những lo&agrave;i rắn độc v&ugrave;a biết ngụy trang vừa tấn c&ocirc;ng nhanh m&agrave; lại sở hữu chất kịch độc chết người đ&aacute;ng sợ.', '<p>\r\n	<strong>Rắn Mamba Đen</strong>: Sống trong c&aacute;c bui c&acirc;y miền đ&ocirc;ng Ch&acirc;u Phi, chiều d&agrave;i trung b&igrave;nh: 2 &ndash; 3m. Ch&uacute;ng hung hăng v&agrave; di chuyển nhanh, m&agrave;u sắc đa dạng. Nọc độc rắn Mamba đen c&oacute; thể giết chết một con người trong 30 ph&uacute;t đến 2 giờ g&acirc;y buồn ngủ, c&aacute;c vấn đề về thần kinh, t&ecirc; liệt, v&agrave; kh&oacute; thở.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.208015001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Mũi Thương</strong>: Sống ở khu vực miền Nam v&agrave; Đ&ocirc;ng Mexico, Trung Mỹ, miền Bắc Colombia v&agrave; Ecuador, c&oacute; chiều d&agrave;i từ 1-2m. Nọc độc của ch&uacute;ng tấn c&ocirc;ng thẳng v&agrave;o m&aacute;u, tạo ra c&aacute;c cục m&aacute;u đ&ocirc;ng g&acirc;y chết người. Con non ph&aacute;t triển b&ecirc;n trong cơ thể của rắn mẹ, v&agrave; một c&aacute; thể rắn c&oacute; thể sinh sản tới 100 rắn con.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.355217001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Boomslang</strong>: Sống ở miền Nam Ch&acirc;u Phi, c&oacute; chiều d&agrave;i trung b&igrave;nh: 1,2- 1,5 m&eacute;t. Ch&uacute;ng c&oacute; khả năng ngụy trang kh&oacute; tin th&agrave;nh một c&agrave;nh c&acirc;y khi săn mồi. Ch&uacute;ng thường ti&ecirc;m từ từ một lượng nọc độc nhỏ để giết con mồi v&agrave; chỉ tấn c&ocirc;ng khi bị k&iacute;ch động.&nbsp;<span style=\"text-decoration: underline;\"><strong>Lo&agrave;i rắn</strong></span>&nbsp;n&agrave;y c&oacute; tuổi đời khoảng 8 năm.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.449734001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Hổ phương Đ&ocirc;ng</strong>: Sống chủ yếu ở nước &Uacute;c, c&oacute; chiều d&agrave;i trung b&igrave;nh: 1- 1,8m. Nọc của rắn Hổ phương Đ&ocirc;ng l&agrave;&nbsp;<span style=\"text-decoration: underline;\"><strong>loại độc tố</strong></span>&nbsp;đặc hiệu tấn c&ocirc;ng v&agrave;o hệ thống thần kinh trung ương con mồi. N&oacute; c&ograve;n g&acirc;y ra tổn thương cơ bắp v&agrave; đ&ocirc;ng m&aacute;u. Với việc ph&acirc;n hủy m&ocirc; cơ, n&oacute; c&oacute; thể dẫn đến hỏng thận.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.588136001409543641.jpg\" /></p>\r\n<p style=\"text-align: center;\">\r\n	<strong>Rắn Lục Vảy Răng Cưa</strong>: Sống ở trung t&acirc;m v&agrave; miền t&acirc;y nam ch&acirc;u &Aacute;, c&oacute; chiều d&agrave;i trung b&igrave;nh: 0,4-0,6m. Lo&agrave;i rắn n&agrave;y rất hung h&atilde;n n&ecirc;n số người chết v&igrave; ch&uacute;ng lại nhiều nhất mặc d&ugrave; nọc của ch&uacute;ng giết &iacute;t hơn 10% những nạn nh&acirc;n kh&ocirc;ng được điều trị kịp thời.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.693813001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Cạp Nong</strong>: Sống ở Đ&ocirc;ng Nam &Aacute;, c&oacute; chiều d&agrave;i trung b&igrave;nh: 1,6m. Lo&agrave;i rắn n&agrave;y th&iacute;ch những nơi ẩm ướt, những nơi hoang vắng, đồng cỏ, v&ugrave;ng trồng trọt, hoặc rừng thưa thấp. Nọc độc của rắn cạp nong c&oacute; thể g&acirc;y t&ecirc; liệt bằng c&aacute;ch tấn c&ocirc;ng v&agrave;o hệ thống thần kinh trung ương.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.802984001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Hổ mang Ch&uacute;a</strong>: Sống ở miền Đ&ocirc;ng v&agrave; Đ&ocirc;ng Nam &Aacute;, c&oacute; chiều d&agrave;i trung b&igrave;nh: 3m. Đ&acirc;y l&agrave; lo&agrave;i rắn độc lớn nhất tr&ecirc;n thế giới, con trưởng th&agrave;nh d&agrave;i tới 4,5 m&eacute;t v&agrave; nặng hơn 20 kg. N&oacute; c&oacute; thể ti&ecirc;m một lượng nọc độc lớn v&agrave;o con mồi, đủ để giết chết một người trong v&ograve;ng 30 ph&uacute;t. C&aacute;c triệu chứng ban đầu chủ yếu li&ecirc;n quan đến thần kinh.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.904536001409543641.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Taipan Ven Biển</strong>: Sống ở miền Bắc nước &Uacute;c, miền Nam Papua New Guinea, v&agrave; miền Nam Indonesia. Ch&uacute;ng c&oacute; chiều d&agrave;i trung b&igrave;nh 2m. Vết cắn của lo&agrave;i rắn n&agrave;y g&acirc;y chết người trong hơn 80% trường hợp v&agrave; nọc của ch&uacute;ng c&oacute; thể giết một người trong v&ograve;ng nửa giờ đồng hồ. Nạn nh&acirc;n sẽ trải qua cảm gi&aacute;c buồn ngủ, mờ mắt, co giật, v&agrave; kh&oacute; thở.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.045973001409543642.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn Taipan</strong>: Sống ở nội địa nước &Uacute;c, c&oacute; chiều d&agrave;i trung b&igrave;nh: 1,8m. Nọc độc từ một vết cắn của lo&agrave;i rắn n&agrave;y đủ để giết 100 người đ&agrave;n &ocirc;ng trưởng th&agrave;nh, tương đương với hơn 200.000 con chuột. C&ograve;n được gọi l&agrave; &ldquo;&aacute;c x&agrave;&rdquo;, Taipan l&agrave; lo&agrave;i rắn độc nhất tr&ecirc;n thế giới.</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.243832001409543642.jpg\" /></p>\r\n<p>\r\n	<strong>Rắn hổ lục đầu gi&aacute;o v&agrave;ng</strong>: Sống ở đảo Ilha de Queimada Grande, hay c&ograve;n gọi l&agrave; Đảo Rắn ở Brazil, c&oacute; chiều d&agrave;i trung b&igrave;nh: 70cm. Lo&agrave;i rắn n&agrave;y c&oacute; chiếc đầu nhọn như mũi gi&aacute;o đặc trưng. Con trưởng th&agrave;nh c&oacute; chiều d&agrave;i chỉ 1,2m. Trong nọc của rắn hổ lục đầu gi&aacute;o v&agrave;ng chứa chất độc g&acirc;y hại m&aacute;u c&oacute; thể l&agrave;m tan m&ocirc; v&agrave; thịt của con mồi k&egrave;m triệu chứng: ph&ugrave; nề, đau cục bộ, buồn n&ocirc;n, n&ocirc;n mửa, tụ m&aacute;u, n&ocirc;n v&agrave; tiểu ra m&aacute;u, suy thận&hellip;</p>\r\n<p style=\"text-align: center;\">\r\n	<img src=\"http://langquetoi.com/images/news/117/700_0.358645001409543642.jpg\" /></p>', '0', '0', '0', '0', '1', '0', null, null, null, null, null, '1409543966', '0', '1409543641', '0', '0');

-- ----------------------------
-- Table structure for slm_products
-- ----------------------------
DROP TABLE IF EXISTS `slm_products`;
CREATE TABLE `slm_products` (
  `id` int(11) NOT NULL AUTO_INCREMENT,
  `name` varchar(255) DEFAULT NULL COMMENT 'Tên sản phẩm',
  `name_alias` varchar(255) DEFAULT NULL,
  `category_id` int(6) DEFAULT NULL COMMENT 'Id của danh mục sản phẩm',
  `category_name` varchar(100) DEFAULT NULL COMMENT 'Tên của danh mục sản phâm',
  `id_ncc` int(11) DEFAULT '1',
  `name_ncc` varchar(255) DEFAULT NULL,
  `parent_id` int(6) DEFAULT NULL,
  `description` text,
  `content` text COMMENT 'Mô tả sản phẩm',
  `store` int(11) DEFAULT NULL COMMENT 'số lượng trong kho',
  `quantity_sell` int(10) DEFAULT '0' COMMENT 'số lượng đã bán',
  `type_price` tinyint(2) DEFAULT '0' COMMENT 'Kiểu hiển thị tiền: 0: có giá, 1: không có giá,liên hệ',
  `contact_price` varchar(255) DEFAULT NULL,
  `price` decimal(10,0) DEFAULT NULL COMMENT 'Giá bán',
  `price_market` decimal(10,0) DEFAULT NULL COMMENT 'giá thị trường',
  `price_input` decimal(10,0) DEFAULT NULL COMMENT 'giá nhập',
  `discount` int(11) NOT NULL COMMENT '% giảm giá',
  `images` varchar(255) DEFAULT NULL COMMENT 'Ảnh chính',
  `images_other` text COMMENT 'Các ảnh khác lưu dạng serialize',
  `images_other_temp` text,
  `start_time` int(11) DEFAULT NULL,
  `end_time` int(11) DEFAULT NULL,
  `approval` tinyint(2) DEFAULT NULL,
  `status` tinyint(1) DEFAULT '1' COMMENT 'Trạng thái của sản phẩm 0 : Ẩn : 1 :Hiện',
  `page_keywords` varchar(1000) DEFAULT NULL COMMENT 'Keyword tìm kiếm',
  `page_descriptions` varchar(2000) DEFAULT NULL COMMENT 'Description tìm kiếm',
  `position` int(6) DEFAULT NULL,
  `hot` tinyint(2) DEFAULT '0' COMMENT '1: Hang hot',
  `create_time` int(11) DEFAULT NULL,
  `create_user_id` int(6) DEFAULT NULL,
  `create_user_name` varchar(100) DEFAULT NULL,
  `modify_time` int(11) DEFAULT NULL,
  `modify_user_id` int(6) DEFAULT NULL,
  `modify_user_name` varchar(100) DEFAULT NULL,
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=511 DEFAULT CHARSET=utf8;

-- ----------------------------
-- Records of slm_products
-- ----------------------------
INSERT INTO `slm_products` VALUES ('458', 'Nấm rơm', '500g', '26', 'Nấm rơm', '2', 'Đại lý sim khác', '0', null, '<p style=\"text-align: justify;\">\r\n	So với rau cải hay tr&aacute;i c&acirc;y th&igrave; thời gian bảo quản của nấm ở nhiệt độ b&igrave;nh thường ngắn hơn nhiều, đặc biệt l&agrave; khi chất đống hoặc đổ chồng l&ecirc;n nhau trong th&ugrave;ng hoặc cần x&eacute;. Một số lo&agrave;i nấm c&oacute; thể tiếp tục ph&aacute;t triển trở th&agrave;nh dạng trưởng th&agrave;nh, như nấm rơm. Tai nấm từ dạng b&uacute;p chuyển sang h&igrave;nh d&ugrave;. N&oacute;i chung phẩm chất giảm v&agrave; kh&ocirc;ng được người ti&ecirc;u d&ugrave;ng ưa chuộng.&nbsp;<br />\r\n	<br />\r\n	<strong>Mẹo mua nấm</strong><br />\r\n	<br />\r\n	Mua loại c&oacute; m&agrave;u sắc tươi, m&ugrave;i thơm hấp dẫn. Tr&aacute;nh chọn nấm bị dập n&aacute;t, c&oacute; m&ugrave;i &ocirc;i. Vết cắt rỉ ra chất trắng sữa l&agrave; dấu hiệu của nấm độc.<br />\r\n	&nbsp;<br />\r\n	Tr&aacute;nh những c&acirc;y nấm c&oacute; vết th&acirc;m hoặc bị nhăn tr&ecirc;n ch&oacute;p, kh&ocirc;ng n&ecirc;n chọn nấm bị nhớt.&nbsp;Khi nấm thực sự tươi, bạn sẽ nh&igrave;n thấy một lớp tơ mỏng như giấy tr&ecirc;n nấm.</p>\r\n<div style=\"text-align: justify;\">\r\n	Bạn n&ecirc;n chọn cuống nấm chắc chắn, m&agrave;u sắc đồng đều. Khi nấm bắt đầu gi&agrave;, cuống nấm sẽ tr&ocirc;ng giống như gỗ mục.&nbsp;Nếu nấm đ&atilde; nở v&agrave; bạn c&oacute; thể nh&igrave;n thấy những l&aacute; tia tr&ecirc;n mũ nấm th&igrave; ch&uacute;ng phải th&agrave;nh chuỗi đều, đẹp v&agrave; kh&ocirc; r&aacute;o.</div>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Chế biến nấm đ&uacute;ng c&aacute;ch</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<em>- L&agrave;m sạch nấm:</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Nấm sống trong m&ocirc;i trường tuyệt đối sạch, th&acirc;n nấm lại ở dạng xốp v&agrave; sợi n&ecirc;n khi rửa nấm sẽ l&agrave;m nước đọng lại khiến cho nấm kh&ocirc;ng c&ograve;n được ngọt. V&igrave; vậy kh&ocirc;ng n&ecirc;n rửa kỹ. Đặt nấm trong một c&aacute;i chảo v&agrave; rửa nhanh ch&uacute;ng dưới v&ograve;i nước lạnh, sau đ&oacute;, phải thấm nấm thật kh&ocirc; r&aacute;o.</p>\r\n<p style=\"text-align: justify;\">\r\n	Khi chế biến c&aacute;c loại nấm, bạn n&ecirc;n nhẹ nh&agrave;ng tr&aacute;nh l&agrave;m nấm bị dập n&aacute;t dễ nhiễm khuẩn.<br />\r\n	&nbsp;<br />\r\n	Tuy nhi&ecirc;n, một số loại nấm bắt buộc phải vệ sinh nếu trong qu&aacute; tr&igrave;nh vận chuyển để g&acirc;y bẩn v&agrave;o, nhưng n&ecirc;n rửa dưới v&ograve;i nước dạng hơi sương chứ kh&ocirc;ng rửa trực tiếp nước v&agrave;o th&acirc;n nấm sẽ l&agrave;m hỏng thịt nấm.<br />\r\n	&nbsp;<br />\r\n	Khi sử dụng nấm tuyệt đối phải cắt bỏ ch&acirc;n (cắt gốc) v&igrave; ch&acirc;n nấm l&agrave; nơi tiếp x&uacute;c với chất dinh dưỡng, phần bọc v&agrave; nu&ocirc;i c&acirc;y giống l&agrave; một số chất v&ocirc; cơ m&agrave; ch&uacute;ng ta kh&ocirc;ng n&ecirc;n sử dụng.<br />\r\n	&nbsp;<br />\r\n	Cần ăn nấm được nấu ch&iacute;n ho&agrave;n to&agrave;n, tức l&agrave; khoảng 5 - 10 ph&uacute;t sau khi đun s&ocirc;i. Sau khi ăn nấm xong kh&ocirc;ng n&ecirc;n d&ugrave;ng ngay đồ uống lạnh &nbsp;như tr&agrave; đ&aacute; hoặc c&agrave; ph&ecirc; đ&aacute;, bởi v&igrave; nấm mang t&iacute;nh bổ &acirc;m n&ecirc;n uống ngay đồ lạnh sau đ&oacute; th&igrave; dễ bị lạnh bụng.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>- Nấu nấm:</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Để d&ugrave;ng nấm kh&ocirc;, cho nước ấm (c&oacute; thể d&ugrave;ng nước luộc thịt, rượu&hellip;) v&agrave;o ng&acirc;m tối thiểu 30 ph&uacute;t. Gạn nước, rửa sạch v&agrave; thấm bằng khăn giấy. Nấm đ&ocirc;ng c&ocirc; thường c&oacute; nhiều c&aacute;t bụi b&ecirc;n trong tai nấm n&ecirc;n cần rửa thật kỹ phần tai nấm n&agrave;y.<br />\r\n	<br />\r\n	Dung dịch đ&atilde; d&ugrave;ng để ng&acirc;m nấm kh&ocirc; c&oacute; thể th&ecirc;m v&agrave;o canh, nước hầm. Lọc dung dịch n&agrave;y qua một tấm vải thưa gấp đ&ocirc;i hoặc khăn vải trước khi d&ugrave;ng.</p>\r\n<div style=\"text-align: justify;\">\r\n	Vết cắt ở nấm sẽ chuyển m&agrave;u đen khi tiếp x&uacute;c với kh&ocirc;ng kh&iacute;. V&igrave; vậy, nếu sử dụng nấm tươi, tốt nhất l&agrave; cắt ngay trước khi d&ugrave;ng hoặc d&ugrave;ng nước cốt chanh b&ocirc;i v&agrave;o vết cắt.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	C&oacute; thể l&agrave;m bột nấm dễ d&agrave;ng bằng c&aacute;ch xay nấm kh&ocirc; trong m&aacute;y xay c&agrave; ph&ecirc;, m&aacute;y xay gia vị hoặc m&aacute;y xay sinh tố&hellip; Bột nấm c&oacute; thể sử dụng để nấu soup, n&ecirc;m v&agrave;o nước hầm thịt, hoặc nước xốt.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Tr&aacute;nh sử dụng xoong chảo nh&ocirc;m khi nấu c&aacute;c loại nấm s&aacute;ng m&agrave;u. Nh&ocirc;m sẽ l&agrave;m ngả m&agrave;u nấm.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Nấm chứa rất nhiều nước, n&ecirc;n bạn đừng th&ecirc;m nước khi nấu ch&uacute;ng trong l&ograve; vi ba, cũng đừng để đầy nấm trong t&ocirc;, v&igrave; c&oacute; thể tr&agrave;n nước trong l&ograve;.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Đừng bỏ ph&iacute; cuống nấm, h&atilde;y cắt tỉa thật sạch v&agrave; để d&agrave;nh trong tủ lạnh để nấu soup hoặc nước hầm.</div>\r\n<p style=\"text-align: justify;\">\r\n	Trước khi chế biến c&aacute;c loại nấm như đ&ocirc;ng c&ocirc;, b&agrave;o ngư&hellip; n&ecirc;n ướp ch&uacute;t gia vị rồi hấp sơ hoặc x&agrave;o qua v&agrave; n&ecirc;m nếm gia vị cho vừa ăn. C&aacute;ch n&agrave;y sẽ gi&uacute;p nấm thơm ngon, đậm đ&agrave; hơn sau khi đ&atilde; chế biến th&agrave;nh n&oacute;n ăn.<br />\r\n	<br />\r\n	Tr&aacute;nh nấu nấm qu&aacute; l&acirc;u trong qu&aacute; tr&igrave;nh chế biến. Với c&aacute;c m&oacute;n x&agrave;o chỉ n&ecirc;n cho nấm v&agrave;o khi m&oacute;n ăn đ&atilde; gần ch&iacute;n để giữ được độ gi&ograve;n, dai v&agrave; những chất dinh dưỡng c&oacute; trong nấm.<br />\r\n	<br />\r\n	Sử dụng nấm khi ăn lẩu gần giống với c&aacute;ch d&ugrave;ng rau với lẩu. Chỉ khi ăn mới cho nấm v&agrave;o. Nước lẩu s&ocirc;i khoảng ba ph&uacute;t l&agrave; nấm đ&atilde; c&oacute; thể ăn được. Ri&ecirc;ng nấm kim ch&acirc;m chỉ cần nh&uacute;ng qua v&agrave; ăn ngay khi nấm vừa ch&iacute;n t&aacute;i. Nếu để qu&aacute; l&acirc;u, nấm sẽ mềm nhũn ăn mất ngon.</p>\r\n', null, '0', '0', null, '45000', null, '0', '0', '0.981581001419083302.jpg', 'a:1:{i:0;s:24:\"0.981581001419083302.jpg\";}', null, '1334376640', null, null, '1', '', '', '0', '0', null, '1', 'admin', '1419687576', '1', 'admin');
INSERT INTO `slm_products` VALUES ('486', 'Nấm ngô - hoàng kim', '500g', '43', 'Nấm hoàng kim', '1', null, '0', null, '<p>\r\n	Nấm Ng&ocirc; (hay c&ograve;n gọi l&agrave; Nấm Ho&agrave;ng Kim) l&agrave; một loại nấm gi&agrave;u dinh dưỡng. Nấm c&oacute; m&ugrave;i thơm, chứa nhiều protein do đ&oacute; rất tốt cho sức khỏe, người bệnh ăn nấm Ng&ocirc; v&agrave;o mau phục hồi sức khỏe. Đặc biệt nấm c&oacute; t&aacute;c dụng bổ thận, tr&aacute;ng dương n&ecirc;n rất tốt d&ugrave;ng cho nam giới, nhất l&agrave; nam giới bị nhược dương. Sản phẩm được ph&acirc;n phối bởi C&ocirc;ng ty Cổ phần ph&aacute;t triển Nấm Việt Nam Nấm Việt Nam l&agrave; thương hiệu Nấm sạch của người Việt Nam v&agrave; d&agrave;nh cho người Việt Nam. Nấm Việt Nam cam kết chỉ cung cấp duy nhất c&aacute;c loại nấm Việt Nam c&oacute; nguồn gốc r&otilde; r&agrave;ng, được nu&ocirc;i trồng từ c&aacute;c nh&agrave; m&aacute;y, n&ocirc;ng trại Việt Nam, đủ điều kiện Vệ sinh an to&agrave;n thực phẩm.</p>\r\n<p>\r\n	Nấm Ho&agrave;ng kim l&agrave; loại thực phẩm c&oacute; gi&aacute; trị dinh dưỡng cao,h&agrave;m lượng protein chỉ đứng sau thịt ,c&aacute;, gi&agrave;u c&aacute;c chất kho&aacute;ng v&aacute; c&aacute;c amino acid tan trong nước, amino acid kh&ocirc;ng thể thay thế như : lyzin,tryptophan,c&aacute;c amino acid chứa nh&oacute;m lưu huỳnh ,ngo&agrave;i ra ch&uacute;ng c&ograve;n chứa một lượng lớn c&aacute;c vitamin quan trọng.<br />\r\n	<br />\r\n	Nấm Ho&agrave;ng Kim ( B&agrave;o ngư kim đ&iacute;nh ) l&agrave; nấm ăn ngon nổi tiếng,c&oacute; gi&aacute; trị dinh dưỡng cao,ngo&agrave;i ra c&ograve;n ngăn ngừa một số bệnh như: giảm cholesterol trong m&aacute;u,tiểu đường ,b&eacute;o ph&igrave;,tim mạch,kh&aacute;ng ung thư&hellip;</p>\r\n<p style=\"text-align: justify;\">\r\n	So với rau cải hay tr&aacute;i c&acirc;y th&igrave; thời gian bảo quản của nấm ở nhiệt độ b&igrave;nh thường ngắn hơn nhiều, đặc biệt l&agrave; khi chất đống hoặc đổ chồng l&ecirc;n nhau trong th&ugrave;ng hoặc cần x&eacute;. Một số lo&agrave;i nấm c&oacute; thể tiếp tục ph&aacute;t triển trở th&agrave;nh dạng trưởng th&agrave;nh, như nấm rơm. Tai nấm từ dạng b&uacute;p chuyển sang h&igrave;nh d&ugrave;. N&oacute;i chung phẩm chất giảm v&agrave; kh&ocirc;ng được người ti&ecirc;u d&ugrave;ng ưa chuộng.&nbsp;<br />\r\n	<br />\r\n	<strong>Mẹo mua nấm</strong><br />\r\n	<br />\r\n	Mua loại c&oacute; m&agrave;u sắc tươi, m&ugrave;i thơm hấp dẫn. Tr&aacute;nh chọn nấm bị dập n&aacute;t, c&oacute; m&ugrave;i &ocirc;i. Vết cắt rỉ ra chất trắng sữa l&agrave; dấu hiệu của nấm độc.<br />\r\n	&nbsp;<br />\r\n	Tr&aacute;nh những c&acirc;y nấm c&oacute; vết th&acirc;m hoặc bị nhăn tr&ecirc;n ch&oacute;p, kh&ocirc;ng n&ecirc;n chọn nấm bị nhớt.&nbsp;Khi nấm thực sự tươi, bạn sẽ nh&igrave;n thấy một lớp tơ mỏng như giấy tr&ecirc;n nấm.</p>\r\n<div style=\"text-align: justify;\">\r\n	Bạn n&ecirc;n chọn cuống nấm chắc chắn, m&agrave;u sắc đồng đều. Khi nấm bắt đầu gi&agrave;, cuống nấm sẽ tr&ocirc;ng giống như gỗ mục.&nbsp;Nếu nấm đ&atilde; nở v&agrave; bạn c&oacute; thể nh&igrave;n thấy những l&aacute; tia tr&ecirc;n mũ nấm th&igrave; ch&uacute;ng phải th&agrave;nh chuỗi đều, đẹp v&agrave; kh&ocirc; r&aacute;o.</div>\r\n<p style=\"text-align: justify;\">\r\n	<strong>Chế biến nấm đ&uacute;ng c&aacute;ch</strong></p>\r\n<p style=\"text-align: justify;\">\r\n	<em>- L&agrave;m sạch nấm:</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Nấm sống trong m&ocirc;i trường tuyệt đối sạch, th&acirc;n nấm lại ở dạng xốp v&agrave; sợi n&ecirc;n khi rửa nấm sẽ l&agrave;m nước đọng lại khiến cho nấm kh&ocirc;ng c&ograve;n được ngọt. V&igrave; vậy kh&ocirc;ng n&ecirc;n rửa kỹ. Đặt nấm trong một c&aacute;i chảo v&agrave; rửa nhanh ch&uacute;ng dưới v&ograve;i nước lạnh, sau đ&oacute;, phải thấm nấm thật kh&ocirc; r&aacute;o.</p>\r\n<p style=\"text-align: justify;\">\r\n	Khi chế biến c&aacute;c loại nấm, bạn n&ecirc;n nhẹ nh&agrave;ng tr&aacute;nh l&agrave;m nấm bị dập n&aacute;t dễ nhiễm khuẩn.<br />\r\n	&nbsp;<br />\r\n	Tuy nhi&ecirc;n, một số loại nấm bắt buộc phải vệ sinh nếu trong qu&aacute; tr&igrave;nh vận chuyển để g&acirc;y bẩn v&agrave;o, nhưng n&ecirc;n rửa dưới v&ograve;i nước dạng hơi sương chứ kh&ocirc;ng rửa trực tiếp nước v&agrave;o th&acirc;n nấm sẽ l&agrave;m hỏng thịt nấm.<br />\r\n	&nbsp;<br />\r\n	Khi sử dụng nấm tuyệt đối phải cắt bỏ ch&acirc;n (cắt gốc) v&igrave; ch&acirc;n nấm l&agrave; nơi tiếp x&uacute;c với chất dinh dưỡng, phần bọc v&agrave; nu&ocirc;i c&acirc;y giống l&agrave; một số chất v&ocirc; cơ m&agrave; ch&uacute;ng ta kh&ocirc;ng n&ecirc;n sử dụng.<br />\r\n	&nbsp;<br />\r\n	Cần ăn nấm được nấu ch&iacute;n ho&agrave;n to&agrave;n, tức l&agrave; khoảng 5 - 10 ph&uacute;t sau khi đun s&ocirc;i. Sau khi ăn nấm xong kh&ocirc;ng n&ecirc;n d&ugrave;ng ngay đồ uống lạnh &nbsp;như tr&agrave; đ&aacute; hoặc c&agrave; ph&ecirc; đ&aacute;, bởi v&igrave; nấm mang t&iacute;nh bổ &acirc;m n&ecirc;n uống ngay đồ lạnh sau đ&oacute; th&igrave; dễ bị lạnh bụng.&nbsp;</p>\r\n<p style=\"text-align: justify;\">\r\n	<em>- Nấu nấm:</em></p>\r\n<p style=\"text-align: justify;\">\r\n	Để d&ugrave;ng nấm kh&ocirc;, cho nước ấm (c&oacute; thể d&ugrave;ng nước luộc thịt, rượu&hellip;) v&agrave;o ng&acirc;m tối thiểu 30 ph&uacute;t. Gạn nước, rửa sạch v&agrave; thấm bằng khăn giấy. Nấm đ&ocirc;ng c&ocirc; thường c&oacute; nhiều c&aacute;t bụi b&ecirc;n trong tai nấm n&ecirc;n cần rửa thật kỹ phần tai nấm n&agrave;y.<br />\r\n	<br />\r\n	Dung dịch đ&atilde; d&ugrave;ng để ng&acirc;m nấm kh&ocirc; c&oacute; thể th&ecirc;m v&agrave;o canh, nước hầm. Lọc dung dịch n&agrave;y qua một tấm vải thưa gấp đ&ocirc;i hoặc khăn vải trước khi d&ugrave;ng.</p>\r\n<div style=\"text-align: justify;\">\r\n	Vết cắt ở nấm sẽ chuyển m&agrave;u đen khi tiếp x&uacute;c với kh&ocirc;ng kh&iacute;. V&igrave; vậy, nếu sử dụng nấm tươi, tốt nhất l&agrave; cắt ngay trước khi d&ugrave;ng hoặc d&ugrave;ng nước cốt chanh b&ocirc;i v&agrave;o vết cắt.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	C&oacute; thể l&agrave;m bột nấm dễ d&agrave;ng bằng c&aacute;ch xay nấm kh&ocirc; trong m&aacute;y xay c&agrave; ph&ecirc;, m&aacute;y xay gia vị hoặc m&aacute;y xay sinh tố&hellip; Bột nấm c&oacute; thể sử dụng để nấu soup, n&ecirc;m v&agrave;o nước hầm thịt, hoặc nước xốt.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Tr&aacute;nh sử dụng xoong chảo nh&ocirc;m khi nấu c&aacute;c loại nấm s&aacute;ng m&agrave;u. Nh&ocirc;m sẽ l&agrave;m ngả m&agrave;u nấm.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Nấm chứa rất nhiều nước, n&ecirc;n bạn đừng th&ecirc;m nước khi nấu ch&uacute;ng trong l&ograve; vi ba, cũng đừng để đầy nấm trong t&ocirc;, v&igrave; c&oacute; thể tr&agrave;n nước trong l&ograve;.</div>\r\n<div style=\"text-align: justify;\">\r\n	&nbsp;</div>\r\n<div style=\"text-align: justify;\">\r\n	Đừng bỏ ph&iacute; cuống nấm, h&atilde;y cắt tỉa thật sạch v&agrave; để d&agrave;nh trong tủ lạnh để nấu soup hoặc nước hầm.</div>\r\n<p style=\"text-align: justify;\">\r\n	Trước khi chế biến c&aacute;c loại nấm như đ&ocirc;ng c&ocirc;, b&agrave;o ngư&hellip; n&ecirc;n ướp ch&uacute;t gia vị rồi hấp sơ hoặc x&agrave;o qua v&agrave; n&ecirc;m nếm gia vị cho vừa ăn. C&aacute;ch n&agrave;y sẽ gi&uacute;p nấm thơm ngon, đậm đ&agrave; hơn sau khi đ&atilde; chế biến th&agrave;nh n&oacute;n ăn.<br />\r\n	<br />\r\n	Tr&aacute;nh nấu nấm qu&aacute; l&acirc;u trong qu&aacute; tr&igrave;nh chế biến. Với c&aacute;c m&oacute;n x&agrave;o chỉ n&ecirc;n cho nấm v&agrave;o khi m&oacute;n ăn đ&atilde; gần ch&iacute;n để giữ được độ gi&ograve;n, dai v&agrave; những chất dinh dưỡng c&oacute; trong nấm.<br />\r\n	<br />\r\n	Sử dụng nấm khi ăn lẩu gần giống với c&aacute;ch d&ugrave;ng rau với lẩu. Chỉ khi ăn mới cho nấm v&agrave;o. Nước lẩu s&ocirc;i khoảng ba ph&uacute;t l&agrave; nấm đ&atilde; c&oacute; thể ăn được. Ri&ecirc;ng nấm kim ch&acirc;m chỉ cần nh&uacute;ng qua v&agrave; ăn ngay khi nấm vừa ch&iacute;n t&aacute;i. Nếu để qu&aacute; l&acirc;u, nấm sẽ mềm nhũn ăn mất ngon.</p>\r\n', null, '0', '0', null, '35000', null, '25000', '0', '0.721879001419233810.jpg', 'a:1:{i:0;s:24:\"0.721879001419233810.jpg\";}', null, '1419083362', null, null, '1', '', '', '1', '0', null, '1', 'admin', '1419687639', '1', 'admin');
